        [1847] = {20187602,1,1,"孙权",306,0,"听闻关云长将军家有一女，不如子瑜去替我儿孙登向关小姐求亲，以示我们孙刘联盟的诚意，如何？","听闻关云长将军家有一女，不如子瑜去替我儿孙登向关小姐求亲，以示我们孙刘联盟的诚意，如何？",1,"","","诸葛瑾",319,},
