        [1859] = {20187703,3,2,"诸葛亮",203,0,"虎父无犬子。将军颇有当年云长将军之风，蜀汉天下有关将军父子相助，实乃吾之幸事也。","虎父无犬子。将军颇有当年云长将军之风，蜀汉天下有关将军父子相助，实乃吾之幸事也。",1,"","","关索",239,},
        [1860] = {20187801,1,1,"陆逊",311,0,"当今英雄各据一方互相争雄，要想战胜敌人，我们需要大量的人马。","当今英雄各据一方互相争雄，要想战胜敌人，我们需要大量的人马。",1,"","","孙权",306,},
