        [1872] = {20187902,3,1,"吴国太",322,0,"希望那玄德可以好好待我女儿。","希望那玄德可以好好待我女儿。",1,"","","孙尚香",310,},
        [1873] = {20188001,1,1,"吕布",403,0,"文远！你是我的得力部下，我受奸人所害被曹操捉住，你快带剩下的兄弟们来救我！","文远！你是我的得力部下，我受奸人所害被曹操捉住，你快带剩下的兄弟们来救我！",1,"","","张辽",109,},
        [1874] = {20188001,2,2,"曹操",103,0,"文远将军跟着吕布实在是掩盖住了你的才华，以后就跟着我吧，你将会是我麾下一员不可多得的猛将。","文远将军跟着吕布实在是掩盖住了你的才华，以后就跟着我吧，你将会是我麾下一员不可多得的猛将。",1,"","","张辽",109,},
