        [1876] = {20188002,1,2,"关羽",205,0,"休得多言，我是不会投降的！","休得多言，我是不会投降的！",1,"","","张辽",109,},
        [1877] = {20188002,2,1,"张辽",109,0,"云长兄，你先投降，有我在不用怕，等有了刘备的消息，你想走就走。","云长兄，你先投降，有我在不用怕，等有了刘备的消息，你想走就走。",1,"","","关羽",205,},
