        [1879] = {20188002,4,1,"张辽",109,0,"信我的，放心吧。","信我的，放心吧。",1,"","","关羽",205,},
        [1880] = {20188003,1,1,"张辽",109,0,"此次胜利，主要是我军准备充分，加上将士们置之死地而后生的勇气，才能大获全胜。","此次胜利，主要是我军准备充分，加上将士们置之死地而后生的勇气，才能大获全胜。",1,"","","",0,},
        [1881] = {20188003,2,2,"孙权",306,0,"张辽真乃猛将，八百骑兵竟能击败我大军，真是轻敌了。","张辽真乃猛将，八百骑兵竟能击败我大军，真是轻敌了。",1,"","","张辽",109,},
        [1882] = {20188003,3,1,"孙权",306,0,"据说小儿夜哭时听闻张辽之名便能止住。经此一役，张辽已经威震江东了。","据说小儿夜哭时听闻张辽之名便能止住。经此一役，张辽已经威震江东了。",1,"","","张辽",109,},
        [1883] = {20188101,1,1,"曹植",116,0,"吾虽薄德，位为籓侯，犹庶几戮力上国，流惠下民，建永世之业，流金石之功。","吾虽薄德，位为籓侯，犹庶几戮力上国，流惠下民，建永世之业，流金石之功。",1,"","","杨修",131,},
        [1884] = {20188101,2,1,"曹植",116,0,"岂徒以翰墨为勋绩，辞颂为君子哉？","岂徒以翰墨为勋绩，辞颂为君子哉？",1,"","","杨修",131,},
        [1885] = {20188101,3,1,"曹植",116,0,"若吾志不果，吾道不行，亦将采史官之实录，辩时俗之得失，定仁义之衷，成一家之言。","若吾志不果，吾道不行，亦将采史官之实录，辩时俗之得失，定仁义之衷，成一家之言。",1,"","","杨修",131,},
        [1886] = {20188101,4,1,"曹植",116,0,"虽未能藏之名山，将以传之同好，此要之白首，岂可以今日论乎！其言之不怍，恃惠子之知我也。","虽未能藏之名山，将以传之同好，此要之白首，岂可以今日论乎！其言之不怍，恃惠子之知我也。",1,"","","杨修",131,},
        [1887] = {20188101,5,2,"杨修",131,0,"谢子建赏识，士为知己者死而无憾，不成功，便成仁。","谢子建赏识，士为知己者死而无憾，不成功，便成仁。",1,"","","曹植",116,},
        [1888] = {20188102,1,1,"司马懿",101,0,"丞相欲试曹丕和曹植的才华，命其各出邺城城门，私下里让人吩咐看守大门的士兵不准放行。","丞相欲试曹丕和曹植的才华，命其各出邺城城门，私下里让人吩咐看守大门的士兵不准放行。",1,"","","杨修",131,},
