        [2136] = {1804,1,2,"张邈",138,0,"将军果然了得，竟然胜了我！我愿意将这把宝刀送给你！","将军果然了得，竟然胜了我！我愿意将这把宝刀送给你！",1,"","","主角",1,},
        [2137] = {1804,2,1,"主角",1,0,"那还是算了，你这把宝刀，被我打得都卷边了，你还是自己留着切葱吧！","那还是算了，你这把宝刀，被我打得都卷边了，你还是自己留着切葱吧！",1,"","","",999,},
        [2138] = {1804,3,2,"张邈",138,0,"没想到我八厨张邈，也有被人嫌弃的一天！","没想到我八厨张邈，也有被人嫌弃的一天！",1,"","","",999,},
        [2139] = {1805,1,2,"于禁",119,0,"主公且在城外，容我们先入城！","主公且在城外，容我们先入城！",1,"","","曹操",103,},
        [2140] = {1805,2,1,"曹操",103,0,"我不自往，谁敢向前！","我不自往，谁敢向前！",1,"","","于禁",119,},
        [2141] = {1805,3,1,"主角",1,0,"曹操呢？怎么一眨眼就不见了，刚才还听到他和人说话呢！","曹操呢？怎么一眨眼就不见了，刚才还听到他和人说话呢！",1,"","","张星彩",216,},
        [2142] = {1805,4,2,"张星彩",216,0,"主公，这曹操误信了陈宫和吕布的奸计，已经领兵冲进城去了！","主公，这曹操误信了陈宫和吕布的奸计，已经领兵冲进城去了！",1,"","","",999,},
        [2143] = {1805,5,2,"张星彩",216,0,"里面火光冲天四处浓烟，不知道曹操怎么样了！刚才遇到乐进李典、典韦等人正四处追寻曹操！","里面火光冲天四处浓烟，不知道曹操怎么样了！刚才遇到乐进李典、典韦等人正四处追寻曹操！",1,"","","",999,},
        [2144] = {1805,6,1,"主角",1,0,"我们打败吕布，快去救人。","我们打败吕布，快去救人。",1,"","","",999,},
        [2145] = {1806,1,2,"吕布",403,0,"哈哈，这一战打得痛快！小英雄，你我也不是外人，若不是你，哪有我和貂蝉的今天！罢了，就看在你的面上，今日就放过这曹操！","哈哈，这一战打得痛快！小英雄，你我也不是外人，若不是你，哪有我和貂蝉的今天！罢了，就看在你的面上，今日就放过这曹操！",1,"","","主角",1,},
        [2146] = {1806,2,1,"主角",1,0,"好说，吕温侯果然爽快！","好说，吕温侯果然爽快！",1,"","","吕布",403,},
        [2147] = {1806,3,2,"曹操",103,0,"刚才城门上崩下一块火梁，正砸中我手臂，手臂须发，尽被烧伤！","刚才城门上崩下一块火梁，正砸中我手臂，手臂须发，尽被烧伤！",1,"","","主角",1,},
        [2148] = {1806,4,2,"曹操",103,0,"若不是小英雄及时赶到，打败吕布，今日当真危险！这块名将令就送给小英雄以表谢意！","若不是小英雄及时赶到，打败吕布，今日当真危险！这块名将令就送给小英雄以表谢意！",1,"","","",999,},
        [2149] = {1806,5,1,"主角",1,0,"请曹兄遵守诺言撤兵徐州！","请曹兄遵守诺言撤兵徐州！",1,"","","",999,},
        [2150] = {1806,6,2,"曹操",103,0,"小英雄尽管放心，我早已派人从徐州撤兵，上次避而不见，还请小英雄见谅。","小英雄尽管放心，我早已派人从徐州撤兵，上次避而不见，还请小英雄见谅。",1,"","","",999,},
        [2151] = {1806,7,1,"主角",1,0,"好，既然曹兄信守诺言，那我也就不计较那么多了。曹兄，告辞，我要回徐州找大哥。","好，既然曹兄信守诺言，那我也就不计较那么多了。曹兄，告辞，我要回徐州找大哥。",1,"","","",999,},
        [2152] = {1806,8,2,"曹操",103,0,"小英雄慢走！这次误中匹夫之计，这濮阳城，我迟早要夺回来！","小英雄慢走！这次误中匹夫之计，这濮阳城，我迟早要夺回来！",1,"","","",999,},
        [2153] = {1901,1,2,"陶谦",434,0,"来人速将徐州大印拿来！","来人速将徐州大印拿来！",1,"","","刘备",202,},
        [2154] = {1901,2,1,"刘备",202,0,"陶公这是何意？","陶公这是何意？",1,"","","",999,},
        [2155] = {1901,3,2,"陶谦",434,0,"玄德，你仪表轩昂，语言豁达，德广才高，又是汉室宗亲之后，我情愿将徐州相让！","玄德，你仪表轩昂，语言豁达，德广才高，又是汉室宗亲之后，我情愿将徐州相让！",1,"","","",999,},
        [2156] = {1901,4,1,"刘备",202,0,"此时绝不敢应命。刘备虽汉室宗亲，但功德微博，不敢领命。陶公何不让给袁绍？","此时绝不敢应命。刘备虽汉室宗亲，但功德微博，不敢领命。陶公何不让给袁绍？",1,"","","",999,},
        [2157] = {1901,5,2,"陶谦",434,0,"袁绍不过是冢中枯木，何足挂齿！他怎能与君相比！","袁绍不过是冢中枯木，何足挂齿！他怎能与君相比！",1,"","","",999,},
        [2158] = {1901,6,1,"刘备",202,0,"我此次带兵前来相救徐州，是为大义，绝不敢有半分私心。陶公所托，恕备不敢领受。","我此次带兵前来相救徐州，是为大义，绝不敢有半分私心。陶公所托，恕备不敢领受。",1,"","","",999,},
        [2159] = {1902,1,2,"陶谦",434,0,"小英雄年少有为，胸有大志，陶谦想请小英雄帮一个忙。","小英雄年少有为，胸有大志，陶谦想请小英雄帮一个忙。",1,"","","主角",1,},
        [2160] = {1902,2,1,"主角",1,0,"请陶公直言。","请陶公直言。",1,"","","",999,},
        [2161] = {1902,3,2,"陶谦",434,0,"今天下扰乱，老夫年迈，儿子不才，不堪国家重任。欲将这徐州太守之位传给刘备，谁知刘备竟不肯受，请小英雄帮忙劝劝刘备。","今天下扰乱，老夫年迈，儿子不才，不堪国家重任。欲将这徐州太守之位传给刘备，谁知刘备竟不肯受，请小英雄帮忙劝劝刘备。",1,"","","",999,},
        [2162] = {1902,4,1,"主角",1,0,"奇怪，你为什么不传给你儿子呢！","奇怪，你为什么不传给你儿子呢！",1,"","","",999,},
        [2163] = {1902,5,2,"陶谦",434,0,"小英雄有所不知，我有两子，长子商，次子鹰，其才皆不堪任。这可怎生是好？（说罢老泪纵横）","小英雄有所不知，我有两子，长子商，次子鹰，其才皆不堪任。这可怎生是好？（说罢老泪纵横）",1,"","","",999,},
        [2164] = {1902,6,1,"主角",1,0,"陶公别急，我这就去劝我大哥！","陶公别急，我这就去劝我大哥！",1,"","","",999,},
        [2165] = {1903,1,2,"关羽",205,0,"四弟，大哥知道你要来劝，让我和翼德拦着你。","四妹，大哥知道你要来劝，让我和翼德拦着你。",1,"","","主角",1,},
        [2166] = {1903,2,1,"主角",1,0,"二哥，得罪了！正好借此机会我们比试一番。","二哥，得罪了！正好借此机会我们比试一番。",1,"","","",999,},
        [2167] = {1904,1,1,"张星彩",216,0,"爹爹，你好生糊涂，拦着我主公做什么？","爹爹，你好生糊涂，拦着我主公做什么？",1,"","","张飞",206,},
        [2168] = {1904,2,2,"张飞",206,0,"乖女儿，军令在前，你别怪爹爹。","乖女儿，军令在前，你别怪爹爹。",1,"","","",999,},
        [2169] = {1904,3,1,"张星彩",216,0,"那等会战斗的时候，你可不许伤着我主公。","那等会战斗的时候，你可不许伤着我主公。",1,"","","",999,},
        [2170] = {1904,4,2,"张飞",206,0,"好好好，我的宝贝心肝说的话，爹爹一定照办！放心，爹爹会点到为止的。","好好好，我的宝贝心肝说的话，爹爹一定照办！放心，爹爹会点到为止的。",1,"","","",999,},
        [2171] = {1905,1,1,"主角",1,0,"大哥，这徐州素来为四争之地，北有曹操占据兖州，兵多粮广；南有袁术，虎视眈眈。倘若你不来接徐州之位，恐怕徐州百姓危矣。","大哥，这徐州素来为四争之地，北有曹操占据兖州，兵多粮广；南有袁术，虎视眈眈。倘若你不来接徐州之位，恐怕徐州百姓危矣。",1,"","","刘备",202,},
        [2172] = {1905,2,2,"刘备",202,0,"四弟所言极是，是大哥迂腐了。只不过，大哥孤身一人，安能当此大任！","四妹所言极是，是大哥迂腐了。只不过，大哥孤身一人，安能当此大任！",1,"","","",999,},
        [2173] = {1905,3,1,"主角",1,0,"徐州的孙乾、糜竺、陈登自会辅助于你，还有我们兄弟几个呢。大哥，你就别推辞了！","徐州的孙乾、糜竺、陈登自会辅助于你，还有我们兄弟几个呢。大哥，你就别推辞了！",1,"","","",999,},
        [2174] = {1905,4,2,"刘备",202,0,"好，那就听四弟一言！","好，那就听四妹一言！",1,"","","",999,},
        [2175] = {1905,5,2,"刘备",202,0,"四弟，这块名将令，是陶公临死托我转送于你，感谢你让曹操退兵，解徐州之急！","四妹，这块名将令，是陶公临死托我转送于你，感谢你让曹操退兵，解徐州之急！",1,"","","",999,},
        [2176] = {1905,6,1,"主角",1,0,"谢谢大哥！四弟就不客气受领了，哈哈！","谢谢大哥！四妹就不客气受领了，哈哈！",1,"","","",999,},
        [2177] = {2001,1,1,"主角",1,0,"大哥在徐州已经稳定，我辞别了大哥，来了长安，想看看长安现今怎么样了。","大哥在徐州已经稳定，我辞别了大哥，来了长安，想看看长安现今怎么样了。",1,"","","",0,},
        [2178] = {2001,2,1,"主角",1,0,"啊！上次来长安杀董卓时，长安还一片繁华，现在为何如此残破凋敝！星彩，你可知发生了何事？","啊！上次来长安杀董卓时，长安还一片繁华，现在为何如此残破凋敝！星彩，你可知发生了何事？",1,"","","张星彩",216,},
        [2179] = {2001,3,2,"张星彩",216,0,"主公，李傕郭汜占领长安后劫持天子，把持朝政，但互相猜忌，多次打斗，这次打了好几个月，死了几万人，把好好的长安几乎变成一片废墟。","主公，李傕郭汜占领长安后劫持天子，把持朝政，但互相猜忌，多次打斗，这次打了好几个月，死了几万人，把好好的长安几乎变成一片废墟。",1,"","","",999,},
        [2180] = {2001,4,2,"关银屏",217,0,"哼，狗咬狗一嘴毛！当初那贾诩不是说会牵制李傕郭汜，保护百官与天子吗？","哼，狗咬狗一嘴毛！当初那贾诩不是说会牵制李傕郭汜，保护百官与天子吗？",1,"","","",999,},
        [2181] = {2001,5,1,"主角",1,0,"走，我们去找杀李傕郭汜，找贾诩，救天子！","走，我们去找杀李傕郭汜，找贾诩，救天子！",1,"","","",999,},
        [2182] = {2002,1,2,"汉献帝",425,0,"小英雄，你误会了贾诩。若不是他从中斡旋，恐怕李傕郭汜早已杀了朕。","小英雄，你误会了贾诩。若不是他从中斡旋，恐怕李傕郭汜早已杀了朕。",1,"","","主角",1,},
        [2183] = {2002,2,1,"主角",1,0,"原来这样。贾诩，看在皇帝的面子上，我不杀你。","原来这样。贾诩，看在皇帝的面子上，我不杀你。",1,"","","",999,},
        [2184] = {2002,3,1,"贾诩",407,0,"陛下，那李傕郭汜已有杀我之心，我必须离开长安。（贾诩离开）","陛下，那李傕郭汜已有杀我之心，我必须离开长安。（贾诩离开）",1,"","","",999,},
        [2185] = {2002,4,2,"汉献帝",425,0,"贾诩也走了，从此以后谁来保护朕？朕这个皇帝怎么当的这么惨。","贾诩也走了，从此以后谁来保护朕？朕这个皇帝怎么当的这么惨。",1,"","","",999,},
        [2186] = {2002,5,1,"主角",1,0,"陛下，不如东归洛阳，离开长安这是非之地。","陛下，不如东归洛阳，离开长安这是非之地。",1,"","","",999,},
        [2187] = {2002,6,2,"汉献帝",425,0,"朕思东都久矣，恐怕李傕郭汜不会同意。","朕思东都久矣，恐怕李傕郭汜不会同意。",1,"","","",999,},
        [2188] = {2002,7,1,"主角",1,0,"陛下勿忧！郭汜已被我打跑，我这就去找李傕！他不同意也得同意！","陛下勿忧！郭汜已被我打跑，我这就去找李傕！他不同意也得同意！",1,"","","",999,},
        [2189] = {2003,1,2,"关银屏",217,0,"杨奉，你身为汉家臣子，不护佑陛下，何故为虎作伥？","杨奉，你身为汉家臣子，不护佑陛下，何故为虎作伥？",1,"","","杨奉",134,},
        [2190] = {2003,2,2,"关银屏",217,0,"李傕贪而无谋，又喜妖邪之术，不奖赏军将，反倒重赏女巫，此等老大，你跟着他混有什么好处。","李傕贪而无谋，又喜妖邪之术，不奖赏军将，反倒重赏女巫，此等老大，你跟着他混有什么好处。",1,"","","",999,},
        [2191] = {2003,3,1,"杨奉",134,0,"不错！我跟着他出生入死，难道功劳还不如女巫吗？你们等着，我这就去召集兵马，反了李傕。","不错！我跟着他出生入死，难道功劳还不如女巫吗？你们等着，我这就去召集兵马，反了李傕。",1,"","","",999,},
        [2192] = {2003,4,2,"关银屏",217,0,"耶，成功策反一员大将！有了杨奉的军马，主公可以高枕无忧了。","耶，成功策反一员大将！有了杨奉的军马，主公可以高枕无忧了。",1,"","","主角",1,},
        [2193] = {2003,5,1,"主角",1,0,"我看杨奉这人不怎么样，倒是他手下持大斧的大将看着不错，这人是谁？","我看杨奉这人不怎么样，倒是他手下持大斧的大将看着不错，这人是谁？",1,"","","",999,},
        [2194] = {2003,6,2,"关银屏",217,0,"主公，此人乃河东杨县人，姓徐明晃，字公明。","主公，此人乃河东杨县人，姓徐明晃，字公明。",1,"","","",999,},
        [2195] = {2003,7,1,"主角",1,0,"原来是名将徐晃啊，跟着杨奉真是可惜了。","原来是名将徐晃啊，跟着杨奉真是可惜了。",1,"","","",999,},
        [2196] = {2004,1,1,"汉献帝",425,0,"我这皇帝当的真是狼狈啊，前有董卓弄权火烧洛阳，强行掳我到了长安，董卓死后，又有李傕郭汜乱长安……","我这皇帝当的真是狼狈啊，前有董卓弄权火烧洛阳，强行掳我到了长安，董卓死后，又有李傕郭汜乱长安……",1,"","","董承",121,},
        [2197] = {2004,2,1,"汉献帝",425,0,"颠沛流离，历经艰险，好不容易回了洛阳，又有韩暹居功自傲为所欲为，又碰上饥荒，跟着我的大臣，连口饱饭都吃不上，哎！","颠沛流离，历经艰险，好不容易回了洛阳，又有韩暹居功自傲为所欲为，又碰上饥荒，跟着我的大臣，连口饱饭都吃不上，哎！",1,"","","",999,},
        [2198] = {2004,3,2,"董承",121,0,"陛下，臣有一计，可密诏兖州牧曹操勤王，曹操兵多粮广，可保陛下日后无忧。","陛下，臣有一计，可密诏兖州牧曹操勤王，曹操兵多粮广，可保陛下日后无忧。",1,"","","",999,},
        [2199] = {2004,4,1,"主角",1,0,"这有何难，曹操是我的老相识了，我这就去通知曹操。","这有何难，曹操是我的老相识了，我这就去通知曹操。",1,"","","",999,},
        [2200] = {2004,5,2,"董承",121,0,"只怕那杨奉不太乐意，他手下那员大将徐晃可有些难对付。","只怕那杨奉不太乐意，他手下那员大将徐晃可有些难对付。",1,"","","",999,},
        [2201] = {2004,6,1,"主角",1,0,"杨奉韩暹不足道，徐晃乃真良将，我去会一会他。","杨奉韩暹不足道，徐晃乃真良将，我去会一会他。",1,"","","",999,},
        [2202] = {2005,1,1,"主角",1,0,"公之勇略，世所罕有，奈何屈身于杨、韩之徒？","公之勇略，世所罕有，奈何屈身于杨、韩之徒？",1,"","","徐晃",114,},
        [2203] = {2005,2,2,"徐晃",114,0,"吾固知杨奉、韩暹非立业之人，只是从之久矣，不忍相舍。","吾固知杨奉、韩暹非立业之人，只是从之久矣，不忍相舍。",1,"","","",999,},
        [2204] = {2005,3,1,"主角",1,0,"良禽择木而栖，贤臣择主从事，兖州牧曹操乃当世英雄，好贤礼士，你可以去投靠他。","良禽择木而栖，贤臣择主从事，兖州牧曹操乃当世英雄，好贤礼士，你可以去投靠他。",1,"","","",999,},
        [2205] = {2005,4,2,"徐晃",114,0,"多谢小英雄一语惊醒梦中人，这块名将令就作为谢礼。（徐晃告辞）","多谢小英雄一语惊醒梦中人，这块名将令就作为谢礼。（徐晃告辞）",1,"","","",999,},
        [2206] = {2005,5,1,"主角",1,0,"这年年天灾，皇帝虽然东归洛阳，却没有州郡供奉，又没有军队可守护，肯定不是长久之计。","这年年天灾，皇帝虽然东归洛阳，却没有州郡供奉，又没有军队可守护，肯定不是长久之计。",1,"","","关银屏",217,},
        [2207] = {2005,6,2,"关银屏",217,0,"这皇帝当得可真憋屈，还是主公好，逍遥自在，没人敢欺负。","这皇帝当得可真憋屈，还是主公好，逍遥自在，没人敢欺负。",1,"","","",999,},
        [2208] = {2005,7,1,"主角",1,0,"哈哈，谁让他是汉献帝呢！我就不一样了！我虽然不是皇帝，但是我最强啊！这可是个靠实力说话的世界！","哈哈，谁让他是汉献帝呢！我就不一样了！我虽然不是皇帝，但是我最强啊！这可是个靠实力说话的世界！",1,"","","",999,},
        [2209] = {2005,8,2,"关银屏",217,0,"主公，接下来我们去哪里？","主公，接下来我们去哪里？",1,"","","",999,},
        [2210] = {2005,9,1,"主角",1,0,"嘿嘿，走，我带你们看点好玩的。","嘿嘿，走，我带你们看点好玩的。",1,"","","",999,},
        [2211] = {2101,1,2,"张星彩",216,0,"主公，我们这是去哪里啊？","主公，我们这是去哪里啊？",1,"","","主角",1,},
        [2212] = {2101,2,1,"主角",1,0,"袁绍与曹操是当今最大的两股势力，走，我们去看看对于皇帝东归洛阳有什么想法。","袁绍与曹操是当今最大的两股势力，走，我们去看看对于皇帝东归洛阳有什么想法。",1,"","","",999,},
        [2213] = {2101,3,2,"张星彩",216,0,"主公，你似乎胸有成竹啊，是不是已经预料到袁绍和曹操的想法了呢？","主公，你似乎胸有成竹啊，是不是已经预料到袁绍和曹操的想法了呢？",1,"","","",999,},
        [2214] = {2101,4,1,"主角",1,0,"保密！","保密！",1,"","","",999,},
        [2215] = {2101,5,2,"关银屏",217,0,"哈哈，主公，我们看热闹不嫌事大，走！","哈哈，主公，我们看热闹不嫌事大，走！",1,"","","",999,},
        [2216] = {2102,1,1,"沮授",422,0,"将军生于宰辅世家，以忠义匡济天下。今皇上流离失所，将军应该早迎大驾，在邺城建都。","将军生于宰辅世家，以忠义匡济天下。今皇上流离失所，将军应该早迎大驾，在邺城建都。",1,"","","袁绍",411,},
        [2217] = {2102,2,1,"沮授",422,0,"挟天子以令诸侯，蓄兵马以讨不臣。那时，还有谁能抵御！","挟天子以令诸侯，蓄兵马以讨不臣。那时，还有谁能抵御！",1,"","","",999,},
        [2218] = {2102,3,2,"袁绍",411,0,"郭图，你怎么看？","郭图，你怎么看？",0,"","","",999,},
        [2219] = {2102,4,1,"郭图",444,0,"逢迎天子大为不妥！汉室衰微已经很久了，要重新振兴谈何容易！","逢迎天子大为不妥！汉室衰微已经很久了，要重新振兴谈何容易！",1,"","","",999,},
        [2220] = {2102,5,1,"郭图",444,0,"如果我们把天子迎到自己身边，那么动不动都得上表请示。服从命令就失去权力，不服从就有抗拒诏命的罪名，这不是好办法。","如果我们把天子迎到自己身边，那么动不动都得上表请示。服从命令就失去权力，不服从就有抗拒诏命的罪名，这不是好办法。",1,"","","",999,},
        [2221] = {2102,6,2,"袁绍",411,0,"当初董卓屠戮洛阳要杀我，我袁绍单枪匹马，从渤海起家，号召天下英雄讨伐董卓，统领十八路诸侯。","当初董卓屠戮洛阳要杀我，我袁绍单枪匹马，从渤海起家，号召天下英雄讨伐董卓，统领十八路诸侯。",1,"","","",999,},
        [2222] = {2102,7,2,"袁绍",411,0,"如今我已入主冀州，剿灭黑山黄巾，统一河北指日可待，何必迎来天子，对我指手画脚。","如今我已入主冀州，剿灭黑山黄巾，统一河北指日可待，何必迎来天子，对我指手画脚。",1,"","","",999,},
        [2223] = {2102,8,1,"沮授",422,0,"将军，取胜在于不失时机，如果我们不先下手，一定会有人抢在前头！","将军，取胜在于不失时机，如果我们不先下手，一定会有人抢在前头！",1,"","","",999,},
        [2224] = {2102,9,2,"袁绍",411,0,"沮授不必多言，我心已定！","沮授不必多言，我心已定！",1,"","","",999,},
        [2225] = {2103,1,2,"荀彧",102,0,"昔晋文公纳周襄王，而诸侯服从；汉高祖为义帝发丧，而天下归心。","昔晋文公纳周襄王，而诸侯服从；汉高祖为义帝发丧，而天下归心。",1,"","","曹操",103,},
        [2226] = {2103,2,2,"荀彧",102,0,"今天子蒙尘，将军应奉天子以从众望，不世之略。若不早图，人将先我而为之。","今天子蒙尘，将军应奉天子以从众望，不世之略。若不早图，人将先我而为之。",1,"","","",999,},
        [2227] = {2103,3,2,"董昭",133,0,"现在洛阳诸将人多意杂，不一定服从，要成就大事，不仅要逢迎天子，更要迁都许昌，才是上策！","现在洛阳诸将人多意杂，不一定服从，要成就大事，不仅要逢迎天子，更要迁都许昌，才是上策！",1,"","","",999,},
        [2228] = {2103,4,1,"曹操",103,0,"此吾之本志也，事不宜迟，我这就报奏天子，以临许都。","此吾之本志也，事不宜迟，我这就报奏天子，以临许都。",1,"","","",999,},
        [2229] = {2104,1,1,"曹操",103,0,"臣向蒙国恩，刻思图报。今傕、汜二贼，罪恶贯盈；臣有精兵二十余万，以顺讨逆，无不克捷。陛下善保龙体，以社稷为重！","臣向蒙国恩，刻思图报。今傕、汜二贼，罪恶贯盈；臣有精兵二十余万，以顺讨逆，无不克捷。陛下善保龙体，以社稷为重！",1,"","","汉献帝",425,},
        [2230] = {2104,2,2,"汉献帝",425,0,"有劳爱卿！","有劳爱卿！",1,"","","",999,},
        [2231] = {2104,3,1,"曹操",103,0,"陛下，东都荒废久矣，不可修葺，更兼转运粮食艰辛。许都城郭宫室，钱粮民物，足可备用。臣敢请驾幸许都，望陛下从之。","陛下，东都荒废久矣，不可修葺，更兼转运粮食艰辛。许都城郭宫室，钱粮民物，足可备用。臣敢请驾幸许都，望陛下从之。",1,"","","",999,},
        [2232] = {2104,4,2,"汉献帝",425,0,"久经颠沛流离，形势初定，再次迁都恐遭人非议。","久经颠沛流离，形势初定，再次迁都恐遭人非议。",1,"","","",999,},
        [2233] = {2104,5,1,"曹操",103,0,"陛下，事不宜迟，请陛下速速移驾许都！","陛下，事不宜迟，请陛下速速移驾许都！",1,"","","",999,},
        [2234] = {2104,6,2,"汉献帝",425,0,"那，就依卿所言，迁都许昌！","那，就依卿所言，迁都许昌！",1,"","","",999,},
        [2235] = {2105,1,1,"主角",1,0,"哈哈，果然不出我所料，曹操才是敢逢迎天子的真英雄！","哈哈，果然不出我所料，曹操才是敢逢迎天子的真英雄！",1,"","","关银屏",217,},
