        [2237] = {2105,3,2,"张星彩",216,0,"主公，曹操挟天子以令诸侯，从此大权尽归曹氏，汉家天下已有名无实，到时候献帝恐怕……","主公，曹操挟天子以令诸侯，从此大权尽归曹氏，汉家天下已有名无实，到时候献帝恐怕……",1,"","","",999,},
        [2238] = {2105,4,1,"主角",1,0,"星彩多虑了！眼下这局面，曹操想要时局稳定，必定不会杀献帝，献帝这辈子活到终老没什么问题。","星彩多虑了！眼下这局面，曹操想要时局稳定，必定不会杀献帝，献帝这辈子活到终老没什么问题。",1,"","","",999,},
