        [2240] = {2105,6,1,"主角",1,0,"献帝封我做了扬威将军，我也尽职护送他去许都！走，去许昌！","献帝封我做了扬威将军，我也尽职护送他去许都！走，去许昌！",1,"","","",999,},
        [2241] = {2106,1,2,"曹操",103,0,"自志才亡后，莫可与计事者。汝、颍固多奇士，令君啊，谁可以继之？","自志才亡后，莫可与计事者。汝、颍固多奇士，令君啊，谁可以继之？",1,"","","荀彧",102,},
        [2242] = {2106,2,1,"荀彧",102,0,"颍川郭嘉才策谋略，乃世之奇士。","颍川郭嘉才策谋略，乃世之奇士。",1,"","","",999,},
        [2243] = {2106,3,1,"主角",1,0,"大名鼎鼎的郭嘉啊！曹兄，让我先去会一会他！","大名鼎鼎的郭嘉啊！曹兄，让我先去会一会他！",1,"","","",999,},
        [2244] = {2107,1,1,"荀彧",102,0,"奉孝，这位就是我曾经提及过的小英雄，就是他平定十常侍之乱，凤仪亭斩杀董卓，败李傕郭汜，护送天子东归洛阳。","奉孝，这位就是我曾经提及过的小英雄，就是他平定十常侍之乱，凤仪亭斩杀董卓，败李傕郭汜，护送天子东归洛阳。",1,"","","郭嘉",104,},
        [2245] = {2107,2,1,"荀彧",102,0,"天子已昭告天下，封他为扬威将军。他却不爱功名利禄，只领虚职，不肯领军。","天子已昭告天下，封他为扬威将军。他却不爱功名利禄，只领虚职，不肯领军。",1,"","","",999,},
        [2246] = {2107,3,2,"郭嘉",104,0,"小英雄竟是扬威将军！久仰久仰。","小英雄竟是扬威将军！久仰久仰。",1,"","","主角",1,},
        [2247] = {2107,4,2,"郭嘉",104,0,"将军文韬武略，胸襟广阔，真乃当世之豪杰，令人钦佩！奉孝愿将这枚名将令送给将军！","将军文韬武略，胸襟广阔，真乃当世之豪杰，令人钦佩！奉孝愿将这枚名将令送给将军！",1,"","","",999,},
        [2248] = {2107,5,1,"主角",1,0,"多谢多谢，过奖过奖，哈哈哈哈！","多谢多谢，过奖过奖，哈哈哈哈！",1,"","","",999,},
        [2249] = {35,1,2,"关银屏",217,0,"星彩，我知道很多三国的秘密哦，你想不想知道？","星彩，我知道很多三国的秘密哦，你想不想知道？",1,"","","张星彩",216,},
