        [2251] = {35,3,2,"关银屏",217,0,"比如说，曹操年少时曾和袁绍一起去抢新娘，徐庶年少时喜欢练剑行侠仗义，袁术是所有人的爸爸……","比如说，曹操年少时曾和袁绍一起去抢新娘，徐庶年少时喜欢练剑行侠仗义，袁术是所有人的爸爸……",1,"","","",999,},
        [2252] = {35,4,1,"张星彩",216,0,"哈哈，原来名将们都有这么好玩的故事啊，去哪里看这些故事呢？","哈哈，原来名将们都有这么好玩的故事啊，去哪里看这些故事呢？",1,"","","",999,},
        [2253] = {35,5,2,"关银屏",217,0,"就在名将副本里！挑战名将副本，可以结识更多名将，发掘名将背后的故事。","就在名将副本里！挑战名将副本，可以结识更多名将，发掘名将背后的故事。",1,"","","",999,},
        [2254] = {35,6,1,"张星彩",216,0,"主公，那我们快去挑战去第一个名将副本吧。","主公，那我们快去挑战去第一个名将副本吧。",1,"","","",999,},
        [2255] = {36,1,2,"关银屏",217,0,"主公，首次通关名将副本，可以获得大量元宝奖励，元宝可是真正的好东西呢。","主公，首次通关名将副本，可以获得大量元宝奖励，元宝可是真正的好东西呢。",1,"","","张星彩",216,},
        [2256] = {36,2,1,"张星彩",216,0,"挑战名将副本还可以获得将魂，将魂能在武将商店兑换高品质武将！","挑战名将副本还可以获得将魂，将魂能在武将商店兑换高品质武将！",1,"","","",999,},
        [2257] = {36,3,2,"关银屏",217,0,"主公记得每天都要来挑战名将副本哦！","主公记得每天都要来挑战名将副本哦！",1,"","","",999,},
        [2258] = {37,1,1,"主角",1,0,"星彩，我拿到不少蓝将和绿将，品质太低好像没什么用啊！","星彩，我拿到不少蓝将和绿将，品质太低好像没什么用啊！",1,"","","张星彩",216,},
        [2259] = {37,2,2,"张星彩",216,0,"主公，绿色以上的武将都可以#回收#，回收可以获得非常有用的资源哦！","主公，绿色以上的武将都可以#回收#，回收可以获得非常有用的资源哦！",1,"","","",999,},
        [2260] = {37,3,2,"张星彩",216,0,"现在，让星彩告诉您怎么#回收#武将吧。","现在，让星彩告诉您怎么#回收#武将吧。",1,"","","",999,},
        [2261] = {38,1,2,"张星彩",216,0,"主公看到了吧，回收武将获得的将魂，可以在武将商店购买武将和其他珍贵资源呢。","主公看到了吧，回收武将获得的将魂，可以在武将商店购买武将和其他珍贵资源呢。",1,"","","主角",1,},
        [2262] = {38,2,1,"主角",1,0,"看到了！将魂能购买武将、突破丹和武将经验。哈哈，我要把蓝将绿将全部回收换将魂去！","看到了！将魂能购买武将、突破丹和武将经验。哈哈，我要把蓝将绿将全部回收换将魂去！",1,"","","",999,},
