        [2267] = {39,1,2,"关银屏",217,0,"主公，西南边关常有#南蛮入侵#，他们很狡猾，总是趁我们在主线战斗和扫荡的时候来入侵。","主公，西南边关常有#南蛮入侵#，他们很狡猾，总是趁我们在主线战斗和扫荡的时候来入侵。",1,"","","主角",1,},
