        [2269] = {39,3,2,"关银屏",217,0,"主公别急，对我们来说，打南蛮可是不错的战斗机会呢！因为打南蛮可以获得神兵资源。","主公别急，对我们来说，打南蛮可是不错的战斗机会呢！因为打南蛮可以获得神兵资源。",1,"","","",999,},
        [2270] = {39,4,1,"主角",1,0,"神兵？什么是神兵？听起来很厉害的样子。","神兵？什么是神兵？听起来很厉害的样子。",1,"","","",999,},
        [2271] = {39,5,2,"关银屏",217,0,"每个武将都有自己的#专属神兵#，比如我爹爹关羽的神兵是青龙偃月刀，大乔姐姐的神兵是莲华幽伞。","每个武将都有自己的#专属神兵#，比如我爹爹关羽的神兵是青龙偃月刀，大乔姐姐的神兵是莲华幽伞。",1,"","","",999,},
        [2272] = {39,6,2,"关银屏",217,0,"主公，多打几次南蛮，就可以去神兵商店购买神兵了。","主公，多打几次南蛮，就可以去神兵商店购买神兵了。",1,"","","",999,},
        [2273] = {39,7,1,"主角",1,0,"我已经迫不及待想获得神兵了！走，打南蛮去！","我已经迫不及待想获得神兵了！走，打南蛮去！",1,"","","",999,},
        [2274] = {40,1,2,"张星彩",216,0,"哇，恭喜主公升到58级！58级可以穿戴#锦囊#了呢！","哇，恭喜主公升到58级！58级可以穿戴#锦囊#了呢！",1,"","","主角",1,},
        [2275] = {40,2,1,"主角",1,0,"#锦囊#？锦囊有什么用？","#锦囊#？锦囊有什么用？",1,"","","",999,},
        [2276] = {40,3,2,"张星彩",216,0,"锦囊可以强化武将的特性，比如关银屏的定位是输出，穿戴关银屏锦囊就可以加攻击；貂蝉的定位是减怒，穿戴貂蝉锦囊就可以减怒。","锦囊可以强化武将的特性，比如关银屏的定位是输出，穿戴关银屏锦囊就可以加攻击；貂蝉的定位是减怒，穿戴貂蝉锦囊就可以减怒。",1,"","","",999,},
        [2277] = {40,4,2,"张星彩",216,0,"简单来说，锦囊提供了更多的特性搭配与选择，穿戴锦囊能让武将变得更加厉害。","简单来说，锦囊提供了更多的特性搭配与选择，穿戴锦囊能让武将变得更加厉害。",1,"","","",999,},
        [2278] = {40,5,1,"主角",1,0,"听起来，锦囊很厉害啊。","听起来，锦囊很厉害啊。",1,"","","",999,},
        [2279] = {40,6,2,"张星彩",216,0,"是呢！主公，让星彩给您穿戴一个锦囊试试吧。","是呢！主公，让星彩给您穿戴一个锦囊试试吧。",1,"","","",999,},
        [2280] = {41,1,2,"张星彩",216,0,"好厉害！穿戴关银屏锦囊后，主公您的攻击瞬间提升了1000点！","好厉害！穿戴关银屏锦囊后，主公您的攻击瞬间提升了1000点！",1,"","","主角",1,},
        [2281] = {41,2,1,"主角",1,0,"这还只是紫将锦囊，那橙将锦囊岂不是更厉害？","这还只是紫将锦囊，那橙将锦囊岂不是更厉害？",1,"","","",999,},
        [2282] = {41,3,2,"张星彩",216,0,"主公真聪明！锦囊的资质越高，特性也就越厉害。","主公真聪明！锦囊的资质越高，特性也就越厉害。",1,"","","",999,},
        [2283] = {41,4,1,"主角",1,0,"锦囊这么厉害，那在哪能获得锦囊呢？","锦囊这么厉害，那在哪能获得锦囊呢？",1,"","","",999,},
        [2284] = {41,5,2,"张星彩",216,0,"主公，锦囊和官衔印一样，是通过拍卖获得的！军团boss、军团试炼、军团答题等活动都有概率出锦囊。","主公，锦囊和官衔印一样，是通过拍卖获得的！军团boss、军团试炼、军团答题等活动都有概率出锦囊。",1,"","","",999,},
        [2285] = {41,6,1,"主角",1,0,"哦，只要多多参加军团活动和拍卖，就能获得锦囊。","哦，只要多多参加军团活动和拍卖，就能获得锦囊。",1,"","","",999,},
        [2286] = {41,7,2,"张星彩",216,0,"是呢，主公可以去锦囊图鉴看看，研究下如何搭配锦囊更厉害哦。","是呢，主公可以去锦囊图鉴看看，研究下如何搭配锦囊更厉害哦。",1,"","","",999,},
        [2287] = {41,8,1,"主角",1,0,"知道了，谢谢星彩！","知道了，谢谢星彩！",1,"","","",999,},
        [2288] = {41,9,2,"张星彩",216,0,"主公客气了！星彩愿意永远为主公保驾护航！","主公客气了！星彩愿意永远为主公保驾护航！",1,"","","",999,},
        [2289] = {42,1,2,"关银屏",217,0,"主公您已经70级，实力威震天下，战力之高，连神兽都愿意和您并肩战斗了呢！","主公您已经70级，实力威震天下，战力之高，连神兽都愿意和您并肩战斗了呢！",1,"","","主角",1,},
        [2290] = {42,2,1,"主角",1,0,"神兽？是青龙、白虎、朱雀、玄武之类吗？","神兽？是青龙、白虎、朱雀、玄武之类吗？",1,"","","",999,},
        [2291] = {42,3,2,"关银屏",217,0,"是的，在我们神州大地上，自古就流传着神兽的各种传说，只有天命之人和真正的强者才能见到他们的身影，我爹爹关羽就曾经得到过青龙庇佑。","是的，在我们神州大地上，自古就流传着神兽的各种传说，只有天命之人和真正的强者才能见到他们的身影，我爹爹关羽就曾经得到过青龙庇佑。",1,"","","",999,},
        [2292] = {42,4,1,"主角",1,0,"怪不得二哥这么厉害，原来有神兽庇佑！我也想要属于自己的神兽！","怪不得二哥这么厉害，原来有神兽庇佑！我也想要属于自己的神兽！",1,"","","",999,},
        [2293] = {42,5,2,"关银屏",217,0,"主公别急，我们已经为您准备好了第一只神兽，现在就让银屏带您去看看吧。","主公别急，我们已经为您准备好了第一只神兽，现在就让银屏带您去看看吧。",1,"","","",999,},
        [2294] = {43,1,2,"关银屏",217,0,"主公，这就是您的第一个神兽！","主公，这就是您的第一个神兽！",1,"","","主角",1,},
        [2295] = {43,2,1,"主角",1,0,"居然是熊猫父子组合！大熊猫看起来很能打，小熊猫卖萌，太可爱了！","居然是熊猫父子组合！大熊猫看起来很能打，小熊猫卖萌，太可爱了！",1,"","","",999,},
        [2296] = {43,3,2,"关银屏",217,0,"主公，神兽在战斗时站在第7个阵位，相当于多了一个厉害的武将呢。","主公，神兽在战斗时站在第7个阵位，相当于多了一个厉害的武将呢。",1,"","","",999,},
        [2297] = {43,4,1,"主角",1,0,"银屏，熊猫只是蓝色资质，如果我想获得更厉害的神兽，应该去哪获得？","银屏，熊猫只是蓝色资质，如果我想获得更厉害的神兽，应该去哪获得？",1,"","","",999,},
        [2298] = {43,5,2,"关银屏",217,0,"主公可以在神兽商店用#兽魂#兑换神兽。","主公可以在神兽商店用#兽魂#兑换神兽。",1,"","","",999,},
        [2299] = {43,6,1,"主角",1,0,"那#兽魂#如何获得呢？","那#兽魂#如何获得呢？",1,"","","",999,},
        [2300] = {43,7,2,"关银屏",217,0,"神兽家园可以产出兽魂，让银屏带您去看看吧。","神兽家园可以产出兽魂，让银屏带您去看看吧。",1,"","","",999,},
        [2301] = {44,1,2,"关银屏",217,0,"主公，您看，这里就是神兽家园。","主公，您看，这里就是神兽家园。",1,"","","主角",1,},
        [2302] = {44,2,2,"关银屏",217,0,"神兽家园里有一棵神树，此树吸取天地之灵气而成，能够凝结出吸引神兽的兽魂。神树等级越高，产出的兽魂越多。","神兽家园里有一棵神树，此树吸取天地之灵气而成，能够凝结出吸引神兽的兽魂。神树等级越高，产出的兽魂越多。",1,"","","",999,},
        [2303] = {44,3,1,"主角",1,0,"我懂了！升级神树，能获得更多兽魂！有了兽魂，就可以去神兽商店兑换高级神兽。","我懂了！升级神树，能获得更多兽魂！有了兽魂，就可以去神兽商店兑换高级神兽。",1,"","","",999,},
        [2304] = {44,4,2,"关银屏",217,0,"主公真厉害，一下就明白了，所以主公要记得给神树升级哦！","主公真厉害，一下就明白了，所以主公要记得给神树升级哦！",1,"","","",999,},
        [2305] = {44,5,1,"主角",1,0,"那是当然，我想要世界上最厉害的神兽！","那是当然，我想要世界上最厉害的神兽！",1,"","","",999,},
        [2306] = {44,6,2,"关银屏",217,0,"主公加油，问鼎三国，指日可待！","主公加油，问鼎三国，指日可待！",1,"","","",999,},
        [2307] = {2201,1,2,"曹操",103,0,"刘备屯兵徐州，自领州事；近吕布以兵败投之，备使居于小沛，若二人同心引兵来犯，实乃心腹之患。诸位可有何妙计？","刘备屯兵徐州，自领州事；近吕布以兵败投之，备使居于小沛，若二人同心引兵来犯，实乃心腹之患。诸位可有何妙计？",1,"","","荀彧",102,},
        [2308] = {2201,2,1,"荀彧",102,0,"今许都新定，不可造次用兵。彧有一计，名曰“二虎竞食”，让刘备教杀吕布。","今许都新定，不可造次用兵。彧有一计，名曰“二虎竞食”，让刘备教杀吕布。",1,"","","",999,},
        [2309] = {2201,3,2,"曹操",103,0,"适才探子来报，此计已被刘备化解，刘备并未杀吕布。","适才探子来报，此计已被刘备化解，刘备并未杀吕布。",1,"","","",999,},
        [2310] = {2201,4,1,"荀彧",102,0,"又有一计，名曰“驱虎吞狼”之计，明公可明诏刘备讨袁术，再暗令人往袁术处报说刘备要略南郡，袁术必然怒而攻备。两边相并，吕布必生异心。","又有一计，名曰“驱虎吞狼”之计，明公可明诏刘备讨袁术，再暗令人往袁术处报说刘备要略南郡，袁术必然怒而攻备。两边相并，吕布必生异心。",1,"","","",999,},
        [2311] = {2201,5,2,"曹操",103,0,"好计好计！就依令君所言。","好计好计！就依令君所言。",1,"","","",999,},
        [2312] = {2201,6,1,"主角",1,0,"不好，曹操要对付大哥！我们快去找大哥，让他不要中了曹操的奸计！","不好，曹操要对付大哥！我们快去找大哥，让他不要中了曹操的奸计！",1,"","","",0,},
        [2313] = {2202,1,2,"袁术",412,0,"刘备不过是个编草鞋的，如今占据徐州，竟敢与各诸侯同列。我正要去讨伐他，他竟敢来讨伐于我！可恨！","刘备不过是个编草鞋的，如今占据徐州，竟敢与各诸侯同列。我正要去讨伐他，他竟敢来讨伐于我！可恨！",1,"","","刘备",202,},
        [2314] = {2202,2,1,"刘备",202,0,"吾奉天子诏，以讨不臣。汝竟敢抵抗，罪不容诛！","吾奉天子诏，以讨不臣。汝竟敢抵抗，罪不容诛！",1,"","","",999,},
        [2315] = {2202,3,1,"主角",1,0,"大哥，这袁术狂妄的狠，我来挫挫他的锐气！","大哥，这袁术狂妄的狠，我来挫挫他的锐气！",1,"","","",999,},
        [2316] = {2203,1,1,"主角",1,0,"大哥，不要去打袁术，这是曹操之计。","大哥，不要去打袁术，这是曹操之计。",1,"","","",999,},
        [2317] = {2203,2,2,"刘备",202,0,"虽然是计，但王命不可违也。二弟三弟，你们谁可守城？","虽然是计，但王命不可违也。二弟三弟，你们谁可守城？",1,"","","",999,},
        [2318] = {2203,3,2,"张飞",206,0,"小弟愿守城。","小弟愿守城。",1,"","","",999,},
        [2319] = {2203,4,2,"刘备",202,0,"你守不住城。你一则酒后刚强，鞭挞士兵；二则做事轻易不容人，我不放心。","你守不住城。你一则酒后刚强，鞭挞士兵；二则做事轻易不容人，我不放心。",1,"","","",999,},
        [2320] = {2203,5,2,"张飞",206,0,"弟从今以后，不饮酒，不打军士，诸般听人劝谏便了。","弟从今以后，不饮酒，不打军士，诸般听人劝谏便了。",1,"","","",999,},
        [2321] = {2203,6,1,"主角",1,0,"三哥，你好好守城，我帮二哥大哥去打袁术。","三哥，你好好守城，我帮二哥大哥去打袁术。",1,"","","",999,},
        [2322] = {2204,1,1,"主角",1,0,"这吕布当真可恨，竟然趁我们出兵，夺了徐州。","这吕布当真可恨，竟然趁我们出兵，夺了徐州。",1,"","","刘备",202,},
        [2323] = {2204,2,2,"刘备",202,0,"四弟息怒！吕布已将两位夫人及家眷尽数送还，还送了不少粮米缎匹。此事三弟确有过错。我已经与奉先和好如初，他拒徐州，我占小沛。","四妹息怒！吕布已将两位夫人及家眷尽数送还，还送了不少粮米缎匹。此事三弟确有过错。我已经与奉先和好如初，他拒徐州，我占小沛。",1,"","","",999,},
        [2324] = {2204,3,2,"刘备",202,0,"此时如果我与吕布打起来，岂不正中了曹操下怀，让曹操与袁术渔翁得利。","此时如果我与吕布打起来，岂不正中了曹操下怀，让曹操与袁术渔翁得利。",1,"","","",999,},
        [2325] = {2204,4,1,"主角",1,0,"话虽如此，但这口气我可咽不下去。大哥，你等着，我去找吕布，给你讨回公道！","话虽如此，但这口气我可咽不下去。大哥，你等着，我去找吕布，给你讨回公道！",1,"","","",999,},
        [2326] = {2205,1,2,"纪灵",435,0,"刘备何在？吾奉主公袁术之名，提十万之兵，专捉刘备！","刘备何在？吾奉主公袁术之名，提十万之兵，专捉刘备！",1,"","","主角",1,},
        [2327] = {2205,2,1,"主角",1,0,"纪灵，休要妄言！不过是我手下败将！我大哥乃剿灭黄巾的功臣，你比百万黄巾如何，竟敢伤我大哥？","纪灵，休要妄言！不过是我手下败将！我大哥乃剿灭黄巾的功臣，你比百万黄巾如何，竟敢伤我大哥？",1,"","","",999,},
        [2328] = {2205,3,2,"吕布",403,0,"小英雄，且听我一言！","小英雄，且听我一言！",1,"","","",999,},
        [2329] = {2205,4,1,"主角",1,0,"你抢我大哥徐州城，打的就是你！","你抢我大哥徐州城，打的就是你！",1,"","","",999,},
        [2330] = {2206,1,2,"吕布",403,0,"哈哈，小英雄这脾性我喜欢，但我劝你两家不要厮杀。","哈哈，小英雄这脾性我喜欢，但我劝你两家不要厮杀。",1,"","","主角",1,},
        [2331] = {2206,2,2,"吕布",403,0,"那辕门离中军一百五十步，吾若一箭射中戟小枝，你两家罢兵。若射不中，你各自回营，安排厮杀。","那辕门离中军一百五十步，吾若一箭射中戟小枝，你两家罢兵。若射不中，你各自回营，安排厮杀。",1,"","","",999,},
        [2332] = {2206,3,2,"吕布",403,0,"着！（吕布取弓箭来，一箭正射中方天画戟小枝）哈哈哈，此天令你两家罢兵。","着！（吕布取弓箭来，一箭正射中方天画戟小枝）哈哈哈，此天令你两家罢兵。",1,"","","",999,},
        [2333] = {2206,4,1,"主角",1,0,"真是弓开如秋月行天，箭去似流星落地！好，既然是吕布大哥说和，那便罢了。纪灵，若你再敢来进犯，休怪我不客气。","真是弓开如秋月行天，箭去似流星落地！好，既然是吕布大哥说和，那便罢了。纪灵，若你再敢来进犯，休怪我不客气。",1,"","","",999,},
        [2334] = {2206,5,1,"纪灵",435,0,"不敢不敢！我这就退兵！","不敢不敢！我这就退兵！",1,"","","",999,},
        [2335] = {2206,6,2,"吕布",403,0,"玄德，这枚名将令就作为赔礼，徐州城奉先愿还给你！","玄德，这枚名将令就作为赔礼，徐州城奉先愿还给你！",1,"","","",999,},
        [2336] = {2206,7,2,"刘备",202,0,"不可。备欲让给奉先久矣，不若驻扎小沛，依然成犄角之势，抵御外敌。","不可。备欲让给奉先久矣，不若驻扎小沛，依然成犄角之势，抵御外敌。",1,"","","",999,},
        [2337] = {2206,8,2,"刘备",202,0,"四弟，这枚名将令你且收下。","四妹，这枚名将令你且收下。",1,"","","",999,},
        [2338] = {2206,9,1,"主角",1,0,"多谢大哥。以后有我在，那袁术定不敢再来进犯！","多谢大哥。以后有我在，那袁术定不敢再来进犯！",1,"","","",999,},
        [2339] = {2301,1,2,"纪灵",435,0,"主公，这是吕布辕门射戟逼我罢兵，跟我没关系啊。","主公，这是吕布辕门射戟逼我罢兵，跟我没关系啊。",1,"","","袁术",412,},
        [2340] = {2301,2,1,"袁术",412,0,"我送了吕布那么多粮食布匹，他竟然光收钱不办事，还偏袒刘备！","我送了吕布那么多粮食布匹，他竟然光收钱不办事，还偏袒刘备！",1,"","","",999,},
        [2341] = {2301,3,1,"袁术",412,0,"来人，我要提重兵，亲征刘备，兼讨伐吕布。","来人，我要提重兵，亲征刘备，兼讨伐吕布。",1,"","","",999,},
        [2342] = {2301,4,2,"纪灵",435,0,"主公不可造次。吕布勇力过人，兼有徐州之地，若吕布与刘备首尾相连，更加麻烦。","主公不可造次。吕布勇力过人，兼有徐州之地，若吕布与刘备首尾相连，更加麻烦。",1,"","","",999,},
        [2343] = {2301,5,2,"纪灵",435,0,"我听说吕布有一个女儿，已及笄。主公有子也，可令人求亲于吕布。","我听说吕布有一个女儿，已及笄。主公有子也，可令人求亲于吕布。",1,"","","",999,},
        [2344] = {2301,6,1,"袁术",412,0,"好计策，到时候我和吕布成了亲家，再许之以利，这天下还有谁能挡我。称帝指日可待呀。","好计策，到时候我和吕布成了亲家，再许之以利，这天下还有谁能挡我。称帝指日可待呀。",1,"","","",999,},
        [2345] = {2302,1,1,"吕布",403,0,"袁术派人来求亲，你们怎么看待这事？","袁术派人来求亲，你们怎么看待这事？",1,"","","严氏",227,},
        [2346] = {2302,2,2,"严氏",227,0,"袁公路久镇淮南，兵多粮广，早晚会做皇帝。他只有一个儿子，我们女儿嫁过去，将来就是皇后啊。","袁公路久镇淮南，兵多粮广，早晚会做皇帝。他只有一个儿子，我们女儿嫁过去，将来就是皇后啊。",1,"","","",999,},
        [2347] = {2302,3,2,"陈宫",416,0,"今天下诸侯互相争雄，北，今公与袁公路，诸侯肯定嫉妒。如果被人伏兵夺走怎么办。即可送灵雎到寿春，先居别馆，然后择机吉成亲，可万无一失。","今天下诸侯互相争雄，北，今公与袁公路，诸侯肯定嫉妒。如果被人伏兵夺走怎么办。即可送灵雎到寿春，先居别馆，然后择机吉成亲，可万无一失。",1,"","","",999,},
        [2348] = {2302,4,1,"吕布",403,0,"哈哈，那就让吕灵雎嫁过去把。来人，速速回报袁术，我吕布愿与他结为儿女亲家。","哈哈，那就让吕灵雎嫁过去把。来人，速速回报袁术，我吕布愿与他结为儿女亲家。",1,"","","",999,},
        [2349] = {2303,1,1,"陈珪",133,0,"闻将军僵尸，特来吊丧。","闻将军僵尸，特来吊丧。",1,"","","吕布",403,},
        [2350] = {2303,2,2,"吕布",403,0,"何出此言？","何出此言？",1,"","","",999,},
        [2351] = {2303,3,1,"陈珪",133,0,"袁术有称帝之意，等他称帝，则等同于造反。公与反贼结亲，到时候肯定为天下所不为容。","袁术有称帝之意，等他称帝，则等同于造反。公与反贼结亲，到时候肯定为天下所不为容。",1,"","","",999,},
        [2352] = {2303,4,1,"主角",1,0,"总算赶到了！吕布，听说你要将女儿嫁给袁术的儿子，万万不可！","总算赶到了！吕布，听说你要将女儿嫁给袁术的儿子，万万不可！",1,"","","",999,},
        [2353] = {2303,5,2,"吕布",403,0,"小英雄为何如此说？","小英雄为何如此说？",1,"","","",999,},
        [2354] = {2303,6,1,"主角",1,0,"当初李傕郭汜之后，你投靠袁术，他却四处排挤于你，你忘了吗？袁术这等小人，你跟着他，将来必定招祸啊。","当初李傕郭汜之后，你投靠袁术，他却四处排挤于你，你忘了吗？袁术这等小人，你跟着他，将来必定招祸啊。",1,"","","",999,},
        [2355] = {2303,7,2,"吕布",403,0,"我信你！陈宫误我！来人，速速将灵雎追回！","我信你！陈宫误我！来人，速速将灵雎追回！",1,"","","",999,},
        [2356] = {2303,8,1,"主角",1,0,"事不宜迟，我去！","事不宜迟，我去！",1,"","","",999,},
        [2357] = {2304,1,2,"貂蝉",404,0,"奉先，小英雄已将灵雎追回！","奉先，小英雄已将灵雎追回！",1,"","","主角",1,},
        [2358] = {2304,2,1,"主角",1,0,"貂蝉姑娘拜托之事，在下自当竭力照搬！","貂蝉姑娘拜托之事，在下自当竭力照搬！",1,"","","",999,},
        [2359] = {2304,3,2,"貂蝉",404,0,"奉先，妾身以为，婚嫁之事，虽有父母之命媒妁之言，但灵雎的意愿也不可忽视。她若不愿意嫁，岂不误了终身","奉先，妾身以为，婚嫁之事，虽有父母之命媒妁之言，但灵雎的意愿也不可忽视。她若不愿意嫁，岂不误了终身",1,"","","",999,},
        [2360] = {2304,4,2,"吕布",403,0,"蝉儿所言极是。","蝉儿所言极是。",1,"","","",999,},
        [2361] = {2304,5,1,"主角",1,0,"吕布，你乃当时豪杰，何必用自己女儿做前程赌注。","吕布，你乃当时豪杰，何必用自己女儿做前程赌注。",1,"","","",999,},
        [2362] = {2304,6,2,"吕布",403,0,"说得对！我吕布虎步江淮，何惧天下任何人！我吕布的女儿，自然是想嫁就嫁，想不嫁就不嫁！这就去问灵雎的意思。","说得对！我吕布虎步江淮，何惧天下任何人！我吕布的女儿，自然是想嫁就嫁，想不嫁就不嫁！这就去问灵雎的意思。",1,"","","",999,},
        [2363] = {2305,1,2,"吕布",403,0,"小英雄这块名将令你且收下，若不是你，险些误了大事！","小英雄这块名将令你且收下，若不是你，险些误了大事！",1,"","","主角",1,},
        [2364] = {2305,2,1,"主角",1,0,"好说好说！多谢啦！","好说好说！多谢啦！",1,"","","",999,},
        [2365] = {2305,3,2,"吕灵雎",418,0,"爹爹，我才不要嫁给袁术的儿子我要嫁给一个大英雄，才不要嫁给小怂包！","爹爹，我才不要嫁给袁术的儿子我要嫁给一个大英雄，才不要嫁给小怂包！",1,"","","",999,},
        [2366] = {2305,4,2,"吕布",403,0,"灵雎，爹爹以后再也不强迫你嫁人了。你说，你想嫁给哪位英雄？","灵雎，爹爹以后再也不强迫你嫁人了。你说，你想嫁给哪位英雄？",1,"","","",999,},
        [2367] = {2305,5,2,"吕灵雎",418,0,"我听说天子新封了一位扬威将军，年少有为，武艺高强，我想嫁给他！","我不想嫁人！听说天子新封了一位扬威将军，年少有为，英姿飒爽，我想与她结为姐妹共闯天下！",1,"","","",999,},
        [2368] = {2305,6,1,"主角",1,0,"啊，你想嫁给我？（那样的话，那貂蝉岂不是成了我娘亲，万万不可！）","什么？你想与我结拜结脉？（那样的话，貂蝉姐姐岂不是成了我娘亲，万万不可！）",1,"","","",999,},
        [2369] = {2305,7,1,"主角",1,0,"多谢姑娘美意，我还年轻，不想成亲，告辞！","多谢姑娘美意，我还有要事在身，告辞！",1,"","","",999,},
        [2370] = {2305,8,2,"吕灵雎",418,0,"扬威将军，你别逃，就算你逃到天涯海角，我也要嫁给你！","扬威将军，你别逃，就算你逃到天涯海角，我也要和你做姐妹！",1,"","","",999,},
        [2371] = {2401,1,2,"吕布",403,0,"玄德，我辕门射戟，救你大难，你何故假扮山贼，夺我一百五十匹好马？","玄德，我辕门射戟，救你大难，你何故假扮山贼，夺我一百五十匹好马？",1,"","","刘备",202,},
        [2372] = {2401,2,1,"刘备",202,0,"备因缺马，令人四下收买，安敢夺兄马匹。","备因缺马，令人四下收买，安敢夺兄马匹。",1,"","","",999,},
        [2373] = {2401,3,1,"张飞",206,0,"吕布，是我夺了你好马，你别诬陷我哥哥。","吕布，是我夺了你好马，你别诬陷我哥哥。",1,"","","",999,},
        [2374] = {2401,4,2,"吕布",403,0,"环眼贼，你竟敢累次藐视我？","环眼贼，你竟敢累次藐视我？",1,"","","",999,},
        [2375] = {2401,5,1,"张飞",206,0,"我夺你马你便恼，你夺我哥哥的徐州便不说了？","我夺你马你便恼，你夺我哥哥的徐州便不说了？",1,"","","",999,},
        [2376] = {2401,6,2,"吕布",403,0,"哼，那便各凭本事，我这便领军夺回我的马！","哼，那便各凭本事，我这便领军夺回我的马！",1,"","","",999,},
        [2377] = {2402,1,2,"关银屏",217,0,"主公，主公，不好了！那吕布领了兵马，正在攻打小沛！吕布兵马数倍于我爹爹，这可怎么办是好？","主公，主公，不好了！那吕布领了兵马，正在攻打小沛！吕布兵马数倍于我爹爹，这可怎么办是好？",1,"","","主角",1,},
        [2378] = {2402,2,2,"张星彩",216,0,"肯定又是我爹爹坏事了！爹爹素来急躁鲁莽，又爱喝酒误事，这可怎么办啊？","肯定又是我爹爹坏事了！爹爹素来急躁鲁莽，又爱喝酒误事，这可怎么办啊？",1,"","","",999,},
        [2379] = {2402,3,1,"主角",1,0,"星彩，银屏，不要着急，我这就去解围！","星彩，银屏，不要着急，我这就去解围！",1,"","","",999,},
        [2380] = {2402,4,1,"主角",1,0,"吕兄，我若帮你把马匹找回，你便撤兵，我若帮你找不回，你再发兵如何？","吕兄，我若帮你把马匹找回，你便撤兵，我若帮你找不回，你再发兵如何？",1,"","","",999,},
        [2381] = {2402,5,2,"吕布",403,0,"好，看小英雄面，那便如此。","好，看小英雄面，那便如此。",1,"","","",999,},
        [2382] = {2403,1,2,"关羽",205,0,"四弟，你怎么帮着外人打大哥？","四妹，你怎么帮着外人打大哥？",1,"","","主角",1,},
        [2383] = {2403,2,1,"主角",1,0,"二哥勿怪，我自有道理！","二哥勿怪，我自有道理！",1,"","","",999,},
        [2384] = {2404,1,2,"张飞",206,0,"四弟，你若胳膊肘往外拐，帮着那三姓家奴，休怪我不认你！","四妹，你若胳膊肘往外拐，帮着那三姓家奴，休怪我不认你！",1,"","","主角",1,},
        [2385] = {2404,2,1,"主角",1,0,"三哥，你别误会，听我把话说完。","三哥，你别误会，听我把话说完。",1,"","","",999,},
        [2386] = {2404,3,2,"张飞",206,0,"谁要听你聒噪！嘚！吃我一矛！","谁要听你聒噪！嘚！吃我一矛！",1,"","","",999,},
        [2387] = {2405,1,1,"主角",1,0,"三哥，你误会了。我是来帮你和大哥二哥三个的，这不过是缓兵之计。","三哥，你误会了。我是来帮你和大哥二哥三个的，这不过是缓兵之计。",1,"","","张飞",206,},
        [2388] = {2405,2,1,"主角",1,0,"那吕布身边有陈宫，陈宫一向对大哥不力，趁吕布此刻提兵需要时间，我护着你们出城去，这小沛不要也罢！","那吕布身边有陈宫，陈宫一向对大哥不力，趁吕布此刻提兵需要时间，我护着你们出城去，这小沛不要也罢！",1,"","","",999,},
        [2389] = {2405,3,2,"张飞",206,0,"原来如此，三哥一向是个粗人，多有得罪之处，四弟你可别恼。","原来如此，三哥一向是个粗人，多有得罪之处，四妹你可别恼。",1,"","","",999,},
        [2390] = {2405,4,2,"张星彩",216,0,"爹爹，你也太爱喝酒了，喝酒误事，险些害死大家。以后可不许再喝酒了。","爹爹，你也太爱喝酒了，喝酒误事，险些害死大家。以后可不许再喝酒了。",1,"","","",999,},
        [2391] = {2405,5,1,"主角",1,0,"不如弃城走许都，投奔曹操。","不如弃城走许都，投奔曹操。",1,"","","",999,},
        [2392] = {2405,6,1,"主角",1,0,"曹操所恨者，不过吕布。吕布之前打了他的兖州，让他险些丧命。","曹操所恨者，不过吕布。吕布之前打了他的兖州，让他险些丧命。",1,"","","",999,},
        [2393] = {2405,7,2,"刘备",202,0,"四弟言之有理。","四妹言之有理。",1,"","","",999,},
        [2394] = {2405,8,2,"张飞",206,0,"四弟，是我误会你了，三哥送你一块名将令，你且消消气。","四妹，是我误会你了，三哥送你一块名将令，你且消消气。",1,"","","",999,},
        [2395] = {2501,1,1,"主角",1,0,"大哥他们已经在许都安定下来。星彩，银屏，我们好久没出去游历了，要不出去转转？","大哥他们已经在许都安定下来。星彩，银屏，我们好久没出去游历了，要不出去转转？",1,"","","张星彩",216,},
        [2396] = {2501,2,2,"张星彩",216,0,"最近一直帮着我爹爹他们，真是辛苦主公了。","最近一直帮着我爹爹他们，真是辛苦主公了。",1,"","","",999,},
        [2397] = {2501,3,2,"关银屏",217,0,"主公，我们去哪里？在中原呆了这么久，要不换换新鲜口味，去江东转转。","主公，我们去哪里？在中原呆了这么久，要不换换新鲜口味，去江东转转。",1,"","","",999,},
        [2398] = {2501,4,1,"主角",1,0,"自孙坚去世后，孙策跟着袁术，退居江南，礼贤下士，我们去看看，看能不能帮上忙吧。","自孙坚去世后，孙策跟着袁术，退居江南，礼贤下士，我们去看看，看能不能帮上忙吧。",1,"","","",999,},
        [2399] = {2501,5,2,"关银屏",217,0,"好啊，江东小霸王，又帅又霸气，我们就去找他.","好啊，江东小霸王，又帅又霸气，我们就去找他.",1,"","","",999,},
        [2400] = {2502,1,1,"主角",1,0,"孙兄，好久不见，怎么看着不太高兴啊！","孙兄，好久不见，怎么看着不太高兴啊！",1,"","","孙策",301,},
        [2401] = {2502,2,2,"孙策",301,0,"袁术为人反复无常，言而无信，他许诺人名我为九江太守，却改用自己的亲信。后面又派我打庐江太守陆康，说要人名我为庐江太守，结果又改用自己的亲信刘勋。真是太让人失望了。","袁术为人反复无常，言而无信，他许诺人名我为九江太守，却改用自己的亲信。后面又派我打庐江太守陆康，说要人名我为庐江太守，结果又改用自己的亲信刘勋。真是太让人失望了。",1,"","","",999,},
        [2402] = {2502,3,2,"孙策",301,0,"我父亲孙坚如此英雄，我却寄人篱下，不能继承父亲的志向啊。","我父亲孙坚如此英雄，我却寄人篱下，不能继承父亲的志向啊。",1,"","","",999,},
        [2403] = {2502,4,1,"主角",1,0,"孙策，那袁术不是什么好人，又如此言而无信。不如召集你父亲的旧部，趁机收取江东，往江东打下一片基业。","孙策，那袁术不是什么好人，又如此言而无信。不如召集你父亲的旧部，趁机收取江东，往江东打下一片基业。",1,"","","",999,},
        [2404] = {2502,5,2,"孙策",301,0,"只怕袁术不肯放我！","只怕袁术不肯放我！",1,"","","",999,},
        [2405] = {2502,6,1,"主角",1,0,"袁术一直想当皇帝，你把你父亲留下的传国玉玺送给袁术，他自会同意。","袁术一直想当皇帝，你把你父亲留下的传国玉玺送给袁术，他自会同意。",1,"","","",999,},
        [2406] = {2502,7,2,"孙策",301,0,"此计可行！我舅舅丹阳太守吴景被扬州刺史刘瑶所逼，正好借这个机会。","此计可行！我舅舅丹阳太守吴景被扬州刺史刘瑶所逼，正好借这个机会。",1,"","","",999,},
        [2407] = {2502,8,1,"主角",1,0,"嘿嘿，如果他不同意，我就打得他同意。","嘿嘿，如果他不同意，我就打得他同意。",1,"","","",999,},
        [2408] = {2503,1,2,"袁术",412,0,"我袁术是所有人的爸爸，你们不能这样对我。","我袁术是所有人的爸爸，你们不能这样对我。",1,"","","主角",1,},
        [2409] = {2503,2,2,"关银屏",217,0,"主公的手下败将，也敢称自己是所有人的爸爸，看打！","主公的手下败将，也敢称自己是所有人的爸爸，看打！",1,"","","",999,},
        [2410] = {2503,3,1,"主角",1,0,"我今天就打得你喊我爸爸，你信不信。","我今天就打得你喊我爸爸，你信不信。",1,"","","",999,},
        [2411] = {2504,1,2,"孙策",301,0,"此次能顺利回到江东，真是多亏了小英雄，策感激不尽！","此次能顺利回到江东，真是多亏了小英雄，策感激不尽！",1,"","","主角",1,},
        [2412] = {2504,2,1,"主角",1,0,"你我兄弟客气了！助人者天助","你我兄弟客气了！助人者天助",1,"","","",999,},
        [2413] = {2504,3,1,"主角",1,0,"一个好汉三个帮，你想在江东打下基业，先招募几个江东士族的人才。","一个好汉三个帮，你想在江东打下基业，先招募几个江东士族的人才。",1,"","","",999,},
        [2414] = {2504,4,1,"主角",1,0,"吴郡的四大望族张朱陆顾，以张家的张昭为首。这张昭有经天纬地之才，只要你先拿下这张昭，再礼贤下士，其他望族自然前来投靠。","吴郡的四大望族张朱陆顾，以张家的张昭为首。这张昭有经天纬地之才，只要你先拿下这张昭，再礼贤下士，其他望族自然前来投靠。",1,"","","",999,},
        [2415] = {2505,1,2,"关银屏",217,0,"主公快看，前面那人是谁？资质风流，仪容秀丽，好一个美男子！","主公快看，前面那人是谁？资质风流，仪容秀丽，好一个美男子！",1,"","","主角",1,},
        [2416] = {2505,2,1,"主角",1,0,"此人当真气度不凡，孙兄，你可认识？","此人当真气度不凡，孙兄，你可认识？",1,"","","",999,},
        [2417] = {2505,3,2,"孙策",301,0,"这是我的旧时，周瑜，周公瑾。我在寿春之时，与他一见如故，推诚相待。我父亲经过荆州去讨伐董卓时，我孙氏一族便是居住在周瑜的宅院内。升堂拜母，互通有无，世称为“总角之好”","这是我的旧时，周瑜，周公瑾。我在寿春之时，与他一见如故，推诚相待。我父亲经过荆州去讨伐董卓时，我孙氏一族便是居住在周瑜的宅院内。升堂拜母，互通有无，世称为“总角之好”",1,"","","",999,},
        [2418] = {2505,4,1,"主角",1,0,"啊，原来是名将周瑜！太好了，我先与他切磋一番！","啊，原来是名将周瑜！太好了，我先与他切磋一番！",1,"","","",999,},
        [2419] = {2506,1,1,"孙策",301,0,"公瑾，这便是我曾向你提及的小英雄，天子已册封他为扬威 将军。我这次摆脱袁术控制，夺回我父亲旧部，也多亏了他。","公瑾，这便是我曾向你提及的小英雄，天子已册封他为扬威 将军。我这次摆脱袁术控制，夺回我父亲旧部，也多亏了他。",1,"","","周瑜",303,},
        [2420] = {2506,2,2,"周瑜",303,0,"原来竟是扬威将军，久仰久仰。","原来竟是扬威将军，久仰久仰。",1,"","","",999,},
        [2421] = {2506,3,1,"孙策",301,0,"我们三人年纪相仿，一见如故，不如结拜为兄弟如何。","我们三人年纪相仿，一见如故，不如结拜为兄弟如何。",1,"","","",999,},
        [2422] = {2506,4,1,"主角",1,0,"我已经与刘备关羽张飞结拜为兄弟。不如你们两个结拜，我给你们做见证人。","我已经与刘备关羽张飞结拜为兄弟。不如你们两个结拜，我给你们做见证人。",1,"","","",999,},
        [2423] = {2506,5,1,"孙策",301,0,"皇天在上,厚土在下，我孙策与周瑜今日在此结拜为兄弟,今后坦诚以对,肝胆相照。愿承父志，与公瑾共谋天下。","皇天在上,厚土在下，我孙策与周瑜今日在此结拜为兄弟,今后坦诚以对,肝胆相照。愿承父志，与公瑾共谋天下。",1,"","","",999,},
        [2424] = {2506,6,2,"周瑜",303,0,"皇天在上,厚土在下，我周瑜与孙策今日在此结拜为兄弟,今后坦诚以对,肝胆相照。周瑜愿施犬马之力，与伯符共图大事。","皇天在上,厚土在下，我周瑜与孙策今日在此结拜为兄弟,今后坦诚以对,肝胆相照。周瑜愿施犬马之力，与伯符共图大事。",1,"","","",999,},
        [2425] = {2506,7,1,"孙策",301,0,"这块名将令，便送给小兄弟，作为我二人结拜的见证礼！","这块名将令，便送给小兄弟，作为我二人结拜的见证礼！",1,"","","",999,},
        [2426] = {2601,1,1,"主角",1,0,"孙兄，我们这江东之战，从哪里先打起来？","孙兄，我们这江东之战，从哪里先打起来？",1,"","","孙策",301,},
        [2427] = {2601,2,2,"孙策",301,0,"小英雄，我们先去打屯兵曲阿的扬州刺史刘繇。","小英雄，我们先去打屯兵曲阿的扬州刺史刘繇。",1,"","","",999,},
        [2428] = {2601,3,1,"主角",1,0,"既是扬州刺史，为何会在曲阿，中间隔着一条江呢。","既是扬州刺史，为何会在曲阿，中间隔着一条江呢。",1,"","","",999,},
        [2429] = {2601,4,2,"孙策",301,0,"小英雄有所不知，朝廷任命刘繇担任扬州刺史，治所是寿春，但如今诸侯割据，寿春早已被袁术占领。","小英雄有所不知，朝廷任命刘繇担任扬州刺史，治所是寿春，但如今诸侯割据，寿春早已被袁术占领。",1,"","","",999,},
        [2430] = {2601,5,2,"孙策",301,0,"我舅舅丹阳太守吴景和我堂兄孙贲，帮助刘繇在曲阿设立了治所。没想到刘繇站稳跟脚后，反倒恩将仇报，驱逐我的舅舅，抢占地盘。","我舅舅丹阳太守吴景和我堂兄孙贲，帮助刘繇在曲阿设立了治所。没想到刘繇站稳跟脚后，反倒恩将仇报，驱逐我的舅舅，抢占地盘。",1,"","","",999,},
        [2431] = {2601,6,1,"主角",1,0,"如此忘恩负义之人，孙兄你歇着，我帮你打！","如此忘恩负义之人，孙兄你歇着，我帮你打！",1,"","","",999,},
        [2432] = {2602,1,1,"主角",1,0,"这扬州刺史刘繇不过尔尔。","这扬州刺史刘繇不过尔尔。",1,"","","张星彩",216,},
        [2433] = {2602,2,2,"张星彩",216,0,"主公英明神武，区区一个刘繇怎么可能是主公的对手！","主公英明神武，区区一个刘繇怎么可能是主公的对手！",1,"","","",999,},
        [2434] = {2602,3,1,"主角",1,0,"但是刘繇那个手下，却是个人物。我想起来了，他不是那个帮孔融送信的太史慈吗，他怎么跟着刘繇了！","但是刘繇那个手下，却是个人物。我想起来了，他不是那个帮孔融送信的太史慈吗，他怎么跟着刘繇了！",1,"","","",999,},
        [2435] = {2602,4,2,"张星彩",216,0,"主公好记性！正是他。太史慈猿臂善射，弦不虚发，是个真正的神射手。他跟刘繇是老乡，所以互相帮衬。","主公好记性！正是他。太史慈猿臂善射，弦不虚发，是个真正的神射手。他跟刘繇是老乡，所以互相帮衬。",1,"","","",999,},
        [2436] = {2602,5,1,"主角",1,0,"原来如此。如果能收服这太史慈，倒是一员猛将。","原来如此。如果能收服这太史慈，倒是一员猛将。",1,"","","",999,},
        [2437] = {2603,1,2,"严白虎",138,0,"来者何人？吾乃东吴德王严白虎！怕了吧！","来者何人？吾乃东吴德王严白虎！怕了吧！",1,"","","主角",1,},
        [2438] = {2603,2,1,"主角",1,0,"白虎算什么？我左青龙,右白虎,老牛在腰间,龙头在胸口!人挡杀人,佛挡杀佛！","白虎算什么？我左青龙,右白虎,老牛在腰间,龙头在胸口!人挡杀人,佛挡杀佛！",1,"","","",999,},
        [2439] = {2603,3,2,"严白虎",138,0,"小子，少说大话，也不怕风大闪了舌头！","小子，少说大话，也不怕风大闪了舌头！",1,"","","",999,},
        [2440] = {2603,4,2,"张星彩",216,0,"这严白虎乃是山贼出生，自封为东吴德王，占据吴郡，主公与他对战之时，可得小心哦！","这严白虎乃是山贼出生，自封为东吴德王，占据吴郡，主公与他对战之时，可得小心哦！",1,"","","",999,},
        [2441] = {2603,5,1,"主角",1,0,"区区一个小山贼也敢占据吴郡，我要打得他落花流水，屁滚尿流！开战吧！","区区一个小山贼也敢占据吴郡，我要打得他落花流水，屁滚尿流！开战吧！",1,"","","",999,},
        [2442] = {2604,1,1,"王朗",133,0,"孙策小儿，既然败在你手上，要杀要剐但随君便，我王朗绝对不会追随于你。","孙策小儿，既然败在你手上，要杀要剐但随君便，我王朗绝对不会追随于你。",1,"","","孙策",301,},
        [2443] = {2604,2,2,"孙策",301,0,"王朗，既然你不愿意追随于我，那便罢！我孙策从来不强迫任何人！那曹操已经上表征召你去许昌，我这便派人送你去许昌。","王朗，既然你不愿意追随于我，那便罢！我孙策从来不强迫任何人！那曹操已经上表征召你去许昌，我这便派人送你去许昌。",1,"","","",999,},
        [2444] = {2604,3,1,"主角",1,0,"王朗竟然不识抬举，孙兄你真是胸怀广阔，令人佩服！","王朗竟然不识抬举，孙兄你真是胸怀广阔，令人佩服！",1,"","","",999,},
        [2445] = {2604,4,2,"孙策",301,0,"哈哈哈，不敢当不敢当。","哈哈哈，不敢当不敢当。",1,"","","",999,},
        [2446] = {2605,1,1,"主角",1,0,"就剩下太史慈了。孙兄，我去帮你迎战太史慈！","就剩下太史慈了。孙兄，我去帮你迎战太史慈！",1,"","","孙策",301,},
        [2447] = {2605,2,2,"孙策",301,0,"上次神亭与太史慈一战，当时我抢得他背后手戟，而太史慈则夺去我的头盔，还未打得尽兴胜负未分，怎能轻易让给你？","上次神亭与太史慈一战，当时我抢得他背后手戟，而太史慈则夺去我的头盔，还未打得尽兴胜负未分，怎能轻易让给你？",1,"","","",999,},
        [2448] = {2605,3,1,"主角",1,0,"不如我们切磋一番，胜者与太史慈交战，如何？","不如我们切磋一番，胜者与太史慈交战，如何？",1,"","","",999,},
        [2449] = {2605,4,2,"孙策",301,0,"哈哈哈，好，那就一战！","哈哈哈，好，那就一战！",1,"","","",999,},
        [2450] = {2606,1,2,"太史慈",305,0,"扬威将军果然厉害，某佩服之极，这枚名将令愿送给小将军。","扬威将军果然厉害，某佩服之极，这枚名将令愿送给小将军。",1,"","","主角",1,},
        [2451] = {2606,2,1,"主角",1,0,"多谢多谢！","多谢多谢！",1,"","","",999,},
        [2452] = {2606,3,2,"孙策",301,0,"子义真丈夫也！刘繇蠢辈，竟不能用为大将，才会大败。子义今后之路，我当与卿共闯。","子义真丈夫也！刘繇蠢辈，竟不能用为大将，才会大败。子义今后之路，我当与卿共闯。",1,"","","",999,},
        [2453] = {2606,4,2,"太史慈",305,0,"刘君新破，士卒离心，某欲自往收拾余众，以助明公。","刘君新破，士卒离心，某欲自往收拾余众，以助明公。",1,"","","",999,},
        [2454] = {2606,5,2,"孙策",301,0,"好，此诚策之所愿。明日日中，望公来还。（太史慈应诺而去）","好，此诚策之所愿。明日日中，望公来还。（太史慈应诺而去）",1,"","","",999,},
        [2455] = {2606,6,1,"主角",1,0,"孙兄，你不怕太史慈去必不来了吗。","孙兄，你不怕太史慈去必不来了吗。",1,"","","",999,},
        [2456] = {2606,7,2,"孙策",301,0,"子义乃信义之士，必不被我。","子义乃信义之士，必不被我。",1,"","","",999,},
        [2457] = {2606,8,1,"主角",1,0,"不瞒你说，曹操闻太史慈威名，向他寄了一封书信，内无多物，而放了少量当归，寓意太史慈应当向其投诚。没想到，最后还是被孙兄你收了去。哈哈哈！","不瞒你说，曹操闻太史慈威名，向他寄了一封书信，内无多物，而放了少量当归，寓意太史慈应当向其投诚。没想到，最后还是被孙兄你收了去。哈哈哈！",1,"","","",999,},
        [2458] = {2606,9,2,"孙策",301,0,"我孙策一向疑人不用，用人不疑。这太史慈定是我东吴大将，哈哈！","我孙策一向疑人不用，用人不疑。这太史慈定是我东吴大将，哈哈！",1,"","","",999,},
        [2459] = {2701,1,1,"关银屏",217,0,"前边那个男子在做什么，练功吗？","前边那个男子在做什么，练功吗？",1,"","","华佗",402,},
        [2460] = {2701,2,2,"华佗",402,0,"（一边练五禽戏一边喃喃自语）虎举、虎扑；鹿抵、鹿奔；熊运、熊晃；猿提、猿摘；鸟伸、鸟飞……","（一边练五禽戏一边喃喃自语）虎举、虎扑；鹿抵、鹿奔；熊运、熊晃；猿提、猿摘；鸟伸、鸟飞……",1,"","","",999,},
        [2461] = {2701,3,1,"张星彩",216,0,"啊，那不是我师父吗！师父！真没想到能在这里见到你！","啊，那不是我师父吗！师父！真没想到能在这里见到你！",1,"","","",999,},
        [2462] = {2701,4,2,"华佗",402,0,"哈哈，是乖徒儿星彩啊！为师一向云游四海四处行医，最近正巧到了江东。","哈哈，是乖徒儿星彩啊！为师一向云游四海四处行医，最近正巧到了江东。",1,"","","",999,},
        [2463] = {2701,5,1,"张星彩",216,0,"师父，这便是我家主公，主公，这便是我师父华佗！","师父，这便是我家主公，主公，这便是我师父华佗！",1,"","","",999,},
        [2464] = {2701,6,2,"华佗",402,0,"哦，这位小英雄就是扬威将军！果然年少有为！（真不亏为那左慈所说天命之人啊！）","哦，这位小英雄就是扬威将军！果然年少有为！（真不亏为那左慈所说天命之人啊！）",1,"","","",999,},
        [2465] = {2701,7,1,"主角",1,0,"幸会幸会，原来是名医华佗！","幸会幸会，原来是名医华佗！",1,"","","",999,},
        [2466] = {2701,8,2,"华佗",402,0,"星彩，这套五禽戏你且收好，可送给你家主公，可强身健体，延年益寿，永葆年轻。老夫有事在身要去会稽，先行告辞。","星彩，这套五禽戏你且收好，可送给你家主公，可强身健体，延年益寿，永葆年轻。老夫有事在身要去会稽，先行告辞。",1,"","","",999,},
        [2467] = {2701,9,1,"关银屏",217,0,"星彩，你师父真年轻……咦，怎么就走了！还真是个怪人！","星彩，你师父真年轻……咦，怎么就走了！还真是个怪人！",1,"","","",999,},
        [2468] = {2701,10,1,"张星彩",216,0,"师父一向如此，请主公和银屏姐姐不要见怪。","师父一向如此，请主公和银屏姐姐不要见怪。",1,"","","",999,},
        [2469] = {2702,1,2,"张星彩",216,0,"快看，这宣城怎么被山贼给围住了！这么多山贼！","快看，这宣城怎么被山贼给围住了！这么多山贼！",1,"","","主角",1,},
        [2470] = {2702,2,1,"主角",1,0,"宣城不是孙策之弟孙权镇守的吗！我们快进城去救孙权！","宣城不是孙策之弟孙权镇守的吗！我们快进城去救孙权！",1,"","","",999,},
        [2471] = {2702,3,2,"关银屏",217,0,"这么多山贼！哈哈，杀山贼咯！","这么多山贼！哈哈，杀山贼咯！",1,"","","",999,},
        [2472] = {2703,1,2,"周泰",316,0,"多谢相救！请诸位保护好孙权将军！（说完，周泰重伤倒地昏迷）","多谢相救！请诸位保护好孙权将军！（说完，周泰重伤倒地昏迷）",1,"","","主角",1,},
        [2473] = {2703,2,2,"孙权",306,0,"周泰将军！他是为了救我才受了这么多伤！","周泰将军！他是为了救我才受了这么多伤！",1,"","","",999,},
        [2474] = {2703,3,1,"主角",1,0,"这周泰真是一条汉子，我生平最敬重此等忠义之士！星彩，你快想办法医治他！","这周泰真是一条汉子，我生平最敬重此等忠义之士！星彩，你快想办法医治他！",1,"","","",999,},
        [2475] = {2703,4,1,"张星彩",216,0,"让我看看，这周泰身中十二枪，伤得太重了，我只能用金针封穴，暂时帮他保住性命。想要彻底医治，还得请我师父华佗来。","让我看看，这周泰身中十二枪，伤得太重了，我只能用金针封穴，暂时帮他保住性命。想要彻底医治，还得请我师父华佗来。",1,"","","",999,},
        [2476] = {2703,5,2,"孙权",306,0,"多谢姑娘！敢问令师华佗现在何处！","多谢姑娘！敢问令师华佗现在何处！",1,"","","",999,},
        [2477] = {2703,6,1,"张星彩",216,0,"我们进城之前遇到过我师父，他说去会稽了。","我们进城之前遇到过我师父，他说去会稽了。",1,"","","",999,},
        [2478] = {2703,7,2,"孙权",306,0,"速速派人去会稽，请神医华佗来为周泰治病！","速速派人去会稽，请神医华佗来为周泰治病！",1,"","","",999,},
        [2479] = {2704,1,2,"华佗",402,0,"扬威将军果然了得！这块名将令愿送给将军！","扬威将军果然了得！这块名将令愿送给将军！",1,"","","主角",1,},
        [2480] = {2704,2,1,"主角",1,0,"先得华神医送五禽戏，现在又赠名将令，真是太感谢了！","先得华神医送五禽戏，现在又赠名将令，真是太感谢了！",1,"","","",999,},
        [2481] = {2704,3,2,"华佗",402,0,"将军乃是结束这乱世的天命之人！老夫研究的五禽戏，请将军勤加练习，必然能延年益寿，强身健体。","将军乃是结束这乱世的天命之人！老夫研究的五禽戏，请将军勤加练习，必然能延年益寿，强身健体。",1,"","","",999,},
        [2482] = {2704,4,1,"主角",1,0,"请问，华神医这张脸如此年轻，可是练五禽戏练出来的？","请问，华神医这张脸如此年轻，可是练五禽戏练出来的？",1,"","","",999,},
        [2483] = {2704,5,2,"华佗",402,0,"不错！","不错！",1,"","","",999,},
        [2484] = {2704,6,1,"主角",1,0,"那你尽管放心，我保证每天早练一遍，晚练一遍！青春不老，永远年轻！哈哈！星彩、银屏，你们跟我一起练，将来给你们找个好婆家！","那你尽管放心，我保证每天早练一遍，晚练一遍！青春不老，永远年轻！哈哈！星彩、银屏，你们跟我一起练，将来给你们找个好婆家！",1,"","","",999,},
        [2485] = {2704,7,2,"张星彩",216,0,"我才不要找婆家，我要永远跟着主公！","我才不要找婆家，我要永远跟着主公！",1,"","","",999,},
        [2486] = {2704,8,2,"关银屏",217,0,"我也是，我也是！我要永远为主公战斗！","我也是，我也是！我要永远为主公战斗！",1,"","","",999,},
        [2487] = {2704,9,1,"主角",1,0,"哈哈哈哈！好好好！听说陈留又有黄巾余党了！走，我们去剿灭黄巾吧！","哈哈哈哈！好好好！听说陈留又有黄巾余党了！走，我们去剿灭黄巾吧！",1,"","","",999,},
        [2488] = {2801,1,1,"主角",1,0,"这陈留也算中原腹地，怎么如此民不聊生，到处都是饥民？","这陈留也算中原腹地，怎么如此民不聊生，到处都是饥民？",1,"","","张星彩",216,},
        [2489] = {2801,2,2,"张星彩",216,0,"主公，这两年蝗灾很严重，庄稼都被蝗虫吃光了。关东一境，每谷一斛，直钱五十贯，人民相食，惨不忍睹啊。","主公，这两年蝗灾很严重，庄稼都被蝗虫吃光了。关东一境，每谷一斛，直钱五十贯，人民相食，惨不忍睹啊。",1,"","","主角",1,},
        [2490] = {2801,3,1,"主角",1,0,"此地是曹操的势力范围，我们去找曹操要些粮食，救济灾民。","此地是曹操的势力范围，我们去找曹操要些粮食，救济灾民。",1,"","","张星彩",216,},
        [2491] = {2802,1,1,"主角",1,0,"曹兄，我一路从江东赶来陈留，却见到此地遍地是灾民，曹兄何不拿些粮食出来赈济灾民？","曹兄，我一路从江东赶来陈留，却见到此地遍地是灾民，曹兄何不拿些粮食出来赈济灾民？",1,"","","曹操",103,},
        [2492] = {2802,2,2,"曹操",103,0,"小兄弟你有所不知，最近蝗灾严重，我军中也没多少粮食，况且刚与吕布交战，我这边也正在发愁呢？","小兄弟你有所不知，最近蝗灾严重，我军中也没多少粮食，况且刚与吕布交战，我这边也正在发愁呢？",1,"","","主角",1,},
        [2493] = {2802,3,2,"荀彧",102,0,"两位，黄巾余党何仪、黄劭等，劫掠州郡，多有金帛、粮食，","两位，黄巾余党何仪、黄劭等，劫掠州郡，多有金帛、粮食，",1,"","","主角",1,},
        [2494] = {2802,4,2,"荀彧",102,0,"此等贼徒，又容易破；破而取其粮，以养三军，朝廷喜，百姓悦，乃顺天之事也。","此等贼徒，又容易破；破而取其粮，以养三军，朝廷喜，百姓悦，乃顺天之事也。",1,"","","主角",1,},
        [2495] = {2802,5,1,"主角",1,0,"好主意，曹兄，剿灭黄巾，我为先锋，你等我胜利归来的好消息。","好主意，曹兄，剿灭黄巾，我为先锋，你等我胜利归来的好消息。",1,"","","荀彧",102,},
        [2496] = {2803,1,2,"黄劭",134,0,"我乃截天夜叉黄劭！尔等何人？","我乃截天夜叉黄劭！尔等何人？",1,"","","关银屏",217,},
        [2497] = {2803,2,1,"关银屏",217,0,"我主公乃当今天子亲封的扬威将军，黄巾贼人，还不束手就擒！","我主公乃当今天子亲封的扬威将军，黄巾贼人，还不束手就擒！",1,"","","黄劭",134,},
        [2498] = {2803,3,2,"黄劭",134,0,"什么扬威将军，没听说过！管你是谁，胆敢进犯我黄巾天军，今日小爷要打得你们满地求饶！","什么扬威将军，没听说过！管你是谁，胆敢进犯我黄巾天军，今日小爷要打得你们满地求饶！",1,"","","关银屏",217,},
        [2499] = {2804,1,2,"黄劭",134,0,"小的有眼不识扬威将军，将军饶命啊，饶命啊！","小的有眼不识扬威将军，将军饶命啊，饶命啊！",1,"","","关银屏",217,},
        [2500] = {2804,2,1,"关银屏",217,0,"现在知道找我家主公饶命了！哼！","现在知道找我家主公饶命了！哼！",1,"","","黄劭",134,},
        [2501] = {2804,3,1,"主角",1,0,"行，不要你的命也行，把你寨中粮食金帛一并交出，可饶你不死。","行，不要你的命也行，把你寨中粮食金帛一并交出，可饶你不死。",1,"","","黄劭",134,},
        [2502] = {2804,4,2,"黄劭",134,0,"不瞒将军，我们寨中来了一位大汉，将我数百骑擒在寨中，将军想拿粮食金帛，须得赢过他才是。","不瞒将军，我们寨中来了一位大汉，将我数百骑擒在寨中，将军想拿粮食金帛，须得赢过他才是。",1,"","","主角",1,},
        [2503] = {2804,5,1,"关银屏",217,0,"何方神圣，竟敢抢主公的东西。走，去看看！","何方神圣，竟敢抢主公的东西。走，去看看！",1,"","","黄劭",134,},
        [2504] = {2805,1,2,"关银屏",217,0,"主公快看，就是这个大胖子抢了我们的东西。","主公快看，就是这个大胖子抢了我们的东西。",1,"","","主角",1,},
        [2505] = {2805,2,1,"主角",1,0,"这位壮士身长八尺，腰大十围，看着好生了得。","这位壮士身长八尺，腰大十围，看着好生了得。",1,"","","关银屏",217,},
        [2506] = {2805,3,2,"关银屏",217,0,"主公你太含蓄了，他不就是胖么。大胖子，快将黄巾余党交出！","主公你太含蓄了，他不就是胖么。大胖子，快将黄巾余党交出！",1,"","","主角",1,},
        [2507] = {2805,4,2,"许褚",108,0,"哼哼，你们若赢得我手中这一百八十斤的流星锤，我便献出！如何？","哼哼，你们若赢得我手中这一百八十斤的流星锤，我便献出！如何？",1,"","","主角",1,},
        [2508] = {2805,5,1,"主角",1,0,"银屏，你靠后我来，别被他流星锤误伤了！","银屏，你靠后我来，别被他流星锤误伤了！",1,"","","许褚",108,},
        [2509] = {2806,1,2,"许褚",108,0,"许褚输的心服口服，这枚名将令，愿献给将军！许褚愿意追随将军！","许褚输的心服口服，这枚名将令，愿献给将军！许褚愿意追随将军！",1,"","","主角",1,},
        [2510] = {2806,2,1,"主角",1,0,"我长期四处游历，你不如跟着曹操。","我长期四处游历，你不如跟着曹操。",1,"","","许褚",108,},
        [2511] = {2806,3,1,"主角",1,0,"曹兄，我为你引荐一员大将，此乃许褚，武艺与典韦不相上下。","曹兄，我为你引荐一员大将，此乃许褚，武艺与典韦不相上下。",1,"","","曹操",103,},
        [2512] = {2806,4,2,"曹操",103,0,"哈哈，操幸得两大虎卫，左典韦，右许褚，恐怕这世上再也无人可伤我也！","哈哈，操幸得两大虎卫，左典韦，右许褚，恐怕这世上再也无人可伤我也！",1,"","","主角",1,},
        [2513] = {2901,1,2,"曹操",103,0,"小英雄，张绣用贾诩为谋士，结连刘表，屯兵宛城，你怎么看这事？","小英雄，张绣用贾诩为谋士，结连刘表，屯兵宛城，你怎么看这事？",1,"","","主角",1,},
        [2514] = {2901,2,1,"主角",1,0,"曹兄，我看不如派人劝降张绣。贾诩此人我接触过，有谋断，而且识时务。我来帮曹兄劝降，不废一兵一卒就能拿下宛城。","曹兄，我看不如派人劝降张绣。贾诩此人我接触过，有谋断，而且识时务。我来帮曹兄劝降，不废一兵一卒就能拿下宛城。",1,"","","曹操",103,},
        [2515] = {2901,3,2,"曹操",103,0,"如此甚好，那就多谢小英雄了！","如此甚好，那就多谢小英雄了！",1,"","","主角",1,},
        [2516] = {2902,1,1,"曹操",103,0,"夫人贵姓？","夫人贵姓？",1,"","","邹氏",130,},
        [2517] = {2902,2,2,"邹氏",130,0,"妾乃张济之妻邹氏也。","妾乃张济之妻邹氏也。",1,"","","曹操",103,},
        [2518] = {2902,3,1,"曹操",103,0,"夫人识吾否？","夫人识吾否？",1,"","","邹氏",130,},
        [2519] = {2902,4,2,"邹氏",130,0,"久闻丞相威名，今夕幸得瞻拜。","久闻丞相威名，今夕幸得瞻拜。",1,"","","曹操",103,},
        [2520] = {2902,5,1,"曹操",103,0,"吾为夫人故，特纳张绣之降；不然灭族矣。","吾为夫人故，特纳张绣之降；不然灭族矣。",1,"","","邹氏",130,},
        [2521] = {2902,6,1,"曹操",103,0,"今日得见夫人，乃天幸也。今宵愿同枕席，随吾还都，安享富贵，何如？","今日得见夫人，乃天幸也。今宵愿同枕席，随吾还都，安享富贵，何如？",1,"","","邹氏",130,},
        [2522] = {2902,7,2,"邹氏",130,0,"妾愿追随丞相。","妾愿追随丞相。",1,"","","曹操",103,},
        [2523] = {2903,1,2,"张绣",439,0,"操竟侮辱我婶婶，辱我太甚！定要斩杀曹操，方才泄我心头之恨！","操竟侮辱我婶婶，辱我太甚！定要斩杀曹操，方才泄我心头之恨！",1,"","","贾诩",407,},
        [2524] = {2903,2,1,"贾诩",407,0,"此事不可泄漏。来日等操出帐议事，你可称降兵多有逃亡者，盾兵于中军。","此事不可泄漏。来日等操出帐议事，你可称降兵多有逃亡者，盾兵于中军。",1,"","","张绣",439,},
        [2525] = {2903,3,2,"张绣",439,0,"但那曹操手下典韦特别勇猛，该如何应付？","但那曹操手下典韦特别勇猛，该如何应付？",1,"","","贾诩",407,},
        [2526] = {2903,4,1,"贾诩",407,0,"有何难。明日可请他来吃酒，使尽醉而归。那时便派人偷入帐房，先盗其戟，此人不足畏矣。","有何难。明日可请他来吃酒，使尽醉而归。那时便派人偷入帐房，先盗其戟，此人不足畏矣。",1,"","","张绣",439,},
        [2527] = {2903,5,2,"张绣",439,0,"文和妙计妙计！我定要斩杀曹操","文和妙计妙计！我定要斩杀曹操",1,"","","贾诩",407,},
        [2528] = {2904,1,1,"主角",1,0,"奇怪，这城中怎的如此混乱？张绣不是已经投降曹操了吗？","奇怪，这城中怎的如此混乱？张绣不是已经投降曹操了吗？",1,"","","关银屏",217,},
        [2529] = {2904,2,2,"关银屏",217,0,"主公不好了，那张绣本来已经投降。曹操入宛城后与张绣婶娘邹氏取乐，张绣知晓后，定下计谋，正对付曹操呢。","主公不好了，那张绣本来已经投降。曹操入宛城后与张绣婶娘邹氏取乐，张绣知晓后，定下计谋，正对付曹操呢。",1,"","","主角",1,},
        [2530] = {2904,3,1,"主角",1,0,"那我们快去救曹操。","那我们快去救曹操。",1,"","","关银屏",217,},
        [2531] = {2905,1,2,"张星彩",216,0,"我们来晚了一步，典韦为保护曹操已经身受重伤。","我们来晚了一步，典韦为保护曹操已经身受重伤。",1,"","","主角",1,},
        [2532] = {2905,2,1,"主角",1,0,"星彩，你快帮典韦看看。","星彩，你快帮典韦看看。",1,"","","张星彩",216,},
        [2533] = {2905,3,2,"张星彩",216,0,"主公，星彩无力回天了，典将军的伤势太重了！","主公，星彩无力回天了，典将军的伤势太重了！",1,"","","主角",1,},
        [2534] = {2905,4,2,"典韦",107,0,"我拦住寨门，丞相从寨后骑马奔逃，但追兵甚多，极其危险。","我拦住寨门，丞相从寨后骑马奔逃，但追兵甚多，极其危险。",1,"","","主角",1,},
        [2535] = {2905,5,2,"典韦",107,0,"扬威将军，你与丞相一向亲厚！请你速速前往营救丞相，否则典韦死不瞑目！","扬威将军，你与丞相一向亲厚！请你速速前往营救丞相，否则典韦死不瞑目！",1,"","","主角",1,},
        [2536] = {2905,6,1,"主角",1,0,"典将军放心，我一定会救出曹操！","典将军放心，我一定会救出曹操！",1,"","","典韦",107,},
        [2537] = {2905,7,2,"典韦",107,0,"主公，快走……（典韦低呼一声，即死）","主公，快走……（典韦低呼一声，即死）",1,"","","主角",1,},
        [2538] = {3001,1,2,"张星彩",216,0,"主公，我们一路追赶为何没有看到曹操身影，莫非他不是走的这条路？","主公，我们一路追赶为何没有看到曹操身影，莫非他不是走的这条路？",1,"","","主角",1,},
        [2539] = {3001,2,1,"主角",1,0,"也许是曹操骑着马慌不择路，不知道跑向哪边去了。莫慌，迎面那人好像是曹操手下的于禁。","也许是曹操骑着马慌不择路，不知道跑向哪边去了。莫慌，迎面那人好像是曹操手下的于禁。",1,"","","张星彩",216,},
        [2540] = {3001,3,2,"于禁",119,0,"扬威将军，听闻宛城又乱，你可曾看到丞相？","扬威将军，听闻宛城又乱，你可曾看到丞相？",1,"","","主角",1,},
        [2541] = {3001,4,1,"主角",1,0,"我刚打宛城出来，那张绣突然叛变，典韦将军已经战死，丞相不知逃向何方。","我刚打宛城出来，那张绣突然叛变，典韦将军已经战死，丞相不知逃向何方。",1,"","","于禁",119,},
        [2542] = {3001,5,2,"于禁",119,0,"竟有此等事情！劳烦将军，我这就回兖州调兵遣将来援！","竟有此等事情！劳烦将军，我这就回兖州调兵遣将来援！",1,"","","主角",1,},
        [2543] = {3001,6,1,"主角",1,0,"张绣人多势众，如此甚好，那我们兵分两路，我继续寻找丞相！","张绣人多势众，如此甚好，那我们兵分两路，我继续寻找丞相！",1,"","","于禁",119,},
        [2544] = {3002,1,2,"曹昂",124,0,"父亲，您的马已经中了三箭，如今马眼又中了一箭，不能再跑了。父亲，您快骑孩儿的马快走吧！","父亲，您的马已经中了三箭，如今马眼又中了一箭，不能再跑了。父亲，您快骑孩儿的马快走吧！",1,"","","曹操",103,},
        [2545] = {3002,2,1,"曹操",103,0,"为父骑了你的马，你怎么办？","为父骑了你的马，你怎么办？",1,"","","曹昂",124,},
        [2546] = {3002,3,2,"曹昂",124,0,"父亲大可放心，我武艺高强，留下来断后！（说着一拍马的臀部，马儿驮着曹操疾驰而去，后面箭矢如雨，曹昂竭力厮杀）","父亲大可放心，我武艺高强，留下来断后！（说着一拍马的臀部，马儿驮着曹操疾驰而去，后面箭矢如雨，曹昂竭力厮杀）",1,"","","曹操",103,},
        [2547] = {3003,1,1,"丁夫人",227,0,"（丁夫人听说曹昂战死，怒而回到故乡）曹操，你害死了我的儿子，从此我没什么可留恋的！","（丁夫人听说曹昂战死，怒而回到故乡）曹操，你害死了我的儿子，从此我没什么可留恋的！",1,"","","曹操",103,},
        [2548] = {3003,2,2,"曹操",103,0,"夫人，我们一起坐车回家，可好？","夫人，我们一起坐车回家，可好？",1,"","","丁夫人",227,},
        [2549] = {3003,3,1,"丁夫人",227,0,"不！","不！",1,"","","曹操",103,},
        [2550] = {3003,4,2,"曹操",103,0,"夫人，跟我回去可好？","夫人，跟我回去可好？",1,"","","丁夫人",227,},
        [2551] = {3003,5,1,"丁夫人",227,0,"不！","不！",1,"","","曹操",103,},
        [2552] = {3003,6,2,"曹操",103,0,"我一生做事，没有什么后悔的。假如死后还有灵的话，曹昂如果问我他的母亲在哪，我将怎么回答啊！","我一生做事，没有什么后悔的。假如死后还有灵的话，曹昂如果问我他的母亲在哪，我将怎么回答啊！",1,"","","丁夫人",227,},
        [2553] = {3004,1,2,"夏侯惇",105,0,"扬威将军，我敬你和丞相亲厚，但你何故打杀我统领的青州兵？","扬威将军，我敬你和丞相亲厚，但你何故打杀我统领的青州兵？",1,"","","主角",1,},
        [2554] = {3004,2,1,"主角",1,0,"夏侯将军，竟敢来反问我？你的青州军，当初是我帮丞相收服的黄巾余党。","夏侯将军，竟敢来反问我？你的青州军，当初是我帮丞相收服的黄巾余党。",1,"","","夏侯惇",105,},
        [2555] = {3004,3,1,"主角",1,0,"我从宛城营救丞相，沿途却看到青州军劫掠民家，我便杀了！怎滴，将军不服？","我从宛城营救丞相，沿途却看到青州军劫掠民家，我便杀了！怎滴，将军不服？",1,"","","夏侯惇",105,},
        [2556] = {3004,4,2,"夏侯惇",105,0,"夏侯惇鲁莽！竟有如此事情，都怪夏侯惇治军不严！","夏侯惇鲁莽！竟有如此事情，都怪夏侯惇治军不严！",1,"","","主角",1,},
        [2557] = {3005,1,2,"曹操",103,0,"于禁，青州兵说你趁宛城之事，趁机造反，你可知罪！","于禁，青州兵说你趁宛城之事，趁机造反，你可知罪！",1,"","","于禁",119,},
        [2558] = {3005,2,1,"主角",1,0,"曹兄，此事乃天大的冤枉！","曹兄，此事乃天大的冤枉！",1,"","","曹操",103,},
        [2559] = {3005,3,2,"曹操",103,0,"哦？","哦？",1,"","","主角",1,},
        [2560] = {3005,4,1,"主角",1,0,"那青州黄巾原本是黄巾贼，你可还记得。他们中有些人贼性不改，趁乱打家劫舍，我和于禁将军见到，便狠狠教训了这些人。","那青州黄巾原本是黄巾贼，你可还记得。他们中有些人贼性不改，趁乱打家劫舍，我和于禁将军见到，便狠狠教训了这些人。",1,"","","曹操",103,},
        [2561] = {3005,5,2,"曹操",103,0,"原来如此。于禁，险些错怪了你，为何不早些向我报备呢？","原来如此。于禁，险些错怪了你，为何不早些向我报备呢？",1,"","","于禁",119,},
        [2562] = {3005,6,1,"于禁",119,0,"当时情况紧急，敌人在后面追来，我首先抗敌，曹公是明智的人，正是谣言止于智者。","当时情况紧急，敌人在后面追来，我首先抗敌，曹公是明智的人，正是谣言止于智者。",1,"","","曹操",103,},
        [2563] = {3005,7,2,"曹操",103,0,"将军在匆忙之中，能整兵坚垒，任谤任劳，使反败为胜，虽古之名将，何以加兹！","将军在匆忙之中，能整兵坚垒，任谤任劳，使反败为胜，虽古之名将，何以加兹！",1,"","","主角",1,},
        [2564] = {3005,8,1,"主角",1,0,"曹兄英明！","曹兄英明！",1,"","","曹操",103,},
        [2565] = {3005,9,2,"曹操",103,0,"宛城一役，吾折长子、爱侄，俱无深痛，独号泣典韦也！","宛城一役，吾折长子、爱侄，俱无深痛，独号泣典韦也！",1,"","","主角",1,},
        [2566] = {3101,1,1,"袁术",412,0,"昔汉高祖不过泗上一亭长，而有天下；今历年四百，气数已尽，海内鼎沸。","昔汉高祖不过泗上一亭长，而有天下；今历年四百，气数已尽，海内鼎沸。",1,"","","阁象",133,},
        [2567] = {3101,2,1,"袁术",412,0,"吾家四世三公，百姓所归；吾效应天顺人，正位九五。尔众人以为何如？","吾家四世三公，百姓所归；吾效应天顺人，正位九五。尔众人以为何如？",1,"","","阁象",133,},
        [2568] = {3101,3,2,"阁象",133,0,"不可。昔周后稷积德累功，至于文王，三分天下有其二，犹以服事殷。明公家世虽贵，未若有周之盛；汉室虽微，未若殷纣之暴也。此事决不可行。","不可。昔周后稷积德累功，至于文王，三分天下有其二，犹以服事殷。明公家世虽贵，未若有周之盛；汉室虽微，未若殷纣之暴也。此事决不可行。",1,"","","袁术",412,},
        [2569] = {3101,4,1,"袁术",412,0,"吾袁姓出于陈。陈乃大舜之后。以土承火，正应其运。又谶云：代汉者，当涂高也。吾字公路，正应其谶。","吾袁姓出于陈。陈乃大舜之后。以土承火，正应其运。又谶云：代汉者，当涂高也。吾字公路，正应其谶。",1,"","","阁象",133,},
        [2570] = {3101,5,1,"袁术",412,0,"又有传国玉玺。若不为君，背天道也。吾意已决，多言者斩！","又有传国玉玺。若不为君，背天道也。吾意已决，多言者斩！",1,"","","阁象",133,},
        [2571] = {3102,1,2,"张星彩",216,0,"主公，听说那袁术胆大妄为，在寿春要称帝！","主公，听说那袁术胆大妄为，在寿春要称帝！",1,"","","主角",1,},
        [2572] = {3102,2,2,"关银屏",217,0,"袁术此人一向心术不正，竟然要称帝！呵呵哒！我家主公可是大汉天子亲封的扬威将军！","袁术此人一向心术不正，竟然要称帝！呵呵哒！我家主公可是大汉天子亲封的扬威将军！",1,"","","主角",1,},
        [2573] = {3102,3,1,"主角",1,0,"袁术这个便宜皇帝要是能坐稳，我就脱光了在洛阳城上爬三圈！哼！","袁术这个便宜皇帝要是能坐稳，我就脱光了在洛阳城上爬三圈！哼！",1,"","","关银屏",217,},
        [2574] = {3102,4,2,"张星彩",216,0,"主公，现在天气很冷，你脱光了在洛阳城上爬，会冻死的！","主公，现在天气很冷，你脱光了在洛阳城上爬，会冻死的！",1,"","","主角",1,},
        [2575] = {3102,5,2,"关银屏",217,0,"星彩，你这个笑话太冷了！","星彩，你这个笑话太冷了！",1,"","","主角",1,},
        [2576] = {3102,6,1,"主角",1,0,"（尴尬大笑）走走走，找人一起打袁术去！","（尴尬大笑）走走走，找人一起打袁术去！",1,"","","关银屏",217,},
        [2577] = {3103,1,1,"主角",1,0,"袁术，你妄自称帝，被天下诸侯所不齿，我已联合曹操、刘备与吕布，三路大军正向向寿春开进，你必将成为众矢之的！","袁术，你妄自称帝，被天下诸侯所不齿，我已联合曹操、刘备与吕布，三路大军正向向寿春开进，你必将成为众矢之的！",1,"","","袁术",412,},
        [2578] = {3103,2,2,"袁术",412,0,"玉玺在手，天下我有！谁敢拦我，我就灭了他！","玉玺在手，天下我有！谁敢拦我，我就灭了他！",1,"","","主角",1,},
        [2579] = {3104,1,2,"关银屏",217,0,"这袁术，真是搞笑！主公你看他，打不赢就跑了！好在没白来，得了一块名将令！","这袁术，真是搞笑！主公你看他，打不赢就跑了！好在没白来，得了一块名将令！",1,"","","主角",1,},
        [2580] = {3104,2,1,"主角",1,0,"这袁术虽然被我们打败了，但他手底下势力范围还是很大，我们还得想想办法才行。","这袁术虽然被我们打败了，但他手底下势力范围还是很大，我们还得想想办法才行。",1,"","","关银屏",217,},
        [2581] = {3104,3,2,"关银屏",217,0,"袁术占据了富庶的南阳、汝南和江淮一带，地盘大，人口多，钱粮充足，得想办法削弱他的势力才行。","袁术占据了富庶的南阳、汝南和江淮一带，地盘大，人口多，钱粮充足，得想办法削弱他的势力才行。",1,"","","主角",1,},
        [2582] = {3104,4,1,"主角",1,0,"无妨！袁术的势力，别的不说，江东这一块，我可以给他消灭了！","无妨！袁术的势力，别的不说，江东这一块，我可以给他消灭了！",1,"","","关银屏",217,},
        [2583] = {3104,5,2,"关银屏",217,0,"当初孙坚从洛阳找到玉玺，带到了江东，后面被袁术抢了去，孙策原本就对袁术有一肚子意见！","当初孙坚从洛阳找到玉玺，带到了江东，后面被袁术抢了去，孙策原本就对袁术有一肚子意见！",1,"","","主角",1,},
        [2584] = {3104,6,1,"主角",1,0,"走，往江东走一趟，找孙策叙叙旧去！","走，往江东走一趟，找孙策叙叙旧去！",1,"","","关银屏",217,},
        [2585] = {3201,1,2,"孙策",301,0,"小英雄难得来我江东，你帮过我这么多次，好比我的亲人！我定要好好招待！来人，招呼好酒好菜！","小英雄难得来我江东，你帮过我这么多次，好比我的亲人！我定要好好招待！来人，招呼好酒好菜！",1,"","","主角",1,},
        [2586] = {3201,2,1,"主角",1,0,"孙兄客气，哈哈！那可得把你珍藏的佳酿拿出来，可别舍不得！","孙兄客气，哈哈！那可得把你珍藏的佳酿拿出来，可别舍不得！",1,"","","孙策",301,},
        [2587] = {3201,3,2,"孙策",301,0,"好说好说，来，我敬你一杯！","好说好说，来，我敬你一杯！",1,"","","主角",1,},
        [2588] = {3201,4,1,"主角",1,0,"（酒意正憨）孙兄，我有一事相劝！","（酒意正憨）孙兄，我有一事相劝！",1,"","","孙策",301,},
        [2589] = {3201,5,2,"孙策",301,0,"你我不是外人，什么事情你尽管说！","你我不是外人，什么事情你尽管说！",1,"","","主角",1,},
        [2590] = {3201,6,1,"主角",1,0,"袁术僭号天子，众叛亲离，必将成为众矢之的，你还是与他断交的好。","袁术僭号天子，众叛亲离，必将成为众矢之的，你还是与他断交的好。",1,"","","孙策",301,},
        [2591] = {3201,7,2,"孙策",301,0,"哈哈，真是英雄所见略同。袁术早就有意称帝，我就曾写信劝阻，谁料他不听。再听你今天这番话，我更要与他决裂！","哈哈，真是英雄所见略同。袁术早就有意称帝，我就曾写信劝阻，谁料他不听。再听你今天这番话，我更要与他决裂！",1,"","","主角",1,},
        [2592] = {3201,8,1,"主角",1,0,"哈哈，孙兄有见地！","哈哈，孙兄有见地！",1,"","","孙策",301,},
        [2593] = {3201,9,2,"孙策",301,0,"我近日正要攻打庐江，一统江东六郡，小英雄可愿助我一臂之力？","我近日正要攻打庐江，一统江东六郡，小英雄可愿助我一臂之力？",1,"","","主角",1,},
        [2594] = {3201,10,1,"主角",1,0,"两肋插刀，在所不辞！","两肋插刀，在所不辞！",1,"","","孙策",301,},
        [2595] = {3201,11,2,"孙策",301,0,"哈哈！小英雄果然豪气千云！","哈哈！小英雄果然豪气千云！",1,"","","主角",1,},
        [2596] = {3202,1,2,"孙策",301,0,"庐江太守刘勋逃亡许昌投奔曹操去了。","庐江太守刘勋逃亡许昌投奔曹操去了。",1,"","","主角",1,},
        [2597] = {3202,2,1,"主角",1,0,"黄祖也被我们杀跑了，可惜没帮孙兄报父仇！","黄祖也被我们杀跑了，可惜没帮孙兄报父仇！",1,"","","孙策",301,},
        [2598] = {3202,3,2,"孙策",301,0,"不急在一时，等我一统江东，我迟早会砍了他的狗头，给我父祭天！","不急在一时，等我一统江东，我迟早会砍了他的狗头，给我父祭天！",1,"","","主角",1,},
        [2599] = {3202,4,1,"主角",1,0,"还剩下一个豫章，走，一鼓作气，全部拿下！","还剩下一个豫章，走，一鼓作气，全部拿下！",1,"","","孙策",301,},
        [2600] = {3203,1,1,"主角",1,0,"今天孙兄格外高兴啊！","今天孙兄格外高兴啊！",1,"","","孙策",301,},
        [2601] = {3203,2,2,"孙策",301,0,"哈哈，虞翻劝降成功，这豫章不废一兵一卒就拿下，我自然高兴！","哈哈，虞翻劝降成功，这豫章不废一兵一卒就拿下，我自然高兴！",1,"","","主角",1,},
        [2602] = {3203,3,1,"主角",1,0,"孙兄手下真是人才济济，咦，前面那个少年是谁？一表人才，谈吐不凡，一看就是大家之后。","孙兄手下真是人才济济，咦，前面那个少年是谁？一表人才，谈吐不凡，一看就是大家之后。",1,"","","孙策",301,},
        [2603] = {3203,4,2,"孙策",301,0,"难得有被小英雄如此夸的人物，走，我们上前仔细瞧瞧是谁。","难得有被小英雄如此夸的人物，走，我们上前仔细瞧瞧是谁。",1,"","","主角",1,},
        [2604] = {3204,1,2,"孙策",301,0,"我是孙策，你是何人？","我是孙策，你是何人？",1,"","","陆逊",311,},
        [2605] = {3204,2,1,"陆逊",311,0,"回将军，小生陆逊，字伯言，乃吴郡人士。","回将军，小生陆逊，字伯言，乃吴郡人士。",1,"","","孙策",301,},
        [2606] = {3204,3,2,"孙策",301,0,"我听闻吴郡有四大家族陆、顾、朱、张。你可是来自陆家？","我听闻吴郡有四大家族陆、顾、朱、张。你可是来自陆家？",1,"","","陆逊",311,},
        [2607] = {3204,4,1,"陆逊",311,0,"正是。","正是。",1,"","","孙策",301,},
        [2608] = {3204,5,2,"孙策",301,0,"很好，谦谦君子，不卑不亢，有读书人的风范，我看你小子将来必有大成，可曾婚配？","很好，谦谦君子，不卑不亢，有读书人的风范，我看你小子将来必有大成，可曾婚配？",1,"","","陆逊",311,},
        [2609] = {3204,6,1,"陆逊",311,0,"回将军，尚未婚配。","回将军，尚未婚配。",1,"","","孙策",301,},
        [2610] = {3204,7,2,"孙策",301,0,"哈哈，那好，你这个女婿我要定了！我将长女许给你，待她及笄，你便上门来迎娶。","哈哈，那好，你这个女婿我要定了！我将长女许给你，待她及笄，你便上门来迎娶。",1,"","","陆逊",311,},
        [2611] = {3205,1,2,"孙策",301,0,"小英雄，你看我新收的女婿如何？","小英雄，你看我新收的女婿如何？",1,"","","主角",1,},
        [2612] = {3205,2,1,"主角",1,0,"出生名门，谦谦君子，温润如玉，饱读诗书，前途不可限量。","出生名门，谦谦君子，温润如玉，饱读诗书，前途不可限量。",1,"","","孙策",301,},
        [2613] = {3205,3,2,"孙策",301,0,"哈哈哈，我也觉得不错，今天真是高兴，来来来，趁着酒兴，我们一起出游打猎去！","哈哈哈，我也觉得不错，今天真是高兴，来来来，趁着酒兴，我们一起出游打猎去！",1,"","","主角",1,},
