        [2615] = {3205,5,2,"孙策",301,0,"怕什么，我可是小霸王孙策！如今我已平定六郡，江东都是我的地界！还有谁敢对我不利！我知道你喜欢名将令，命人四处搜集得了一块赠你！","怕什么，我可是小霸王孙策！如今我已平定六郡，江东都是我的地界！还有谁敢对我不利！我知道你喜欢名将令，命人四处搜集得了一块赠你！",1,"","","主角",1,},
        [2616] = {3205,6,1,"主角",1,0,"孙兄果然霸气，多谢多谢！","孙兄果然霸气，多谢多谢！",1,"","","孙策",301,},
        [2617] = {3301,1,2,"孙策",301,0,"小英雄，可否请你再帮一个忙！","小英雄，可否请你再帮一个忙！",1,"","","主角",1,},
        [2618] = {3301,2,1,"主角",1,0,"孙兄可是为乔公的两个女儿大乔、小乔而来？","孙兄可是为乔公的两个女儿大乔、小乔而来？",1,"","","孙策",301,},
        [2619] = {3301,3,2,"孙策",301,0,"小英雄还真是神机妙算。","小英雄还真是神机妙算。",1,"","","主角",1,},
        [2620] = {3301,4,1,"主角",1,0,"哈哈，我家星彩与这小乔乃是相识，她早就告诉我了！","哈哈，我家星彩与这小乔乃是相识，她早就告诉我了！",1,"","","孙策",301,},
        [2621] = {3301,5,2,"孙策",301,0,"原来如此，孙某诚心实意请你帮忙，我与公瑾曾与姐妹二人偶遇，顿时惊为天人，看上了这对姐妹。","原来如此，孙某诚心实意请你帮忙，我与公瑾曾与姐妹二人偶遇，顿时惊为天人，看上了这对姐妹。",1,"","","主角",1,},
        [2622] = {3301,6,2,"孙策",301,0,"但这乔公却因我破宛城之故，不愿意将两个女儿嫁给我们。你是当今天子亲封的扬威将军，你去替我二人求亲，他定不会推辞。","但这乔公却因我破宛城之故，不愿意将两个女儿嫁给我们。你是当今天子亲封的扬威将军，你去替我二人求亲，他定不会推辞。",1,"","","主角",1,},
        [2623] = {3301,7,1,"主角",1,0,"好，我去试试，这顿喜酒我喝定了！","好，我去试试，这顿喜酒我喝定了！",1,"","","孙策",301,},
        [2624] = {3302,1,1,"主角",1,0,"咦，星彩怎么是你？","咦，星彩怎么是你？",1,"","","张星彩",216,},
        [2625] = {3302,2,2,"张星彩",216,0,"主公，乔公说你得通过大小乔姐妹二人的比试，他才会答应这门亲事！","主公，乔公说你得通过大小乔姐妹二人的比试，他才会答应这门亲事！",1,"","","主角",1,},
        [2626] = {3302,3,1,"主角",1,0,"哈哈，那倒简单，不用多费口舌，我这么厉害，通过比试轻轻松松啊！","哈哈，那倒简单，不用多费口舌，我这么厉害，通过比试轻轻松松啊！",1,"","","张星彩",216,},
        [2627] = {3302,4,2,"张星彩",216,0,"主公这么厉害，那就来迎战吧！","主公这么厉害，那就来迎战吧！",1,"","","主角",1,},
        [2628] = {3303,1,2,"关银屏",217,0,"孙尚香姐姐，你的武艺如此高强，将来一定要嫁个大英雄哦！","孙尚香姐姐，你的武艺如此高强，将来一定要嫁个大英雄哦！",1,"","","孙尚香",310,},
        [2629] = {3303,2,1,"孙尚香",310,0,"弓马何须忌红妆？嫁人做什么，我要上战场杀敌！","弓马何须忌红妆？嫁人做什么，我要上战场杀敌！",1,"","","关银屏",217,},
        [2630] = {3303,3,2,"关银屏",217,0,"小姐姐，你只有嫁给大英雄，才有机会帮夫君上战场啊！你若嫁个普通男儿，这辈子就只能相夫教子了！","小姐姐，你只有嫁给大英雄，才有机会帮夫君上战场啊！你若嫁个普通男儿，这辈子就只能相夫教子了！",1,"","","孙尚香",310,},
        [2631] = {3303,4,1,"孙尚香",310,0,"你说的也有几分道理，我去找我母亲，这辈子，非英雄我不嫁！","你说的也有几分道理，我去找我母亲，这辈子，非英雄我不嫁！",1,"","","关银屏",217,},
        [2632] = {3304,1,2,"张星彩",216,0,"大乔姐姐，你好美啊，真是国色天香，楚楚动人啊！","大乔姐姐，你好美啊，真是国色天香，楚楚动人啊！",1,"","","大乔",302,},
