        [2634] = {3304,3,1,"关银屏",217,0,"我关银屏的大长腿才是天下第一！","我关银屏的大长腿才是天下第一！",1,"","","张星彩",216,},
        [2635] = {3304,4,2,"张星彩",216,0,"……关银屏！","……关银屏！",1,"","","关银屏",217,},
        [2636] = {3304,5,1,"关银屏",217,0,"好了，别互相吹捧了！大乔姐姐，主公让我问你，你愿意嫁给孙策吗？","好了，别互相吹捧了！大乔姐姐，主公让我问你，你愿意嫁给孙策吗？",1,"","","大乔",302,},
        [2637] = {3304,6,1,"大乔",302,0,"我，我，愿意嫁给孙郎！","我，我，愿意嫁给孙郎！",1,"","","张星彩",216,},
        [2638] = {3304,7,2,"关银屏",217,0,"哈哈，那可太好了！郎才女貌，天生一对！","哈哈，那可太好了！郎才女貌，天生一对！",1,"","","大乔",302,},
        [2639] = {3305,1,2,"小乔",304,0,"传说中的扬威将军，我终于见到你了！还请原谅小乔的莽撞！","传说中的扬威将军，我终于见到你了！还请原谅小乔的莽撞！",1,"","","主角",1,},
        [2640] = {3305,2,1,"主角",1,0,"此话怎讲？","此话怎讲？",1,"","","小乔",304,},
        [2641] = {3305,3,2,"小乔",304,0,"其实我与公瑾早已相识，我的小白兔就是公瑾送的！拉姐姐和孙郎见面，是我的主意！定下这场比试，也是我的主意！","其实我与公瑾早已相识，我的小白兔就是公瑾送的！拉姐姐和孙郎见面，是我的主意！定下这场比试，也是我的主意！",1,"","","主角",1,},
        [2642] = {3305,4,1,"关银屏",217,0,"小乔妹妹你真是调皮，人小鬼大，看不出来啊！","小乔妹妹你真是调皮，人小鬼大，看不出来啊！",1,"","","小乔",304,},
        [2643] = {3305,5,2,"小乔",304,0,"人家定下这场比试，就是想见见传说中的扬威将军！","人家定下这场比试，就是想见见传说中的扬威将军！",1,"","","关银屏",217,},
        [2644] = {3305,6,2,"小乔",304,0,"扬威将军，小乔虽然远在江东，但早就听说过你的大名。","扬威将军，小乔虽然远在江东，但早就听说过你的大名。",1,"","","主角",1,},
        [2645] = {3305,7,2,"小乔",304,0,"今日一见，将军武艺非凡，姿容秀雅，好生厉害！公瑾说你喜欢名将令，我这有一块，愿意送给将军！","今日一见，将军武艺非凡，姿容秀雅，好生厉害！公瑾说你喜欢名将令，我这有一块，愿意送给将军！",1,"","","主角",1,},
        [2646] = {3305,8,1,"主角",1,0,"哈哈，多谢！既然你们姐妹二人都愿意，那就早些成亲，请我们喝喜酒吧！","哈哈，多谢！既然你们姐妹二人都愿意，那就早些成亲，请我们喝喜酒吧！",1,"","","小乔",304,},
        [2647] = {3401,1,2,"周瑜",303,0,"扬威将军，公瑾此次特来拜谢，若不是，恐怕这门亲事还没这么顺利。","扬威将军，公瑾此次特来拜谢，若不是，恐怕这门亲事还没这么顺利。",1,"","","主角",1,},
        [2648] = {3401,2,1,"主角",1,0,"哈哈，自古佳人出江东，大小乔姐妹国色天香花容月貌，你和伯符抱得美人归，真令人艳羡。","哈哈，自古佳人出江东，大小乔姐妹国色天香花容月貌，你和伯符抱得美人归，真令人艳羡。",1,"","","周瑜",303,},
        [2649] = {3401,3,2,"周瑜",303,0,"扬威将军说笑了……","扬威将军说笑了……",1,"","","主角",1,},
        [2650] = {3401,4,1,"主角",1,0,"快给公瑾把酒满上！来来来，我先干为敬，你随意！","快给公瑾把酒满上！来来来，我先干为敬，你随意！",1,"","","周瑜",303,},
        [2651] = {3401,5,2,"周瑜",303,0,"将军怕是喝多了！","将军怕是喝多了！",1,"","","主角",1,},
        [2652] = {3401,6,1,"主角",1,0,"没，我还没醉！走，那不是太史慈将军吗，我要找他切磋去！","没，我还没醉！走，那不是太史慈将军吗，我要找他切磋去！",1,"","","周瑜",303,},
        [2653] = {3402,1,2,"关银屏",217,0,"太史慈将军，我家主公喝醉了！想要与你切戳武艺！","太史慈将军，我家主公喝醉了！想要与你切戳武艺！",1,"","","太史慈",305,},
        [2654] = {3402,2,1,"太史慈",305,0,"银屏姑娘放心，我一定点到为止，定不会伤害到你家主公！","银屏姑娘放心，我一定点到为止，定不会伤害到你家主公！",1,"","","关银屏",217,},
        [2655] = {3402,3,2,"关银屏",217,0,"将军你想多了！我家主公本就厉害，喝醉之后只会更强！我是担心他醉后伤了你，特意来提醒你的！","将军你想多了！我家主公本就厉害，喝醉之后只会更强！我是担心他醉后伤了你，特意来提醒你的！",1,"","","太史慈",305,},
        [2656] = {3402,4,1,"太史慈",305,0,"……那就谢谢姑娘提醒。","……那就谢谢姑娘提醒。",1,"","","关银屏",217,},
        [2657] = {3403,1,2,"张星彩",216,0,"主公，星彩为您准备了醒酒汤！喝过醒酒汤，舒服多了吧。","主公，星彩为您准备了醒酒汤！喝过醒酒汤，舒服多了吧。",1,"","","主角",1,},
        [2658] = {3403,2,1,"主角",1,0,"谢谢星彩，我好多了！昨夜一时兴起喝太多了，哈哈哈。","谢谢星彩，我好多了！昨夜一时兴起喝太多了，哈哈哈。",1,"","","张星彩",216,},
        [2659] = {3403,3,2,"张星彩",216,0,"主公，门外有几位英雄求见，已等候多时了。","主公，门外有几位英雄求见，已等候多时了。",1,"","","主角",1,},
        [2660] = {3403,4,1,"主角",1,0,"哦，都是谁？","哦，都是谁？",1,"","","张星彩",216,},
        [2661] = {3403,5,2,"张星彩",216,0,"这是他们的拜帖，请主公过目！","这是他们的拜帖，请主公过目！",1,"","","主角",1,},
        [2662] = {3403,6,1,"主角",1,0,"原来是鲁肃、吕蒙，通知一下周瑜，我们一起去见见。","原来是鲁肃、吕蒙，通知一下周瑜，我们一起去见见。",1,"","","张星彩",216,},
        [2663] = {3404,1,2,"鲁肃",312,0,"鲁肃拜见将军！","鲁肃拜见将军！",1,"","","主角",1,},
        [2664] = {3404,2,1,"主角",1,0,"子敬兄，久仰久仰！我早听公瑾提过，你与他乃是旧时。","子敬兄，久仰久仰！我早听公瑾提过，你与他乃是旧时。",1,"","","鲁肃",312,},
        [2665] = {3404,3,1,"周瑜",303,0,"当初我任居巢长，听闻子敬兄素来乐善好施，便请他资助一些粮食。子敬兄二话不说，立即将一整仓粮食，足足三千斛米，赠给了我。","当初我任居巢长，听闻子敬兄素来乐善好施，便请他资助一些粮食。子敬兄二话不说，立即将一整仓粮食，足足三千斛米，赠给了我。",1,"","","鲁肃",312,},
        [2666] = {3404,4,1,"主角",1,0,"子敬兄当真豪爽！民间盛传你是乐善好施的财神爷，果不其然啊！","子敬兄当真豪爽！民间盛传你是乐善好施的财神爷，果不其然啊！",1,"","","鲁肃",312,},
        [2667] = {3404,5,2,"鲁肃",312,0,"哈哈！将军日后若有何难处大可以来找我，子敬定不推辞！","哈哈！将军日后若有何难处大可以来找我，子敬定不推辞！",1,"","","主角",1,},
        [2668] = {3405,1,2,"吕蒙",307,0,"吕蒙拜见将军！","吕蒙拜见将军！",1,"","","主角",1,},
        [2669] = {3405,2,1,"主角",1,0,"子明年纪轻轻，却如此不凡，果真是英雄出少年！","子明年纪轻轻，却如此不凡，果真是英雄出少年！",1,"","","吕蒙",307,},
        [2670] = {3405,3,2,"吕蒙",307,0,"将军过誉了！","将军过誉了！",1,"","","主角",1,},
        [2671] = {3405,4,1,"主角",1,0,"咦，你怎么随身带着船锚和竹简？","咦，你怎么随身带着船锚和竹简？",1,"","","吕蒙",307,},
        [2672] = {3405,5,2,"吕蒙",307,0,"这船锚乃是我特意打造的兵器，竹简则是兵书，子敬兄建议我熟读兵书，学以报国。","这船锚乃是我特意打造的兵器，竹简则是兵书，子敬兄建议我熟读兵书，学以报国。",1,"","","主角",1,},
        [2673] = {3405,6,1,"主角",1,0,"了得了得！子明将来前途不可限量，江东果然人才济济！","了得了得！子明将来前途不可限量，江东果然人才济济！",1,"","","吕蒙",307,},
        [2674] = {3406,1,2,"周瑜",303,0,"扬威将军，请看！","扬威将军，请看！",1,"","","主角",1,},
        [2675] = {3406,2,1,"主角",1,0,"这是曹操写给你的信！","这是曹操写给你的信！",1,"","","周瑜",303,},
        [2676] = {3406,3,2,"周瑜",303,0,"曹操竟妄想游说于我，让我投奔于他。","曹操竟妄想游说于我，让我投奔于他。",1,"","","主角",1,},
        [2677] = {3406,4,1,"主角",1,0,"哈哈，公瑾文韬武略，乃当时英才，曹操想招揽你，再正常不过。","哈哈，公瑾文韬武略，乃当时英才，曹操想招揽你，再正常不过。",1,"","","周瑜",303,},
        [2678] = {3406,5,2,"周瑜",303,0,"话虽如此，但我与伯符的情谊岂容挑拨！如今曹操天子令诸侯，素有野心，恐怕将来迟早有一战。","话虽如此，但我与伯符的情谊岂容挑拨！如今曹操天子令诸侯，素有野心，恐怕将来迟早有一战。",1,"","","主角",1,},
        [2679] = {3406,6,1,"主角",1,0,"公瑾大可放心，时日尚早！现袁绍据冀州，袁术据淮南，又有吕布虎视眈眈，曹操三面受敌，难以应付，更何况与江东隔着一条长江！","公瑾大可放心，时日尚早！现袁绍据冀州，袁术据淮南，又有吕布虎视眈眈，曹操三面受敌，难以应付，更何况与江东隔着一条长江！",1,"","","周瑜",303,},
        [2680] = {3406,7,2,"周瑜",303,0,"将军高瞻远瞩，心怀天下，令人佩服。将军此次来江东，帮了我们不少大忙，这块名将令，愿送给将军。","将军高瞻远瞩，心怀天下，令人佩服。将军此次来江东，帮了我们不少大忙，这块名将令，愿送给将军。",1,"","","主角",1,},
        [2681] = {3501,1,1,"夏侯渊",117,0,"丞相，若想荡平这乱世，需先降张绣，再定河北。","丞相，若想荡平这乱世，需先降张绣，再定河北。",1,"","","曹操",103,},
