        [2861] = {4303,2,1,"曹操",103,0,"孤让他前往徐州堵截袁术。","孤让他前往徐州堵截袁术。",1,"","","程昱",121,},
        [2862] = {4303,3,2,"程昱",121,0,"昔刘备为豫州牧时，某等请杀之，丞相不听。","昔刘备为豫州牧时，某等请杀之，丞相不听。",1,"","","曹操",103,},
        [2863] = {4303,4,2,"程昱",121,0,"今日又与之兵，此放龙入海，纵虎归山也，后欲治之，其可得乎？","今日又与之兵，此放龙入海，纵虎归山也，后欲治之，其可得乎？",1,"","","曹操",103,},
        [2864] = {4303,5,2,"郭嘉",104,0,"丞相纵不杀备，亦不当使之去。古人云：一日纵敌，万世之患。望丞相察之。","丞相纵不杀备，亦不当使之去。古人云：一日纵敌，万世之患。望丞相察之。",1,"","","曹操",103,},
