        [2871] = {4304,5,2,"许褚",108,0,"奉丞相命，特请将军回去，别有商议。","奉丞相命，特请将军回去，别有商议。",1,"","","刘备",202,},
        [2872] = {4304,6,1,"刘备",202,0,"将在外，君命有所不受。吾面过君，又蒙丞相钧语。","将在外，君命有所不受。吾面过君，又蒙丞相钧语。",1,"","","许褚",108,},
