        [2876] = {4401,1,1,"刘备",202,0,"徐州刺史，袁术情况如何？","徐州刺史，袁术情况如何？",1,"","","车胄",142,},
        [2877] = {4401,2,2,"车胄",142,0,"袁术奢侈太过，部下雷薄、陈兰皆投嵩山去了。","袁术奢侈太过，部下雷薄、陈兰皆投嵩山去了。",1,"","","刘备",202,},
        [2878] = {4401,3,2,"车胄",142,0,"袁术势甚衰，作书让帝号于袁绍，收拾人马、宫禁御用之物，马上就到徐州。","袁术势甚衰，作书让帝号于袁绍，收拾人马、宫禁御用之物，马上就到徐州。",1,"","","刘备",202,},
        [2879] = {4401,4,1,"刘备",202,0,"来得正好，谁愿领命前去？","来得正好，谁愿领命前去？",1,"","","车胄",142,},
        [2880] = {4401,5,2,"主角",1,0,"我愿领命前去斩杀那袁术逆贼！","我愿领命前去斩杀那袁术逆贼！",1,"","","刘备",202,},
