        [2884] = {4402,3,2,"纪灵",435,0,"休得妄言！","休得妄言！",1,"","","主角",1,},
        [2885] = {4403,1,1,"刘备",202,0,"汝反逆不道，吾今奉明诏前来讨汝！汝先锋纪灵已被我等斩杀。","汝反逆不道，吾今奉明诏前来讨汝！汝先锋纪灵已被我等斩杀。",1,"","","袁术",412,},
        [2886] = {4403,2,1,"刘备",202,0,"快束手就擒。","快束手就擒。",1,"","","袁术",412,},
        [2887] = {4403,3,2,"袁术",412,0,"织席编屦小辈，安敢轻我！","织席编屦小辈，安敢轻我！",1,"","","刘备",202,},
        [2888] = {4404,1,1,"袁术",412,0,"伙夫何在！伙夫何在！","伙夫何在！伙夫何在！",1,"","","",0,},
        [2889] = {4404,2,2,"伙夫",138,0,"我在，大人有何吩咐。","我在，大人有何吩咐。",1,"","","袁术",412,},
        [2890] = {4404,3,1,"袁术",412,0,"米饭如此粗糙，难以下咽，快取蜜水给我止渴。","米饭如此粗糙，难以下咽，快取蜜水给我止渴。",1,"","","伙夫",138,},
        [2891] = {4404,4,2,"伙夫",138,0,"前被扬威将军与刘备所败，后遇群盗偷袭，早已粮食尽绝。","前被扬威将军与刘备所败，后遇群盗偷袭，早已粮食尽绝。",1,"","","袁术",412,},
