        [2894] = {4404,7,1,"袁术",412,0,"吾乃四世三公名门之后，今日竟落得如此境地！可恶！（随即倒于地下，吐血斗余而死。）","吾乃四世三公名门之后，今日竟落得如此境地！可恶！（随即倒于地下，吐血斗余而死。）",1,"","","伙夫",138,},
        [2895] = {4501,1,2,"曹操",103,0,"好一个衣带诏！想密谋除掉我？","好一个衣带诏！想密谋除掉我？",1,"","","汉献帝",425,},
        [2896] = {4501,2,2,"曹操",103,0,"反贼董承已被孤满门抄斩，你那董妃也难逃一死！","反贼董承已被孤满门抄斩，你那董妃也难逃一死！",1,"","","汉献帝",425,},
        [2897] = {4501,3,1,"汉献帝",425,0,"我不知道什么衣带诏，纯属董承一人所为，与我无关，与我无关！","我不知道什么衣带诏，纯属董承一人所为，与我无关，与我无关！",1,"","","曹操",103,},
