        [2899] = {4502,1,1,"曹操",103,0,"刘备斩纪灵败袁术，令朱灵、路昭回许都，表面上留下军马保守徐州，实则早有反叛之心。","刘备斩纪灵败袁术，令朱灵、路昭回许都，表面上留下军马保守徐州，实则早有反叛之心。",1,"","","车胄",142,},
        [2900] = {4502,2,1,"曹操",103,0,"你徐州刺史应设计将其杀掉。","你徐州刺史应设计将其杀掉。",1,"","","车胄",142,},
        [2901] = {4502,3,2,"车胄",142,0,"臣领钧旨。","臣领钧旨。",1,"","","曹操",103,},
        [2902] = {4503,1,1,"车胄",142,0,"元龙，刘备有谋逆反叛之心。","元龙，刘备有谋逆反叛之心。",1,"","","陈登",432,},
        [2903] = {4503,2,1,"车胄",142,0,"曹丞相要我秘密杀掉刘备，你可有计策？","曹丞相要我秘密杀掉刘备，你可有计策？",1,"","","陈登",432,},
        [2904] = {4503,3,2,"陈登",432,0,"此事极易。今刘备出城招民，不日将还。","此事极易。今刘备出城招民，不日将还。",1,"","","车胄",142,},
        [2905] = {4503,4,2,"陈登",432,0,"将军可命军士伏于瓮城边，只作接他，待马到来，一刀斩之。","将军可命军士伏于瓮城边，只作接他，待马到来，一刀斩之。",1,"","","车胄",142,},
        [2906] = {4503,5,2,"陈登",432,0,"某在城上射住后军，大事济矣！","某在城上射住后军，大事济矣！",1,"","","车胄",142,},
        [2907] = {4503,6,1,"车胄",142,0,"就依元龙你的计策来办。","就依元龙你的计策来办。",1,"","","陈登",432,},
        [2908] = {4504,1,2,"陈登",432,0,"云长兄，曹操命车胄暗杀你家刘备。","云长兄，曹操命车胄暗杀你家刘备。",1,"","","关羽",205,},
        [2909] = {4504,2,2,"陈登",432,0,"想在玄德兄出城招民归来时，于瓮城边将其斩杀。","想在玄德兄出城招民归来时，于瓮城边将其斩杀。",1,"","","关羽",205,},
        [2910] = {4504,3,1,"关羽",205,0,"那就将计就计，我乘夜扮作曹军先于备到徐州，引车胄出迎，袭而杀之。","那就将计就计，我乘夜扮作曹军先于备到徐州，引车胄出迎，袭而杀之。",1,"","","陈登",432,},
        [2911] = {4504,4,2,"陈登",432,0,"好，若车胄出城，我在城上乱箭夹击。","好，若车胄出城，我在城上乱箭夹击。",1,"","","关羽",205,},
        [2912] = {4505,1,2,"关羽",205,0,"得陈登相告，车胄奉曹操之命想要谋害你。","得陈登相告，车胄奉曹操之命想要谋害你。",1,"","","刘备",202,},
        [2913] = {4505,2,2,"关羽",205,0,"我与陈登已将计就计将其斩杀！","我与陈登已将计就计将其斩杀！",1,"","","刘备",202,},
        [2914] = {4505,3,1,"刘备",202,0,"曹操若来，如之奈何？","曹操若来，如之奈何？",1,"","","关羽",205,},
        [2915] = {4505,4,2,"关羽",205,0,"弟与张飞迎之。","弟与张飞迎之。",1,"","","刘备",202,},
        [2916] = {4505,5,1,"刘备",202,0,"杀了曹操心腹之人，如何肯休？","杀了曹操心腹之人，如何肯休？",1,"","","关羽",205,},
        [2917] = {4505,6,2,"陈登",432,0,"玄德莫慌，你入徐州便是，我有一计，可助你退曹操。","玄德莫慌，你入徐州便是，我有一计，可助你退曹操。",1,"","","刘备",202,},
        [2918] = {4601,1,2,"陈登",432,0,"袁绍虎踞冀、青、幽、并诸郡，带甲百万，文官武将极多，曹操还是畏惧他的。","袁绍虎踞冀、青、幽、并诸郡，带甲百万，文官武将极多，曹操还是畏惧他的。",1,"","","刘备",202,},
        [2919] = {4601,2,2,"陈登",432,0,"今何不写书遣人到彼求救？","今何不写书遣人到彼求救？",1,"","","刘备",202,},
        [2920] = {4601,3,1,"刘备",202,0,"袁绍向来不与我往来，今日又消灭了他弟，安肯相助？","袁绍向来不与我往来，今日又消灭了他弟，安肯相助？",1,"","","陈登",432,},
        [2921] = {4601,4,2,"陈登",432,0,"此间有一人与袁绍三世通家，若得其一书致绍，袁绍必来相助。","此间有一人与袁绍三世通家，若得其一书致绍，袁绍必来相助。",1,"","","刘备",202,},
        [2922] = {4601,5,1,"刘备",202,0,"何人也？","何人也？",1,"","","陈登",432,},
