        [2924] = {4601,7,1,"刘备",202,0,"莫非郑康成先生乎。","莫非郑康成先生乎。",1,"","","陈登",432,},
        [2925] = {4601,8,2,"陈登",432,0,"然也。","然也。",1,"","","刘备",202,},
        [2926] = {4602,1,1,"袁绍",411,0,"郑尚书有书来，令我起兵助刘备，攻曹操。起兵是乎？不起兵是乎？","郑尚书有书来，令我起兵助刘备，攻曹操。起兵是乎？不起兵是乎？",1,"","","",0,},
        [2927] = {4602,2,2,"沮授",422,0,"明公以众克寡，以强攻弱，讨汉贼以扶王室：起兵是也。","明公以众克寡，以强攻弱，讨汉贼以扶王室：起兵是也。",1,"","","袁绍",411,},
        [2928] = {4602,3,1,"袁绍",411,0,"所言正合我心。","所言正合我心。",1,"","","沮授",422,},
        [2929] = {4602,4,1,"袁绍",411,0,"田丰、荀谌、许攸为谋士，颜良、文丑为将军，领兵三十万，望黎阳进发。","田丰、荀谌、许攸为谋士，颜良、文丑为将军，领兵三十万，望黎阳进发。",1,"","","沮授",422,},
        [2930] = {4602,5,1,"袁绍",411,0,"令书记陈琳作讨曹操檄文，把曹操的罪状公布于天下。","令书记陈琳作讨曹操檄文，把曹操的罪状公布于天下。",1,"","","沮授",422,},
        [2931] = {4603,1,2,"孔融",417,0,"曹操，书写你罪状的檄文已传遍许都。","曹操，书写你罪状的檄文已传遍许都。",1,"","","曹操",103,},
        [2932] = {4603,2,1,"曹操",103,0,"孤已知晓。方患头风，见了那檄文，竟出了一身冷汗，不觉头风顿愈！","孤已知晓。方患头风，见了那檄文，竟出了一身冷汗，不觉头风顿愈！",1,"","","孔融",417,},
        [2933] = {4603,3,2,"孔融",417,0,"头风愈了就好，可是那袁绍势大，不可与战，只可与和。","头风愈了就好，可是那袁绍势大，不可与战，只可与和。",1,"","","曹操",103,},
        [2934] = {4603,4,1,"荀彧",102,0,"袁绍无用之人，何必议和？","袁绍无用之人，何必议和？",1,"","","孔融",417,},
        [2935] = {4603,5,2,"孔融",417,0,"袁绍士广民强，其部下如许攸、郭图、审配、逢纪皆智谋之士。","袁绍士广民强，其部下如许攸、郭图、审配、逢纪皆智谋之士。",1,"","","荀彧",102,},
        [2936] = {4603,6,2,"孔融",417,0,"田丰、沮授皆忠臣也，颜良、文丑勇冠三军，其余高览、张郃、淳于琼等俱世之名将。","田丰、沮授皆忠臣也，颜良、文丑勇冠三军，其余高览、张郃、淳于琼等俱世之名将。",1,"","","荀彧",102,},
        [2937] = {4603,7,2,"孔融",417,0,"何谓绍为无用之人乎","何谓绍为无用之人乎",1,"","","荀彧",102,},
        [2938] = {4603,8,1,"荀彧",102,0,"绍兵多而不整，田丰刚而犯上，许攸贪而不智，审配专而无谋，逢纪果而无用。","绍兵多而不整，田丰刚而犯上，许攸贪而不智，审配专而无谋，逢纪果而无用。",1,"","","孔融",417,},
        [2939] = {4603,9,1,"荀彧",102,0,"此数人者，势不相容，必生内变，颜良、文丑，匹夫之勇，一战可擒。","此数人者，势不相容，必生内变，颜良、文丑，匹夫之勇，一战可擒。",1,"","","孔融",417,},
        [2940] = {4603,10,1,"荀彧",102,0,"其余碌碌等辈，纵有百万，何足道哉！","其余碌碌等辈，纵有百万，何足道哉！",1,"","","孔融",417,},
        [2941] = {4603,11,2,"曹操",103,0,"皆不出荀文若之料。","皆不出荀文若之料。",1,"","","荀彧",102,},
        [2942] = {4603,12,2,"曹操",103,0,"令刘岱、后军王忠引军五万，打着孤旗号，去徐州攻刘备。","令刘岱、后军王忠引军五万，打着孤旗号，去徐州攻刘备。",1,"","","荀彧",102,},
        [2943] = {4604,1,1,"刘备",202,0,"袁本初虽屯兵黎阳，奈谋臣不和，尚未进取。","袁本初虽屯兵黎阳，奈谋臣不和，尚未进取。",1,"","","陈登",432,},
        [2944] = {4604,2,1,"刘备",202,0,"曹操不知在何处。闻黎阳军中，无操旗号，如何这里却反有他旗号？","曹操不知在何处。闻黎阳军中，无操旗号，如何这里却反有他旗号？",1,"","","陈登",432,},
        [2945] = {4604,3,2,"陈登",432,0,"操诡计百出，必以河北为重，亲自监督，却故意不建旗号，乃于此处虚张旗号，操不在此。","操诡计百出，必以河北为重，亲自监督，却故意不建旗号，乃于此处虚张旗号，操不在此。",1,"","","刘备",202,},
        [2946] = {4604,4,1,"刘备",202,0,"谁可探听虚实？","谁可探听虚实？",1,"","","陈登",432,},
        [2947] = {4604,5,2,"关羽",205,0,"小弟愿往。","小弟愿往。",1,"","","刘备",202,},
        [2948] = {4604,6,2,"主角",1,0,"我愿助云长一臂之力，一同前去。","我愿助云长一臂之力，一同前去。",1,"","","刘备",202,},
        [2949] = {4605,1,2,"王忠",134,0,"丞相到此，缘何不降？","丞相到此，缘何不降？",1,"","","关羽",205,},
        [2950] = {4605,2,1,"关羽",205,0,"请丞相出阵，我自有话说。","请丞相出阵，我自有话说。",1,"","","王忠",134,},
        [2951] = {4605,3,2,"王忠",134,0,"丞相岂肯轻见你！","丞相岂肯轻见你！",1,"","","关羽",205,},
        [2952] = {4605,4,1,"关羽",205,0,"丞相并不在此，尔等岂容你戏弄。","丞相并不在此，尔等岂容你戏弄。",1,"","","王忠",134,},
        [2953] = {4605,5,1,"主角",1,0,"待我等生擒了你再说！","待我等生擒了你再说！",1,"","","王忠",134,},
        [2954] = {4606,1,2,"主角",1,0,"王忠已被尔等生擒！","王忠已被尔等生擒！",1,"","","刘备",202,},
        [2955] = {4606,2,1,"刘备",202,0,"好！好极了！","好！好极了！",1,"","","主角",1,},
        [2956] = {4606,3,2,"张飞",206,0,"二哥捉了王忠，我去生擒刘岱来！","二哥捉了王忠，我去生擒刘岱来！",1,"","","刘备",202,},
        [2957] = {4606,4,1,"刘备",202,0,"刘岱昔为兖州刺史，虎牢关伐董卓时，也是一镇诸侯，今日为前军，不可轻敌。","刘岱昔为兖州刺史，虎牢关伐董卓时，也是一镇诸侯，今日为前军，不可轻敌。",1,"","","张飞",206,},
        [2958] = {4606,5,2,"张飞",206,0,"量此辈何足道哉！我也似二哥与扬威将军那般生擒来便是。","量此辈何足道哉！我也似二哥与扬威将军那般生擒来便是。",1,"","","刘备",202,},
        [2959] = {4606,6,1,"刘备",202,0,"只恐坏了他性命，误我大事。","只恐坏了他性命，误我大事。",1,"","","张飞",206,},
        [2960] = {4606,7,2,"主角",1,0,"莫怕，我与张飞一同前去便是。","莫怕，我与张飞一同前去便是。",1,"","","刘备",202,},
        [2961] = {4606,8,1,"刘备",202,0,"若扬威将军前去，那我就放心了。","若扬威将军前去，那我就放心了。",1,"","","主角",1,},
        [2962] = {4607,1,1,"张飞",206,0,"这刘岱也不过如此，还不是被我生擒了！","这刘岱也不过如此，还不是被我生擒了！",1,"","","",0,},
        [2963] = {4607,2,1,"张飞",206,0,"啊哈哈哈哈哈。","啊哈哈哈哈哈。",1,"","","",0,},
        [2964] = {4608,1,2,"张飞",206,0,"大哥，王忠与刘岱我们给你带回来了！","大哥，王忠与刘岱我们给你带回来了！",1,"","","刘备",202,},
        [2965] = {4608,2,2,"刘岱",121,0,"哼！要杀要剐随便你们！","哼！要杀要剐随便你们！",1,"","","刘备",202,},
        [2966] = {4608,3,1,"刘备",202,0,"冒犯了冒犯，这就给你们松绑！","冒犯了冒犯，这就给你们松绑！",1,"","","刘岱",121,},
        [2967] = {4608,4,1,"刘备",202,0,"前因车胄欲害备，故不得不杀之。丞相错疑备反，遣二将军前来问罪。","前因车胄欲害备，故不得不杀之。丞相错疑备反，遣二将军前来问罪。",1,"","","刘岱",121,},
        [2968] = {4608,5,1,"刘备",202,0,"备受丞相大恩，正思报效，安敢反耶？二将军至许都，望善言为备分诉，备之幸也。","备受丞相大恩，正思报效，安敢反耶？二将军至许都，望善言为备分诉，备之幸也。",1,"","","刘岱",121,},
        [2969] = {4608,6,2,"王忠",134,0,"谢不杀之恩。","谢不杀之恩。",1,"","","刘备",202,},
        [2970] = {4608,7,2,"刘岱",121,0,"谢不杀之恩，当于丞相处方便，以某两家老小保使君。","谢不杀之恩，当于丞相处方便，以某两家老小保使君。",1,"","","刘备",202,},
        [2971] = {4608,8,1,"刘备",202,0,"这就帮两位备马，将兵马还于两位。","这就帮两位备马，将兵马还于两位。",1,"","","刘岱",121,},
        [2972] = {4609,1,1,"张飞",206,0,"站住！","站住！",1,"","","刘岱",121,},
        [2973] = {4609,2,1,"张飞",206,0,"我哥哥忒没分晓！捉住贼将如何又放了？","我哥哥忒没分晓！捉住贼将如何又放了？",1,"","","刘岱",121,},
        [2974] = {4609,3,2,"刘岱",121,0,"你，你，你想如何？","你，你，你想如何？",1,"","","张飞",206,},
        [2975] = {4609,4,2,"关羽",205,0,"不得无礼！","不得无礼！",1,"","","张飞",206,},
        [2976] = {4609,5,2,"关羽",205,0,"既兄长放了，吾弟如何不遵法令？","既兄长放了，吾弟如何不遵法令？",1,"","","张飞",206,},
        [2977] = {4609,6,1,"张飞",206,0,"今番放了，下次又来。","今番放了，下次又来。",1,"","","关羽",205,},
        [2978] = {4609,7,2,"关羽",205,0,"待他再来，杀之未迟。","待他再来，杀之未迟。",1,"","","张飞",206,},
        [2979] = {4609,8,2,"王忠",134,0,"便丞相诛我三族，也不来了。望将军宽恕。","便丞相诛我三族，也不来了。望将军宽恕。",1,"","","张飞",206,},
        [2980] = {4609,9,1,"张飞",206,0,"便是曹操自来，也杀他片甲不回！今番权且寄下两颗头！","便是曹操自来，也杀他片甲不回！今番权且寄下两颗头！",1,"","","王忠",134,},
        [2981] = {4609,10,1,"刘岱",121,0,"王忠，我们快走，怕那燕人再生变。","王忠，我们快走，怕那燕人再生变。",1,"","","王忠",134,},
        [2982] = {4610,1,1,"曹操",103,0,"王忠、刘岱，你们这个两愚将，还有脸回来！给我斩了。","王忠、刘岱，你们这个两愚将，还有脸回来！给我斩了。",1,"","","",0,},
        [2983] = {4610,2,2,"刘岱",121,0,"丞相饶命！","丞相饶命！",1,"","","曹操",103,},
        [2984] = {4610,3,2,"王忠",134,0,"丞相饶命！","丞相饶命！",1,"","","曹操",103,},
        [2985] = {4610,4,2,"孔融",417,0,"二人本非刘备敌手，若斩之，恐失将士之心。","二人本非刘备敌手，若斩之，恐失将士之心。",1,"","","曹操",103,},
        [2986] = {4610,5,1,"曹操",103,0,"死罪可免获罪难逃！","死罪可免获罪难逃！",1,"","","孔融",417,},
        [2987] = {4610,6,1,"曹操",103,0,"给孤剥脱他俩的官爵俸禄，拖出去！","给孤剥脱他俩的官爵俸禄，拖出去！",1,"","","孔融",417,},
        [2988] = {4610,7,2,"刘岱",121,0,"谢孔融谨言，谢丞相不杀之恩！","谢孔融谨言，谢丞相不杀之恩！",1,"","","曹操",103,},
        [2989] = {4610,8,2,"王忠",134,0,"谢丞相不杀之恩！","谢丞相不杀之恩！",1,"","","曹操",103,},
        [2990] = {4701,1,1,"刘备",202,0,"此前，曹操因衣带诏而斩董承、王子服、种辑、吴硕等人。","此前，曹操因衣带诏而斩董承、王子服、种辑、吴硕等人。",1,"","","主角",1,},
        [2991] = {4701,2,1,"刘备",202,0,"连已有五月身孕的董贵妃也不放过。","连已有五月身孕的董贵妃也不放过。",1,"","","主角",1,},
        [2992] = {4701,3,1,"刘备",202,0,"吾亦与衣带诏有关，恐曹操再起兵来犯。","吾亦与衣带诏有关，恐曹操再起兵来犯。",1,"","","主角",1,},
        [2993] = {4701,4,2,"主角",1,0,"莫急，我立刻前往翼州，再请袁绍相助。","莫急，我立刻前往翼州，再请袁绍相助。",1,"","","刘备",202,},
        [2994] = {4702,1,1,"主角",1,0,"本初何故形容憔悴，衣冠不整？","本初何故形容憔悴，衣冠不整？",1,"","","袁绍",411,},
        [2995] = {4702,2,2,"袁绍",411,0,"我将死矣！","我将死矣！",1,"","","主角",1,},
        [2996] = {4702,3,1,"主角",1,0,"主公何出此言？","主公何出此言？",1,"","","袁绍",411,},
        [2997] = {4702,4,2,"袁绍",411,0,"吾生五子，惟最幼者极快吾意；今患疥疮，命已垂绝。吾有何心更论他事乎？","吾生五子，惟最幼者极快吾意；今患疥疮，命已垂绝。吾有何心更论他事乎？",1,"","","主角",1,},
        [2998] = {4702,5,1,"主角",1,0,"今曹操东征刘玄德，许昌空虚，若以义兵乘虚而入，上可以保天子，下可以救万民。","今曹操东征刘玄德，许昌空虚，若以义兵乘虚而入，上可以保天子，下可以救万民。",1,"","","袁绍",411,},
        [2999] = {4702,6,1,"主角",1,0,"此不易得之机会也，惟明公裁之。","此不易得之机会也，惟明公裁之。",1,"","","袁绍",411,},
        [3000] = {4702,7,2,"袁绍",411,0,"奈我心中恍惚，恐有不利。","奈我心中恍惚，恐有不利。",1,"","","主角",1,},
        [3001] = {4702,8,1,"主角",1,0,"天下事为重！","天下事为重！",1,"","","袁绍",411,},
        [3002] = {4703,1,1,"主角",1,0,"初虽恐不利，但仍答应相助。","初虽恐不利，但仍答应相助。",1,"","","刘备",202,},
        [3003] = {4703,2,1,"主角",1,0,"曹操东征吾等，绍可乘虚偷袭许昌，乃围魏救赵之计。","曹操东征吾等，绍可乘虚偷袭许昌，乃围魏救赵之计。",1,"","","刘备",202,},
        [3004] = {4703,3,1,"主角",1,0,"绍并言若再不如意，可来相投。","绍并言若再不如意，可来相投。",1,"","","刘备",202,},
        [3005] = {4703,4,2,"刘备",202,0,"好。","好。",1,"","","主角",1,},
        [3006] = {4801,1,1,"张绣",439,0,"方今袁强曹弱；今毁书叱使，袁绍若至，当如之何？","方今袁强曹弱；今毁书叱使，袁绍若至，当如之何？",1,"","","贾诩",407,},
        [3007] = {4801,2,2,"贾诩",407,0,"不如去从曹操。","不如去从曹操。",1,"","","张绣",439,},
        [3008] = {4801,3,1,"张绣",439,0,"吾先与操有仇，安得相容？","吾先与操有仇，安得相容？",1,"","","贾诩",407,},
        [3009] = {4801,4,2,"贾诩",407,0,"从操其便有三：夫曹公奉天子明诏，征伐天下，其宜从一也。","从操其便有三：夫曹公奉天子明诏，征伐天下，其宜从一也。",1,"","","张绣",439,},
        [3010] = {4801,5,2,"贾诩",407,0,"绍强盛，我以少从之，必不以我为重，操虽弱，得我必喜，其宜从二也。","绍强盛，我以少从之，必不以我为重，操虽弱，得我必喜，其宜从二也。",1,"","","张绣",439,},
        [3011] = {4801,6,2,"贾诩",407,0,"曹公王霸之志，必释私怨，以明德于四海，其宜从三也。愿将军无疑焉。","曹公王霸之志，必释私怨，以明德于四海，其宜从三也。愿将军无疑焉。",1,"","","张绣",439,},
        [3012] = {4802,1,1,"张绣",439,0,"我与贾诩此次来许都，意为投降你曹操。","我与贾诩此次来许都，意为投降你曹操。",1,"","","曹操",103,},
        [3013] = {4802,2,1,"贾诩",407,0,"愿丞相不计前嫌。","愿丞相不计前嫌。",1,"","","曹操",103,},
        [3014] = {4802,3,2,"曹操",103,0,"有小过失，勿记于心。","有小过失，勿记于心。",1,"","","贾诩",407,},
        [3015] = {4802,4,2,"曹操",103,0,"此以后你张绣为扬武将军，贾诩为执金吾使。","此以后你张绣为扬武将军，贾诩为执金吾使。",1,"","","贾诩",407,},
        [3016] = {4901,1,2,"孙乾",140,0,"袁绍迎击曹操，未能发兵救小沛。","袁绍迎击曹操，未能发兵救小沛。",1,"","","刘备",202,},
        [3017] = {4901,2,1,"刘备",202,0,"似此如之奈何？","似此如之奈何？",1,"","","孙乾",140,},
        [3018] = {4901,3,2,"张飞",206,0,"兄长勿忧。曹兵远来，必然困乏；乘其初至，先去劫寨，可破曹操。","兄长勿忧。曹兵远来，必然困乏；乘其初至，先去劫寨，可破曹操。",1,"","","刘备",202,},
        [3019] = {4901,4,1,"刘备",202,0,"素以汝为一勇夫耳。前者捉刘岱时，颇能用计；今献此策，亦中兵法。","素以汝为一勇夫耳。前者捉刘岱时，颇能用计；今献此策，亦中兵法。",1,"","","张飞",206,},
        [3020] = {4902,1,2,"荀彧",102,0,"风从何方来？吹折甚颜色旗？（狂风骤至，将一面牙旗吹折。）","风从何方来？吹折甚颜色旗？（狂风骤至，将一面牙旗吹折。）",1,"","","曹操",103,},
        [3021] = {4902,2,1,"曹操",103,0,"风自东南方来，吹折角上牙旗，旗乃青红二色。","风自东南方来，吹折角上牙旗，旗乃青红二色。",1,"","","荀彧",102,},
        [3022] = {4902,3,2,"荀彧",102,0,"不主别事，今夜刘备必来劫寨。","不主别事，今夜刘备必来劫寨。",1,"","","曹操",103,},
        [3023] = {4903,1,1,"张飞",206,0,"为何偷袭曹营如此简单，营内无多人马。","为何偷袭曹营如此简单，营内无多人马。",1,"","","",0,},
        [3024] = {4903,2,1,"张飞",206,0,"不好，中计了。","不好，中计了。",1,"","","",0,},
        [3025] = {4903,3,2,"徐晃",114,0,"哪里跑！你已经被包围了！","哪里跑！你已经被包围了！",1,"","","张飞",206,},
        [3026] = {4903,4,2,"主角",1,0,"翼德，快随我从这里突围！","翼德，快随我从这里突围！",1,"","","张飞",206,},
        [3027] = {4903,5,1,"张飞",206,0,"扬威将军，多谢舍命来救！","扬威将军，多谢舍命来救！",1,"","","主角",1,},
        [3028] = {4904,1,1,"张飞",206,0,"好不容易突围，快回小沛！","好不容易突围，快回小沛！",1,"","","主角",1,},
        [3029] = {4904,2,2,"主角",1,0,"不可，此去小沛已不保，翼德你先去芒砀山避一避。","不可，此去小沛已不保，翼德你先去芒砀山避一避。",1,"","","张飞",206,},
        [3030] = {4904,3,1,"张飞",206,0,"那玄德怎么办？","那玄德怎么办？",1,"","","主角",1,},
        [3031] = {4904,4,2,"主角",1,0,"莫怕，我去相救，放心。","莫怕，我去相救，放心。",1,"","","张飞",206,},
        [3032] = {4905,1,1,"刘备",202,0,"不好，小沛城中火起，只得放弃小沛了。","不好，小沛城中火起，只得放弃小沛了。",1,"","","",0,},
        [3033] = {4905,2,1,"刘备",202,0,"去投徐州、下邳了。","去投徐州、下邳了。",1,"","","",0,},
