        [3035] = {4905,4,1,"主角",1,0,"玄德快走，我来挡住这夏侯渊。","玄德快走，我来挡住这夏侯渊。",1,"","","夏侯渊",117,},
        [3036] = {4906,1,1,"刘备",202,0,"翼德袭曹营未归，该如何是好？","翼德袭曹营未归，该如何是好？",1,"","","主角",1,},
