        [3038] = {4906,3,2,"主角",1,0,"你有何打算。","你有何打算。",1,"","","刘备",202,},
        [3039] = {4906,4,1,"刘备",202,0,"袁绍有言，倘不如意，可来相投。","袁绍有言，倘不如意，可来相投。",1,"","","主角",1,},
        [3040] = {4906,5,1,"刘备",202,0,"今不若暂往依栖，别作良图。","今不若暂往依栖，别作良图。",1,"","","主角",1,},
        [3041] = {4906,6,2,"主角",1,0,"好，你且去，我去助自徐州失散的云长兄。","好，你且去，我去助自徐州失散的云长兄。",1,"","","刘备",202,},
        [3042] = {5001,1,1,"曹操",103,0,"颜良真勇将也！","颜良真勇将也！",1,"","","",0,},
        [3043] = {5001,2,1,"曹操",103,0,"交马几回合，便连斩我方宋宪、魏续两员大将。","交马几回合，便连斩我方宋宪、魏续两员大将。",1,"","","",0,},
        [3044] = {5001,3,1,"曹操",103,0,"这可如何是好？","这可如何是好？",1,"","","",0,},
        [3045] = {5001,4,2,"程昱",121,0,"某举一人可敌颜良。","某举一人可敌颜良。",1,"","","曹操",103,},
        [3046] = {5001,5,1,"曹操",103,0,"何人？","何人？",1,"","","程昱",121,},
        [3047] = {5001,6,2,"程昱",121,0,"非关公不可。","非关公不可。",1,"","","曹操",103,},
        [3048] = {5001,7,1,"曹操",103,0,"吾恐他立了功便去。","吾恐他立了功便去。",1,"","","程昱",121,},
        [3049] = {5001,8,2,"程昱",121,0,"刘备若在，必投袁绍。今若使云长破袁绍之兵，绍必疑刘备而杀之矣。备既死，云长又安往乎？","刘备若在，必投袁绍。今若使云长破袁绍之兵，绍必疑刘备而杀之矣。备既死，云长又安往乎？",1,"","","曹操",103,},
        [3050] = {5001,9,1,"曹操",103,0,"孤这便差人去请。","孤这便差人去请。",1,"","","程昱",121,},
        [3051] = {5002,1,1,"曹操",103,0,"山下阵中颜良，连诛二将，勇不可当，特请云长商议。","山下阵中颜良，连诛二将，勇不可当，特请云长商议。",1,"","","关羽",205,},
        [3052] = {5002,2,2,"关羽",205,0,"容我观之。","容我观之。",1,"","","曹操",103,},
        [3053] = {5002,3,1,"曹操",103,0,"河北人马，旗帜鲜明，枪刀森布，严整有威，如此雄壮！","河北人马，旗帜鲜明，枪刀森布，严整有威，如此雄壮！",1,"","","关羽",205,},
        [3054] = {5002,4,2,"关羽",205,0,"扬威将军你认为如何？","扬威将军你认为如何？",1,"","","曹操",103,},
        [3055] = {5002,5,1,"主角",1,0,"以吾观之，如土鸡瓦犬耳！","以吾观之，如土鸡瓦犬耳！",1,"","","关羽",205,},
        [3056] = {5002,6,1,"曹操",103,0,"麾盖之下，绣袍金甲，持刀立马者，乃颜良也。","麾盖之下，绣袍金甲，持刀立马者，乃颜良也。",1,"","","关羽",205,},
        [3057] = {5002,7,2,"关羽",205,0,"吾观颜良，如插标卖首耳。","吾观颜良，如插标卖首耳。",1,"","","曹操",103,},
        [3058] = {5002,8,1,"曹操",103,0,"未可轻视。","未可轻视。",1,"","","关羽",205,},
        [3059] = {5002,9,2,"关羽",205,0,"某虽不才，愿去万军中取其首级，来献丞相。","某虽不才，愿去万军中取其首级，来献丞相。",1,"","","曹操",103,},
        [3060] = {5002,10,1,"张辽",109,0,"军中无戏言，汝等不可忽也。","军中无戏言，汝等不可忽也。",1,"","","关羽",205,},
        [3061] = {5002,11,2,"主角",1,0,"我与云长兄去去就回！","我与云长兄去去就回！",1,"","","张辽",109,},
        [3062] = {5003,1,1,"关羽",205,0,"这是颜良首级，接住！（随即将拴于马项之下的颜良首级献于曹操。）","这是颜良首级，接住！（随即将拴于马项之下的颜良首级献于曹操。）",1,"","","曹操",103,},
        [3063] = {5003,2,2,"曹操",103,0,"将军真神人也！","将军真神人也！",1,"","","关羽",205,},
        [3064] = {5003,3,1,"关羽",205,0,"于百万军中取上将之头，如探囊取物耳。","于百万军中取上将之头，如探囊取物耳。",1,"","","曹操",103,},
        [3065] = {5004,1,1,"袁绍",411,0,"何人斩我大将颜良？","何人斩我大将颜良？",1,"","","",0,},
        [3066] = {5004,2,2,"沮授",422,0,"赤面长须使大刀，此必是刘玄德之弟关云长也。","赤面长须使大刀，此必是刘玄德之弟关云长也。",1,"","","袁绍",411,},
        [3067] = {5004,3,1,"袁绍",411,0,"汝弟斩吾爱将，汝必通谋，留尔何用！","汝弟斩吾爱将，汝必通谋，留尔何用！",1,"","","沮授",422,},
        [3068] = {5004,4,2,"刘备",202,0,"明公只听一面之词，而绝向日之情耶？","明公只听一面之词，而绝向日之情耶？",1,"","","袁绍",411,},
        [3069] = {5004,5,2,"刘备",202,0,"备自徐州失散，二弟云长未知存否。","备自徐州失散，二弟云长未知存否。",1,"","","袁绍",411,},
        [3070] = {5004,6,2,"刘备",202,0,"天下同貌者不少，岂赤面长须之人，即为关某也？明公何不察之？","天下同貌者不少，岂赤面长须之人，即为关某也？明公何不察之？",1,"","","袁绍",411,},
        [3071] = {5004,7,1,"袁绍",411,0,"误听汝言，险杀好人。","误听汝言，险杀好人。",1,"","","刘备",202,},
        [3072] = {5005,1,2,"文丑",414,0,"颜良与我如兄弟，今被曹贼所杀，我安得不雪其恨？","颜良与我如兄弟，今被曹贼所杀，我安得不雪其恨？",1,"","","",0,},
        [3073] = {5005,2,1,"袁绍",411,0,"非汝不能报颜良之仇。吾与十万军兵，便渡黄河，追杀曹贼！","非汝不能报颜良之仇。吾与十万军兵，便渡黄河，追杀曹贼！",1,"","","文丑",414,},
        [3074] = {5005,3,2,"沮授",422,0,"不可。今宜留屯延津，分兵官渡，乃为上策。若轻举渡河，设或有变，众皆不能还矣。","不可。今宜留屯延津，分兵官渡，乃为上策。若轻举渡河，设或有变，众皆不能还矣。",1,"","","袁绍",411,},
        [3075] = {5005,4,1,"袁绍",411,0,"皆是汝等迟缓军心，迁延日月，有妨大事！岂不闻兵贵神速乎？","皆是汝等迟缓军心，迁延日月，有妨大事！岂不闻兵贵神速乎？",1,"","","沮授",422,},
        [3076] = {5005,5,2,"沮授",422,0,"上盈其志，下务其功；悠悠黄河，吾其济乎！（说罢便摔门而出。）","上盈其志，下务其功；悠悠黄河，吾其济乎！（说罢便摔门而出。）",1,"","","袁绍",411,},
        [3077] = {5005,6,2,"刘备",202,0,"备蒙大恩，无可报效，意欲与文将军同行。","备蒙大恩，无可报效，意欲与文将军同行。",1,"","","袁绍",411,},
        [3078] = {5005,7,2,"刘备",202,0,"一者报明公之德，二者就探云长的实信。","一者报明公之德，二者就探云长的实信。",1,"","","袁绍",411,},
        [3079] = {5005,8,1,"袁绍",411,0,"好，文丑自领七万军先行，你玄德引三万军随后。","好，文丑自领七万军先行，你玄德引三万军随后。",1,"","","刘备",202,},
        [3080] = {5006,1,1,"曹操",103,0,"文丑为河北名将，谁可擒之？","文丑为河北名将，谁可擒之？",1,"","","",0,},
        [3081] = {5006,2,2,"主角",1,0,"既斩颜良，何惧文丑，待我与云长去去就回。","既斩颜良，何惧文丑，待我与云长去去就回。",1,"","","曹操",103,},
        [3082] = {5006,3,1,"关羽",205,0,"扬威将军，我们走！","扬威将军，我们走！",1,"","","主角",1,},
        [3083] = {5007,1,1,"关羽",205,0,"这是文丑首级，接住！（随即将拴于马项之下的文丑首级献于曹操。）","这是文丑首级，接住！（随即将拴于马项之下的文丑首级献于曹操。）",1,"","","",0,},
        [3084] = {5007,2,2,"曹操",103,0,"连斩袁绍两员大将，云长与扬威将军真乃神人，当世英雄。","连斩袁绍两员大将，云长与扬威将军真乃神人，当世英雄。",1,"","","关羽",205,},
        [3085] = {5008,1,1,"袁绍",411,0,"今番又是关某杀了文丑，你这大耳贼焉敢如此！","今番又是关某杀了文丑，你这大耳贼焉敢如此！",1,"","","刘备",202,},
        [3086] = {5008,2,2,"刘备",202,0,"某有何罪？","某有何罪？",1,"","","袁绍",411,},
        [3087] = {5008,3,1,"袁绍",411,0,"你故使汝弟又坏我一员大将，如何无罪？","你故使汝弟又坏我一员大将，如何无罪？",1,"","","刘备",202,},
        [3088] = {5008,4,2,"刘备",202,0,"容伸一言而死：曹操素忌备，今知备在明公处，恐备助公，故特使云长诛杀二将。","容伸一言而死：曹操素忌备，今知备在明公处，恐备助公，故特使云长诛杀二将。",1,"","","袁绍",411,},
        [3089] = {5008,5,2,"刘备",202,0,"公知必怒。此借公之手以杀刘备也。愿明公思之。","公知必怒。此借公之手以杀刘备也。愿明公思之。",1,"","","袁绍",411,},
        [3090] = {5008,6,1,"袁绍",411,0,"玄德之言是也。汝等几使我受害贤之名。","玄德之言是也。汝等几使我受害贤之名。",1,"","","刘备",202,},
        [3091] = {5008,7,2,"刘备",202,0,"荷明公宽大之恩，无可补报，欲令一心腹人持密书去见云长。","荷明公宽大之恩，无可补报，欲令一心腹人持密书去见云长。",1,"","","袁绍",411,},
        [3092] = {5008,8,2,"刘备",202,0,"使知刘备消息，彼必星夜来到，辅佐明公，共诛曹操，以报颜良、文丑之仇，若何？","使知刘备消息，彼必星夜来到，辅佐明公，共诛曹操，以报颜良、文丑之仇，若何？",1,"","","袁绍",411,},
        [3093] = {5008,9,1,"袁绍",411,0,"吾得云长，胜颜良、文丑十倍也。","吾得云长，胜颜良、文丑十倍也。",1,"","","刘备",202,},
        [3094] = {5008,10,2,"刘备",202,0,"我这就修下书札，差人送去。","我这就修下书札，差人送去。",1,"","","袁绍",411,},
        [3095] = {5101,1,2,"张辽",109,0,"闻兄在阵上知玄德音信，特来贺喜。","闻兄在阵上知玄德音信，特来贺喜。",1,"","","关羽",205,},
        [3096] = {5101,2,1,"关羽",205,0,"故主虽在，未得一见，何喜之有！","故主虽在，未得一见，何喜之有！",1,"","","张辽",109,},
        [3097] = {5101,3,2,"张辽",109,0,"兄与玄德交，比弟与兄交何如？","兄与玄德交，比弟与兄交何如？",1,"","","关羽",205,},
        [3098] = {5101,4,1,"关羽",205,0,"我与兄，朋友之交也；我与玄德，是朋友而兄弟、兄弟而主臣者也：岂可共论乎？","我与兄，朋友之交也；我与玄德，是朋友而兄弟、兄弟而主臣者也：岂可共论乎？",1,"","","张辽",109,},
        [3099] = {5101,5,2,"张辽",109,0,"今玄德在河北，兄往从否？","今玄德在河北，兄往从否？",1,"","","关羽",205,},
        [3100] = {5101,6,1,"关羽",205,0,"昔日之言，安肯背之！文远须为我致意丞相。","昔日之言，安肯背之！文远须为我致意丞相。",1,"","","张辽",109,},
        [3101] = {5102,1,2,"曹操",103,0,"云长行何太速？","云长行何太速？",1,"","","关羽",205,},
        [3102] = {5102,2,1,"关羽",205,0,"关某前曾禀过丞相。今故主在河北，不由某不急去。","关某前曾禀过丞相。今故主在河北，不由某不急去。",1,"","","曹操",103,},
        [3103] = {5102,3,1,"关羽",205,0,"累次造府，不得参见，故拜书告辞，封金挂印，纳还丞相。望丞相勿忘昔日之言。","累次造府，不得参见，故拜书告辞，封金挂印，纳还丞相。望丞相勿忘昔日之言。",1,"","","曹操",103,},
        [3104] = {5102,4,2,"曹操",103,0,"吾欲取信于天下，安肯有负前言。恐将军途中乏用，特具路资相送。（说罢托过黄金一盘。）","吾欲取信于天下，安肯有负前言。恐将军途中乏用，特具路资相送。（说罢托过黄金一盘。）",1,"","","关羽",205,},
        [3105] = {5102,5,1,"关羽",205,0,"累蒙恩赐，尚有余资。留此黄金以赏将士。","累蒙恩赐，尚有余资。留此黄金以赏将士。",1,"","","曹操",103,},
        [3106] = {5102,6,2,"曹操",103,0,"特以少酬大功于万一，何必推辞？","特以少酬大功于万一，何必推辞？",1,"","","关羽",205,},
        [3107] = {5102,7,1,"关羽",205,0,"区区微劳，何足挂齿。","区区微劳，何足挂齿。",1,"","","曹操",103,},
        [3108] = {5102,8,2,"曹操",103,0,"云长天下义士，恨吾福薄，不得相留。锦袍一领，略表寸心。","云长天下义士，恨吾福薄，不得相留。锦袍一领，略表寸心。",1,"","","关羽",205,},
        [3109] = {5102,9,1,"关羽",205,0,"蒙丞相赐袍，异日更得相会。","蒙丞相赐袍，异日更得相会。",1,"","","曹操",103,},
        [3110] = {5103,1,2,"孔秀",131,0,"将军何往？","将军何往？",1,"","","关羽",205,},
        [3111] = {5103,2,1,"关羽",205,0,"某辞丞相，特往河北寻兄。","某辞丞相，特往河北寻兄。",1,"","","孔秀",131,},
        [3112] = {5103,3,2,"孔秀",131,0,"河北袁绍，正是丞相对头。将军此去，必有丞相文凭？","河北袁绍，正是丞相对头。将军此去，必有丞相文凭？",1,"","","关羽",205,},
        [3113] = {5103,4,1,"关羽",205,0,"因行期慌迫，不曾讨得。","因行期慌迫，不曾讨得。",1,"","","孔秀",131,},
        [3114] = {5103,5,2,"孔秀",131,0,"既无文凭，待我差人禀过丞相，方可放行。","既无文凭，待我差人禀过丞相，方可放行。",1,"","","关羽",205,},
        [3115] = {5103,6,1,"关羽",205,0,"待去禀时，须误了我行程。","待去禀时，须误了我行程。",1,"","","孔秀",131,},
        [3116] = {5103,7,2,"孔秀",131,0,"法度所拘，不得不如此。","法度所拘，不得不如此。",1,"","","关羽",205,},
        [3117] = {5103,8,1,"关羽",205,0,"汝不容我过关乎？","汝不容我过关乎？",1,"","","孔秀",131,},
        [3118] = {5103,9,2,"孔秀",131,0,"汝要过去，留下老小为质。","汝要过去，留下老小为质。",1,"","","关羽",205,},
        [3119] = {5103,10,1,"关羽",205,0,"岂容你放肆，看刀。","岂容你放肆，看刀。",1,"","","孔秀",131,},
        [3120] = {5104,1,2,"韩福",127,0,"来者何人？","来者何人？",1,"","","关羽",205,},
        [3121] = {5104,2,1,"关羽",205,0,"吾汉寿亭侯关某，敢借过路。","吾汉寿亭侯关某，敢借过路。",1,"","","韩福",127,},
        [3122] = {5104,3,2,"韩福",127,0,"有曹丞相文凭否？","有曹丞相文凭否？",1,"","","关羽",205,},
        [3123] = {5104,4,1,"关羽",205,0,"事冗不曾讨得。","事冗不曾讨得。",1,"","","韩福",127,},
        [3124] = {5104,5,2,"韩福",127,0,"吾奉承相钧命，镇守此地，专一盘诘往来奸细。若无文凭，即系逃窜。","吾奉承相钧命，镇守此地，专一盘诘往来奸细。若无文凭，即系逃窜。",1,"","","关羽",205,},
        [3125] = {5104,6,1,"关羽",205,0,"东岭孔秀，已被吾杀。汝亦欲寻死耶？","东岭孔秀，已被吾杀。汝亦欲寻死耶？",1,"","","韩福",127,},
        [3126] = {5104,7,1,"关羽",205,0,"看刀！","看刀！",1,"","","韩福",127,},
        [3127] = {5105,1,2,"卞喜",121,0,"将军名震天下，谁不敬仰！今归皇叔，足见忠义！","将军名震天下，谁不敬仰！今归皇叔，足见忠义！",1,"","","关羽",205,},
        [3128] = {5105,2,1,"关羽",205,0,"我可刚斩了那孔秀、韩福！","我可刚斩了那孔秀、韩福！",1,"","","卞喜",121,},
        [3129] = {5105,3,2,"卞喜",121,0,"将军杀之是也。某见丞相，代禀衷曲。","将军杀之是也。某见丞相，代禀衷曲。",1,"","","关羽",205,},
        [3130] = {5105,4,1,"关羽",205,0,"哈哈哈哈，算你识时务。","哈哈哈哈，算你识时务。",1,"","","卞喜",121,},
        [3131] = {5105,5,2,"卞喜",121,0,"云长是否愿到镇国寺饮茶，寺中有一僧，乃关公你同乡人。","云长是否愿到镇国寺饮茶，寺中有一僧，乃关公你同乡人。",1,"","","关羽",205,},
        [3132] = {5105,6,1,"关羽",205,0,"甚好。","甚好。",1,"","","卞喜",121,},
        [3133] = {5106,1,1,"关羽",205,0,"卞君请关某，是好意，还是歹意？","卞君请关某，是好意，还是歹意？",1,"","","卞喜",121,},
        [3134] = {5106,2,2,"卞喜",121,0,"当然是好意。","当然是好意。",1,"","","关羽",205,},
        [3135] = {5106,3,1,"关羽",205,0,"吾以汝为好人，安敢如此！","吾以汝为好人，安敢如此！",1,"","","卞喜",121,},
        [3136] = {5106,4,1,"关羽",205,0,"我可看到那藏身的刀斧手了。（说罢怒砍了那刀斧手与卞喜。）","我可看到那藏身的刀斧手了。（说罢怒砍了那刀斧手与卞喜。）",1,"","","卞喜",121,},
        [3137] = {5107,1,2,"王植",141,0,"将军于路驱驰，夫人车上劳困，且请入城，馆驿中暂歇一宵，来日登途未迟。","将军于路驱驰，夫人车上劳困，且请入城，馆驿中暂歇一宵，来日登途未迟。",1,"","","关羽",205,},
        [3138] = {5107,2,1,"关羽",205,0,"甚好，烦请先行，我与二嫂随后就到。","甚好，烦请先行，我与二嫂随后就到。",1,"","","王植",141,},
        [3139] = {5107,3,2,"主角",1,0,"万万不可！","万万不可！",1,"","","关羽",205,},
        [3140] = {5107,4,2,"主角",1,0,"王植心怀不仁，欲害将军。","王植心怀不仁，欲害将军。",1,"","","关羽",205,},
        [3141] = {5107,5,1,"关羽",205,0,"怎讲？","怎讲？",1,"","","主角",1,},
        [3142] = {5107,6,2,"主角",1,0,"我刚查看馆驿四周，发现满布干柴引火之物，他定是想三更放火烧馆驿。","我刚查看馆驿四周，发现满布干柴引火之物，他定是想三更放火烧馆驿。",1,"","","关羽",205,},
        [3143] = {5107,7,1,"关羽",205,0,"扬威将军先护送二嫂上车出城，我去斩了那王植。","扬威将军先护送二嫂上车出城，我去斩了那王植。",1,"","","主角",1,},
        [3144] = {5108,1,2,"秦琪",122,0,"来者何人？","来者何人？",1,"","","关羽",205,},
        [3145] = {5108,2,1,"关羽",205,0,"汉寿亭侯关某也。","汉寿亭侯关某也。",1,"","","秦琪",122,},
        [3146] = {5108,3,2,"秦琪",122,0,"今欲何往？","今欲何往？",1,"","","关羽",205,},
        [3147] = {5108,4,1,"关羽",205,0,"欲投河北去寻兄长刘玄德，敬来借渡。","欲投河北去寻兄长刘玄德，敬来借渡。",1,"","","秦琪",122,},
        [3148] = {5108,5,2,"秦琪",122,0,"丞相公文何在？","丞相公文何在？",1,"","","关羽",205,},
        [3149] = {5108,6,1,"关羽",205,0,"吾不受丞相节制，有甚公文！","吾不受丞相节制，有甚公文！",1,"","","秦琪",122,},
        [3150] = {5108,7,2,"秦琪",122,0,"吾奉夏侯将军将令，守把关隘，你便插翅，也飞不过去！","吾奉夏侯将军将令，守把关隘，你便插翅，也飞不过去！",1,"","","关羽",205,},
        [3151] = {5108,8,1,"关羽",205,0,"你知我于路斩戮拦截者乎？","你知我于路斩戮拦截者乎？",1,"","","秦琪",122,},
        [3152] = {5108,9,2,"秦琪",122,0,"你只杀得无名下将，敢杀我么？","你只杀得无名下将，敢杀我么？",1,"","","关羽",205,},
        [3153] = {5108,10,1,"关羽",205,0,"汝比颜良、文丑若何？","汝比颜良、文丑若何？",1,"","","秦琪",122,},
        [3154] = {5108,11,2,"秦琪",122,0,"竟如此小看我，啊啊啊啊！引马来战！","竟如此小看我，啊啊啊啊！引马来战！",1,"","","关羽",205,},
        [3155] = {5109,1,2,"夏侯惇",105,0,"关某休走！","关某休走！",1,"","","关羽",205,},
        [3156] = {5109,2,1,"关羽",205,0,"汝来赶我，有失丞相大度。","汝来赶我，有失丞相大度。",1,"","","夏侯惇",105,},
        [3157] = {5109,3,2,"夏侯惇",105,0,"丞相无明文传报，汝于路杀人，又斩吾部将，无礼太甚！我特来擒你，献与丞相发落！","丞相无明文传报，汝于路杀人，又斩吾部将，无礼太甚！我特来擒你，献与丞相发落！",1,"","","关羽",205,},
        [3158] = {5110,1,2,"张辽",109,0,"云长、元让，休得争战！","云长、元让，休得争战！",1,"","","",0,},
        [3159] = {5110,2,2,"张辽",109,0,"奉丞相钧旨：因闻知云长斩关杀将，恐于路有阻，特差我传谕各处关隘，任便放行。","奉丞相钧旨：因闻知云长斩关杀将，恐于路有阻，特差我传谕各处关隘，任便放行。",1,"","","",0,},
        [3160] = {5110,3,1,"夏侯惇",105,0,"秦琪是蔡阳之甥。他将秦琪托付我处，今被关某所杀，怎肯干休？","秦琪是蔡阳之甥。他将秦琪托付我处，今被关某所杀，怎肯干休？",1,"","","张辽",109,},
        [3161] = {5110,4,2,"张辽",109,0,"我见蔡将军，自有分解。既丞相大度，教放云长去，公等不可废丞相之意。","我见蔡将军，自有分解。既丞相大度，教放云长去，公等不可废丞相之意。",1,"","","夏侯惇",105,},
        [3162] = {5110,5,1,"夏侯惇",105,0,"哼，既然丞相说了我能如何！","哼，既然丞相说了我能如何！",1,"","","张辽",109,},
        [3163] = {5110,6,2,"张辽",109,0,"云长今欲何往？","云长今欲何往？",1,"","","夏侯惇",105,},
        [3164] = {5110,7,1,"关羽",205,0,"闻兄长又不在袁绍处，吾今将遍天下寻之。","闻兄长又不在袁绍处，吾今将遍天下寻之。",1,"","","张辽",109,},
        [3165] = {5110,8,2,"张辽",109,0,"既未知玄德下落，且再回见丞相，若何？","既未知玄德下落，且再回见丞相，若何？",1,"","","关羽",205,},
        [3166] = {5110,9,1,"关羽",205,0,"安有是理！文远回见丞相，幸为我谢罪。","安有是理！文远回见丞相，幸为我谢罪。",1,"","","张辽",109,},
        [3167] = {5201,1,2,"周仓",234,0,"此关将军也，周仓参拜。","此关将军也，周仓参拜。",1,"","","关羽",205,},
        [3168] = {5201,2,1,"关羽",205,0,"壮士何处曾识关某来？","壮士何处曾识关某来？",1,"","","周仓",234,},
        [3169] = {5201,3,2,"周仓",234,0,"旧随黄巾张宝时，曾识尊颜；恨失身贼党，不得相随。","旧随黄巾张宝时，曾识尊颜；恨失身贼党，不得相随。",1,"","","关羽",205,},
        [3170] = {5201,4,2,"周仓",234,0,"今日幸得拜见。愿将军不弃，收为步卒，早晚执鞭随镫，死亦甘心！","今日幸得拜见。愿将军不弃，收为步卒，早晚执鞭随镫，死亦甘心！",1,"","","关羽",205,},
        [3171] = {5201,5,1,"关羽",205,0,"汝若随我，汝手下人伴若何？","汝若随我，汝手下人伴若何？",1,"","","周仓",234,},
        [3172] = {5201,6,2,"周仓",234,0,"愿从则俱从；不愿从者，听之可也。","愿从则俱从；不愿从者，听之可也。",1,"","","关羽",205,},
        [3173] = {5201,7,2,"周仓",234,0,"仓只身步行，跟随将军，虽万里不辞也！","仓只身步行，跟随将军，虽万里不辞也！",1,"","","关羽",205,},
        [3174] = {5202,1,2,"廖化",222,0,"关将军且住！","关将军且住！",1,"","","关羽",205,},
        [3175] = {5202,2,1,"关羽",205,0,"汝何人也？","汝何人也？",1,"","","廖化",222,},
        [3176] = {5202,3,2,"廖化",222,0,"吾本襄阳人，姓廖，名化，字元俭。因世乱流落江湖，聚众五百余人，劫掠为生。","吾本襄阳人，姓廖，名化，字元俭。因世乱流落江湖，聚众五百余人，劫掠为生。",1,"","","关羽",205,},
        [3177] = {5202,4,2,"廖化",222,0,"恰才同伴杜远下山巡哨，误将两夫人劫掠上山。","恰才同伴杜远下山巡哨，误将两夫人劫掠上山。",1,"","","关羽",205,},
        [3178] = {5202,5,2,"廖化",222,0,"吾问从者，知是大汉刘皇叔夫人，且闻将军护送在此，吾即欲送下山来。","吾问从者，知是大汉刘皇叔夫人，且闻将军护送在此，吾即欲送下山来。",1,"","","关羽",205,},
        [3179] = {5202,6,2,"廖化",222,0,"杜远出言不逊，被某杀之。今献头与将军请罪。","杜远出言不逊，被某杀之。今献头与将军请罪。",1,"","","关羽",205,},
        [3180] = {5202,7,1,"关羽",205,0,"二夫人何在？","二夫人何在？",1,"","","廖化",222,},
        [3181] = {5202,8,2,"廖化",222,0,"现在山中。","现在山中。",1,"","","关羽",205,},
        [3182] = {5202,9,1,"关羽",205,0,"速带我去前去！","速带我去前去！",1,"","","廖化",222,},
        [3183] = {5203,1,2,"关定",133,0,"关公下榻我关定庄，实乃荣幸！","关公下榻我关定庄，实乃荣幸！",1,"","","刘备",202,},
        [3184] = {5203,2,2,"关定",133,0,"吾有二子：长子关宁，学文；次子关平，学武。","吾有二子：长子关宁，学文；次子关平，学武。",1,"","","刘备",202,},
        [3185] = {5203,3,2,"关定",133,0,"今愚意欲遣次子跟随关将军，未识肯容纳否？","今愚意欲遣次子跟随关将军，未识肯容纳否？",1,"","","刘备",202,},
        [3186] = {5203,4,1,"刘备",202,0,"年几何矣？","年几何矣？",1,"","","关定",133,},
        [3187] = {5203,5,2,"关定",133,0,"十八岁矣。","十八岁矣。",1,"","","刘备",202,},
        [3188] = {5203,6,1,"刘备",202,0,"既蒙长者厚意，吾弟尚未有子，今即以贤郎为子，若何？","既蒙长者厚意，吾弟尚未有子，今即以贤郎为子，若何？",1,"","","关定",133,},
        [3189] = {5203,7,2,"关定",133,0,"甚好甚好！","甚好甚好！",1,"","","刘备",202,},
        [3190] = {5203,8,2,"主角",1,0,"恭喜云长喜得从子！","恭喜云长喜得从子！",1,"","","刘备",202,},
        [3191] = {5203,9,1,"关羽",205,0,"哈哈哈哈哈哈。","哈哈哈哈哈哈。",1,"","","主角",1,},
        [3192] = {5301,1,1,"关羽",205,0,"贤弟何故圆睁环眼，倒竖虎须，吼声如雷，挥矛相向。","贤弟何故圆睁环眼，倒竖虎须，吼声如雷，挥矛相向。",1,"","","张飞",206,},
        [3193] = {5301,2,1,"关羽",205,0,"岂忘了桃园结义耶？","岂忘了桃园结义耶？",1,"","","张飞",206,},
        [3194] = {5301,3,2,"张飞",206,0,"你既无义，有何面目来与我相见！","你既无义，有何面目来与我相见！",1,"","","关羽",205,},
        [3195] = {5301,4,1,"关羽",205,0,"我如何无义？","我如何无义？",1,"","","张飞",206,},
        [3196] = {5301,5,2,"张飞",206,0,"你背了兄长，降了曹操，封侯赐爵。今又来赚我！我今与你拼个死活！","你背了兄长，降了曹操，封侯赐爵。今又来赚我！我今与你拼个死活！",1,"","","关羽",205,},
        [3197] = {5301,6,1,"主角",1,0,"你原来不知！莫怪云长兄。","你原来不知！莫怪云长兄。",1,"","","张飞",206,},
        [3198] = {5301,7,1,"主角",1,0,"云长过五关斩六将，千里走单骑，才至古城！","云长过五关斩六将，千里走单骑，才至古城！",1,"","","张飞",206,},
        [3199] = {5301,8,2,"张飞",206,0,"是弟弟错怪二哥了！","是弟弟错怪二哥了！",1,"","","主角",1,},
        [3200] = {5302,1,2,"蔡阳",122,0,"你杀吾外甥秦琪，却原来逃在此！","你杀吾外甥秦琪，却原来逃在此！",1,"","","关羽",205,},
        [3201] = {5302,2,2,"蔡阳",122,0,"吾奉丞相命，特来拿你！","吾奉丞相命，特来拿你！",1,"","","关羽",205,},
        [3202] = {5302,3,1,"关羽",205,0,"弟弟为我击鼓，三通鼓结束前定取其项上人头。","弟弟为我击鼓，三通鼓结束前定取其项上人头。",1,"","","蔡阳",122,},
        [3203] = {5302,4,2,"张飞",206,0,"好！","好！",1,"","","关羽",205,},
        [3204] = {5303,1,2,"周仓",234,0,"我回卧牛山之前，有一将单骑而来，与吾兄裴元绍交锋只一合，便将其刺死。","我回卧牛山之前，有一将单骑而来，与吾兄裴元绍交锋只一合，便将其刺死。",1,"","","刘备",202,},
        [3205] = {5303,2,2,"周仓",234,0,"尽数招降人伴，占住山寨。","尽数招降人伴，占住山寨。",1,"","","刘备",202,},
        [3206] = {5303,3,2,"周仓",234,0,"吾不忿，与那将交战，被他连胜数次，身中三枪，因此来报。","吾不忿，与那将交战，被他连胜数次，身中三枪，因此来报。",1,"","","刘备",202,},
        [3207] = {5303,4,1,"刘备",202,0,"此人怎生模样？姓甚名谁？","此人怎生模样？姓甚名谁？",1,"","","周仓",234,},
        [3208] = {5303,5,2,"周仓",234,0,"极其雄壮，不知姓名。","极其雄壮，不知姓名。",1,"","","刘备",202,},
        [3209] = {5303,6,2,"关羽",205,0,"待我速速前去查看！","待我速速前去查看！",1,"","","刘备",202,},
        [3210] = {5303,7,2,"主角",1,0,"我也一同前去！","我也一同前去！",1,"","","刘备",202,},
        [3211] = {5304,1,1,"刘备",202,0,"来者莫非子龙否？","来者莫非子龙否？",1,"","","",0,},
        [3212] = {5304,2,2,"赵云",201,0,"云自别使君，不想公孙瓒不听人言，以致兵败自焚。","云自别使君，不想公孙瓒不听人言，以致兵败自焚。",1,"","","刘备",202,},
        [3213] = {5304,3,2,"赵云",201,0,"袁绍屡次招云，云想绍亦非用人之人，因此未往。","袁绍屡次招云，云想绍亦非用人之人，因此未往。",1,"","","刘备",202,},
        [3214] = {5304,4,2,"赵云",201,0,"后欲至徐州投使君，又闻徐州失守，云长已归曹操，使君又在袁绍处。","后欲至徐州投使君，又闻徐州失守，云长已归曹操，使君又在袁绍处。",1,"","","刘备",202,},
        [3215] = {5304,5,2,"赵云",201,0,"云几番欲来相投，只恐袁绍见怪。四海飘零，无容身之地。","云几番欲来相投，只恐袁绍见怪。四海飘零，无容身之地。",1,"","","刘备",202,},
        [3216] = {5304,6,2,"赵云",201,0,"前偶过此处，适遇裴元绍下山来欲夺吾马，云因杀之，借此安身。","前偶过此处，适遇裴元绍下山来欲夺吾马，云因杀之，借此安身。",1,"","","刘备",202,},
        [3217] = {5304,7,2,"赵云",201,0,"近闻翼德在古城，欲往投之，未知真实。今幸得遇使君！","近闻翼德在古城，欲往投之，未知真实。今幸得遇使君！",1,"","","刘备",202,},
        [3218] = {5304,8,1,"刘备",202,0,"吾初见子龙，便有留恋不舍之情。今幸得相遇！","吾初见子龙，便有留恋不舍之情。今幸得相遇！",1,"","","赵云",201,},
        [3219] = {5304,9,2,"赵云",201,0,"云奔走四方，择主而事，未有如使君者。今得相随，大称平生。虽肝脑涂地，无恨矣。","云奔走四方，择主而事，未有如使君者。今得相随，大称平生。虽肝脑涂地，无恨矣。",1,"","","刘备",202,},
        [3220] = {5305,1,1,"主角",1,0,"汝既与云长、张飞相会。","汝既与云长、张飞相会。",1,"","","刘备",202,},
        [3221] = {5305,2,1,"主角",1,0,"又得周仓、廖化、关平、赵云相助，必平一方乱世。","又得周仓、廖化、关平、赵云相助，必平一方乱世。",1,"","","刘备",202,},
        [3222] = {5305,3,1,"主角",1,0,"我暂与你们告别，游历江东。","我暂与你们告别，游历江东。",1,"","","刘备",202,},
        [3223] = {5305,4,2,"刘备",202,0,"扬威将军多多保重，他日再见，拜别！","扬威将军多多保重，他日再见，拜别！",1,"","","主角",1,},
        [3224] = {5401,1,1,"孙策",301,0,"听闻许贡私下作书丞相。","听闻许贡私下作书丞相。",1,"","","",0,},
        [3225] = {5401,2,1,"孙策",301,0,"说我孙策骁勇，与项籍相似。朝廷宜外示荣宠，召在京师，不可使居外镇，以为后患。","说我孙策骁勇，与项籍相似。朝廷宜外示荣宠，召在京师，不可使居外镇，以为后患。",1,"","","",0,},
        [3226] = {5401,3,1,"孙策",301,0,"汝欲送我于死地耶！","汝欲送我于死地耶！",1,"","","",0,},
        [3227] = {5401,4,1,"孙策",301,0,"来人，把许贡一家老小给我杀了。","来人，把许贡一家老小给我杀了。",1,"","","",0,},
        [3228] = {5402,1,1,"孙策",301,0,"汝等何人？","汝等何人？",1,"","","",0,},
        [3229] = {5402,2,2,"许贡家客",134,0,"我等是许贡家客，特来为主人报仇！","我等是许贡家客，特来为主人报仇！",1,"","","孙策",301,},
        [3230] = {5402,3,1,"孙策",301,0,"杀贼！","杀贼！",1,"","","许贡家客",134,},
        [3231] = {5402,4,2,"程普",320,0,"伯符，尔等来救。","伯符，尔等来救。",1,"","","孙策",301,},
        [3232] = {5403,1,1,"孙策",301,0,"华伦可在府中？","华伦可在府中？",1,"","","华佗随从",129,},
        [3233] = {5403,2,2,"华佗随从",129,0,"我家先生已往中原去了，只有我们几个徒弟在吴。","我家先生已往中原去了，只有我们几个徒弟在吴。",1,"","","孙策",301,},
        [3234] = {5403,3,1,"孙策",301,0,"那你快帮我看看伤势。","那你快帮我看看伤势。",1,"","","华佗随从",129,},
        [3235] = {5403,4,2,"华佗随从",129,0,"箭头有药，毒已入骨。须静养百日，方可无虞。若怒气冲激，其疮难治。","箭头有药，毒已入骨。须静养百日，方可无虞。若怒气冲激，其疮难治。",1,"","","孙策",301,},
        [3236] = {5403,5,1,"孙策",301,0,"百日？我恨不得即日便愈！","百日？我恨不得即日便愈！",1,"","","华佗随从",129,},
        [3237] = {5404,1,2,"陈震",140,0,"吾受袁绍之托而来，绍欲结东吴为外应，共攻曹操。","吾受袁绍之托而来，绍欲结东吴为外应，共攻曹操。",1,"","","孙策",301,},
        [3238] = {5404,2,1,"孙策",301,0,"不谋而同，不谋而同，我也正有此意，取了那许昌。","不谋而同，不谋而同，我也正有此意，取了那许昌。",1,"","","陈震",140,},
        [3239] = {5404,3,1,"孙策",301,0,"来来来，与我至城楼，喝酒吃肉。","来来来，与我至城楼，喝酒吃肉。",1,"","","陈震",140,},
        [3240] = {5405,1,1,"孙策",301,0,"我宴中宾客，何故互相耳语，纷纷下楼。","我宴中宾客，何故互相耳语，纷纷下楼。",1,"","","张昭",315,},
        [3241] = {5405,2,2,"张昭",315,0,"有于神仙者，今从楼下过，诸将欲往拜之耳。","有于神仙者，今从楼下过，诸将欲往拜之耳。",1,"","","孙策",301,},
        [3242] = {5405,3,1,"孙策",301,0,"是何妖人？快与我擒来！","是何妖人？快与我擒来！",1,"","","张昭",315,},
        [3243] = {5405,4,2,"张昭",315,0,"此人姓于，名吉，寓居东方，往来吴会，普施符水，救人万病，无有不验。","此人姓于，名吉，寓居东方，往来吴会，普施符水，救人万病，无有不验。",1,"","","孙策",301,},
        [3244] = {5405,5,2,"张昭",315,0,"当世呼为神仙，未可轻渎。","当世呼为神仙，未可轻渎。",1,"","","孙策",301,},
        [3245] = {5405,6,1,"孙策",301,0,"速速擒来！违者斩！","速速擒来！违者斩！",1,"","","张昭",315,},
        [3246] = {5406,1,1,"孙策",301,0,"狂道怎敢煽惑人心！","狂道怎敢煽惑人心！",1,"","","于吉",410,},
        [3247] = {5406,2,2,"于吉",410,0,"贫道乃琅琊宫道士，顺帝时曾入山采药，得神书于阳曲泉水上，号曰《太平青领道》。","贫道乃琅琊宫道士，顺帝时曾入山采药，得神书于阳曲泉水上，号曰《太平青领道》。",1,"","","孙策",301,},
        [3248] = {5406,3,2,"于吉",410,0,"凡百余卷，皆治人疾病方术。","凡百余卷，皆治人疾病方术。",1,"","","孙策",301,},
        [3249] = {5406,4,2,"于吉",410,0,"贫道得之，惟务代天宣化，普救万人，未曾取人毫厘之物，安得煽惑人心？","贫道得之，惟务代天宣化，普救万人，未曾取人毫厘之物，安得煽惑人心？",1,"","","孙策",301,},
        [3250] = {5406,5,1,"孙策",301,0,"汝毫不取人，衣服饮食，从何而得？","汝毫不取人，衣服饮食，从何而得？",1,"","","于吉",410,},
        [3251] = {5406,6,1,"孙策",301,0,"汝即黄巾张角之流，今若不诛，必为后患！","汝即黄巾张角之流，今若不诛，必为后患！",1,"","","于吉",410,},
        [3252] = {5406,7,2,"张昭",315,0,"于道人在江东数十年，并无过犯，不可杀害。","于道人在江东数十年，并无过犯，不可杀害。",1,"","","孙策",301,},
        [3253] = {5406,8,1,"孙策",301,0,"此等妖人，君杀之，何异屠猪狗！","此等妖人，君杀之，何异屠猪狗！",1,"","","张昭",315,},
        [3254] = {5406,9,2,"陈震",140,0,"伯符请慎重考虑。","伯符请慎重考虑。",1,"","","孙策",301,},
        [3255] = {5406,10,1,"孙策",301,0,"来人，将其下狱。","来人，将其下狱。",1,"","","陈震",140,},
        [3256] = {5407,1,1,"孙策",301,0,"闻你妖道能祈风祷雨。方今天旱，若求三尺甘霖，则免一死。","闻你妖道能祈风祷雨。方今天旱，若求三尺甘霖，则免一死。",1,"","","于吉",410,},
        [3257] = {5407,2,1,"孙策",301,0,"若午时无雨，即斩你。","若午时无雨，即斩你。",1,"","","于吉",410,},
        [3258] = {5407,3,2,"于吉",410,0,"我这就开坛作法。","我这就开坛作法。",1,"","","孙策",301,},
        [3259] = {5407,4,1,"主角",1,0,"何人做法，街市成河，溪涧皆满，农田皆被淹没。","何人做法，街市成河，溪涧皆满，农田皆被淹没。",1,"","","于吉",410,},
        [3260] = {5407,5,1,"孙策",301,0,"我让你求三尺甘霖，你却水淹江东，你想造反？","我让你求三尺甘霖，你却水淹江东，你想造反？",1,"","","于吉",410,},
        [3261] = {5407,6,2,"于吉",410,0,"哈哈哈哈，我能呼风唤雨，乘风而起，你能奈我何？","哈哈哈哈，我能呼风唤雨，乘风而起，你能奈我何？",1,"","","孙策",301,},
        [3262] = {5407,7,1,"主角",1,0,"这乱世岂容你此等妖道再作乱，我扬威将军灭了你！","这乱世岂容你此等妖道再作乱，我扬威将军灭了你！",1,"","","于吉",410,},
        [3263] = {5408,1,1,"孙策",301,0,"谢英雄出手相助，救江东于水火之中。","谢英雄出手相助，救江东于水火之中。",1,"","","主角",1,},
        [3264] = {5408,2,2,"主角",1,0,"无需谢我，我游历江东，早就听闻这妖道借符水治病为幌子，蛊惑人心。","无需谢我，我游历江东，早就听闻这妖道借符水治病为幌子，蛊惑人心。",1,"","","孙策",301,},
        [3265] = {5408,3,2,"主角",1,0,"今日还作法陷江东人民于危难之中，我就当是为民除害。","今日还作法陷江东人民于危难之中，我就当是为民除害。",1,"","","孙策",301,},
        [3266] = {5408,4,1,"孙策",301,0,"扬威将军出手相助，伯符感激不尽。","扬威将军出手相助，伯符感激不尽。",1,"","","主角",1,},
        [3267] = {5501,1,2,"张昭",315,0,"昨夜风雨交作，及晓，不见了于吉尸首！","昨夜风雨交作，及晓，不见了于吉尸首！",1,"","","孙策",301,},
        [3268] = {5501,2,1,"孙策",301,0,"妖人，果真是妖人！","妖人，果真是妖人！",1,"","","张昭",315,},
        [3269] = {5501,3,2,"张昭",315,0,"于吉妖道怕死后也要加害于你。","于吉妖道怕死后也要加害于你。",1,"","","孙策",301,},
        [3270] = {5501,4,1,"孙策",301,0,"我自幼随父出征，杀人如麻，还怕他这妖道？","我自幼随父出征，杀人如麻，还怕他这妖道？",1,"","","张昭",315,},
        [3271] = {5502,1,1,"孙策",301,0,"近些日子颇为心烦。","近些日子颇为心烦。",1,"","","大乔",302,},
        [3272] = {5502,2,2,"大乔",302,0,"伯符有何烦心事快和我说说。","伯符有何烦心事快和我说说。",1,"","","孙策",301,},
        [3273] = {5502,3,1,"孙策",301,0,"自从我斩了那于吉妖道，便不得安宁。","自从我斩了那于吉妖道，便不得安宁。",1,"","","大乔",302,},
        [3274] = {5502,4,1,"孙策",301,0,"前夜灯影之下，见那吉立妖道站于床前，入玉清观焚香，又见那于吉妖道。","前夜灯影之下，见那吉立妖道站于床前，入玉清观焚香，又见那于吉妖道。",1,"","","大乔",302,},
        [3275] = {5502,5,1,"孙策",301,0,"脑海之中，不断浮现这妖道，你说烦不烦人！","脑海之中，不断浮现这妖道，你说烦不烦人！",1,"","","大乔",302,},
        [3276] = {5502,6,2,"大乔",302,0,"伯符应该是太操劳了，潜心静养，过几日便好。","伯符应该是太操劳了，潜心静养，过几日便好。",1,"","","孙策",301,},
        [3277] = {5502,7,1,"孙策",301,0,"你知道我的急性子，不用休息，征战沙场才是我最好的休息方式。","你知道我的急性子，不用休息，征战沙场才是我最好的休息方式。",1,"","","大乔",302,},
        [3278] = {5502,8,1,"孙策",301,0,"号三军，出城外下寨，传众将商议起兵助袁绍夹攻曹操之事。","号三军，出城外下寨，传众将商议起兵助袁绍夹攻曹操之事。",1,"","","大乔",302,},
        [3279] = {5502,9,2,"大乔",302,0,"汝玉体违和，未可轻动。且待平愈，出兵未迟。","汝玉体违和，未可轻动。且待平愈，出兵未迟。",1,"","","孙策",301,},
        [3280] = {5502,10,1,"孙策",301,0,"我意已决。","我意已决。",1,"","","大乔",302,},
        [3281] = {5503,1,1,"孙策",301,0,"于吉妖道，快现身！","于吉妖道，快现身！",1,"","","",0,},
        [3282] = {5503,2,1,"孙策",301,0,"快现身，谁允你在我军寨之中捣乱。","快现身，谁允你在我军寨之中捣乱。",1,"","","",0,},
        [3283] = {5503,3,1,"孙策",301,0,"啊啊啊啊啊啊！","啊啊啊啊啊啊！",1,"","","",0,},
        [3284] = {5503,4,2,"主角",1,0,"伯符为何帐中大骂？","伯符为何帐中大骂？",1,"","","孙策",301,},
        [3285] = {5503,5,1,"孙策",301,0,"你没看到嘛？那于吉妖道披头散发坐于我帐内！","你没看到嘛？那于吉妖道披头散发坐于我帐内！",1,"","","主角",1,},
        [3286] = {5503,6,2,"主角",1,0,"此处何来于吉，那妖道前日不是被我斩了吗？","此处何来于吉，那妖道前日不是被我斩了吗？",1,"","","孙策",301,},
        [3287] = {5503,7,2,"主角",1,0,"可能近些日子你太过操劳，加之数月前，许贡家客偷袭之伤未愈，焦虑而出现了幻觉。","可能近些日子你太过操劳，加之数月前，许贡家客偷袭之伤未愈，焦虑而出现了幻觉。",1,"","","孙策",301,},
        [3288] = {5503,8,2,"主角",1,0,"没事的！","没事的！",1,"","","孙策",301,},
        [3289] = {5503,9,1,"孙策",301,0,"吾奈何憔悴至此耶！（听完主角之言，引镜自照。）","吾奈何憔悴至此耶！（听完主角之言，引镜自照。）",1,"","","主角",1,},
        [3290] = {5503,10,1,"孙策",301,0,"快看！那于吉妖道明明立于我镜中。","快看！那于吉妖道明明立于我镜中。",1,"","","主角",1,},
        [3291] = {5503,11,1,"孙策",301,0,"啊啊啊啊啊！（孙策拍镜大叫一声，金疮迸裂，昏绝于地。）","啊啊啊啊啊！（孙策拍镜大叫一声，金疮迸裂，昏绝于地。）",1,"","","主角",1,},
        [3292] = {5503,12,2,"主角",1,0,"伯符你没事吧？伯符你醒醒！","伯符你没事吧？伯符你醒醒！",1,"","","孙策",301,},
        [3293] = {5504,1,1,"孙策",301,0,"天下方乱，以吴越之众，三江之固，大可有为。","天下方乱，以吴越之众，三江之固，大可有为。",1,"","","孙权",306,},
        [3294] = {5504,2,1,"孙策",301,0,"若举江东之众，决机于两阵之间，与天下争衡，卿不如我。","若举江东之众，决机于两阵之间，与天下争衡，卿不如我。",1,"","","孙权",306,},
        [3295] = {5504,3,1,"孙策",301,0,"举贤任能，使各尽力以保江东，我不如卿。卿宜念父兄创业之艰难，善自图之！","举贤任能，使各尽力以保江东，我不如卿。卿宜念父兄创业之艰难，善自图之！",1,"","","孙权",306,},
        [3296] = {5504,4,2,"孙权",306,0,"吾年幼，不能任大事，当复如何？","吾年幼，不能任大事，当复如何？",1,"","","孙策",301,},
        [3297] = {5504,5,1,"孙策",301,0,"弟才胜儿十倍，足当大任。倘内事不决，可问张昭。","弟才胜儿十倍，足当大任。倘内事不决，可问张昭。",1,"","","孙权",306,},
        [3298] = {5504,6,1,"孙策",301,0,"外事不决，可问周瑜。","外事不决，可问周瑜。",1,"","","孙权",306,},
        [3299] = {5504,7,1,"孙策",301,0,"扬威将军何在？","扬威将军何在？",1,"","","孙权",306,},
        [3300] = {5504,8,2,"主角",1,0,"我在这里。","我在这里。",1,"","","孙策",301,},
        [3301] = {5504,9,1,"孙策",301,0,"伯符幸得英雄相助，若他日吾弟弟有难，还烦请英雄出手相助。","伯符幸得英雄相助，若他日吾弟弟有难，还烦请英雄出手相助。",1,"","","主角",1,},
        [3302] = {5504,10,2,"主角",1,0,"若有难，不违天理之事，吾定全力相助。","若有难，不违天理之事，吾定全力相助。",1,"","","孙策",301,},
        [3303] = {5504,11,1,"孙策",301,0,"再劳烦扬威将军，转致周郎，尽心辅佐吾弟，休负我平日相知之雅。（言讫，瞑目而逝。）","再劳烦扬威将军，转致周郎，尽心辅佐吾弟，休负我平日相知之雅。（言讫，瞑目而逝。）",1,"","","主角",1,},
        [3304] = {5601,1,1,"孙权",306,0,"今承父兄之业，将何策以守之？","今承父兄之业，将何策以守之？",1,"","","周瑜",303,},
        [3305] = {5601,2,2,"周瑜",303,0,"自古得人者昌，失人者亡。为今之计，须求高明远见之人为辅，然后江东可定也。","自古得人者昌，失人者亡。为今之计，须求高明远见之人为辅，然后江东可定也。",1,"","","孙权",306,},
        [3306] = {5601,3,1,"孙权",306,0,"先兄遗言：内事托子布，外事全赖公瑾。","先兄遗言：内事托子布，外事全赖公瑾。",1,"","","周瑜",303,},
        [3307] = {5601,4,2,"周瑜",303,0,"子布贤达之士，足当大任。瑜不才，恐负倚托之重，愿荐一人以辅将军。","子布贤达之士，足当大任。瑜不才，恐负倚托之重，愿荐一人以辅将军。",1,"","","孙权",306,},
        [3308] = {5601,5,1,"孙权",306,0,"何人？","何人？",1,"","","周瑜",303,},
        [3309] = {5601,6,2,"周瑜",303,0,"姓鲁，名肃，字子敬，临淮东川人也。","姓鲁，名肃，字子敬，临淮东川人也。",1,"","","孙权",306,},
        [3310] = {5601,7,2,"周瑜",303,0,"此人胸怀韬略，腹隐机谋。早年丧父，事母至孝。其家极富，尝散财以济贫乏。","此人胸怀韬略，腹隐机谋。早年丧父，事母至孝。其家极富，尝散财以济贫乏。",1,"","","孙权",306,},
        [3311] = {5601,8,2,"周瑜",303,0,"瑜为居巢长之时，将数百人过临淮，因乏粮，闻鲁肃家有两囷米，各三千斛，因往求助。","瑜为居巢长之时，将数百人过临淮，因乏粮，闻鲁肃家有两囷米，各三千斛，因往求助。",1,"","","孙权",306,},
        [3312] = {5601,9,2,"周瑜",303,0,"肃即指一囷相赠，其慷慨如此。平生好击剑骑射，寓居曲阿。祖母亡，还葬东城。","肃即指一囷相赠，其慷慨如此。平生好击剑骑射，寓居曲阿。祖母亡，还葬东城。",1,"","","孙权",306,},
        [3313] = {5601,10,2,"周瑜",303,0,"其友刘子扬欲约彼往巢湖投郑宝，肃尚踌躇未往。今可速召之。","其友刘子扬欲约彼往巢湖投郑宝，肃尚踌躇未往。今可速召之。",1,"","","孙权",306,},
        [3314] = {5601,11,1,"孙权",306,0,"太好了，就依子布所言。","太好了，就依子布所言。",1,"","","周瑜",303,},
        [3315] = {5602,1,2,"周瑜",303,0,"孙权对你有相慕之意。","孙权对你有相慕之意。",1,"","","鲁肃",312,},
        [3316] = {5602,2,1,"鲁肃",312,0,"近刘子扬约某往巢湖，某将就之。","近刘子扬约某往巢湖，某将就之。",1,"","","周瑜",303,},
        [3317] = {5602,3,2,"周瑜",303,0,"昔马援对光武云：当今之世，非但君择臣，臣亦择君。","昔马援对光武云：当今之世，非但君择臣，臣亦择君。",1,"","","鲁肃",312,},
        [3318] = {5602,4,2,"周瑜",303,0,"今吾孙将军亲贤礼士，纳奇录异，世所罕有。足下不须他计，只同我往投东吴为是。","今吾孙将军亲贤礼士，纳奇录异，世所罕有。足下不须他计，只同我往投东吴为是。",1,"","","鲁肃",312,},
        [3319] = {5602,5,1,"鲁肃",312,0,"我与你一同去见孙权。","我与你一同去见孙权。",1,"","","周瑜",303,},
        [3320] = {5603,1,1,"孙权",306,0,"方今汉室倾危，四方纷扰，孤承父兄余业，思为桓、文之事，君将何以教我？","方今汉室倾危，四方纷扰，孤承父兄余业，思为桓、文之事，君将何以教我？",1,"","","鲁肃",312,},
        [3321] = {5603,2,2,"鲁肃",312,0,"昔汉高祖欲尊事义帝而不获者，以项羽为害也。今之曹操可比项羽，将军何由得为桓、文乎？","昔汉高祖欲尊事义帝而不获者，以项羽为害也。今之曹操可比项羽，将军何由得为桓、文乎？",1,"","","孙权",306,},
        [3322] = {5603,3,2,"鲁肃",312,0,"肃窃料汉室不可复兴，曹操不可卒除。为将军计，惟有鼎足江东以观天下之衅。","肃窃料汉室不可复兴，曹操不可卒除。为将军计，惟有鼎足江东以观天下之衅。",1,"","","孙权",306,},
        [3323] = {5603,4,2,"鲁肃",312,0,"今乘北方多务，剿除黄祖，进伐刘表，竟长江所极而据守之。","今乘北方多务，剿除黄祖，进伐刘表，竟长江所极而据守之。",1,"","","孙权",306,},
        [3324] = {5603,5,2,"鲁肃",312,0,"然后建号帝王，以图天下，此高祖之业也。","然后建号帝王，以图天下，此高祖之业也。",1,"","","孙权",306,},
        [3325] = {5603,6,1,"孙权",306,0,"言之有理言之有理，哈哈哈哈。","言之有理言之有理，哈哈哈哈。",1,"","","鲁肃",312,},
        [3326] = {5604,1,2,"鲁肃",312,0,"我有一人想推荐给你孙权。","我有一人想推荐给你孙权。",1,"","","孙权",306,},
        [3327] = {5604,2,1,"孙权",306,0,"何人也？","何人也？",1,"","","鲁肃",312,},
        [3328] = {5604,3,2,"鲁肃",312,0,"此人博学多才，事母至孝；覆姓诸葛，名瑾，字子瑜，琅琊南阳人也。","此人博学多才，事母至孝；覆姓诸葛，名瑾，字子瑜，琅琊南阳人也。",1,"","","孙权",306,},
        [3329] = {5604,4,1,"孙权",306,0,"速引荐于我。","速引荐于我。",1,"","","鲁肃",312,},
        [3330] = {5605,1,1,"孙权",306,0,"今天下事，子瑜你怎么看？","今天下事，子瑜你怎么看？",1,"","","诸葛瑾",319,},
        [3331] = {5605,2,2,"诸葛瑾",319,0,"勿通袁绍，且顺曹操，然后乘便图之。","勿通袁绍，且顺曹操，然后乘便图之。",1,"","","孙权",306,},
        [3332] = {5605,3,1,"孙权",306,0,"言简意赅，言之有理。","言简意赅，言之有理。",1,"","","诸葛瑾",319,},
        [3333] = {5606,1,1,"主角",1,0,"吾游历江东，亦有一人可推荐于你。","吾游历江东，亦有一人可推荐于你。",1,"","","孙权",306,},
        [3334] = {5606,2,2,"孙权",306,0,"何人也？","何人也？",1,"","","主角",1,},
        [3335] = {5606,3,1,"主角",1,0,"此人姓顾，名雍，字元叹，乃中郎蔡邕之徒；其为人少言语，不饮酒，严厉正大。","此人姓顾，名雍，字元叹，乃中郎蔡邕之徒；其为人少言语，不饮酒，严厉正大。",1,"","","孙权",306,},
        [3336] = {5606,4,2,"孙权",306,0,"恩，可招其为丞，行太守事。","恩，可招其为丞，行太守事。",1,"","","主角",1,},
        [3337] = {5607,1,2,"陈震",140,0,"孙策已亡，孙权继立，深得民心。","孙策已亡，孙权继立，深得民心。",1,"","","袁绍",411,},
        [3338] = {5607,2,2,"陈震",140,0,"曹操封之为将军，结为外应矣。","曹操封之为将军，结为外应矣。",1,"","","袁绍",411,},
        [3339] = {5607,3,1,"袁绍",411,0,"什么！","什么！",1,"","","陈震",140,},
        [3340] = {5607,4,1,"袁绍",411,0,"起冀、青、幽、并等处人马七十余万，攻取许昌。","起冀、青、幽、并等处人马七十余万，攻取许昌。",1,"","","陈震",140,},
        [3341] = {5701,1,2,"沮授",422,0,"我军虽众，而勇猛不及彼军，彼军虽精，而粮草不如我军。","我军虽众，而勇猛不及彼军，彼军虽精，而粮草不如我军。",1,"","","袁绍",411,},
        [3342] = {5701,2,2,"沮授",422,0,"彼军无粮，利在急战，我军有粮，宜且缓守。","彼军无粮，利在急战，我军有粮，宜且缓守。",1,"","","袁绍",411,},
        [3343] = {5701,3,2,"沮授",422,0,"若能旷以日月，则彼军不战自败矣。","若能旷以日月，则彼军不战自败矣。",1,"","","袁绍",411,},
        [3344] = {5701,4,1,"袁绍",411,0,"田丰慢我军心，吾回日必斩之。汝安敢又如此！","田丰慢我军心，吾回日必斩之。汝安敢又如此！",1,"","","沮授",422,},
        [3345] = {5701,5,1,"袁绍",411,0,"将沮授锁禁军中，待我破曹之后，与田丰一体治罪！","将沮授锁禁军中，待我破曹之后，与田丰一体治罪！",1,"","","沮授",422,},
        [3346] = {5702,1,1,"曹操",103,0,"吾于天子之前，保奏你为大将军，今何故谋反？","吾于天子之前，保奏你为大将军，今何故谋反？",1,"","","袁绍",411,},
        [3347] = {5702,2,2,"袁绍",411,0,"汝托名汉相，实为汉贼！罪恶弥天，甚于莽、卓，乃反诬人造反耶！","汝托名汉相，实为汉贼！罪恶弥天，甚于莽、卓，乃反诬人造反耶！",1,"","","曹操",103,},
        [3348] = {5702,3,1,"曹操",103,0,"吾今奉诏讨汝！","吾今奉诏讨汝！",1,"","","袁绍",411,},
        [3349] = {5702,4,2,"袁绍",411,0,"吾奉衣带诏讨贼！","吾奉衣带诏讨贼！",1,"","","曹操",103,},
        [3350] = {5702,5,1,"曹操",103,0,"张辽出战！","张辽出战！",1,"","","袁绍",411,},
        [3351] = {5702,6,2,"袁绍",411,0,"张郃迎战！","张郃迎战！",1,"","","曹操",103,},
        [3352] = {5703,1,1,"袁绍",411,0,"曹军大败，尽退至官渡。","曹军大败，尽退至官渡。",1,"","","",0,},
        [3353] = {5703,2,1,"袁绍",411,0,"命三军逼近官渡下寨。","命三军逼近官渡下寨。",1,"","","",0,},
        [3354] = {5703,3,2,"审配",131,0,"今可拨兵十万守官渡，就曹操寨前筑起土山，令军人下视寨中放箭。","今可拨兵十万守官渡，就曹操寨前筑起土山，令军人下视寨中放箭。",1,"","","袁绍",411,},
        [3355] = {5703,4,2,"审配",131,0,"操若弃此而去，吾得此隘口，许昌可破矣。","操若弃此而去，吾得此隘口，许昌可破矣。",1,"","","袁绍",411,},
        [3356] = {5703,5,1,"袁绍",411,0,"正南言之有理。","正南言之有理。",1,"","","审配",131,},
        [3357] = {5704,1,1,"曹操",103,0,"袁军堆筑土山，欲待出去冲突，又被那审配弓弩手当住咽喉要路，不能前进。","袁军堆筑土山，欲待出去冲突，又被那审配弓弩手当住咽喉要路，不能前进。",1,"","","",0,},
        [3358] = {5704,2,1,"曹操",103,0,"如何是好？","如何是好？",1,"","","",0,},
        [3359] = {5704,3,2,"主角",1,0,"孟德莫担忧！","孟德莫担忧！",1,"","","曹操",103,},
        [3360] = {5704,4,2,"主角",1,0,"我从江东赶来，助你破袁绍之师！","我从江东赶来，助你破袁绍之师！",1,"","","曹操",103,},
        [3361] = {5704,5,1,"曹操",103,0,"太好了，取胜就靠扬威将军你了。","太好了，取胜就靠扬威将军你了。",1,"","","主角",1,},
        [3362] = {5705,1,1,"曹操",103,0,"守这官渡，自八月起，至九月终，军力渐乏，粮草不继。","守这官渡，自八月起，至九月终，军力渐乏，粮草不继。",1,"","","主角",1,},
        [3363] = {5705,2,1,"曹操",103,0,"孤欲弃官渡退回许昌。","孤欲弃官渡退回许昌。",1,"","","主角",1,},
        [3364] = {5705,3,2,"主角",1,0,"我认为还不能退回许昌，不如问问荀彧的意见。","我认为还不能退回许昌，不如问问荀彧的意见。",1,"","","曹操",103,},
        [3365] = {5705,4,1,"曹操",103,0,"文若你怎么看？","文若你怎么看？",1,"","","主角",1,},
        [3366] = {5705,5,2,"荀彧",102,0,"愚以袁绍悉众聚于官渡，欲与明公决胜负，公以至弱当至强。","愚以袁绍悉众聚于官渡，欲与明公决胜负，公以至弱当至强。",1,"","","曹操",103,},
        [3367] = {5705,6,2,"荀彧",102,0,"若不能制，必为所乘：是天下之大机也。","若不能制，必为所乘：是天下之大机也。",1,"","","曹操",103,},
        [3368] = {5705,7,2,"荀彧",102,0,"绍军虽众，而不能用；以公之神武明哲，何向而不济！今军实虽少，未若楚、汉在荥阳、成皋间也。","绍军虽众，而不能用；以公之神武明哲，何向而不济！今军实虽少，未若楚、汉在荥阳、成皋间也。",1,"","","曹操",103,},
        [3369] = {5705,8,2,"荀彧",102,0,"公今画地而守，扼其喉而使不能进，情见势竭，必将有变。此用奇之时，断不可失。惟明公裁察焉。","公今画地而守，扼其喉而使不能进，情见势竭，必将有变。此用奇之时，断不可失。惟明公裁察焉。",1,"","","曹操",103,},
        [3370] = {5705,9,1,"曹操",103,0,"孤立刻命将士效力死守！","孤立刻命将士效力死守！",1,"","","荀彧",102,},
        [3371] = {5801,1,1,"曹操",103,0,"巡哨得知，绍军约退三十余里，大将韩猛早晚运粮至军前接济。","巡哨得知，绍军约退三十余里，大将韩猛早晚运粮至军前接济。",1,"","","荀攸",120,},
        [3372] = {5801,2,2,"荀攸",120,0,"韩猛匹夫之勇耳。若遣一人引轻骑数千，从半路击之，断其粮草，绍军自乱。","韩猛匹夫之勇耳。若遣一人引轻骑数千，从半路击之，断其粮草，绍军自乱。",1,"","","曹操",103,},
        [3373] = {5801,3,1,"曹操",103,0,"谁人可往？","谁人可往？",1,"","","荀攸",120,},
        [3374] = {5801,4,2,"主角",1,0,"我愿领命前往！","我愿领命前往！",1,"","","曹操",103,},
        [3375] = {5801,5,1,"曹操",103,0,"好！","好！",1,"","","主角",1,},
        [3376] = {5802,1,1,"袁绍",411,0,"粮草被劫，该如何是好？","粮草被劫，该如何是好？",1,"","","",0,},
        [3377] = {5802,2,2,"审配",131,0,"行军以粮食为重，不可不用心提防。乌巢乃屯粮之处，必得重兵守之。","行军以粮食为重，不可不用心提防。乌巢乃屯粮之处，必得重兵守之。",1,"","","袁绍",411,},
        [3378] = {5802,3,1,"袁绍",411,0,"吾筹策已定。汝可回邺都监督粮草，休教缺乏。","吾筹策已定。汝可回邺都监督粮草，休教缺乏。",1,"","","审配",131,},
        [3379] = {5802,4,1,"袁绍",411,0,"大将淳于琼，部领督将眭元进、韩莒子、吕威璜、赵睿等，引二万人马，守乌巢。","大将淳于琼，部领督将眭元进、韩莒子、吕威璜、赵睿等，引二万人马，守乌巢。",1,"","","审配",131,},
        [3380] = {5803,1,2,"许攸",121,0,"吾等获得曹操文书。","吾等获得曹操文书。",1,"","","袁绍",411,},
        [3381] = {5803,2,2,"许攸",121,0,"曹操屯军官渡，与我相持已久，许昌必空虚；若分一军星夜掩袭许昌，则许昌可拔，而操可擒也。","曹操屯军官渡，与我相持已久，许昌必空虚；若分一军星夜掩袭许昌，则许昌可拔，而操可擒也。",1,"","","袁绍",411,},
        [3382] = {5803,3,2,"许攸",121,0,"今操粮草已尽，正可乘此机会，两路击之。","今操粮草已尽，正可乘此机会，两路击之。",1,"","","袁绍",411,},
        [3383] = {5803,4,1,"袁绍",411,0,"曹操诡计极多，此书乃诱敌之计也。","曹操诡计极多，此书乃诱敌之计也。",1,"","","许攸",121,},
        [3384] = {5803,5,2,"许攸",121,0,"今若不取，后将反受其害。","今若不取，后将反受其害。",1,"","","袁绍",411,},
        [3385] = {5803,6,1,"袁绍",411,0,"汝在冀州时，尝滥受民间财物，且纵令子侄辈多科税，钱粮入己。","汝在冀州时，尝滥受民间财物，且纵令子侄辈多科税，钱粮入己。",1,"","","许攸",121,},
        [3386] = {5803,7,1,"袁绍",411,0,"滥行匹夫！尚有面目于吾前献计耶！","滥行匹夫！尚有面目于吾前献计耶！",1,"","","许攸",121,},
        [3387] = {5803,8,1,"袁绍",411,0,"汝与曹操有旧，想今亦受他财贿，为他作奸细，啜赚吾军耳！","汝与曹操有旧，想今亦受他财贿，为他作奸细，啜赚吾军耳！",1,"","","许攸",121,},
        [3388] = {5803,9,1,"袁绍",411,0,"本当斩首，今权且寄头在项！可速退出，今后不许相见！","本当斩首，今权且寄头在项！可速退出，今后不许相见！",1,"","","许攸",121,},
        [3389] = {5803,10,2,"许攸",121,0,"忠言逆耳，竖子不足与谋！","忠言逆耳，竖子不足与谋！",1,"","","袁绍",411,},
        [3390] = {5804,1,1,"曹操",103,0,"故友深夜为何而来？","故友深夜为何而来？",1,"","","许攸",121,},
        [3391] = {5804,2,2,"许攸",121,0,"某不能择主，屈身袁绍，言不听，计不从，今特弃之来见故人，愿赐收录。","某不能择主，屈身袁绍，言不听，计不从，今特弃之来见故人，愿赐收录。",1,"","","曹操",103,},
