        [3476] = {6106,3,2,"审配",131,0,"我这就立遗嘱。","我这就立遗嘱。",1,"","","袁绍",411,},
        [3477] = {6106,4,2,"刘夫人",227,0,"本初！（只见袁绍翻身大叫一声，吐血斗余而去。）","本初！（只见袁绍翻身大叫一声，吐血斗余而去。）",1,"","","袁绍",411,},
        [3478] = {6201,1,2,"张春华",118,0,"曹操听闻汝才识，特派人征汝至府中任职。","曹操听闻汝才识，特派人征汝至府中任职。",1,"","","司马懿",101,},
        [3479] = {6201,2,1,"司马懿",101,0,"东汉政权已经被曹氏控制，曹氏又是阉宦之后。","东汉政权已经被曹氏控制，曹氏又是阉宦之后。",1,"","","张春华",118,},
        [3480] = {6201,3,1,"司马懿",101,0,"吾不想屈节在其手下，就借口说吾有风痹症，身体不能起居。","吾不想屈节在其手下，就借口说吾有风痹症，身体不能起居。",1,"","","张春华",118,},
        [3481] = {6201,4,1,"司马懿",101,0,"打发掉使者。","打发掉使者。",1,"","","张春华",118,},
        [3482] = {6201,5,2,"张春华",118,0,"好。","好。",1,"","","司马懿",101,},
        [3483] = {6202,1,2,"张春华",118,0,"不好，仲达！","不好，仲达！",1,"","","司马懿",101,},
        [3484] = {6202,2,2,"张春华",118,0,"曹操不信汝有风痹，深夜再派使者前来。","曹操不信汝有风痹，深夜再派使者前来。",1,"","","司马懿",101,},
        [3485] = {6202,3,1,"司马懿",101,0,"莫慌，你前去厅堂阻拦片刻，我回屋静卧。","莫慌，你前去厅堂阻拦片刻，我回屋静卧。",1,"","","张春华",118,},
        [3486] = {6202,4,1,"司马懿",101,0,"一动不动，让使者看到便如同真染上风痹一般。","一动不动，让使者看到便如同真染上风痹一般。",1,"","","张春华",118,},
        [3487] = {6202,5,2,"张春华",118,0,"好。","好。",1,"","","司马懿",101,},
        [3488] = {6301,1,2,"孙权",306,0,"吾偶得一巨象，特赠予你曹操。","吾偶得一巨象，特赠予你曹操。",1,"","","曹操",103,},
        [3489] = {6301,2,2,"孙权",306,0,"以表心意。","以表心意。",1,"","","曹操",103,},
        [3490] = {6301,3,1,"曹操",103,0,"谢仲谋好意。","谢仲谋好意。",1,"","","孙权",306,},
        [3491] = {6301,4,2,"孙权",306,0,"对了，吾不才，一直不知其重，孟德汝帐下能人谋士甚多。","对了，吾不才，一直不知其重，孟德汝帐下能人谋士甚多。",1,"","","曹操",103,},
        [3492] = {6301,5,2,"孙权",306,0,"若他日称出其重量，一定要告知我仲谋。","若他日称出其重量，一定要告知我仲谋。",1,"","","曹操",103,},
        [3493] = {6301,6,1,"曹操",103,0,"一定一定…","一定一定…",1,"","","孙权",306,},
        [3494] = {6302,1,1,"曹操",103,0,"孙权假意赠象，实则是羞辱我。","孙权假意赠象，实则是羞辱我。",1,"","","",0,},
        [3495] = {6302,2,1,"曹操",103,0,"要是称不出巨象重量，孤的面子往哪里搁。","要是称不出巨象重量，孤的面子往哪里搁。",1,"","","",0,},
        [3496] = {6302,3,1,"曹操",103,0,"你们快帮孤想想办法。","你们快帮孤想想办法。",1,"","","",0,},
        [3497] = {6302,4,2,"夏侯惇",105,0,"只有造一杆顶大的秤来称便是。","只有造一杆顶大的秤来称便是。",1,"","","曹操",103,},
        [3498] = {6302,5,2,"张辽",109,0,"这可要造多大一杆秤呀！再者这大象可是活物，如何使其安静称重！","这可要造多大一杆秤呀！再者这大象可是活物，如何使其安静称重！",1,"","","曹操",103,},
        [3499] = {6302,6,2,"张辽",109,0,"我看只有把它宰了，切成块儿称。","我看只有把它宰了，切成块儿称。",1,"","","曹操",103,},
        [3500] = {6302,7,1,"曹操",103,0,"一介武夫！","一介武夫！",1,"","","张辽",109,},
        [3501] = {6302,8,1,"曹操",103,0,"你这个办法可不行啊，为了称重量，就把大象活活地宰了，不可惜吗？","你这个办法可不行啊，为了称重量，就把大象活活地宰了，不可惜吗？",1,"","","张辽",109,},
        [3502] = {6303,1,1,"曹操",103,0,"谁可有称象之法？","谁可有称象之法？",1,"","","",0,},
        [3503] = {6303,2,2,"曹冲",115,0,"我有个法儿，可以称大象。","我有个法儿，可以称大象。",1,"","","曹操",103,},
        [3504] = {6303,3,1,"曹操",103,0,"如何称象？","如何称象？",1,"","","曹冲",115,},
        [3505] = {6303,4,2,"曹冲",115,0,"这不难，将巨象放于大船上，在水面所达到的地方做上记号。","这不难，将巨象放于大船上，在水面所达到的地方做上记号。",1,"","","曹操",103,},
        [3506] = {6303,5,2,"曹冲",115,0,"再让船重新装上重物，让水面达到刚才记号位置。","再让船重新装上重物，让水面达到刚才记号位置。",1,"","","曹操",103,},
        [3507] = {6303,6,2,"曹冲",115,0,"最后称一下这些重物，便能知道巨象的重量了。","最后称一下这些重物，便能知道巨象的重量了。",1,"","","曹操",103,},
        [3508] = {6303,7,1,"曹操",103,0,"哈哈哈哈，还是吾仓舒聪明。","哈哈哈哈，还是吾仓舒聪明。",1,"","","曹冲",115,},
        [3509] = {6303,8,1,"曹操",103,0,"哈哈哈哈。","哈哈哈哈。",1,"","","曹冲",115,},
        [3510] = {6401,1,1,"曹操",103,0,"随兵至冀州，但袁谭与袁尚入城坚守，袁熙与高干离城三十里下寨，连日攻打不下。","随兵至冀州，但袁谭与袁尚入城坚守，袁熙与高干离城三十里下寨，连日攻打不下。",1,"","","郭嘉",104,},
        [3511] = {6401,2,1,"曹操",103,0,"该如何是好？","该如何是好？",1,"","","郭嘉",104,},
        [3512] = {6401,3,2,"郭嘉",104,0,"袁氏废长立幼，而兄弟之间，权力相并，各自树党，急之则相救，缓之则相争。","袁氏废长立幼，而兄弟之间，权力相并，各自树党，急之则相救，缓之则相争。",1,"","","曹操",103,},
        [3513] = {6401,4,2,"郭嘉",104,0,"不如举兵南向荆州，征讨刘表，以候袁氏兄弟之变，变成而后击之，可一举而定也。","不如举兵南向荆州，征讨刘表，以候袁氏兄弟之变，变成而后击之，可一举而定也。",1,"","","曹操",103,},
        [3514] = {6401,5,1,"曹操",103,0,"奉孝所言极是！","奉孝所言极是！",1,"","","郭嘉",104,},
        [3515] = {6401,6,1,"曹操",103,0,"命贾诩为太守，守黎阳，曹洪引兵守官渡，孤引大军向荆州进兵。","命贾诩为太守，守黎阳，曹洪引兵守官渡，孤引大军向荆州进兵。",1,"","","郭嘉",104,},
        [3516] = {6402,1,1,"袁谭",430,0,"我为长子，反不能承父业；尚乃继母所生，反承大爵，心实不甘。","我为长子，反不能承父业；尚乃继母所生，反承大爵，心实不甘。",1,"","","郭图",444,},
        [3517] = {6402,2,2,"郭图",444,0,"主公可勒兵城外，只做请显甫、审配饮酒，伏刀斧手杀之，大事定矣。","主公可勒兵城外，只做请显甫、审配饮酒，伏刀斧手杀之，大事定矣。",1,"","","袁谭",430,},
        [3518] = {6403,1,2,"审配",131,0,"袁谭邀你单独相聚，此必郭图之计也。主公若往，必遭奸计，不如乘势攻之。","袁谭邀你单独相聚，此必郭图之计也。主公若往，必遭奸计，不如乘势攻之。",1,"","","袁尚",128,},
        [3519] = {6403,2,1,"袁尚",128,0,"我这就引兵前往！（袁尚引兵五万出城。）","我这就引兵前往！（袁尚引兵五万出城。）",1,"","","审配",131,},
        [3520] = {6403,3,2,"袁谭",430,0,"汝为何领兵而来？","汝为何领兵而来？",1,"","","袁尚",128,},
        [3521] = {6403,4,1,"袁尚",128,0,"袁绍立我为嗣，汝是否想造反？","袁绍立我为嗣，汝是否想造反？",1,"","","袁谭",430,},
        [3522] = {6403,5,2,"袁谭",430,0,"汝药死父亲，篡夺爵位，今又来杀兄耶！","汝药死父亲，篡夺爵位，今又来杀兄耶！",1,"","","袁尚",128,},
        [3523] = {6404,1,2,"郭嘉",104,0,"袁谭与吕旷、吕翔来降。","袁谭与吕旷、吕翔来降。",1,"","","曹操",103,},
        [3524] = {6404,2,1,"曹操",103,0,"吾已料定矣。","吾已料定矣。",1,"","","郭嘉",104,},
        [3525] = {6404,3,1,"曹操",103,0,"不过那袁谭刻将军印二颗，暗送与二吕令作内应，想日后造反。","不过那袁谭刻将军印二颗，暗送与二吕令作内应，想日后造反。",1,"","","郭嘉",104,},
        [3526] = {6404,4,1,"曹操",103,0,"岂不知二吕将事情禀报于孤，待我破袁尚之后，必斩了那袁谭。","岂不知二吕将事情禀报于孤，待我破袁尚之后，必斩了那袁谭。",1,"","","郭嘉",104,},
        [3527] = {6404,5,2,"郭嘉",104,0,"丞相英明。","丞相英明。",1,"","","曹操",103,},
        [3528] = {6405,1,1,"曹操",103,0,"孤依冯礼之计，命三百壮士掘地道而入城。","孤依冯礼之计，命三百壮士掘地道而入城。",1,"","","",0,},
        [3529] = {6405,2,1,"曹操",103,0,"不料事情败露，那审配精兵运石击突闸门，冯礼与三百壮士皆死于土内。","不料事情败露，那审配精兵运石击突闸门，冯礼与三百壮士皆死于土内。",1,"","","",0,},
        [3530] = {6405,3,1,"曹操",103,0,"还有何计可施？","还有何计可施？",1,"","","",0,},
        [3531] = {6405,4,2,"许攸",121,0,"吾有一计，何不决漳河之水以淹之？","吾有一计，何不决漳河之水以淹之？",1,"","","曹操",103,},
        [3532] = {6405,5,1,"曹操",103,0,"看似可行，就依子远之计行事。","看似可行，就依子远之计行事。",1,"","","许攸",121,},
        [3533] = {6406,1,1,"袁尚",128,0,"操军在城外掘堑！","操军在城外掘堑！",1,"","","审配",131,},
        [3534] = {6406,2,2,"审配",131,0,"待我观之。","待我观之。",1,"","","袁尚",128,},
        [3535] = {6406,3,2,"审配",131,0,"此举欲决漳河之水以灌城耳。壕深可灌，如此之浅，有何用哉！","此举欲决漳河之水以灌城耳。壕深可灌，如此之浅，有何用哉！",1,"","","袁尚",128,},
        [3536] = {6407,1,2,"许攸",121,0,"审配大意，吾军当夜已添十倍军士并力发掘，比及天明，广深二丈。","审配大意，吾军当夜已添十倍军士并力发掘，比及天明，广深二丈。",1,"","","曹操",103,},
        [3537] = {6407,2,2,"许攸",121,0,"引漳水灌之，城中水深数尺，更兼粮绝，军士皆饿死。","引漳水灌之，城中水深数尺，更兼粮绝，军士皆饿死。",1,"","","曹操",103,},
        [3538] = {6407,3,1,"曹操",103,0,"好！敌将审荣愿降，大开西门，放我兵入。","好！敌将审荣愿降，大开西门，放我兵入。",1,"","","许攸",121,},
        [3539] = {6407,4,1,"曹操",103,0,"速命全军，杀入冀州！","速命全军，杀入冀州！",1,"","","许攸",121,},
        [3540] = {6408,1,1,"曹操",103,0,"卿忠于袁氏，不容不如此。今肯降吾否？","卿忠于袁氏，不容不如此。今肯降吾否？",1,"","","审配",131,},
        [3541] = {6408,2,2,"审配",131,0,"不降！","不降！",1,"","","曹操",103,},
        [3542] = {6408,3,2,"审配",131,0,"吾生为袁氏臣，死为袁氏鬼，不似汝辈谗谄阿谀之贼！可速斩我！","吾生为袁氏臣，死为袁氏鬼，不似汝辈谗谄阿谀之贼！可速斩我！",1,"","","曹操",103,},
        [3543] = {6408,4,1,"曹操",103,0,"迂腐，斩了！","迂腐，斩了！",1,"","","审配",131,},
        [3544] = {6501,1,1,"曹丕",111,0,"二妇人啼哭，何人也？（曹丕先入袁府见两夫人哭泣便问之。）","二妇人啼哭，何人也？（曹丕先入袁府见两夫人哭泣便问之。）",1,"","","刘夫人",227,},
        [3545] = {6501,2,2,"刘夫人",227,0,"妾乃袁将军之妻刘氏也。","妾乃袁将军之妻刘氏也。",1,"","","曹丕",111,},
        [3546] = {6501,3,1,"曹丕",111,0,"此女何人？","此女何人？",1,"","","刘夫人",227,},
        [3547] = {6501,4,2,"刘夫人",227,0,"此次男袁熙之妻甄氏也。因熙出镇幽州，甄氏不肯远行，故留于此。","此次男袁熙之妻甄氏也。因熙出镇幽州，甄氏不肯远行，故留于此。",1,"","","曹丕",111,},
        [3548] = {6501,5,1,"曹丕",111,0,"吾乃曹丞相之子也。愿保汝家。汝勿忧虑。","吾乃曹丞相之子也。愿保汝家。汝勿忧虑。",1,"","","刘夫人",227,},
        [3549] = {6502,1,2,"许攸",121,0,"汝不得我，安得入此门？","汝不得我，安得入此门？",1,"","","曹操",103,},
        [3550] = {6502,2,1,"曹操",103,0,"哈哈哈哈，来，一起入城门。","哈哈哈哈，来，一起入城门。",1,"","","许攸",121,},
        [3551] = {6502,3,1,"曹操",103,0,"我们先行去绍府。","我们先行去绍府。",1,"","","许攸",121,},
        [3552] = {6502,4,1,"曹操",103,0,"谁曾入此门来？","谁曾入此门来？",1,"","","许攸",121,},
        [3553] = {6502,5,2,"徐晃",114,0,"世子在内。","世子在内。",1,"","","曹操",103,},
        [3554] = {6502,6,1,"曹操",103,0,"让他出来！他怎可私自入内！","让他出来！他怎可私自入内！",1,"","","徐晃",114,},
        [3555] = {6502,7,2,"曹丕",111,0,"甄氏玉肌花貌，有倾国之色，我想纳之！","甄氏玉肌花貌，有倾国之色，我想纳之！",1,"","","",0,},
        [3556] = {6502,8,2,"刘夫人",227,0,"非世子不能保全妾家，愿就甄氏为世子执箕帚。","非世子不能保全妾家，愿就甄氏为世子执箕帚。",1,"","","曹操",103,},
        [3557] = {6502,9,1,"曹操",103,0,"好！孤允了。","好！孤允了。",1,"","","刘夫人",227,},
        [3558] = {6503,1,1,"曹操",103,0,"昔日吾与本初乃兄弟之交，而今本初已丧，吾不能不为流涕也！","昔日吾与本初乃兄弟之交，而今本初已丧，吾不能不为流涕也！",1,"","","",0,},
        [3559] = {6503,2,1,"曹操",103,0,"以金帛粮米赐绍妻刘氏，今河北居民遭兵革之难，尽免今年租赋。","以金帛粮米赐绍妻刘氏，今河北居民遭兵革之难，尽免今年租赋。",1,"","","",0,},
        [3560] = {6504,1,1,"许攸",121,0,"汝等无我，安能出入此门乎？","汝等无我，安能出入此门乎？",1,"","","许褚",108,},
        [3561] = {6504,2,2,"许褚",108,0,"吾等千主万死，身冒血战，夺得城池，汝安敢夸口！","吾等千主万死，身冒血战，夺得城池，汝安敢夸口！",1,"","","许攸",121,},
        [3562] = {6504,3,1,"许攸",121,0,"汝等皆匹夫耳，何足道哉！","汝等皆匹夫耳，何足道哉！",1,"","","许褚",108,},
        [3563] = {6504,4,2,"许褚",108,0,"如此无礼，我看你是活腻了！","如此无礼，我看你是活腻了！",1,"","","许攸",121,},
        [3564] = {6505,1,2,"许褚",108,0,"许攸如此无礼，某杀之矣。（随即呈上许攸头颅。）","许攸如此无礼，某杀之矣。（随即呈上许攸头颅。）",1,"","","曹操",103,},
        [3565] = {6505,2,1,"曹操",103,0,"子远与吾旧交，故相戏耳，何故杀之！","子远与吾旧交，故相戏耳，何故杀之！",1,"","","许褚",108,},
        [3566] = {6505,3,2,"许褚",108,0,"此等无礼之徒，时常口出狂言，我看不惯便杀之！","此等无礼之徒，时常口出狂言，我看不惯便杀之！",1,"","","曹操",103,},
