        [3572] = {6602,1,1,"主角",1,0,"袁谭作书求刘表增援，刘表无发兵之意，又自料不能敌吾等，走保南皮。","袁谭作书求刘表增援，刘表无发兵之意，又自料不能敌吾等，走保南皮。",1,"","","曹操",103,},
