        [3575] = {6603,2,2,"主角",1,0,"郭图见阵大乱，急驰入城中，吾拈弓搭箭，射下城壕，人马俱陷。","郭图见阵大乱，急驰入城中，吾拈弓搭箭，射下城壕，人马俱陷。",1,"","","曹操",103,},
