        [3579] = {6604,2,2,"郭嘉",104,0,"可使袁氏降将焦触、张南等自攻之。","可使袁氏降将焦触、张南等自攻之。",1,"","","曹操",103,},
        [3580] = {6604,3,1,"曹操",103,0,"焦触、张南、吕旷、吕翔、马延，各引本部兵，分三路进攻幽州。","焦触、张南、吕旷、吕翔、马延，各引本部兵，分三路进攻幽州。",1,"","","郭嘉",104,},
