        [3582] = {6605,1,2,"曹洪",142,0,"高干寻思无路，只得去投刘表，行至上洛，被都尉王琰所杀，其头颅解送前来。","高干寻思无路，只得去投刘表，行至上洛，被都尉王琰所杀，其头颅解送前来。",1,"","","曹操",103,},
        [3583] = {6605,2,2,"主角",1,0,"袁熙、袁尚兵败将亡，势穷力尽，远投沙漠。","袁熙、袁尚兵败将亡，势穷力尽，远投沙漠。",1,"","","曹操",103,},
        [3584] = {6605,3,2,"主角",1,0,"我今引兵西击，倘刘备、刘表乘虚袭许都，我救应不及，为祸不浅矣，请回师勿进为上。","我今引兵西击，倘刘备、刘表乘虚袭许都，我救应不及，为祸不浅矣，请回师勿进为上。",1,"","","曹操",103,},
        [3585] = {6605,4,2,"郭嘉",104,0,"扬威将军所言错矣。主公虽威震天下，沙漠之人恃其边远，必不设备。","扬威将军所言错矣。主公虽威震天下，沙漠之人恃其边远，必不设备。",1,"","","曹操",103,},
        [3586] = {6605,5,2,"郭嘉",104,0,"乘其无备，卒然击之，必可破也。且袁绍与乌桓有恩，而尚与熙兄弟犹存，不可不除。","乘其无备，卒然击之，必可破也。且袁绍与乌桓有恩，而尚与熙兄弟犹存，不可不除。",1,"","","曹操",103,},
        [3587] = {6605,6,2,"郭嘉",104,0,"刘表坐谈之客耳，自知才不足以御刘备，重任之则恐不能制，轻任之则备不为用。","刘表坐谈之客耳，自知才不足以御刘备，重任之则恐不能制，轻任之则备不为用。",1,"","","曹操",103,},
        [3588] = {6605,7,2,"郭嘉",104,0,"虽虚国远征，公无忧也。","虽虚国远征，公无忧也。",1,"","","曹操",103,},
        [3589] = {6605,8,1,"曹操",103,0,"奉孝之言极是。令大小三军，车数千辆，望前进发。","奉孝之言极是。令大小三军，车数千辆，望前进发。",1,"","","郭嘉",104,},
        [3590] = {6606,1,1,"曹操",103,0,"因我欲平沙漠，使公远涉艰辛，以至染病，吾心何安！（见郭嘉不伏水土，卧病车上。）","因我欲平沙漠，使公远涉艰辛，以至染病，吾心何安！（见郭嘉不伏水土，卧病车上。）",1,"","","郭嘉",104,},
