        [3592] = {6606,3,1,"曹操",103,0,"吾见北地崎岖，意欲回军，若何？","吾见北地崎岖，意欲回军，若何？",1,"","","郭嘉",104,},
        [3593] = {6606,4,2,"郭嘉",104,0,"兵贵神速。今千里袭人，辎重多而难以趋利，不如轻兵兼道以出，掩其不备。","兵贵神速。今千里袭人，辎重多而难以趋利，不如轻兵兼道以出，掩其不备。",1,"","","曹操",103,},
