        [3595] = {6607,1,1,"曹操",103,0,"奉孝死，乃天丧吾也！（等曹操再到易州时，郭嘉已病逝。）","奉孝死，乃天丧吾也！（等曹操再到易州时，郭嘉已病逝。）",1,"","","",0,},
        [3596] = {6607,2,1,"曹操",103,0,"诸君年齿，皆孤等辈，惟奉孝最少，吾欲托以后事。不期中年夭折，使吾心肠崩裂矣！","诸君年齿，皆孤等辈，惟奉孝最少，吾欲托以后事。不期中年夭折，使吾心肠崩裂矣！",1,"","","",0,},
        [3597] = {6607,3,2,"主角",1,0,"郭公临亡，亲笔书此，书中所言，辽东事定矣。","郭公临亡，亲笔书此，书中所言，辽东事定矣。",1,"","","曹操",103,},
