        [3603] = {6609,1,2,"夏侯惇",105,0,"辽东公孙康于馆驿斩袁熙袁尚二人，遣人送其首级至。","辽东公孙康于馆驿斩袁熙袁尚二人，遣人送其首级至。",1,"","","曹操",103,},
