        [3608] = {6609,6,1,"主角",1,0,"公孙康久畏袁氏吞并，二袁往投必疑。若以兵击之，必并力迎敌，急不可下。","公孙康久畏袁氏吞并，二袁往投必疑。若以兵击之，必并力迎敌，急不可下。",1,"","","夏侯惇",105,},
