        [3610] = {6609,8,2,"曹操",103,0,"天生郭奉孝，豪杰冠群英：腹内藏经史，胸中隐甲兵！","天生郭奉孝，豪杰冠群英：腹内藏经史，胸中隐甲兵！",1,"","","主角",1,},
