        [3650] = {6804,4,1,"曹操",103,0,"汝父蔡邕乃孤故友，昔日不能救其性命，今日能救汝也当是救赎。","汝父蔡邕乃孤故友，昔日不能救其性命，今日能救汝也当是救赎。",1,"","","蔡文姬",415,},
        [3651] = {6805,1,1,"曹操",103,0,"文姬故乡陈留已无故人。","文姬故乡陈留已无故人。",1,"","","蔡文姬",415,},
        [3652] = {6805,2,1,"曹操",103,0,"恰巧孤手下董祀，是个懂音律，通书史的才气男子。","恰巧孤手下董祀，是个懂音律，通书史的才气男子。",1,"","","蔡文姬",415,},
        [3653] = {6805,3,1,"曹操",103,0,"孤做个媒如何？","孤做个媒如何？",1,"","","蔡文姬",415,},
        [3654] = {6805,4,2,"蔡文姬",415,0,"文姬谢丞相美意。","文姬谢丞相美意。",1,"","","曹操",103,},
        [3655] = {6901,1,1,"曹操",103,0,"传令下去，征召司马懿为文学掾。","传令下去，征召司马懿为文学掾。",1,"","","",999,},
        [3656] = {6901,2,1,"曹操",103,0,"若这司马懿仍如以往，装病家中。","若这司马懿仍如以往，装病家中。",1,"","","",999,},
        [3657] = {6901,3,1,"曹操",103,0,"便将他逮捕下狱。","便将他逮捕下狱。",1,"","","",999,},
        [3658] = {6902,1,2,"张春华",118,0,"丞相要征召你为文学掾，且已识破你装病之计。","丞相要征召你为文学掾，且已识破你装病之计。",1,"","","司马懿",101,},
        [3659] = {6902,2,2,"张春华",118,0,"让人带话，若再不出仕，便有牢狱之灾。","让人带话，若再不出仕，便有牢狱之灾。",1,"","","司马懿",101,},
        [3660] = {6902,3,1,"司马懿",101,0,"好，吾这就出仕便是。","好，吾这就出仕便是。",1,"","","张春华",118,},
        [3661] = {6903,1,1,"曹操",103,0,"孤观司马懿有时日，他有雄豪志，亦有狼顾之相。","孤观司马懿有时日，他有雄豪志，亦有狼顾之相。",1,"","","曹丕",111,},
        [3662] = {6903,2,1,"曹操",103,0,"绝不是甘为臣下之人，今后必会干预我曹家之事。","绝不是甘为臣下之人，今后必会干预我曹家之事。",1,"","","曹丕",111,},
        [3663] = {6903,3,1,"曹操",103,0,"汝和其关系很好，总是维护他，不过今后一定要警惕。","汝和其关系很好，总是维护他，不过今后一定要警惕。",1,"","","曹丕",111,},
        [3664] = {6903,4,2,"曹丕",111,0,"吾一定铭记于心。","吾一定铭记于心。",1,"","","曹操",103,},
        [3665] = {7001,1,2,"曹操",103,0,"孤头脑疼痛不可忍，遍求良医治疗，不能痊可。","孤头脑疼痛不可忍，遍求良医治疗，不能痊可。",1,"","","主角",1,},
        [3666] = {7001,2,2,"曹操",103,0,"扬威将军你游历四方，可识名医？","扬威将军你游历四方，可识名医？",1,"","","主角",1,},
        [3667] = {7001,3,1,"主角",1,0,"大王知有神医华伦否？","大王知有神医华伦否？",1,"","","曹操",103,},
        [3668] = {7001,4,2,"曹操",103,0,"即江东医周泰者乎？","即江东医周泰者乎？",1,"","","主角",1,},
        [3669] = {7001,5,1,"主角",1,0,"是也。","是也。",1,"","","曹操",103,},
        [3670] = {7001,6,2,"曹操",103,0,"虽闻其名，未知其术。","虽闻其名，未知其术。",1,"","","主角",1,},
        [3671] = {7001,7,1,"主角",1,0,"此人真扁鹊，仓公之流也！","此人真扁鹊，仓公之流也！",1,"","","曹操",103,},
        [3672] = {7001,8,2,"曹操",103,0,"那他现身在何处？","那他现身在何处？",1,"","","主角",1,},
        [3673] = {7001,9,1,"主角",1,0,"现居金城，离此不远，大王何不召之？","现居金城，离此不远，大王何不召之？",1,"","","曹操",103,},
        [3674] = {7001,10,2,"曹操",103,0,"速召之！","速召之！",1,"","","主角",1,},
        [3675] = {7002,1,2,"曹操",103,0,"元化，孤头脑疼痛不可忍，快来看看。","元化，孤头脑疼痛不可忍，快来看看。",1,"","","华佗",402,},
        [3676] = {7002,2,1,"华佗",402,0,"大王头脑疼痛，因患风而起。病根在脑袋中，风涎不能出，枉服汤药，不可治疗。","大王头脑疼痛，因患风而起。病根在脑袋中，风涎不能出，枉服汤药，不可治疗。",1,"","","曹操",103,},
        [3677] = {7002,3,1,"华佗",402,0,"某有一法：先饮麻肺汤，然后用利斧砍开脑袋，取出风涎，方可除根。","某有一法：先饮麻肺汤，然后用利斧砍开脑袋，取出风涎，方可除根。",1,"","","曹操",103,},
        [3678] = {7002,4,2,"曹操",103,0,"汝要杀孤耶！","汝要杀孤耶！",1,"","","华佗",402,},
        [3679] = {7002,5,1,"华佗",402,0,"大王曾闻关公中毒箭，伤其右臂，某刮骨疗毒，关公略无惧色。","大王曾闻关公中毒箭，伤其右臂，某刮骨疗毒，关公略无惧色。",1,"","","曹操",103,},
        [3680] = {7002,6,1,"华佗",402,0,"今大王小可之疾，何多疑焉？","今大王小可之疾，何多疑焉？",1,"","","曹操",103,},
        [3681] = {7002,7,2,"曹操",103,0,"臂痛可刮，脑袋安可砍开？","臂痛可刮，脑袋安可砍开？",1,"","","华佗",402,},
        [3682] = {7002,8,2,"曹操",103,0,"汝必与关公情熟，乘此机会，欲报仇耳！","汝必与关公情熟，乘此机会，欲报仇耳！",1,"","","华佗",402,},
        [3683] = {7002,9,2,"曹操",103,0,"快来人将其拿下！","快来人将其拿下！",1,"","","华佗",402,},
        [3684] = {7002,10,1,"主角",1,0,"似此良医，世罕其匹，未可废也。","似此良医，世罕其匹，未可废也。",1,"","","曹操",103,},
        [3685] = {7002,11,2,"曹操",103,0,"此人欲乘机害我，正与吉平无异！","此人欲乘机害我，正与吉平无异！",1,"","","主角",1,},
        [3686] = {7002,12,2,"曹操",103,0,"拿下！","拿下！",1,"","","主角",1,},
        [3687] = {7003,1,1,"华佗",402,0,"谢扬威将军每日假扮狱卒前来看望，还以酒食相奉。","谢扬威将军每日假扮狱卒前来看望，还以酒食相奉。",1,"","","主角",1,},
        [3688] = {7003,2,2,"主角",1,0,"怎可言谢，是我害你身陷牢狱之中。","怎可言谢，是我害你身陷牢狱之中。",1,"","","华佗",402,},
        [3689] = {7003,3,1,"华佗",402,0,"世事造化难料，莫怪自己。","世事造化难料，莫怪自己。",1,"","","主角",1,},
        [3690] = {7003,4,1,"华佗",402,0,"我今将死，恨有《青囊书》未传于世。","我今将死，恨有《青囊书》未传于世。",1,"","","主角",1,},
        [3691] = {7003,5,1,"华佗",402,0,"我修一书，公可遣人送与我家，取《青囊书》来赠公，以继吾术。","我修一书，公可遣人送与我家，取《青囊书》来赠公，以继吾术。",1,"","","主角",1,},
        [3692] = {7003,6,2,"主角",1,0,"汝术应由汝亲自传于后人，我自有办法相救。","汝术应由汝亲自传于后人，我自有办法相救。",1,"","","华佗",402,},
        [3693] = {7004,1,2,"主角",1,0,"我使狸猫换太子之计，让世人皆以为华佗死于牢中，汝以后切莫再以华佗自居。","我使狸猫换太子之计，让世人皆以为华佗死于牢中，汝以后切莫再以华佗自居。",1,"","","华佗",402,},
        [3694] = {7004,2,2,"主角",1,0,"此外我已差人打点妥当，烦请元化之后隐居荆襄，继续行医救世。","此外我已差人打点妥当，烦请元化之后隐居荆襄，继续行医救世。",1,"","","华佗",402,},
        [3695] = {7004,3,1,"华佗",402,0,"谢扬威将军搭救，元化一定铭记于心，以医术造福世人。","谢扬威将军搭救，元化一定铭记于心，以医术造福世人。",1,"","","主角",1,},
        [3696] = {7004,4,1,"华佗",402,0,"珍重！","珍重！",1,"","","主角",1,},
        [3697] = {7004,5,2,"主角",1,0,"珍重！","珍重！",1,"","","华佗",402,},
        [3698] = {7101,1,1,"刘表",423,0,"降将张武、陈孙在江夏掳掠人民，共谋造反。","降将张武、陈孙在江夏掳掠人民，共谋造反。",1,"","","刘备",202,},
        [3699] = {7101,2,1,"刘表",423,0,"此二贼为祸不小！","此二贼为祸不小！",1,"","","刘备",202,},
        [3700] = {7101,3,2,"刘备",202,0,"吾自到荆州，汝待之甚厚。","吾自到荆州，汝待之甚厚。",1,"","","刘表",423,},
        [3701] = {7101,4,2,"刘备",202,0,"此二贼不须兄长忧虑，备请往讨之。","此二贼不须兄长忧虑，备请往讨之。",1,"","","刘表",423,},
        [3702] = {7101,5,1,"刘表",423,0,"好！汝速领兵三万前去。","好！汝速领兵三万前去。",1,"","","刘备",202,},
        [3703] = {7102,1,1,"刘备",202,0,"阵前江夏贼匪之中，张武尤为显眼。","阵前江夏贼匪之中，张武尤为显眼。",1,"","","",0,},
        [3704] = {7102,2,1,"刘备",202,0,"其所骑之马，极其雄骏，必千里马也。","其所骑之马，极其雄骏，必千里马也。",1,"","","",0,},
        [3705] = {7102,3,2,"主角",1,0,"马再好也要看骑马的人，我去看看他张武有何能耐。","马再好也要看骑马的人，我去看看他张武有何能耐。",1,"","","刘备",202,},
