        [3707] = {7102,5,2,"主角",1,0,"好！","好！",1,"","","张飞",206,},
        [3708] = {7103,1,1,"蔡瑁",443,0,"刘备遣三将居外，而自居荆州，久必为患。","刘备遣三将居外，而自居荆州，久必为患。",1,"","","蔡夫人",446,},
        [3709] = {7103,2,1,"蔡瑁",443,0,"你速去告知刘表。","你速去告知刘表。",1,"","","蔡夫人",446,},
        [3710] = {7103,3,2,"蔡夫人",446,0,"好。","好。",1,"","","蔡瑁",443,},
        [3711] = {7104,1,1,"蔡夫人",446,0,"我闻荆州人多与刘备往来，不可不防之。","我闻荆州人多与刘备往来，不可不防之。",1,"","","刘表",423,},
        [3712] = {7104,2,1,"蔡夫人",446,0,"今容其居住城中，无益，不若遣使他往。","今容其居住城中，无益，不若遣使他往。",1,"","","刘表",423,},
        [3713] = {7104,3,2,"刘表",423,0,"玄德仁人也。","玄德仁人也。",1,"","","蔡夫人",446,},
        [3714] = {7104,4,1,"蔡夫人",446,0,"只恐他人不似汝心。","只恐他人不似汝心。",1,"","","刘表",423,},
        [3715] = {7105,1,2,"蒯越",133,0,"汝所骑之马，从何所得？","汝所骑之马，从何所得？",1,"","","刘表",423,},
        [3716] = {7105,2,1,"刘表",423,0,"此玄德所送也。","此玄德所送也。",1,"","","蒯越",133,},
        [3717] = {7105,3,2,"蒯越",133,0,"昔先兄蒯良，最善相马，越亦颇晓。此马眼下有泪槽，额边生白点，名为的卢，骑则妨主。","昔先兄蒯良，最善相马，越亦颇晓。此马眼下有泪槽，额边生白点，名为的卢，骑则妨主。",1,"","","刘表",423,},
        [3718] = {7105,4,2,"蒯越",133,0,"张武为此马而亡，主公不可乘之。","张武为此马而亡，主公不可乘之。",1,"","","刘表",423,},
        [3719] = {7106,1,1,"刘表",423,0,"昨承惠良马，深感厚意，但贤弟不时征进，可以用之，敬当送还。","昨承惠良马，深感厚意，但贤弟不时征进，可以用之，敬当送还。",1,"","","刘备",202,},
