        [3721] = {7106,3,1,"刘表",423,0,"贤弟久居此间，恐废武事。","贤弟久居此间，恐废武事。",1,"","","刘备",202,},
        [3722] = {7106,4,1,"刘表",423,0,"襄阳属邑新野县，颇有钱粮，弟可引本部军马于本县屯扎，何如？","襄阳属邑新野县，颇有钱粮，弟可引本部军马于本县屯扎，何如？",1,"","","刘备",202,},
        [3723] = {7106,5,2,"刘备",202,0,"好，弟这就前往。","好，弟这就前往。",1,"","","刘表",423,},
        [3724] = {7107,1,2,"伊籍",121,0,"公所骑马，不可乘也。","公所骑马，不可乘也。",1,"","","刘备",202,},
        [3725] = {7107,2,2,"伊籍",121,0,"昨闻蒯异度对刘荆州云：此马名的卢，乘则妨主。因此还公，公岂可复乘之？","昨闻蒯异度对刘荆州云：此马名的卢，乘则妨主。因此还公，公岂可复乘之？",1,"","","刘备",202,},
        [3726] = {7107,3,1,"刘备",202,0,"深感先生见爱。但凡人死生有命，岂马所能妨哉！","深感先生见爱。但凡人死生有命，岂马所能妨哉！",1,"","","伊籍",121,},
        [3727] = {7107,4,2,"伊籍",121,0,"汝高见，十分佩服。","汝高见，十分佩服。",1,"","","刘备",202,},
        [3728] = {7108,1,1,"刘备",202,0,"今曹操悉兵北征，许昌空虚，若以荆襄之众，乘间袭之，大事可就也。","今曹操悉兵北征，许昌空虚，若以荆襄之众，乘间袭之，大事可就也。",1,"","","刘表",423,},
        [3729] = {7108,2,2,"刘表",423,0,"吾坐据九郡足矣，岂可别图？","吾坐据九郡足矣，岂可别图？",1,"","","刘备",202,},
        [3730] = {7108,3,2,"刘表",423,0,"吾闻贤弟在许昌，与曹操青梅煮酒，共论英雄。","吾闻贤弟在许昌，与曹操青梅煮酒，共论英雄。",1,"","","刘备",202,},
        [3731] = {7108,4,2,"刘表",423,0,"贤弟尽举当世名士，操皆不许，而独曰天下英雄，惟使君与操耳！","贤弟尽举当世名士，操皆不许，而独曰天下英雄，惟使君与操耳！",1,"","","刘备",202,},
        [3732] = {7108,5,2,"刘表",423,0,"以曹操之权力，犹不敢居吾弟之先，何虑功业不建乎？","以曹操之权力，犹不敢居吾弟之先，何虑功业不建乎？",1,"","","刘备",202,},
        [3733] = {7108,6,1,"刘备",202,0,"备若有基本，天下碌碌之辈，诚不足虑也。","备若有基本，天下碌碌之辈，诚不足虑也。",1,"","","刘表",423,},
        [3734] = {7109,1,2,"蔡夫人",446,0,"昨夜汝与备交谈。","昨夜汝与备交谈。",1,"","","刘表",423,},
        [3735] = {7109,2,2,"蔡夫人",446,0,"我于屏后听得刘备之言，甚轻觑人，足见其有吞并荆州之意，今若不除，必为后患。","我于屏后听得刘备之言，甚轻觑人，足见其有吞并荆州之意，今若不除，必为后患。",1,"","","刘表",423,},
        [3736] = {7109,3,1,"刘表",423,0,"玄德仁人也，万不可杀之。","玄德仁人也，万不可杀之。",1,"","","蔡夫人",446,},
        [3737] = {7110,1,1,"蔡夫人",446,0,"吾多次劝表杀刘备，但其始终不肯。","吾多次劝表杀刘备，但其始终不肯。",1,"","","蔡瑁",443,},
