        [3740] = {7111,1,2,"伊籍",121,0,"蔡瑁欲害于你，你速速逃离。","蔡瑁欲害于你，你速速逃离。",1,"","","刘备",202,},
        [3741] = {7111,2,1,"刘备",202,0,"未辞景升，如何便去？","未辞景升，如何便去？",1,"","","伊籍",121,},
        [3742] = {7111,3,2,"伊籍",121,0,"公若辞，必遭蔡瑁之害矣。","公若辞，必遭蔡瑁之害矣。",1,"","","刘备",202,},
        [3743] = {7111,4,1,"刘备",202,0,"备谢搭救，这便离去。","备谢搭救，这便离去。",1,"","","伊籍",121,},
        [3744] = {7112,1,2,"蔡瑁",443,0,"刘备有反叛之意，题反诗于壁上，不辞而去矣。","刘备有反叛之意，题反诗于壁上，不辞而去矣。",1,"","","刘表",423,},
        [3745] = {7112,2,1,"刘表",423,0,"诗为何内容？","诗为何内容？",1,"","","蔡瑁",443,},
        [3746] = {7112,3,2,"蔡瑁",443,0,"数年徒守困，空对旧山川。龙岂池中物，乘雷欲上天！","数年徒守困，空对旧山川。龙岂池中物，乘雷欲上天！",1,"","","刘表",423,},
        [3747] = {7112,4,1,"刘表",423,0,"吾与玄德相处许多时，不曾见他作诗，此必外人离间之计也。","吾与玄德相处许多时，不曾见他作诗，此必外人离间之计也。",1,"","","蔡瑁",443,},
        [3748] = {7113,1,2,"蔡瑁",443,0,"近年丰熟，合聚众官于襄阳，以示抚劝之意，请主公一行。","近年丰熟，合聚众官于襄阳，以示抚劝之意，请主公一行。",1,"","","刘表",423,},
        [3749] = {7113,2,1,"刘表",423,0,"吾近日气疾作，实不能行，可令二子为主待客。","吾近日气疾作，实不能行，可令二子为主待客。",1,"","","蔡瑁",443,},
        [3750] = {7113,3,2,"蔡瑁",443,0,"公子年幼，恐失于礼节。","公子年幼，恐失于礼节。",1,"","","刘表",423,},
        [3751] = {7113,4,1,"刘表",423,0,"可往新野请玄德待客。","可往新野请玄德待客。",1,"","","蔡瑁",443,},
        [3752] = {7113,5,2,"蔡瑁",443,0,"好。（心喜刘表正中其计，便差人请玄德赴襄阳。）","好。（心喜刘表正中其计，便差人请玄德赴襄阳。）",1,"","","刘表",423,},
        [3753] = {7114,1,2,"伊籍",121,0,"蔡瑁假意请君至襄阳，实则想设计害君。","蔡瑁假意请君至襄阳，实则想设计害君。",1,"","","刘备",202,},
        [3754] = {7114,2,2,"伊籍",121,0,"城外东、南、北三处，皆有军马守把，惟西门可走，公宜速逃！","城外东、南、北三处，皆有军马守把，惟西门可走，公宜速逃！",1,"","","刘备",202,},
        [3755] = {7114,3,1,"刘备",202,0,"我这就骑的卢马走西门。","我这就骑的卢马走西门。",1,"","","伊籍",121,},
        [3756] = {7115,1,2,"蔡瑁",443,0,"使君何故逃席而去？","使君何故逃席而去？",1,"","","刘备",202,},
        [3757] = {7115,2,1,"刘备",202,0,"吾与汝无仇，何故欲相害？","吾与汝无仇，何故欲相害？",1,"","","蔡瑁",443,},
        [3758] = {7115,3,1,"刘备",202,0,"的卢！驾！（的卢一跃三丈过檀溪，飞上西岸，故得跃马檀溪典故。）","的卢！驾！（的卢一跃三丈过檀溪，飞上西岸，故得跃马檀溪典故。）",1,"","","蔡瑁",443,},
        [3759] = {7201,1,2,"牧童",140,0,"将军莫非破黄巾刘玄德否？","将军莫非破黄巾刘玄德否？",1,"","","刘备",202,},
        [3760] = {7201,2,1,"刘备",202,0,"汝乃村僻小童，何以知吾姓字！","汝乃村僻小童，何以知吾姓字！",1,"","","牧童",140,},
        [3761] = {7201,3,2,"牧童",140,0,"我本不知，因常侍师父，有客到日，多曾说有一刘玄德，身长七尺五寸，垂手过膝，目能自顾其耳，乃当世之英雌。","我本不知，因常侍师父，有客到日，多曾说有一刘玄德，身长七尺五寸，垂手过膝，目能自顾其耳，乃当世之英雌。",1,"","","刘备",202,},
        [3762] = {7201,4,2,"牧童",140,0,"今观将军如此模样，想必是也。","今观将军如此模样，想必是也。",1,"","","刘备",202,},
        [3763] = {7201,5,1,"刘备",202,0,"汝师何人也？","汝师何人也？",1,"","","牧童",140,},
        [3764] = {7201,6,2,"牧童",140,0,"吾师覆姓司马，名徽，字德操，颍川人也，道号水镜先生。","吾师覆姓司马，名徽，字德操，颍川人也，道号水镜先生。",1,"","","刘备",202,},
        [3765] = {7201,7,1,"刘备",202,0,"汝师今居何处？","汝师今居何处？",1,"","","牧童",140,},
        [3766] = {7201,8,2,"牧童",140,0,"前面林中，便是庄院。","前面林中，便是庄院。",1,"","","刘备",202,},
        [3767] = {7201,9,1,"刘备",202,0,"吾正是刘玄德，汝可引我去拜见你师父。","吾正是刘玄德，汝可引我去拜见你师父。",1,"","","牧童",140,},
        [3768] = {7202,1,2,"司马徽",226,0,"明公何来？","明公何来？",1,"","","刘备",202,},
        [3769] = {7202,2,1,"刘备",202,0,"偶尔经由此地，因小童相指，得拜尊颜，不胜万幸！","偶尔经由此地，因小童相指，得拜尊颜，不胜万幸！",1,"","","司马徽",226,},
        [3770] = {7202,3,2,"司马徽",226,0,"公不必隐讳，吾观公气色，已知公今必逃难至此。","公不必隐讳，吾观公气色，已知公今必逃难至此。",1,"","","刘备",202,},
        [3771] = {7202,4,1,"刘备",202,0,"玄德惭愧。","玄德惭愧。",1,"","","司马徽",226,},
        [3772] = {7202,5,2,"司马徽",226,0,"吾久闻明公大名，何故至今犹落魄不偶耶？","吾久闻明公大名，何故至今犹落魄不偶耶？",1,"","","刘备",202,},
        [3773] = {7202,6,1,"刘备",202,0,"命途多蹇，所以至此。","命途多蹇，所以至此。",1,"","","司马徽",226,},
        [3774] = {7202,7,2,"司马徽",226,0,"不然，盖因将军左右不得其人耳。","不然，盖因将军左右不得其人耳。",1,"","","刘备",202,},
        [3775] = {7202,8,1,"刘备",202,0,"备虽不才，文有孙乾、糜竺、简雍之辈，武有关、张、赵云之流，竭忠辅相，颇赖其力。","备虽不才，文有孙乾、糜竺、简雍之辈，武有关、张、赵云之流，竭忠辅相，颇赖其力。",1,"","","司马徽",226,},
        [3776] = {7202,9,2,"司马徽",226,0,"关、张、赵云，皆万人敌，惜无善用之之人，若孙乾、糜竺辈，乃白面书生，非经纶济世之才也。","关、张、赵云，皆万人敌，惜无善用之之人，若孙乾、糜竺辈，乃白面书生，非经纶济世之才也。",1,"","","刘备",202,},
        [3777] = {7202,10,1,"刘备",202,0,"备亦尝侧身以求山谷之遗贤，奈未遇其人何！","备亦尝侧身以求山谷之遗贤，奈未遇其人何！",1,"","","司马徽",226,},
        [3778] = {7202,11,2,"司马徽",226,0,"岂不闻孔子云十室之邑必有忠信，何谓无人？","岂不闻孔子云十室之邑必有忠信，何谓无人？",1,"","","刘备",202,},
        [3779] = {7202,12,1,"刘备",202,0,"备愚昧不识，愿赐指教。","备愚昧不识，愿赐指教。",1,"","","司马徽",226,},
        [3780] = {7202,13,2,"司马徽",226,0,"公闻荆襄诸郡小儿谣言乎？其谣曰：八九年间始欲衰，至十三年无孑遗。","公闻荆襄诸郡小儿谣言乎？其谣曰：八九年间始欲衰，至十三年无孑遗。",1,"","","刘备",202,},
        [3781] = {7202,14,2,"司马徽",226,0,"到头天命有所归，泥中蟠龙向天飞。此谣始于建安初：建安八年，刘景升丧却前妻，便生家乱，此所谓始欲衰也。","到头天命有所归，泥中蟠龙向天飞。此谣始于建安初：建安八年，刘景升丧却前妻，便生家乱，此所谓始欲衰也。",1,"","","刘备",202,},
        [3782] = {7202,15,2,"司马徽",226,0,"无孑遗者，不久则景升将逝，文武零落无孑遗矣，天命有归，龙向天飞，盖应在将军也。","无孑遗者，不久则景升将逝，文武零落无孑遗矣，天命有归，龙向天飞，盖应在将军也。",1,"","","刘备",202,},
        [3783] = {7202,16,1,"刘备",202,0,"备安敢当此！","备安敢当此！",1,"","","司马徽",226,},
        [3784] = {7202,17,2,"司马徽",226,0,"今天下之奇才，尽在于此，公当往求之。","今天下之奇才，尽在于此，公当往求之。",1,"","","刘备",202,},
        [3785] = {7202,18,1,"刘备",202,0,"奇才安在？果系何人？","奇才安在？果系何人？",1,"","","司马徽",226,},
        [3786] = {7202,19,2,"司马徽",226,0,"天色已晚，将军可于此暂宿一宵，明日当言之。","天色已晚，将军可于此暂宿一宵，明日当言之。",1,"","","刘备",202,},
        [3787] = {7203,1,1,"司马徽",226,0,"元直为何深夜而来？","元直为何深夜而来？",1,"","","徐庶",212,},
        [3788] = {7203,2,2,"徐庶",212,0,"久闻刘景升善善恶恶，特往谒之。及至相见，徒有虚名，盖善善而不能用，恶恶而不能去者也。故遗书别之，而来至此。","久闻刘景升善善恶恶，特往谒之。及至相见，徒有虚名，盖善善而不能用，恶恶而不能去者也。故遗书别之，而来至此。",1,"","","司马徽",226,},
        [3789] = {7203,3,1,"司马徽",226,0,"公怀王佐之才，宜择人而事，奈何轻身往见景升乎？且英雄豪杰，只在眼前，公自不识耳。","公怀王佐之才，宜择人而事，奈何轻身往见景升乎？且英雄豪杰，只在眼前，公自不识耳。",1,"","","徐庶",212,},
        [3790] = {7203,4,2,"徐庶",212,0,"先生之言是也。","先生之言是也。",1,"","","司马徽",226,},
        [3791] = {7204,1,1,"刘备",202,0,"昨夜来者是谁？","昨夜来者是谁？",1,"","","司马徽",226,},
        [3792] = {7204,2,2,"司马徽",226,0,"此吾友也。","此吾友也。",1,"","","刘备",202,},
        [3793] = {7204,3,1,"刘备",202,0,"可否与之一见？","可否与之一见？",1,"","","司马徽",226,},
        [3794] = {7204,4,2,"司马徽",226,0,"此人欲往投明主，已到他处去了。","此人欲往投明主，已到他处去了。",1,"","","刘备",202,},
        [3795] = {7204,5,1,"刘备",202,0,"汝友何名？","汝友何名？",1,"","","司马徽",226,},
        [3796] = {7204,6,2,"司马徽",226,0,"好！好！（笑而不答。）","好！好！（笑而不答。）",1,"","","刘备",202,},
        [3797] = {7204,7,1,"刘备",202,0,"伏龙、凤雏，果系何人？","伏龙、凤雏，果系何人？",1,"","","司马徽",226,},
        [3798] = {7204,8,2,"司马徽",226,0,"好！好！（笑而不答。）","好！好！（笑而不答。）",1,"","","刘备",202,},
        [3799] = {7204,9,1,"刘备",202,0,"那水镜先生可愿出山相助，同扶汉室。","那水镜先生可愿出山相助，同扶汉室。",1,"","","司马徽",226,},
        [3800] = {7204,10,2,"司马徽",226,0,"山野闲散之人，不堪世用。","山野闲散之人，不堪世用。",1,"","","刘备",202,},
        [3801] = {7204,11,2,"司马徽",226,0,"自有胜吾十倍者来助公，公宜访之。","自有胜吾十倍者来助公，公宜访之。",1,"","","刘备",202,},
        [3802] = {7205,1,2,"牧童",140,0,"有一将军，引数百人到庄来也。","有一将军，引数百人到庄来也。",1,"","","刘备",202,},
        [3803] = {7205,2,1,"刘备",202,0,"是何人前来？","是何人前来？",1,"","","牧童",140,},
        [3804] = {7205,3,2,"赵云",201,0,"是我子龙。","是我子龙。",1,"","","刘备",202,},
        [3805] = {7205,4,2,"赵云",201,0,"某夜回县，寻不见主公，连夜跟问到此。","某夜回县，寻不见主公，连夜跟问到此。",1,"","","刘备",202,},
        [3806] = {7205,5,2,"赵云",201,0,"主公可作速回县。只恐有人来县中厮杀。","主公可作速回县。只恐有人来县中厮杀。",1,"","","刘备",202,},
        [3807] = {7205,6,1,"刘备",202,0,"好，我们这就回新野。","好，我们这就回新野。",1,"","","赵云",201,},
        [3808] = {7206,1,2,"徐庶",212,0,"天地反覆兮，火欲殂，大厦将崩兮，一木难扶。山谷有贤兮，欲投明主，明主求贤兮，却不知吾。","天地反覆兮，火欲殂，大厦将崩兮，一木难扶。山谷有贤兮，欲投明主，明主求贤兮，却不知吾。",1,"","","刘备",202,},
        [3809] = {7206,2,1,"刘备",202,0,"此人莫非水镜所言伏龙、凤雏乎？","此人莫非水镜所言伏龙、凤雏乎？",1,"","","徐庶",212,},
        [3810] = {7206,3,2,"徐庶",212,0,"某乃颍上人也，姓单，名福。久闻使君纳士招贤，欲来投托，未敢辄造，故行歌于市，以动尊听耳。","某乃颍上人也，姓单，名福。久闻使君纳士招贤，欲来投托，未敢辄造，故行歌于市，以动尊听耳。",1,"","","刘备",202,},
        [3811] = {7206,4,2,"徐庶",212,0,"适使君所乘之马，再乞一观。","适使君所乘之马，再乞一观。",1,"","","刘备",202,},
        [3812] = {7206,5,2,"徐庶",212,0,"此非的卢马乎？虽是千里马，却只妨主，不可乘也。","此非的卢马乎？虽是千里马，却只妨主，不可乘也。",1,"","","刘备",202,},
        [3813] = {7206,6,1,"刘备",202,0,"此马助我跃檀溪，救吾一命。","此马助我跃檀溪，救吾一命。",1,"","","徐庶",212,},
        [3814] = {7206,7,2,"徐庶",212,0,"此乃救主，非妨主也，终必妨一主，某有一法可禳。","此乃救主，非妨主也，终必妨一主，某有一法可禳。",1,"","","刘备",202,},
        [3815] = {7206,8,1,"刘备",202,0,"愿闻禳法。","愿闻禳法。",1,"","","徐庶",212,},
        [3816] = {7206,9,2,"徐庶",212,0,"公意中有仇怨之人，可将此马赐之，待妨过了此人，然后乘之，自然无事。","公意中有仇怨之人，可将此马赐之，待妨过了此人，然后乘之，自然无事。",1,"","","刘备",202,},
        [3817] = {7206,10,1,"刘备",202,0,"公初至此，不教吾以正道，便教作利己妨人之事，备不敢闻教。","公初至此，不教吾以正道，便教作利己妨人之事，备不敢闻教。",1,"","","徐庶",212,},
        [3818] = {7206,11,2,"徐庶",212,0,"向闻使君仁德，未敢便信，故以此言相试耳。","向闻使君仁德，未敢便信，故以此言相试耳。",1,"","","刘备",202,},
        [3819] = {7206,12,1,"刘备",202,0,"备安能有仁德及人，惟先生教之。","备安能有仁德及人，惟先生教之。",1,"","","徐庶",212,},
        [3820] = {7206,13,2,"徐庶",212,0,"吾自颍上来此，闻新野之人歌曰，新野牧，刘皇叔，自到此，民丰足。可见使君之仁德及人也。","吾自颍上来此，闻新野之人歌曰，新野牧，刘皇叔，自到此，民丰足。可见使君之仁德及人也。",1,"","","刘备",202,},
        [3821] = {7206,14,1,"刘备",202,0,"玄德幸得先生相助，今日起拜汝为军师，调练本部人马。","玄德幸得先生相助，今日起拜汝为军师，调练本部人马。",1,"","","徐庶",212,},
        [3822] = {7301,1,2,"徐庶",212,0,"曹仁屯兵樊城，必起大军来战。","曹仁屯兵樊城，必起大军来战。",1,"","","刘备",202,},
        [3823] = {7301,2,1,"刘备",202,0,"当何以迎之？","当何以迎之？",1,"","","徐庶",212,},
        [3824] = {7301,3,2,"徐庶",212,0,"彼若尽提兵而来，樊城空虚，可乘间夺之。","彼若尽提兵而来，樊城空虚，可乘间夺之。",1,"","","刘备",202,},
        [3825] = {7301,4,1,"刘备",202,0,"何计应对？","何计应对？",1,"","","徐庶",212,},
        [3826] = {7301,5,2,"徐庶",212,0,"烦请玄德兄附耳。","烦请玄德兄附耳。",1,"","","刘备",202,},
        [3827] = {7302,1,1,"刘备",202,0,"曹军鸣鼓进军，布成一阵。","曹军鸣鼓进军，布成一阵。",1,"","","徐庶",212,},
        [3828] = {7302,2,1,"刘备",202,0,"识吾阵势？","识吾阵势？",1,"","","徐庶",212,},
        [3829] = {7302,3,2,"徐庶",212,0,"此八门金锁阵也。八门者：休、生、伤、杜、景、死、惊、开。","此八门金锁阵也。八门者：休、生、伤、杜、景、死、惊、开。",1,"","","刘备",202,},
        [3830] = {7302,4,2,"徐庶",212,0,"如从生门、景门、开门而入则吉；从伤门、惊门、休门而入则伤；从杜门、死们而人则亡。","如从生门、景门、开门而入则吉；从伤门、惊门、休门而入则伤；从杜门、死们而人则亡。",1,"","","刘备",202,},
        [3831] = {7302,5,2,"徐庶",212,0,"今八门虽布得整齐，只是中间通欠主持。如从东南角上生门击人，往正西景门而出，其阵必乱。","今八门虽布得整齐，只是中间通欠主持。如从东南角上生门击人，往正西景门而出，其阵必乱。",1,"","","刘备",202,},
        [3832] = {7302,6,1,"刘备",202,0,"传令，军士把住阵角，扬威将军引五百军从东南而入，径往西出。","传令，军士把住阵角，扬威将军引五百军从东南而入，径往西出。",1,"","","徐庶",212,},
        [3833] = {7302,7,2,"主角",1,0,"好，我这就领兵破了那八门金锁阵。","好，我这就领兵破了那八门金锁阵。",1,"","","刘备",202,},
        [3834] = {7303,1,1,"曹仁",106,0,"刘备军中必有能者，吾阵竟为所破。","刘备军中必有能者，吾阵竟为所破。",1,"","","李典",124,},
        [3835] = {7303,2,2,"李典",124,0,"吾虽在此，甚忧樊城。","吾虽在此，甚忧樊城。",1,"","","曹仁",106,},
        [3836] = {7303,3,1,"曹仁",106,0,"今晚去劫寨！如得胜，再作计议，如不胜，便退军回樊城。","今晚去劫寨！如得胜，再作计议，如不胜，便退军回樊城。",1,"","","李典",124,},
        [3837] = {7303,4,2,"李典",124,0,"不可，刘备必有准备。","不可，刘备必有准备。",1,"","","曹仁",106,},
        [3838] = {7303,5,1,"曹仁",106,0,"若如此多疑，何以用兵！","若如此多疑，何以用兵！",1,"","","李典",124,},
        [3839] = {7304,1,2,"徐庶",212,0,"今夜曹仁必来劫寨。","今夜曹仁必来劫寨。",1,"","","刘备",202,},
        [3840] = {7304,2,1,"刘备",202,0,"何以敌之？","何以敌之？",1,"","","徐庶",212,},
        [3841] = {7304,3,2,"徐庶",212,0,"吾已预算定。","吾已预算定。",1,"","","刘备",202,},
        [3842] = {7304,4,2,"徐庶",212,0,"云长疾行樊城外静候，待曹仁出城后迅速夺城。","云长疾行樊城外静候，待曹仁出城后迅速夺城。",1,"","","刘备",202,},
        [3843] = {7304,5,2,"徐庶",212,0,"尔等留空寨，赵云与扬威将军埋伏寨外，奇袭前来的曹仁大军，将其逼至河边。","尔等留空寨，赵云与扬威将军埋伏寨外，奇袭前来的曹仁大军，将其逼至河边。",1,"","","刘备",202,},
        [3844] = {7304,6,2,"徐庶",212,0,"张飞埋伏于河边作响应，合力将其逼回樊城，而云长再于樊城出击成最终合围之势。","张飞埋伏于河边作响应，合力将其逼回樊城，而云长再于樊城出击成最终合围之势。",1,"","","刘备",202,},
        [3845] = {7304,7,1,"刘备",202,0,"妙哉妙哉！","妙哉妙哉！",1,"","","徐庶",212,},
        [3846] = {7305,1,1,"曹仁",106,0,"不好，刘备早有准备，攻其不下又失樊城。","不好，刘备早有准备，攻其不下又失樊城。",1,"","","",0,},
        [3847] = {7305,2,1,"曹仁",106,0,"速撤回许都。","速撤回许都。",1,"","","",0,},
        [3848] = {7306,1,2,"曹仁",106,0,"尔等损将折兵之事，望丞相治罪。","尔等损将折兵之事，望丞相治罪。",1,"","","曹操",103,},
        [3849] = {7306,2,1,"曹操",103,0,"胜负乃军家之常。但不知谁为刘备画策？","胜负乃军家之常。但不知谁为刘备画策？",1,"","","曹仁",106,},
        [3850] = {7306,3,2,"曹仁",106,0,"单福之计。","单福之计。",1,"","","曹操",103,},
        [3851] = {7306,4,1,"曹操",103,0,"单福何人也？","单福何人也？",1,"","","曹仁",106,},
        [3852] = {7306,5,2,"程昱",121,0,"此非单福也。此人幼好学击剑，中平末年，尝为人报仇杀人，披发涂面而走，为吏所获。","此非单福也。此人幼好学击剑，中平末年，尝为人报仇杀人，披发涂面而走，为吏所获。",1,"","","曹操",103,},
        [3853] = {7306,6,2,"程昱",121,0,"问其姓名不答，吏乃缚于车上，击鼓行于市，今市人识之，虽有识者不敢言，而同伴窃解救之。","问其姓名不答，吏乃缚于车上，击鼓行于市，今市人识之，虽有识者不敢言，而同伴窃解救之。",1,"","","曹操",103,},
        [3854] = {7306,7,2,"程昱",121,0,"乃更姓名而逃，折节向学，遍访名师，尝与司马徽谈论。此人乃颍川徐庶，字元直，单福乃其托名耳。","乃更姓名而逃，折节向学，遍访名师，尝与司马徽谈论。此人乃颍川徐庶，字元直，单福乃其托名耳。",1,"","","曹操",103,},
        [3855] = {7306,8,1,"曹操",103,0,"徐庶之才，比君何如？","徐庶之才，比君何如？",1,"","","程昱",121,},
        [3856] = {7306,9,2,"程昱",121,0,"十倍于昱。","十倍于昱。",1,"","","曹操",103,},
        [3857] = {7306,10,1,"曹操",103,0,"惜乎贤士归于刘备！羽翼成矣？奈何？","惜乎贤士归于刘备！羽翼成矣？奈何？",1,"","","程昱",121,},
        [3858] = {7306,11,2,"程昱",121,0,"徐庶虽在彼，丞相要用，召来不难。","徐庶虽在彼，丞相要用，召来不难。",1,"","","曹操",103,},
        [3859] = {7306,12,1,"曹操",103,0,"安得彼来归？","安得彼来归？",1,"","","程昱",121,},
        [3860] = {7306,13,2,"程昱",121,0,"徐庶为人至孝。幼丧其父，止有老母在堂。现今其弟徐康已亡，老母无人侍养。","徐庶为人至孝。幼丧其父，止有老母在堂。现今其弟徐康已亡，老母无人侍养。",1,"","","曹操",103,},
        [3861] = {7306,14,2,"程昱",121,0,"丞相可使人赚其母至许昌，令作书召其子，则徐庶必至矣。","丞相可使人赚其母至许昌，令作书召其子，则徐庶必至矣。",1,"","","曹操",103,},
        [3862] = {7307,1,1,"徐庶",212,0,"某本颍川徐庶，字元直，为因逃难，更名单福。前闻刘景升招贤纳士，特往见之，及与论事，方知是无用之人，故作书别之。","某本颍川徐庶，字元直，为因逃难，更名单福。前闻刘景升招贤纳士，特往见之，及与论事，方知是无用之人，故作书别之。",1,"","","刘备",202,},
        [3863] = {7307,2,1,"徐庶",212,0,"夤夜至司马水镜庄上，诉说其事。水镜深责庶不识主，因说刘豫州在此，何不事之？","夤夜至司马水镜庄上，诉说其事。水镜深责庶不识主，因说刘豫州在此，何不事之？",1,"","","刘备",202,},
        [3864] = {7307,3,1,"徐庶",212,0,"庶故作狂歌于市以动使君，幸蒙不弃，即赐重用。","庶故作狂歌于市以动使君，幸蒙不弃，即赐重用。",1,"","","刘备",202,},
        [3865] = {7307,4,1,"徐庶",212,0,"争奈老母今被曹操奸计赚至许昌囚禁，将欲加害。老母手书来唤，庶不容不去。","争奈老母今被曹操奸计赚至许昌囚禁，将欲加害。老母手书来唤，庶不容不去。",1,"","","刘备",202,},
        [3866] = {7307,5,1,"徐庶",212,0,"非不欲效犬马之劳，以报使君，奈慈亲被执，不得尽力。今当告归，容图后会。","非不欲效犬马之劳，以报使君，奈慈亲被执，不得尽力。今当告归，容图后会。",1,"","","刘备",202,},
        [3867] = {7307,6,2,"刘备",202,0,"子母乃天性之亲，元直无以备为念。待与老夫人相见之后，或者再得奉教。","子母乃天性之亲，元直无以备为念。待与老夫人相见之后，或者再得奉教。",1,"","","徐庶",212,},
        [3868] = {7308,1,1,"孙乾",140,0,"元直天下奇才，久在新野，尽知我军中虚实。今若使归曹操，必然重用，我其危矣。","元直天下奇才，久在新野，尽知我军中虚实。今若使归曹操，必然重用，我其危矣。",1,"","","刘备",202,},
        [3869] = {7308,2,1,"孙乾",140,0,"主公宜苦留之，切勿放去。操见元直不去，必斩其母。元直知母死，必为母报仇。力攻曹操也。","主公宜苦留之，切勿放去。操见元直不去，必斩其母。元直知母死，必为母报仇。力攻曹操也。",1,"","","刘备",202,},
        [3870] = {7308,3,2,"刘备",202,0,"不可。使人杀其母，而吾用其子，不仁也；留之不使去，以绝其子母之道，不义也。吾宁死，不为不仁不义之事。","不可。使人杀其母，而吾用其子，不仁也；留之不使去，以绝其子母之道，不义也。吾宁死，不为不仁不义之事。",1,"","","孙乾",140,},
        [3871] = {7309,1,1,"刘备",202,0,"备分浅缘薄，不能与先生相聚。望先生善事新主，以成功名。","备分浅缘薄，不能与先生相聚。望先生善事新主，以成功名。",1,"","","徐庶",212,},
        [3872] = {7309,2,2,"徐庶",212,0,"某才微智浅，深荷使君重用。今不幸半途而别，实为老母故也。纵使曹操相逼，庶亦终身不设一谋。","某才微智浅，深荷使君重用。今不幸半途而别，实为老母故也。纵使曹操相逼，庶亦终身不设一谋。",1,"","","刘备",202,},
        [3873] = {7309,3,1,"刘备",202,0,"先生既去，刘备亦将远遁山林矣。","先生既去，刘备亦将远遁山林矣。",1,"","","徐庶",212,},
        [3874] = {7309,4,2,"徐庶",212,0,"某所以与使君共图王霸之业者，恃此方寸耳，今以老母之故，方寸乱矣，纵使在此，无益于事。","某所以与使君共图王霸之业者，恃此方寸耳，今以老母之故，方寸乱矣，纵使在此，无益于事。",1,"","","刘备",202,},
        [3875] = {7309,5,2,"徐庶",212,0,"使君宜别求高贤辅佐，共图大业，何便灰心如此？","使君宜别求高贤辅佐，共图大业，何便灰心如此？",1,"","","刘备",202,},
        [3876] = {7309,6,1,"刘备",202,0,"天下高贤，无有出先生右者。","天下高贤，无有出先生右者。",1,"","","徐庶",212,},
        [3877] = {7309,7,2,"徐庶",212,0,"某樗栎庸材，何敢当此重誉。","某樗栎庸材，何敢当此重誉。",1,"","","刘备",202,},
        [3878] = {7309,8,1,"刘备",202,0,"先生此去，天各一方，未知相会却在何日！","先生此去，天各一方，未知相会却在何日！",1,"","","徐庶",212,},
        [3879] = {7309,9,2,"徐庶",212,0,"不劳使君远送，庶就此告别！","不劳使君远送，庶就此告别！",1,"","","刘备",202,},
        [3880] = {7309,10,1,"刘备",202,0,"珍重！","珍重！",1,"","","徐庶",212,},
        [3881] = {7310,1,1,"刘备",202,0,"元直为何复回？","元直为何复回？",1,"","","徐庶",212,},
        [3882] = {7310,2,2,"徐庶",212,0,"某因心绪如麻，忘却一事，此间有一奇士，只在襄阳城外二十里隆中。使君何不求之？","某因心绪如麻，忘却一事，此间有一奇士，只在襄阳城外二十里隆中。使君何不求之？",1,"","","刘备",202,},
        [3883] = {7310,3,1,"刘备",202,0,"敢烦元直为备请来相见。","敢烦元直为备请来相见。",1,"","","徐庶",212,},
        [3884] = {7310,4,2,"徐庶",212,0,"此人不可屈致，使君可亲往求之。若得此人，无异周得吕望、汉得张良也。","此人不可屈致，使君可亲往求之。若得此人，无异周得吕望、汉得张良也。",1,"","","刘备",202,},
        [3885] = {7310,5,1,"刘备",202,0,"此人比先生才德何如？","此人比先生才德何如？",1,"","","徐庶",212,},
        [3886] = {7310,6,2,"徐庶",212,0,"以某比之，譬犹驽马并麒麟、寒鸦配鸾凤耳。此人每尝自比管仲，乐毅。以吾观之，管、乐殆不及此人。","以某比之，譬犹驽马并麒麟、寒鸦配鸾凤耳。此人每尝自比管仲，乐毅。以吾观之，管、乐殆不及此人。",1,"","","刘备",202,},
        [3887] = {7310,7,2,"徐庶",212,0,"此人有经天纬地之才，盖天下一人也！","此人有经天纬地之才，盖天下一人也！",1,"","","刘备",202,},
        [3888] = {7310,8,1,"刘备",202,0,"愿闻此人姓名。","愿闻此人姓名。",1,"","","徐庶",212,},
        [3889] = {7310,9,2,"徐庶",212,0,"此人乃琅琊阳都人，覆姓诸葛，名亮，字孔明，乃汉司隶校尉诸葛丰之后。","此人乃琅琊阳都人，覆姓诸葛，名亮，字孔明，乃汉司隶校尉诸葛丰之后。",1,"","","刘备",202,},
        [3890] = {7310,10,2,"徐庶",212,0,"所居之地有一冈，名卧龙冈，因自号为卧龙先生。","所居之地有一冈，名卧龙冈，因自号为卧龙先生。",1,"","","刘备",202,},
        [3891] = {7310,11,2,"徐庶",212,0,"此人乃绝代奇才，使君急宜枉驾见之。若此人肯相辅佐，何愁天下不定乎！","此人乃绝代奇才，使君急宜枉驾见之。若此人肯相辅佐，何愁天下不定乎！",1,"","","刘备",202,},
        [3892] = {7310,12,1,"刘备",202,0,"昔水镜先生曾为备言：伏龙、凤雏，两人得一，可安天下。今所云莫非即伏龙、凤雏乎？","昔水镜先生曾为备言：伏龙、凤雏，两人得一，可安天下。今所云莫非即伏龙、凤雏乎？",1,"","","徐庶",212,},
        [3893] = {7310,13,2,"徐庶",212,0,"凤雏乃襄阳庞统也。伏龙正是诸葛孔明。","凤雏乃襄阳庞统也。伏龙正是诸葛孔明。",1,"","","刘备",202,},
        [3894] = {7310,14,1,"刘备",202,0,"今日方知伏龙、凤雏之语。何期大贤只在目前！非先生言，备有眼如盲也！","今日方知伏龙、凤雏之语。何期大贤只在目前！非先生言，备有眼如盲也！",1,"","","徐庶",212,},
        [3895] = {7311,1,1,"徐庶",212,0,"庶本欲事刘豫州，奈老母为曹操所囚，驰书来召，只得舍之而往。","庶本欲事刘豫州，奈老母为曹操所囚，驰书来召，只得舍之而往。",1,"","","诸葛亮",203,},
        [3896] = {7311,2,1,"徐庶",212,0,"临行时，将公荐与玄德。玄德即日将来奉谒，望公勿推阻，即展平生之大才以辅之，幸甚！","临行时，将公荐与玄德。玄德即日将来奉谒，望公勿推阻，即展平生之大才以辅之，幸甚！",1,"","","诸葛亮",203,},
        [3897] = {7311,3,2,"诸葛亮",203,0,"君以我为享祭之牺牲乎！","君以我为享祭之牺牲乎！",1,"","","徐庶",212,},
        [3898] = {7311,4,1,"徐庶",212,0,"庶惭愧。","庶惭愧。",1,"","","诸葛亮",203,},
        [3899] = {7312,1,1,"刘备",202,0,"汉室末胄、涿郡愚夫，久闻先生大名，如雷贯耳。","汉室末胄、涿郡愚夫，久闻先生大名，如雷贯耳。",1,"","","诸葛亮",203,},
        [3900] = {7312,2,1,"刘备",202,0,"昨两次晋谒，不得一见，已书贱名于文几，未审得入览否？","昨两次晋谒，不得一见，已书贱名于文几，未审得入览否？",1,"","","诸葛亮",203,},
        [3901] = {7312,3,2,"诸葛亮",203,0,"南阳野人，疏懒性成，屡蒙将军枉临，不胜愧赧。","南阳野人，疏懒性成，屡蒙将军枉临，不胜愧赧。",1,"","","刘备",202,},
        [3902] = {7312,4,2,"诸葛亮",203,0,"昨观书意，足见将军忧民忧国之心，但恨亮年幼才疏，有误下问。","昨观书意，足见将军忧民忧国之心，但恨亮年幼才疏，有误下问。",1,"","","刘备",202,},
        [3903] = {7312,5,1,"刘备",202,0,"司马德操之言，徐元直之语，岂虚谈哉？望先生不弃鄙贱，曲赐教诲。","司马德操之言，徐元直之语，岂虚谈哉？望先生不弃鄙贱，曲赐教诲。",1,"","","诸葛亮",203,},
        [3904] = {7312,6,2,"诸葛亮",203,0,"德操、元直，世之高士。亮乃一耕夫耳，安敢谈天下事？二公谬举矣。将军奈何舍美玉而求顽石乎？","德操、元直，世之高士。亮乃一耕夫耳，安敢谈天下事？二公谬举矣。将军奈何舍美玉而求顽石乎？",1,"","","刘备",202,},
        [3905] = {7312,7,1,"刘备",202,0,"大丈夫抱经世奇才，岂可空老于林泉之下？愿先生以天下苍生为念，开备愚鲁而赐教。","大丈夫抱经世奇才，岂可空老于林泉之下？愿先生以天下苍生为念，开备愚鲁而赐教。",1,"","","诸葛亮",203,},
        [3906] = {7312,8,2,"诸葛亮",203,0,"愿闻将军之志。","愿闻将军之志。",1,"","","刘备",202,},
        [3907] = {7312,9,1,"刘备",202,0,"汉室倾颓，奸臣窃命，备不量力，欲伸大义于天下，而智术浅短，迄无所就。惟先生开其愚而拯其厄，实为万幸！","汉室倾颓，奸臣窃命，备不量力，欲伸大义于天下，而智术浅短，迄无所就。惟先生开其愚而拯其厄，实为万幸！",1,"","","诸葛亮",203,},
        [3908] = {7312,10,2,"诸葛亮",203,0,"此西川五十四州之图也。将军欲成霸业，北让曹操占天时，南让孙权占地利，将军可占人和。","此西川五十四州之图也。将军欲成霸业，北让曹操占天时，南让孙权占地利，将军可占人和。",1,"","","刘备",202,},
        [3909] = {7312,11,2,"诸葛亮",203,0,"先取荆州为家，后即取西川建基业，以成鼎足之势，然后可图中原也。","先取荆州为家，后即取西川建基业，以成鼎足之势，然后可图中原也。",1,"","","刘备",202,},
        [3910] = {7312,12,1,"刘备",202,0,"先生之言，顿开茅塞，使备如拨云雾而睹青天。但荆州刘表、益州刘璋，皆汉室宗亲，备安忍夺之？","先生之言，顿开茅塞，使备如拨云雾而睹青天。但荆州刘表、益州刘璋，皆汉室宗亲，备安忍夺之？",1,"","","诸葛亮",203,},
        [3911] = {7312,13,2,"诸葛亮",203,0,"亮夜观天象，刘表不久人世，刘璋非立业之主，久后必归将军。","亮夜观天象，刘表不久人世，刘璋非立业之主，久后必归将军。",1,"","","刘备",202,},
        [3912] = {7312,14,1,"刘备",202,0,"备虽名微德薄，愿先生不弃鄙贱，出山相助。备当拱听明诲。","备虽名微德薄，愿先生不弃鄙贱，出山相助。备当拱听明诲。",1,"","","诸葛亮",203,},
        [3913] = {7312,15,2,"诸葛亮",203,0,"亮久乐耕锄，懒于应世，不能奉命。","亮久乐耕锄，懒于应世，不能奉命。",1,"","","刘备",202,},
        [3914] = {7312,16,1,"刘备",202,0,"先生不出，如苍生何！","先生不出，如苍生何！",1,"","","诸葛亮",203,},
        [3915] = {7312,17,2,"诸葛亮",203,0,"将军既不相弃，愿效犬马之劳。","将军既不相弃，愿效犬马之劳。",1,"","","刘备",202,},
        [3916] = {7401,1,2,"吕蒙",307,0,"有黄祖部将来降。","有黄祖部将来降。",1,"","","孙权",306,},
        [3917] = {7401,2,1,"孙权",306,0,"何人也？细细道来！","何人也？细细道来！",1,"","","吕蒙",307,},
        [3918] = {7401,3,2,"吕蒙",307,0,"甘宁，巴郡临江人也，颇通书史，有气力，好游侠。","甘宁，巴郡临江人也，颇通书史，有气力，好游侠。",1,"","","孙权",306,},
        [3919] = {7401,4,2,"吕蒙",307,0,"其腰悬铜铃，人听铃声，尽皆避之，又尝以西川锦作帆幔，时人皆称为锦帆贼。","其腰悬铜铃，人听铃声，尽皆避之，又尝以西川锦作帆幔，时人皆称为锦帆贼。",1,"","","孙权",306,},
        [3920] = {7401,5,2,"吕蒙",307,0,"此前后悔前非，改行从善，引众投刘表。","此前后悔前非，改行从善，引众投刘表。",1,"","","孙权",306,},
        [3921] = {7401,6,2,"吕蒙",307,0,"见表不能成事，即欲来投东吴，却被黄祖留住在夏口。前东吴破祖时，祖得甘宁之力，救回夏口，乃待宁甚薄。","见表不能成事，即欲来投东吴，却被黄祖留住在夏口。前东吴破祖时，祖得甘宁之力，救回夏口，乃待宁甚薄。",1,"","","孙权",306,},
        [3922] = {7401,7,2,"吕蒙",307,0,"都督苏飞屡荐宁于祖，祖视宁为劫江之贼，不重用，宁因此怀恨。","都督苏飞屡荐宁于祖，祖视宁为劫江之贼，不重用，宁因此怀恨。",1,"","","孙权",306,},
        [3923] = {7401,8,2,"吕蒙",307,0,"某具言主公求贤若渴，不记旧恨，况各为其主，又何恨焉？宁欣然引众渡江，来见主公，乞钧旨定夺。","某具言主公求贤若渴，不记旧恨，况各为其主，又何恨焉？宁欣然引众渡江，来见主公，乞钧旨定夺。",1,"","","孙权",306,},
        [3924] = {7401,9,1,"孙权",306,0,"扬威将军曾救吾兄孙策救江东于水火之中，今之事，汝有何看法？","扬威将军曾救吾兄孙策救江东于水火之中，今之事，汝有何看法？",1,"","","吕蒙",307,},
        [3925] = {7401,10,2,"主角",1,0,"以吾拙见，得兴霸，破黄祖必矣。","以吾拙见，得兴霸，破黄祖必矣。",1,"","","孙权",306,},
        [3926] = {7401,11,1,"孙权",306,0,"速引其相见。","速引其相见。",1,"","","主角",1,},
        [3927] = {7402,1,1,"孙权",306,0,"兴霸来此，大获我心，岂有记恨之理？请无怀疑。愿教我以破黄祖之策。","兴霸来此，大获我心，岂有记恨之理？请无怀疑。愿教我以破黄祖之策。",1,"","","甘宁",308,},
        [3928] = {7402,2,2,"甘宁",308,0,"今汉祚日危，曹操终必篡窃。南荆之地操所必争也。刘表无远虑，其子又愚劣，不能承业传基，明公宜早图之。","今汉祚日危，曹操终必篡窃。南荆之地操所必争也。刘表无远虑，其子又愚劣，不能承业传基，明公宜早图之。",1,"","","孙权",306,},
        [3929] = {7402,3,2,"甘宁",308,0,"若迟，则操先图之矣。今宜先取黄祖。祖今年老昏迈，务于货利，侵求吏民，人心皆怨，战具不修，军无法律。","若迟，则操先图之矣。今宜先取黄祖。祖今年老昏迈，务于货利，侵求吏民，人心皆怨，战具不修，军无法律。",1,"","","孙权",306,},
        [3930] = {7402,4,2,"甘宁",308,0,"明公若往攻之，其势必破。既破祖军，鼓行而西，据楚关而图巴、蜀，霸业可定也。","明公若往攻之，其势必破。既破祖军，鼓行而西，据楚关而图巴、蜀，霸业可定也。",1,"","","孙权",306,},
        [3931] = {7402,5,1,"孙权",306,0,"此金玉之论也！","此金玉之论也！",1,"","","甘宁",308,},
        [3932] = {7402,6,1,"孙权",306,0,"命周瑜为大都督，总水陆军兵，吕蒙与扬威将军为前部先锋，董袭与甘宁为副将。","命周瑜为大都督，总水陆军兵，吕蒙与扬威将军为前部先锋，董袭与甘宁为副将。",1,"","","甘宁",308,},
        [3933] = {7402,7,1,"孙权",306,0,"吾自领大军十万，征讨黄祖。","吾自领大军十万，征讨黄祖。",1,"","","甘宁",308,},
        [3934] = {7403,1,1,"黄祖",127,0,"细作探知，孙权领大军十万前来。","细作探知，孙权领大军十万前来。",1,"","","",0,},
        [3935] = {7403,2,1,"黄祖",127,0,"令苏飞为大将，陈就、邓龙为先锋，尽起江夏之兵迎敌。","令苏飞为大将，陈就、邓龙为先锋，尽起江夏之兵迎敌。",1,"","","",0,},
        [3936] = {7404,1,1,"主角",1,0,"苏飞、陈就、邓龙皆被吾斩，祖军大败。","苏飞、陈就、邓龙皆被吾斩，祖军大败。",1,"","","孙权",306,},
        [3937] = {7404,2,1,"主角",1,0,"黄祖兵败将亡，弃江夏，往荆州而走。","黄祖兵败将亡，弃江夏，往荆州而走。",1,"","","孙权",306,},
        [3938] = {7404,3,2,"孙权",306,0,"追！","追！",1,"","","主角",1,},
        [3939] = {7405,1,1,"黄祖",127,0,"我向日不曾轻待汝，今何相逼耶？","我向日不曾轻待汝，今何相逼耶？",1,"","","甘宁",308,},
        [3940] = {7405,2,2,"甘宁",308,0,"吾昔在江夏，多立功绩，汝乃以劫江贼待我，今日尚有何说！","吾昔在江夏，多立功绩，汝乃以劫江贼待我，今日尚有何说！",1,"","","黄祖",127,},
        [3941] = {7405,3,2,"主角",1,0,"穷寇莫辩，杀！","穷寇莫辩，杀！",1,"","","黄祖",127,},
        [3942] = {7406,1,1,"孙权",306,0,"黄祖已死，将其头颅带回江东祭献于亡父灵前。","黄祖已死，将其头颅带回江东祭献于亡父灵前。",1,"","","",0,},
        [3943] = {7406,2,1,"孙权",306,0,"重赏三军，升甘宁为都尉。","重赏三军，升甘宁为都尉。",1,"","","",0,},
        [3944] = {7406,3,2,"张昭",315,0,"孤城不可守，不如且回江东，刘表知我破黄祖，必来报仇。","孤城不可守，不如且回江东，刘表知我破黄祖，必来报仇。",1,"","","孙权",306,},
        [3945] = {7406,4,2,"张昭",315,0,"我以逸待劳，必败刘表，表败而后乘势攻之，荆襄可得也。","我以逸待劳，必败刘表，表败而后乘势攻之，荆襄可得也。",1,"","","孙权",306,},
        [3946] = {7406,5,1,"孙权",306,0,"好，弃江夏，班师回江东。","好，弃江夏，班师回江东。",1,"","","张昭",315,},
        [3947] = {7501,1,1,"刘备",202,0,"刘表差人来请吾赴荆州议事。","刘表差人来请吾赴荆州议事。",1,"","","诸葛亮",203,},
        [3948] = {7501,2,2,"诸葛亮",203,0,"此必因江东破了黄祖，故请主公商议报仇之策也。某当与主公同往，相机而行，自有良策。","此必因江东破了黄祖，故请主公商议报仇之策也。某当与主公同往，相机而行，自有良策。",1,"","","刘备",202,},
        [3949] = {7501,3,1,"刘备",202,0,"今见景升，当若何对答？","今见景升，当若何对答？",1,"","","诸葛亮",203,},
        [3950] = {7501,4,2,"诸葛亮",203,0,"当先谢襄阳之事。他若令主公去征讨江东，切不可应允，但说容归新野，整顿军马。","当先谢襄阳之事。他若令主公去征讨江东，切不可应允，但说容归新野，整顿军马。",1,"","","刘备",202,},
        [3951] = {7502,1,1,"刘表",423,0,"吾已悉知贤弟被害之事。当时即欲斩蔡瑁之首，以献贤弟，因众人告危，故姑恕之。贤弟幸勿见罪。","吾已悉知贤弟被害之事。当时即欲斩蔡瑁之首，以献贤弟，因众人告危，故姑恕之。贤弟幸勿见罪。",1,"","","刘备",202,},
        [3952] = {7502,2,2,"刘备",202,0,"非干蔡将军之事，想皆下人所为耳。","非干蔡将军之事，想皆下人所为耳。",1,"","","刘表",423,},
        [3953] = {7502,3,1,"刘表",423,0,"今江夏失守，黄祖遇害，故请贤弟共议报复之策。","今江夏失守，黄祖遇害，故请贤弟共议报复之策。",1,"","","刘备",202,},
        [3954] = {7502,4,2,"刘备",202,0,"黄祖性暴，不能用人，故致此祸。今若兴兵南征，倘曹操北来，又当奈何？","黄祖性暴，不能用人，故致此祸。今若兴兵南征，倘曹操北来，又当奈何？",1,"","","刘表",423,},
        [3955] = {7502,5,1,"刘表",423,0,"吾今年老多病，不能理事，贤弟可来助我。我死之后，弟便为荆州之主也。","吾今年老多病，不能理事，贤弟可来助我。我死之后，弟便为荆州之主也。",1,"","","刘备",202,},
        [3956] = {7502,6,2,"刘备",202,0,"兄何出此言！量备安敢当此重任。","兄何出此言！量备安敢当此重任。",1,"","","刘表",423,},
        [3957] = {7502,7,2,"刘备",202,0,"容徐思良策，先行告退。","容徐思良策，先行告退。",1,"","","刘表",423,},
        [3958] = {7503,1,1,"诸葛亮",203,0,"景升欲以荆州付主公，奈何却之？","景升欲以荆州付主公，奈何却之？",1,"","","刘备",202,},
        [3959] = {7503,2,2,"刘备",202,0,"景升待我，恩礼交至，安忍乘其危而夺之？","景升待我，恩礼交至，安忍乘其危而夺之？",1,"","","诸葛亮",203,},
        [3960] = {7503,3,1,"诸葛亮",203,0,"真仁慈之主也！","真仁慈之主也！",1,"","","刘备",202,},
        [3961] = {7504,1,2,"刘琦",128,0,"继母不能相容，性命只在旦夕，望叔父怜而救之。","继母不能相容，性命只在旦夕，望叔父怜而救之。",1,"","","刘备",202,},
        [3962] = {7504,2,1,"刘备",202,0,"此贤侄家事耳，奈何问我？","此贤侄家事耳，奈何问我？",1,"","","刘琦",128,},
        [3963] = {7504,3,2,"刘琦",128,0,"请叔父救之！","请叔父救之！",1,"","","刘备",202,},
        [3964] = {7504,4,1,"刘备",202,0,"来日我使孔明回拜贤侄，定有妙计相告。","来日我使孔明回拜贤侄，定有妙计相告。",1,"","","刘琦",128,},
        [3965] = {7504,5,2,"刘琦",128,0,"谢叔父。","谢叔父。",1,"","","刘备",202,},
        [3966] = {7505,1,2,"刘琦",128,0,"琦不见容于继母，幸先生一言相救。","琦不见容于继母，幸先生一言相救。",1,"","","诸葛亮",203,},
        [3967] = {7505,2,1,"诸葛亮",203,0,"亮客寄于此，岂敢与人骨肉之事？倘有漏泄，为害不浅。","亮客寄于此，岂敢与人骨肉之事？倘有漏泄，为害不浅。",1,"","","刘琦",128,},
        [3968] = {7505,3,2,"刘琦",128,0,"既承光顾，安敢慢别。","既承光顾，安敢慢别。",1,"","","诸葛亮",203,},
        [3969] = {7505,4,2,"刘琦",128,0,"继母不见容，乞先生一言救我。","继母不见容，乞先生一言救我。",1,"","","诸葛亮",203,},
        [3970] = {7505,5,1,"诸葛亮",203,0,"此非亮所敢谋也。","此非亮所敢谋也。",1,"","","刘琦",128,},
        [3971] = {7505,6,2,"刘琦",128,0,"继母不见容，琦命在旦夕，先生忍无一言相救乎？","继母不见容，琦命在旦夕，先生忍无一言相救乎？",1,"","","诸葛亮",203,},
        [3972] = {7505,7,1,"诸葛亮",203,0,"吾有一策。","吾有一策。",1,"","","刘琦",128,},
        [3973] = {7505,8,2,"刘琦",128,0,"愿即赐教。","愿即赐教。",1,"","","诸葛亮",203,},
        [3974] = {7505,9,1,"诸葛亮",203,0,"公子岂不闻申生、重耳之事乎？申生在内而亡，重耳在外而安。","公子岂不闻申生、重耳之事乎？申生在内而亡，重耳在外而安。",1,"","","刘琦",128,},
        [3975] = {7505,10,1,"诸葛亮",203,0,"今黄祖新亡，江夏乏人守御，公子何不上言，乞屯兵守江夏，则可以避祸矣。","今黄祖新亡，江夏乏人守御，公子何不上言，乞屯兵守江夏，则可以避祸矣。",1,"","","刘琦",128,},
        [3976] = {7505,11,2,"刘琦",128,0,"谢孔明相助。","谢孔明相助。",1,"","","诸葛亮",203,},
        [3977] = {7506,1,2,"刘表",423,0,"刘琦上言，欲守江夏。吾犹豫未决。","刘琦上言，欲守江夏。吾犹豫未决。",1,"","","刘备",202,},
        [3978] = {7506,2,1,"刘备",202,0,"江夏重地，固非他人可守，正须公子自往。东南之事，兄父子当之；西北之事，备愿当之。","江夏重地，固非他人可守，正须公子自往。东南之事，兄父子当之；西北之事，备愿当之。",1,"","","刘表",423,},
        [3979] = {7506,3,2,"刘表",423,0,"近闻曹操于邺郡作玄武池以练水军，必有南征之意，不可不防。","近闻曹操于邺郡作玄武池以练水军，必有南征之意，不可不防。",1,"","","刘备",202,},
        [3980] = {7506,4,1,"刘备",202,0,"备已知之，兄勿忧虑。遂拜辞回新野。","备已知之，兄勿忧虑。遂拜辞回新野。",1,"","","刘表",423,},
        [3981] = {7507,1,1,"主角",1,0,"吾听闻孔明兄最近正在挑选老婆。","吾听闻孔明兄最近正在挑选老婆。",1,"","","诸葛亮",203,},
        [3982] = {7507,2,1,"主角",1,0,"我游历四方，识一女子，才华出众，汝可见之。","我游历四方，识一女子，才华出众，汝可见之。",1,"","","诸葛亮",203,},
        [3983] = {7507,3,2,"诸葛亮",203,0,"何人也？","何人也？",1,"","","主角",1,},
        [3984] = {7507,4,1,"主角",1,0,"荆州沔阳名士黄承彦之女，黄月英。","荆州沔阳名士黄承彦之女，黄月英。",1,"","","诸葛亮",203,},
        [3985] = {7507,5,2,"诸葛亮",203,0,"我听闻其长相丑陋，黄头发，黑皮肤，扬威将军为何荐于我？","我听闻其长相丑陋，黄头发，黑皮肤，扬威将军为何荐于我？",1,"","","主角",1,},
        [3986] = {7507,6,1,"主角",1,0,"世人乱言怎可乱信，汝当亲自去见之。","世人乱言怎可乱信，汝当亲自去见之。",1,"","","诸葛亮",203,},
        [3987] = {7508,1,1,"诸葛亮",203,0,"世人皆言黄承彦千金为阿丑。","世人皆言黄承彦千金为阿丑。",1,"","","黄月英",204,},
        [3988] = {7508,2,1,"诸葛亮",203,0,"今知世人乱言，汝聪明美丽、才华出众、笔下滔滔，武艺超群。","今知世人乱言，汝聪明美丽、才华出众、笔下滔滔，武艺超群。",1,"","","黄月英",204,},
        [3989] = {7508,3,1,"诸葛亮",203,0,"妄言者实为有眼不识荆山玉。","妄言者实为有眼不识荆山玉。",1,"","","黄月英",204,},
        [3990] = {7508,4,2,"黄月英",204,0,"谢先生褒奖。","谢先生褒奖。",1,"","","诸葛亮",203,},
        [3991] = {7508,5,2,"黄月英",204,0,"特赠羽扇一把，望珍之。","特赠羽扇一把，望珍之。",1,"","","诸葛亮",203,},
        [3992] = {7508,6,1,"诸葛亮",203,0,"为何送吾一把羽扇？","为何送吾一把羽扇？",1,"","","黄月英",204,},
        [3993] = {7508,7,2,"黄月英",204,0,"诸葛先生，汝跟家父畅谈天下大事，讲到你的胸怀大计，气宇轩昂、眉飞色舞。","诸葛先生，汝跟家父畅谈天下大事，讲到你的胸怀大计，气宇轩昂、眉飞色舞。",1,"","","诸葛亮",203,},
        [3994] = {7508,8,2,"黄月英",204,0,"但吾发现讲到曹操、孙权时，你眉头深锁、忧心于中，故送此扇子你用于遮面。","但吾发现讲到曹操、孙权时，你眉头深锁、忧心于中，故送此扇子你用于遮面。",1,"","","诸葛亮",203,},
        [3995] = {7508,9,1,"诸葛亮",203,0,"吾领深意，大丈夫做事要沉得住气，不能情绪波动，感情用事，更不能让人家发现，被轻视、被鄙视，而成不了大事。","吾领深意，大丈夫做事要沉得住气，不能情绪波动，感情用事，更不能让人家发现，被轻视、被鄙视，而成不了大事。",1,"","","黄月英",204,},
        [3996] = {7508,10,1,"诸葛亮",203,0,"吾今后当扇不离手！","吾今后当扇不离手！",1,"","","黄月英",204,},
        [3997] = {7509,1,1,"主角",1,0,"月英可合孔明之意？","月英可合孔明之意？",1,"","","诸葛亮",203,},
        [3998] = {7509,2,2,"诸葛亮",203,0,"聪明美丽、才华出众，乃荆州明玉，吾甚是喜欢！","聪明美丽、才华出众，乃荆州明玉，吾甚是喜欢！",1,"","","主角",1,},
        [3999] = {7509,3,1,"主角",1,0,"孔明兄难掩喜悦之情，不稳重不稳重，啊哈哈哈哈。","孔明兄难掩喜悦之情，不稳重不稳重，啊哈哈哈哈。",1,"","","诸葛亮",203,},
        [4000] = {7509,4,2,"诸葛亮",203,0,"吾这就携礼提亲去！","吾这就携礼提亲去！",1,"","","主角",1,},
        [4001] = {7601,1,2,"夏侯惇",105,0,"近闻刘备在新野，每日教演士卒，必为后患，可早图之。","近闻刘备在新野，每日教演士卒，必为后患，可早图之。",1,"","","曹操",103,},
        [4002] = {7601,2,1,"曹操",103,0,"孤命汝为都督，于禁、李典、夏侯兰、韩浩为副将，领兵十万，直抵博望城，以窥新野。","孤命汝为都督，于禁、李典、夏侯兰、韩浩为副将，领兵十万，直抵博望城，以窥新野。",1,"","","夏侯惇",105,},
        [4003] = {7601,3,2,"荀彧",102,0,"刘备英雄，今更兼诸葛亮为军师，不可轻敌。","刘备英雄，今更兼诸葛亮为军师，不可轻敌。",1,"","","曹操",103,},
        [4004] = {7601,4,1,"夏侯惇",105,0,"刘备鼠辈耳，吾必擒之。","刘备鼠辈耳，吾必擒之。",1,"","","荀彧",102,},
        [4005] = {7601,5,2,"徐庶",212,0,"将军勿轻视刘玄德。今玄德得诸葛亮为辅，如虎生翼矣。","将军勿轻视刘玄德。今玄德得诸葛亮为辅，如虎生翼矣。",1,"","","夏侯惇",105,},
        [4006] = {7601,6,1,"曹操",103,0,"诸葛亮何人也？","诸葛亮何人也？",1,"","","徐庶",212,},
        [4007] = {7601,7,2,"徐庶",212,0,"亮字孔明，道号卧龙先生。有经天纬地之才，出鬼入神之计，真当世之奇才，非可小觑。","亮字孔明，道号卧龙先生。有经天纬地之才，出鬼入神之计，真当世之奇才，非可小觑。",1,"","","曹操",103,},
        [4008] = {7601,8,1,"曹操",103,0,"比公若何？","比公若何？",1,"","","徐庶",212,},
        [4009] = {7601,9,2,"徐庶",212,0,"庶安敢比亮？庶如萤火之光，亮乃皓月之明也。","庶安敢比亮？庶如萤火之光，亮乃皓月之明也。",1,"","","曹操",103,},
        [4010] = {7601,10,2,"夏侯惇",105,0,"元直之言谬矣。吾看诸葛亮如草芥耳，何足惧哉！吾若不一阵生擒刘备，活捉诸葛，愿将首级献与丞相。","元直之言谬矣。吾看诸葛亮如草芥耳，何足惧哉！吾若不一阵生擒刘备，活捉诸葛，愿将首级献与丞相。",1,"","","曹操",103,},
        [4011] = {7601,11,1,"曹操",103,0,"汝早报捷书，以慰吾心。","汝早报捷书，以慰吾心。",1,"","","夏侯惇",105,},
        [4012] = {7602,1,1,"刘备",202,0,"曹操差夏侯惇引兵十万，杀奔新野来了。如何迎敌？","曹操差夏侯惇引兵十万，杀奔新野来了。如何迎敌？",1,"","","张飞",206,},
        [4013] = {7602,2,2,"张飞",206,0,"可着孔明前去迎敌便了。","可着孔明前去迎敌便了。",1,"","","刘备",202,},
        [4014] = {7602,3,1,"刘备",202,0,"智赖孔明，勇须二弟，何可推调？","智赖孔明，勇须二弟，何可推调？",1,"","","张飞",206,},
        [4015] = {7602,4,2,"张飞",206,0,"哼！云长，我们走。","哼！云长，我们走。",1,"","","刘备",202,},
        [4016] = {7603,1,1,"刘备",202,0,"孔明，夏侯惇引兵十万而来，如何迎敌？","孔明，夏侯惇引兵十万而来，如何迎敌？",1,"","","诸葛亮",203,},
        [4017] = {7603,2,2,"诸葛亮",203,0,"恐关、张二人不肯听吾号令，主公若欲亮行兵，乞假剑印。","恐关、张二人不肯听吾号令，主公若欲亮行兵，乞假剑印。",1,"","","刘备",202,},
        [4018] = {7603,3,1,"刘备",202,0,"吾这就将剑印付于你。","吾这就将剑印付于你。",1,"","","诸葛亮",203,},
        [4019] = {7604,1,1,"刘备",202,0,"吾已将剑印付于孔明，你们务必听其号令。","吾已将剑印付于孔明，你们务必听其号令。",1,"","","张飞",206,},
        [4020] = {7604,2,2,"张飞",206,0,"且听令，看他如何调度。","且听令，看他如何调度。",1,"","","刘备",202,},
        [4021] = {7604,3,1,"诸葛亮",203,0,"博望之左有山，名曰豫山。右有林，名曰安林，可以埋伏军马。","博望之左有山，名曰豫山。右有林，名曰安林，可以埋伏军马。",1,"","","张飞",206,},
        [4022] = {7604,4,1,"诸葛亮",203,0,"云长可引一千军往豫山埋伏，等彼军至，放过休敌，其辎重粮草，必在后面，但看南面火起，可纵兵出击，就焚其粮草。","云长可引一千军往豫山埋伏，等彼军至，放过休敌，其辎重粮草，必在后面，但看南面火起，可纵兵出击，就焚其粮草。",1,"","","张飞",206,},
        [4023] = {7604,5,1,"诸葛亮",203,0,"翼德可引一千军去安林背后山谷中埋伏，只看南面火起，便可出，向博望城旧屯粮草处纵火烧之。","翼德可引一千军去安林背后山谷中埋伏，只看南面火起，便可出，向博望城旧屯粮草处纵火烧之。",1,"","","张飞",206,},
        [4024] = {7604,6,1,"诸葛亮",203,0,"关平、刘封可引五百军，预备引火之物，于博望坡后两边等候，至初更兵到，便可放火矣。","关平、刘封可引五百军，预备引火之物，于博望坡后两边等候，至初更兵到，便可放火矣。",1,"","","张飞",206,},
        [4025] = {7604,7,1,"诸葛亮",203,0,"于樊城取回赵云，令为前部，不要赢，只要输，主公自引一军为后援。各须依计而行，勿使有失。","于樊城取回赵云，令为前部，不要赢，只要输，主公自引一军为后援。各须依计而行，勿使有失。",1,"","","张飞",206,},
        [4026] = {7604,8,2,"关羽",205,0,"我等皆出迎敌，未审军师却作何事？","我等皆出迎敌，未审军师却作何事？",1,"","","诸葛亮",203,},
        [4027] = {7604,9,1,"诸葛亮",203,0,"我只坐守县城。","我只坐守县城。",1,"","","关羽",205,},
        [4028] = {7604,10,2,"张飞",206,0,"我们都去厮杀，你却在家里坐地，好自在！","我们都去厮杀，你却在家里坐地，好自在！",1,"","","诸葛亮",203,},
        [4029] = {7604,11,1,"诸葛亮",203,0,"剑印在此，违令者斩！","剑印在此，违令者斩！",1,"","","张飞",206,},
        [4030] = {7604,12,1,"刘备",202,0,"岂不闻运筹帷幄之中，决胜千里之外？二弟不可违令。","岂不闻运筹帷幄之中，决胜千里之外？二弟不可违令。",1,"","","张飞",206,},
        [4031] = {7604,13,2,"张飞",206,0,"哼！","哼！",1,"","","刘备",202,},
        [4032] = {7604,14,2,"关羽",205,0,"我们且看他的计应也不应，那时却来问他未迟。","我们且看他的计应也不应，那时却来问他未迟。",1,"","","刘备",202,},
        [4033] = {7604,15,1,"诸葛亮",203,0,"主公今日可便引兵就博望山下屯住。来日黄昏，敌军必到，主公便弃营而走，但见火起，即回军掩杀。","主公今日可便引兵就博望山下屯住。来日黄昏，敌军必到，主公便弃营而走，但见火起，即回军掩杀。",1,"","","关羽",205,},
        [4034] = {7604,16,1,"诸葛亮",203,0,"吾与糜竺、糜芳引五百军守县。","吾与糜竺、糜芳引五百军守县。",1,"","","关羽",205,},
        [4035] = {7605,1,2,"夏侯惇",105,0,"徐元直在丞相面前，夸诸葛亮为天人，今观其用兵，乃以此等军马为前部，与吾对敌，正如驱犬羊与虎豹斗耳！","徐元直在丞相面前，夸诸葛亮为天人，今观其用兵，乃以此等军马为前部，与吾对敌，正如驱犬羊与虎豹斗耳！",1,"","","",0,},
        [4036] = {7605,2,2,"夏侯惇",105,0,"吾于丞相前夸口。要活捉刘备、诸葛亮，今必应吾言矣。","吾于丞相前夸口。要活捉刘备、诸葛亮，今必应吾言矣。",1,"","","",0,},
        [4037] = {7605,3,1,"赵云",201,0,"休得妄言！让你见识见识我手中长枪。","休得妄言！让你见识见识我手中长枪。",1,"","","夏侯惇",105,},
        [4038] = {7605,4,2,"夏侯惇",105,0,"汝等随刘备，如孤魂随鬼耳！","汝等随刘备，如孤魂随鬼耳！",1,"","","赵云",201,},
        [4039] = {7606,1,1,"李典",124,0,"赵云诈降诱敌，恐有埋伏。","赵云诈降诱敌，恐有埋伏。",1,"","","夏侯惇",105,},
        [4040] = {7606,2,2,"夏侯惇",105,0,"敌军如此，虽十面埋伏，吾何惧哉！","敌军如此，虽十面埋伏，吾何惧哉！",1,"","","李典",124,},
        [4041] = {7606,3,2,"夏侯惇",105,0,"直取博望坡！","直取博望坡！",1,"","","李典",124,},
        [4042] = {7607,1,1,"李典",124,0,"欺敌者必败。南道路狭，山川相逼。","欺敌者必败。南道路狭，山川相逼。",1,"","","于禁",119,},
        [4043] = {7607,2,1,"李典",124,0,"树木丛杂，倘彼用火攻，奈何？","树木丛杂，倘彼用火攻，奈何？",1,"","","于禁",119,},
        [4044] = {7607,3,2,"于禁",119,0,"君言是也。吾当往前为都督言之，君可止住后军。","君言是也。吾当往前为都督言之，君可止住后军。",1,"","","李典",124,},
        [4045] = {7607,4,2,"于禁",119,0,"后军慢行！前军都督且住！","后军慢行！前军都督且住！",1,"","","李典",124,},
        [4046] = {7607,5,2,"于禁",119,0,"南道路狭，山川相逼，树木丛杂，可防火攻。","南道路狭，山川相逼，树木丛杂，可防火攻。",1,"","","李典",124,},
        [4047] = {7607,6,1,"夏侯惇",105,0,"军马勿进！","军马勿进！",1,"","","于禁",119,},
        [4048] = {7608,1,1,"夏侯惇",105,0,"李典、于禁曾言及此，悔之不及！","李典、于禁曾言及此，悔之不及！",1,"","","",0,},
        [4049] = {7608,2,1,"夏侯惇",105,0,"今尝败绩，收拾余部，速回许昌。","今尝败绩，收拾余部，速回许昌。",1,"","","",0,},
        [4050] = {7609,1,1,"张飞",206,0,"孔明真英杰也！","孔明真英杰也！",1,"","","诸葛亮",203,},
        [4051] = {7609,2,1,"关羽",205,0,"吾等佩服。","吾等佩服。",1,"","","诸葛亮",203,},
        [4052] = {7609,3,2,"诸葛亮",203,0,"夏侯惇虽败去，曹操必自引大军来。","夏侯惇虽败去，曹操必自引大军来。",1,"","","关羽",205,},
        [4053] = {7609,4,1,"刘备",202,0,"似此如之奈何？","似此如之奈何？",1,"","","诸葛亮",203,},
        [4054] = {7609,5,2,"诸葛亮",203,0,"亮有一计，可敌曹军。","亮有一计，可敌曹军。",1,"","","刘备",202,},
        [4055] = {7609,6,1,"刘备",202,0,"请孔明先生明示。","请孔明先生明示。",1,"","","诸葛亮",203,},
        [4056] = {7609,7,2,"诸葛亮",203,0,"新野小县，不可久居，近闻刘景升病在危笃，可乘此机会，取彼荆州为安身之地，庶可拒曹操也。","新野小县，不可久居，近闻刘景升病在危笃，可乘此机会，取彼荆州为安身之地，庶可拒曹操也。",1,"","","刘备",202,},
        [4057] = {7609,8,1,"刘备",202,0,"公言甚善，但备受景升之恩，安忍图之！","公言甚善，但备受景升之恩，安忍图之！",1,"","","诸葛亮",203,},
        [4058] = {7609,9,2,"诸葛亮",203,0,"今若不取，后悔何及！","今若不取，后悔何及！",1,"","","刘备",202,},
        [4059] = {7609,10,1,"刘备",202,0,"吾宁死，不忍作负义之事。","吾宁死，不忍作负义之事。",1,"","","诸葛亮",203,},
        [4060] = {7609,11,2,"诸葛亮",203,0,"且再作商议。","且再作商议。",1,"","","刘备",202,},
        [4061] = {7701,1,2,"夏侯惇",105,0,"惇遭诸葛亮诡计，用火攻破我军。","惇遭诸葛亮诡计，用火攻破我军。",1,"","","曹操",103,},
        [4062] = {7701,2,1,"曹操",103,0,"汝自幼用兵，岂不知狭处须防火攻？","汝自幼用兵，岂不知狭处须防火攻？",1,"","","夏侯惇",105,},
        [4063] = {7701,3,2,"夏侯惇",105,0,"李典、于禁曾言及此，悔之不及！","李典、于禁曾言及此，悔之不及！",1,"","","曹操",103,},
        [4064] = {7701,4,1,"曹操",103,0,"胜负乃兵家常事，不必放在心上。","胜负乃兵家常事，不必放在心上。",1,"","","夏侯惇",105,},
        [4065] = {7701,5,2,"夏侯惇",105,0,"刘备如此猖狂，真腹心之患也，不可不急除。","刘备如此猖狂，真腹心之患也，不可不急除。",1,"","","曹操",103,},
        [4066] = {7701,6,1,"曹操",103,0,"吾所虑者，刘备、孙权耳，余皆不足介意，今当乘此时扫平江南。","吾所虑者，刘备、孙权耳，余皆不足介意，今当乘此时扫平江南。",1,"","","夏侯惇",105,},
        [4067] = {7701,7,1,"曹操",103,0,"起大兵五十万，曹仁、曹洪为第一队，张辽、张郃为第二队。","起大兵五十万，曹仁、曹洪为第一队，张辽、张郃为第二队。",1,"","","夏侯惇",105,},
        [4068] = {7701,8,1,"曹操",103,0,"夏侯渊、夏侯惇为第三队，于禁、李典为第四队，孤自领诸将为第五队，每队各引兵十万。","夏侯渊、夏侯惇为第三队，于禁、李典为第四队，孤自领诸将为第五队，每队各引兵十万。",1,"","","夏侯惇",105,},
        [4069] = {7701,9,1,"曹操",103,0,"许褚为折冲将军，引兵三千为先锋，选定建安十三年秋七月丙午日出师。","许褚为折冲将军，引兵三千为先锋，选定建安十三年秋七月丙午日出师。",1,"","","夏侯惇",105,},
        [4070] = {7702,1,2,"孔融",417,0,"刘备，刘表皆汉室宗亲，不可轻伐；孙权虎踞六郡，且有大江之险，亦不易取，今丞相兴此无义之师，恐失天下之望。","刘备，刘表皆汉室宗亲，不可轻伐；孙权虎踞六郡，且有大江之险，亦不易取，今丞相兴此无义之师，恐失天下之望。",1,"","","曹操",103,},
        [4071] = {7702,2,1,"曹操",103,0,"刘备、刘表、孙权皆逆命之臣，岂容不讨！","刘备、刘表、孙权皆逆命之臣，岂容不讨！",1,"","","孔融",417,},
        [4072] = {7702,3,1,"曹操",103,0,"退下！如有再谏者，必斩。","退下！如有再谏者，必斩。",1,"","","孔融",417,},
        [4073] = {7703,1,1,"孔融",417,0,"哼！","哼！",1,"","","",0,},
        [4074] = {7703,2,1,"孔融",417,0,"以至不仁伐至仁，安得不败乎！","以至不仁伐至仁，安得不败乎！",1,"","","",0,},
        [4075] = {7704,1,2,"郗虑",131,0,"融平日每每狎侮丞相，又与祢衡相善，衡赞融曰仲尼不死，融赞衡曰颜回复生。","融平日每每狎侮丞相，又与祢衡相善，衡赞融曰仲尼不死，融赞衡曰颜回复生。",1,"","","曹操",103,},
        [4076] = {7704,2,2,"郗虑",131,0,"向者祢衡之辱丞相，乃融使之也。","向者祢衡之辱丞相，乃融使之也。",1,"","","曹操",103,},
        [4077] = {7704,3,2,"郗虑",131,0,"昨日出丞相府，曾言：以至不仁伐至仁，安得不败乎！","昨日出丞相府，曾言：以至不仁伐至仁，安得不败乎！",1,"","","曹操",103,},
        [4078] = {7704,4,1,"曹操",103,0,"好他个孔融！","好他个孔融！",1,"","","郗虑",131,},
        [4079] = {7704,5,1,"曹操",103,0,"尽收融家小并二子，皆斩之。","尽收融家小并二子，皆斩之。",1,"","","郗虑",131,},
        [4080] = {7801,1,2,"刘表",423,0,"我病已入膏肓，不久便死矣，特托孤于贤弟。我子无才，恐不能承父业，我死之后，贤弟可自领荆州。","我病已入膏肓，不久便死矣，特托孤于贤弟。我子无才，恐不能承父业，我死之后，贤弟可自领荆州。",1,"","","刘备",202,},
        [4081] = {7801,2,1,"刘备",202,0,"备当竭力以辅贤侄，安敢有他意乎！","备当竭力以辅贤侄，安敢有他意乎！",1,"","","刘表",423,},
        [4082] = {7801,3,1,"刘备",202,0,"曹操自统大兵至，玄德先回新野，托孤之事再议。","曹操自统大兵至，玄德先回新野，托孤之事再议。",1,"","","刘表",423,},
        [4083] = {7802,1,2,"刘琦",128,0,"吾守江夏，知父病危，速来探望。","吾守江夏，知父病危，速来探望。",1,"","","蔡瑁",443,},
        [4084] = {7802,2,1,"蔡瑁",443,0,"公子奉父命镇守江夏，其任至重，今擅离职守，倘东吴兵至，如之奈何？","公子奉父命镇守江夏，其任至重，今擅离职守，倘东吴兵至，如之奈何？",1,"","","刘琦",128,},
        [4085] = {7802,3,1,"蔡瑁",443,0,"若见主公，主公必生嗔怒，病将转增，非孝也，宜速回。","若见主公，主公必生嗔怒，病将转增，非孝也，宜速回。",1,"","","刘琦",128,},
        [4086] = {7802,4,2,"刘琦",128,0,"这…","这…",1,"","","蔡瑁",443,},
        [4087] = {7802,5,2,"刘琦",128,0,"烦请德珪多照料主公，若有变故速联！","烦请德珪多照料主公，若有变故速联！",1,"","","蔡瑁",443,},
        [4088] = {7803,1,1,"蔡夫人",446,0,"刘表已死，立嗣之事该如何是好？","刘表已死，立嗣之事该如何是好？",1,"","","蔡瑁",443,},
        [4089] = {7803,2,2,"蔡瑁",443,0,"可假写遗嘱，令次子刘琮为荆州之主，然后举哀报丧。","可假写遗嘱，令次子刘琮为荆州之主，然后举哀报丧。",1,"","","蔡夫人",446,},
        [4090] = {7803,3,1,"蔡夫人",446,0,"好！","好！",1,"","","蔡瑁",443,},
        [4091] = {7804,1,1,"刘琮",223,0,"吾父弃世，吾兄现在江夏，更有叔父玄德在新野。汝等立我为主。倘兄与叔兴兵问罪，如何解释？","吾父弃世，吾兄现在江夏，更有叔父玄德在新野。汝等立我为主。倘兄与叔兴兵问罪，如何解释？",1,"","","",999,},
        [4092] = {7804,2,2,"李珪",133,0,"公子之言甚善。今可急发哀书至江夏，请大公子为荆州之主，就命玄德一同理事。","公子之言甚善。今可急发哀书至江夏，请大公子为荆州之主，就命玄德一同理事。",1,"","","刘琮",223,},
        [4093] = {7804,3,2,"李珪",133,0,"北可以敌曹操，南可以拒孙权。此万全之策也。","北可以敌曹操，南可以拒孙权。此万全之策也。",1,"","","刘琮",223,},
        [4094] = {7804,4,1,"蔡瑁",443,0,"汝何人，敢乱言以逆主公遗命！","汝何人，敢乱言以逆主公遗命！",1,"","","李珪",133,},
        [4095] = {7804,5,2,"李珪",133,0,"汝内外朋谋，假称遗命，废长立幼，眼见荆襄九郡，送于蔡氏之手！故主有灵，必当殛汝！","汝内外朋谋，假称遗命，废长立幼，眼见荆襄九郡，送于蔡氏之手！故主有灵，必当殛汝！",1,"","","蔡瑁",443,},
        [4096] = {7804,6,1,"蔡瑁",443,0,"推出去斩了！","推出去斩了！",1,"","","李珪",133,},
        [4097] = {7805,1,1,"刘琮",223,0,"曹操引大军而来，如何是好。","曹操引大军而来，如何是好。",1,"","","王粲",121,},
        [4098] = {7805,2,2,"王粲",121,0,"将军自料比曹公何如？","将军自料比曹公何如？",1,"","","刘琮",223,},
        [4099] = {7805,3,1,"刘琮",223,0,"不如也。","不如也。",1,"","","王粲",121,},
        [4100] = {7805,4,2,"王粲",121,0,"曹公兵强将勇，足智多谋。擒吕布于下邳，摧袁绍于官渡，逐刘备于陇右，破乌桓于白狼，枭除荡定者，不可胜计。","曹公兵强将勇，足智多谋。擒吕布于下邳，摧袁绍于官渡，逐刘备于陇右，破乌桓于白狼，枭除荡定者，不可胜计。",1,"","","刘琮",223,},
        [4101] = {7805,5,2,"王粲",121,0,"今以大军南下荆襄，势难抵敌。傅、蒯二君之谋，乃长策也。将军不可迟疑，致生后悔。","今以大军南下荆襄，势难抵敌。傅、蒯二君之谋，乃长策也。将军不可迟疑，致生后悔。",1,"","","刘琮",223,},
        [4102] = {7805,6,1,"刘琮",223,0,"先生见教极是，那母亲如何看？","先生见教极是，那母亲如何看？",1,"","","王粲",121,},
        [4103] = {7805,7,2,"蔡夫人",446,0,"我亦同意降曹。","我亦同意降曹。",1,"","","刘琮",223,},
        [4104] = {7805,8,1,"刘琮",223,0,"吾这就献上降书。","吾这就献上降书。",1,"","","蔡夫人",446,},
        [4105] = {7806,1,1,"刘备",202,0,"刘琦守江夏，蔡夫人与蔡瑁不来报丧，竟假写遗嘱立刘琮为主。","刘琦守江夏，蔡夫人与蔡瑁不来报丧，竟假写遗嘱立刘琮为主。",1,"","","",0,},
        [4106] = {7806,2,1,"刘备",202,0,"莫想那刘琮已将荆襄九郡献与曹操矣！","莫想那刘琮已将荆襄九郡献与曹操矣！",1,"","","",0,},
        [4107] = {7806,3,1,"刘备",202,0,"吾兄临危托孤于我，今若执其子而夺其地，异日死于九泉之下，何面目复见吾兄乎？","吾兄临危托孤于我，今若执其子而夺其地，异日死于九泉之下，何面目复见吾兄乎？",1,"","","",0,},
        [4108] = {7806,4,2,"主角",1,0,"可恨，我这就去教训教训他们。","可恨，我这就去教训教训他们。",1,"","","刘备",202,},
        [4109] = {7807,1,1,"刘备",202,0,"今曹兵已至宛城，何以拒敌？","今曹兵已至宛城，何以拒敌？",1,"","","诸葛亮",203,},
        [4110] = {7807,2,2,"诸葛亮",203,0,"不战，走樊城以避之。","不战，走樊城以避之。",1,"","","刘备",202,},
        [4111] = {7807,3,1,"刘备",202,0,"就依孔明之计。","就依孔明之计。",1,"","","诸葛亮",203,},
        [4112] = {7901,1,1,"刘备",202,0,"曹兵已到博望，孔明快与我商议拒敌之计。","曹兵已到博望，孔明快与我商议拒敌之计。",1,"","","诸葛亮",203,},
        [4113] = {7901,2,2,"诸葛亮",203,0,"主公且宽心。前番一把火，烧了夏侯惇大半人马，今番曹军又来，必教他中这条计。","主公且宽心。前番一把火，烧了夏侯惇大半人马，今番曹军又来，必教他中这条计。",1,"","","刘备",202,},
        [4114] = {7901,3,1,"刘备",202,0,"何计？","何计？",1,"","","诸葛亮",203,},
        [4115] = {7901,4,2,"诸葛亮",203,0,"此前还有要事要做。","此前还有要事要做。",1,"","","刘备",202,},
        [4116] = {7901,5,2,"诸葛亮",203,0,"还烦请告新野老幼男女，愿从者，即于今日皆跟我往樊城暂避，不可自误。","还烦请告新野老幼男女，愿从者，即于今日皆跟我往樊城暂避，不可自误。",1,"","","刘备",202,},
        [4117] = {7902,1,1,"刘备",202,0,"何计治敌？","何计治敌？",1,"","","诸葛亮",203,},
        [4118] = {7902,2,2,"诸葛亮",203,0,"孙乾往河边调拨船只，救济百姓，糜竺护送各官家眷到樊城。云长引一千军去白河上流头埋伏，各带布袋，多装沙土，遏住白河之水。","孙乾往河边调拨船只，救济百姓，糜竺护送各官家眷到樊城。云长引一千军去白河上流头埋伏，各带布袋，多装沙土，遏住白河之水。",1,"","","刘备",202,},
        [4119] = {7902,3,2,"诸葛亮",203,0,"至来日三更后，只听下流头人喊马嘶，急取起布袋，放水淹之，却顺水杀将下来接应。","至来日三更后，只听下流头人喊马嘶，急取起布袋，放水淹之，却顺水杀将下来接应。",1,"","","刘备",202,},
        [4120] = {7902,4,2,"诸葛亮",203,0,"张飞引一千军去博陵渡口埋伏。此处水势最慢，曹军被淹，必从此逃难，可便乘势杀来接应。","张飞引一千军去博陵渡口埋伏。此处水势最慢，曹军被淹，必从此逃难，可便乘势杀来接应。",1,"","","刘备",202,},
        [4121] = {7902,5,2,"诸葛亮",203,0,"赵云引军三千，分为四队，自领一队伏于东门外，其三队分伏西、南、北三门，却先于城内人家屋上，多藏硫黄焰硝引火之物。","赵云引军三千，分为四队，自领一队伏于东门外，其三队分伏西、南、北三门，却先于城内人家屋上，多藏硫黄焰硝引火之物。",1,"","","刘备",202,},
        [4122] = {7902,6,2,"诸葛亮",203,0,"曹军入城，必安歇民房。来日黄昏后，必有大风，但看风起，便令西、南、北三门伏军尽将火箭射入城去。","曹军入城，必安歇民房。来日黄昏后，必有大风，但看风起，便令西、南、北三门伏军尽将火箭射入城去。",1,"","","刘备",202,},
        [4123] = {7902,7,2,"诸葛亮",203,0,"待城中火势大作，却于城外呐喊助威，只留东门放他出走，汝却于东门外从后击之。","待城中火势大作，却于城外呐喊助威，只留东门放他出走，汝却于东门外从后击之。",1,"","","刘备",202,},
        [4124] = {7902,8,2,"诸葛亮",203,0,"天明会合关、张二将，收军回樊城。再令糜芳、刘封二人带二千军。一半红旗，一半青旗，去新野城外三十里鹊尾坡前屯住。","天明会合关、张二将，收军回樊城。再令糜芳、刘封二人带二千军。一半红旗，一半青旗，去新野城外三十里鹊尾坡前屯住。",1,"","","刘备",202,},
        [4125] = {7902,9,2,"诸葛亮",203,0,"一见曹军到，红旗军走在左，青旗军走在右。他心疑必不敢追。汝二人却去分头埋伏。","一见曹军到，红旗军走在左，青旗军走在右。他心疑必不敢追。汝二人却去分头埋伏。",1,"","","刘备",202,},
        [4126] = {7902,10,2,"诸葛亮",203,0,"只望城中火起，便可追杀败兵，然后却来白河上流头接应。","只望城中火起，便可追杀败兵，然后却来白河上流头接应。",1,"","","刘备",202,},
        [4127] = {7902,11,1,"刘备",202,0,"好，各将依计行事。","好，各将依计行事。",1,"","","诸葛亮",203,},
        [4128] = {7903,1,2,"许褚",108,0,"鹊尾坡前一簇人马，尽打青、红旗号。","鹊尾坡前一簇人马，尽打青、红旗号。",1,"","","曹仁",106,},
        [4129] = {7903,2,2,"许褚",108,0,"前面必有伏兵。我兵只在此处住下。","前面必有伏兵。我兵只在此处住下。",1,"","","曹仁",106,},
        [4130] = {7903,3,1,"曹仁",106,0,"此是疑兵，必无埋伏。可速进兵。我当催军继至。","此是疑兵，必无埋伏。可速进兵。我当催军继至。",1,"","","许褚",108,},
        [4131] = {7904,1,1,"曹仁",106,0,"城中亦不见一人，竟是一座空城了。","城中亦不见一人，竟是一座空城了。",1,"","","",0,},
        [4132] = {7904,2,1,"曹仁",106,0,"此是势孤计穷，故尽带百姓逃窜去了，我军权且在城安歇，来日平明进兵。","此是势孤计穷，故尽带百姓逃窜去了，我军权且在城安歇，来日平明进兵。",1,"","","",0,},
        [4133] = {7904,3,1,"曹仁",106,0,"各军走乏，都已饥饿，皆去夺房造饭。","各军走乏，都已饥饿，皆去夺房造饭。",1,"","","",0,},
        [4134] = {7905,1,1,"许褚",108,0,"西、南、北三门皆火起。","西、南、北三门皆火起。",1,"","","曹仁",106,},
        [4135] = {7905,2,2,"曹仁",106,0,"东门无火，走东门。","东门无火，走东门。",1,"","","许褚",108,},
        [4136] = {7905,3,1,"赵云",201,0,"曹仁，哪里跑！","曹仁，哪里跑！",1,"","","曹仁",106,},
        [4137] = {7905,4,1,"糜芳",133,0,"贼人，哪里跑！","贼人，哪里跑！",1,"","","曹仁",106,},
        [4138] = {7905,5,1,"张飞",206,0,"曹贼快拿命来！","曹贼快拿命来！",1,"","","曹仁",106,},
        [4139] = {7906,1,2,"曹仁",106,0,"丞相，此役失败尔等有过！","丞相，此役失败尔等有过！",1,"","","曹操",103,},
        [4140] = {7906,2,1,"曹操",103,0,"胜负乃兵家常事，不必放在心上。","胜负乃兵家常事，不必放在心上。",1,"","","曹仁",106,},
        [4141] = {7906,3,1,"曹操",103,0,"诸葛村夫，安敢如此！","诸葛村夫，安敢如此！",1,"","","曹仁",106,},
        [4142] = {7906,4,1,"曹操",103,0,"传令三军，漫山塞野，尽至新野下寨。一面搜山，一面填塞白河。","传令三军，漫山塞野，尽至新野下寨。一面搜山，一面填塞白河。",1,"","","曹仁",106,},
        [4143] = {7906,5,1,"曹操",103,0,"大军分作八路，一齐去取樊城。","大军分作八路，一齐去取樊城。",1,"","","曹仁",106,},
        [4144] = {8001,1,2,"刘晔",121,0,"丞相初至襄阳，必须先买民心，今刘备尽迁新野百姓入樊城，若我兵径进，二县为齑粉矣，不如先使人招降刘备。","丞相初至襄阳，必须先买民心，今刘备尽迁新野百姓入樊城，若我兵径进，二县为齑粉矣，不如先使人招降刘备。",1,"","","曹操",103,},
        [4145] = {8001,2,2,"刘晔",121,0,"备即不降，亦可见我爱民之心，若其来降，则荆州之地，可不战而定也。","备即不降，亦可见我爱民之心，若其来降，则荆州之地，可不战而定也。",1,"","","曹操",103,},
        [4146] = {8001,3,1,"曹操",103,0,"谁可为使？","谁可为使？",1,"","","刘晔",121,},
        [4147] = {8001,4,2,"刘晔",121,0,"徐庶与刘备至厚，今现在军中，何不命他一往？","徐庶与刘备至厚，今现在军中，何不命他一往？",1,"","","曹操",103,},
        [4148] = {8001,5,1,"曹操",103,0,"他去恐不复来。","他去恐不复来。",1,"","","刘晔",121,},
        [4149] = {8001,6,2,"刘晔",121,0,"他若不来，贻笑于人矣，丞相勿疑。","他若不来，贻笑于人矣，丞相勿疑。",1,"","","曹操",103,},
        [4150] = {8002,1,1,"曹操",103,0,"我本欲踏平樊城，奈怜众百姓之命。公可往说刘备，如肯来降，免罪赐爵，若更执迷，军民共戮，玉石俱焚。","我本欲踏平樊城，奈怜众百姓之命。公可往说刘备，如肯来降，免罪赐爵，若更执迷，军民共戮，玉石俱焚。",1,"","","徐庶",212,},
        [4151] = {8002,2,1,"曹操",103,0,"吾知公忠义，故特使公往。愿勿相负。","吾知公忠义，故特使公往。愿勿相负。",1,"","","徐庶",212,},
        [4152] = {8002,3,2,"徐庶",212,0,"吾愿往。","吾愿往。",1,"","","曹操",103,},
        [4153] = {8003,1,2,"徐庶",212,0,"曹操使庶来招降使君，乃假买民心也，今彼分兵八路，填白河而进。樊城恐不可守，宜速作行计。","曹操使庶来招降使君，乃假买民心也，今彼分兵八路，填白河而进。樊城恐不可守，宜速作行计。",1,"","","刘备",202,},
        [4154] = {8003,2,1,"刘备",202,0,"君即来，何须回。","君即来，何须回。",1,"","","徐庶",212,},
        [4155] = {8003,3,2,"徐庶",212,0,"某若不还，恐惹人笑。今老母已丧，抱恨终天。身虽在彼，誓不为设一谋，公有卧龙辅佐，何愁大业不成。庶请辞。","某若不还，恐惹人笑。今老母已丧，抱恨终天。身虽在彼，誓不为设一谋，公有卧龙辅佐，何愁大业不成。庶请辞。",1,"","","刘备",202,},
        [4156] = {8003,4,1,"刘备",202,0,"备不敢强留，珍重。","备不敢强留，珍重。",1,"","","徐庶",212,},
        [4157] = {8004,1,2,"徐庶",212,0,"回丞相，玄德并无降意。","回丞相，玄德并无降意。",1,"","","曹操",103,},
        [4158] = {8004,2,1,"曹操",103,0,"那孤便即日进兵！","那孤便即日进兵！",1,"","","徐庶",212,},
        [4159] = {8005,1,1,"孙乾",140,0,"百姓愿随使君，同过江！","百姓愿随使君，同过江！",1,"","","刘备",202,},
        [4160] = {8005,2,2,"刘备",202,0,"为吾一人而使百姓遭此大难，吾何生哉！","为吾一人而使百姓遭此大难，吾何生哉！",1,"","","孙乾",140,},
        [4161] = {8005,3,1,"孙乾",140,0,"玄德万万不可自责！","玄德万万不可自责！",1,"","","刘备",202,},
        [4162] = {8005,4,1,"孙乾",140,0,"曹军将至，应迅速渡江！","曹军将至，应迅速渡江！",1,"","","刘备",202,},
        [4163] = {8005,5,2,"刘备",202,0,"速保护百姓，渡南岸，行襄阳。","速保护百姓，渡南岸，行襄阳。",1,"","","孙乾",140,},
        [4164] = {8006,1,1,"刘备",202,0,"刘琮贤侄，吾但欲救百姓，并无他念。可否开襄阳门。","刘琮贤侄，吾但欲救百姓，并无他念。可否开襄阳门。",1,"","","刘琮",223,},
        [4165] = {8006,2,2,"刘琮",223,0,"开门必危及尔等！","开门必危及尔等！",1,"","","刘备",202,},
        [4166] = {8006,3,2,"魏延",210,0,"蔡瑁、张允卖国之贼！刘使君乃仁德之人，今为救民而来投，何得相拒！","蔡瑁、张允卖国之贼！刘使君乃仁德之人，今为救民而来投，何得相拒！",1,"","","刘备",202,},
        [4167] = {8006,4,2,"魏延",210,0,"我放下吊桥，刘皇叔快领兵入城，共杀卖国之贼！","我放下吊桥，刘皇叔快领兵入城，共杀卖国之贼！",1,"","","刘备",202,},
        [4168] = {8006,5,1,"刘备",202,0,"休惊百姓！","休惊百姓！",1,"","","魏延",210,},
        [4169] = {8007,1,1,"刘备",202,0,"本欲保民，若入襄阳反害民也！吾不愿入！","本欲保民，若入襄阳反害民也！吾不愿入！",1,"","","",0,},
        [4170] = {8007,2,2,"诸葛亮",203,0,"江陵乃荆州要地，不如先取江陵为家！","江陵乃荆州要地，不如先取江陵为家！",1,"","","刘备",202,},
        [4171] = {8007,3,1,"刘备",202,0,"正合吾心！","正合吾心！",1,"","","诸葛亮",203,},
        [4172] = {8007,4,1,"刘备",202,0,"命各将引百姓，尽离襄阳大路，望江陵而走。","命各将引百姓，尽离襄阳大路，望江陵而走。",1,"","","诸葛亮",203,},
        [4173] = {8101,1,1,"曹操",103,0,"蒯越为江陵太守樊城侯，傅巽、王粲等皆为关内侯，而以刘琮为青州刺史，即日启程到任。","蒯越为江陵太守樊城侯，傅巽、王粲等皆为关内侯，而以刘琮为青州刺史，即日启程到任。",1,"","","",0,},
        [4174] = {8101,2,2,"刘琮",223,0,"琮不愿为官，愿守父母乡土。","琮不愿为官，愿守父母乡土。",1,"","","曹操",103,},
        [4175] = {8101,3,1,"曹操",103,0,"青州近帝都，教你随朝为官，免在荆襄被人图害。","青州近帝都，教你随朝为官，免在荆襄被人图害。",1,"","","刘琮",223,},
        [4176] = {8101,4,2,"刘琮",223,0,"烦请丞相收回任命。","烦请丞相收回任命。",1,"","","曹操",103,},
        [4177] = {8101,5,1,"曹操",103,0,"即刻启程到任！退下。","即刻启程到任！退下。",1,"","","刘琮",223,},
        [4178] = {8101,6,2,"刘琮",223,0,"是…","是…",1,"","","曹操",103,},
        [4179] = {8101,7,1,"曹操",103,0,"于禁何在？","于禁何在？",1,"","","",0,},
        [4180] = {8101,8,2,"于禁",119,0,"臣在。","臣在。",1,"","","曹操",103,},
        [4181] = {8101,9,1,"曹操",103,0,"你引轻骑追刘琮母子杀之，以绝后患。","你引轻骑追刘琮母子杀之，以绝后患。",1,"","","于禁",119,},
        [4182] = {8101,10,2,"于禁",119,0,"臣领命。","臣领命。",1,"","","曹操",103,},
        [4183] = {8102,1,1,"于禁",119,0,"我奉丞相令，教来杀汝母子！可早纳下首级！","我奉丞相令，教来杀汝母子！可早纳下首级！",1,"","","蔡夫人",446,},
        [4184] = {8102,2,2,"蔡夫人",446,0,"求将军放过我们母子吧！","求将军放过我们母子吧！",1,"","","于禁",119,},
        [4185] = {8102,3,1,"于禁",119,0,"众将士动手！","众将士动手！",1,"","","蔡夫人",446,},
        [4186] = {8103,1,2,"于禁",119,0,"刘备带领百姓，日行止十数里，计程只有三百余里。","刘备带领百姓，日行止十数里，计程只有三百余里。",1,"","","曹操",103,},
        [4187] = {8103,2,1,"曹操",103,0,"精选五千铁骑，星夜前进，限一日一夜，赶上刘备。","精选五千铁骑，星夜前进，限一日一夜，赶上刘备。",1,"","","于禁",119,},
        [4188] = {8103,3,1,"曹操",103,0,"大军陆续随后而进。","大军陆续随后而进。",1,"","","于禁",119,},
        [4189] = {8104,1,1,"刘备",202,0,"狂风起，尘土冲天，平遮红日，此何兆也？","狂风起，尘土冲天，平遮红日，此何兆也？",1,"","","",0,},
        [4190] = {8104,2,2,"简雍",237,0,"此大凶之兆也。应在今夜。主公可速弃百姓而走。","此大凶之兆也。应在今夜。主公可速弃百姓而走。",1,"","","刘备",202,},
        [4191] = {8104,3,1,"刘备",202,0,"百姓从新野相随至此，吾安忍弃之？","百姓从新野相随至此，吾安忍弃之？",1,"","","简雍",237,},
        [4192] = {8104,4,2,"简雍",237,0,"主公若恋而不弃，祸不远矣。","主公若恋而不弃，祸不远矣。",1,"","","刘备",202,},
        [4193] = {8104,5,1,"刘备",202,0,"前面是何处？","前面是何处？",1,"","","简雍",237,},
        [4194] = {8104,6,2,"简雍",237,0,"前面是当阳县。有座山名为景山。","前面是当阳县。有座山名为景山。",1,"","","刘备",202,},
        [4195] = {8104,7,1,"刘备",202,0,"就此山扎住。","就此山扎住。",1,"","","简雍",237,},
        [4196] = {8105,1,2,"糜芳",133,0,"赵子龙反投曹操去了也！","赵子龙反投曹操去了也！",1,"","","刘备",202,},
        [4197] = {8105,2,1,"刘备",202,0,"子龙是我故交，安肯反乎？","子龙是我故交，安肯反乎？",1,"","","糜芳",133,},
        [4198] = {8105,3,2,"张飞",206,0,"他今见我等势穷力尽，或者反投曹操，以图富贵耳！","他今见我等势穷力尽，或者反投曹操，以图富贵耳！",1,"","","刘备",202,},
        [4199] = {8105,4,1,"刘备",202,0,"子龙从我于患难，心如铁石，非富贵所能动摇也。","子龙从我于患难，心如铁石，非富贵所能动摇也。",1,"","","张飞",206,},
        [4200] = {8105,5,2,"糜芳",133,0,"我亲见他投西北去了。","我亲见他投西北去了。",1,"","","刘备",202,},
        [4201] = {8105,6,2,"张飞",206,0,"待我亲自寻他去。若撞见时，一枪刺死！","待我亲自寻他去。若撞见时，一枪刺死！",1,"","","刘备",202,},
        [4202] = {8105,7,1,"刘备",202,0,"休错疑了。岂不见你二兄诛颜良、文丑之事乎？子龙此去，必有事故。吾料子龙必不弃我也。","休错疑了。岂不见你二兄诛颜良、文丑之事乎？子龙此去，必有事故。吾料子龙必不弃我也。",1,"","","张飞",206,},
        [4203] = {8105,8,2,"张飞",206,0,"我且领兵长坂桥上候着，看看子龙回不回来。","我且领兵长坂桥上候着，看看子龙回不回来。",1,"","","刘备",202,},
        [4204] = {8106,1,1,"赵云",201,0,"主公将甘、糜二夫人与小主人阿斗，托付在我身上。","主公将甘、糜二夫人与小主人阿斗，托付在我身上。",1,"","","",0,},
        [4205] = {8106,2,1,"赵云",201,0,"今日军中失散，有何面目去见主人？不如去决一死战，好歹要寻主母与小主人下落！","今日军中失散，有何面目去见主人？不如去决一死战，好歹要寻主母与小主人下落！",1,"","","",0,},
        [4206] = {8107,1,1,"赵云",201,0,"曾见两位主母否？","曾见两位主母否？",1,"","","简雍",237,},
        [4207] = {8107,2,2,"简雍",237,0,"二主母弃了车仗，抱阿斗而走。","二主母弃了车仗，抱阿斗而走。",1,"","","赵云",201,},
        [4208] = {8107,3,2,"简雍",237,0,"我飞马赶去，转过山坡，被一将刺了一枪，跌下马来，马被夺了去。","我飞马赶去，转过山坡，被一将刺了一枪，跌下马来，马被夺了去。",1,"","","赵云",201,},
        [4209] = {8107,4,2,"简雍",237,0,"我只见甘夫人披头跣足，相随一伙百姓妇女，投南而走。","我只见甘夫人披头跣足，相随一伙百姓妇女，投南而走。",1,"","","赵云",201,},
        [4210] = {8107,5,1,"赵云",201,0,"你骑我从骑之马回报玄德，我上天入地，好歹寻主母与小主人来。","你骑我从骑之马回报玄德，我上天入地，好歹寻主母与小主人来。",1,"","","简雍",237,},
        [4211] = {8107,6,1,"赵云",201,0,"如寻不见，死在沙场上也！","如寻不见，死在沙场上也！",1,"","","简雍",237,},
        [4212] = {8108,1,2,"简雍",237,0,"乱军之中子龙相救，让我回报主公。","乱军之中子龙相救，让我回报主公。",1,"","","刘备",202,},
        [4213] = {8108,2,2,"简雍",237,0,"子龙上天入地，寻二主母与小主人来，如寻不见，便死在沙场之上！","子龙上天入地，寻二主母与小主人来，如寻不见，便死在沙场之上！",1,"","","刘备",202,},
        [4214] = {8108,3,1,"刘备",202,0,"子龙此去凶险，该如何是好？","子龙此去凶险，该如何是好？",1,"","","简雍",237,},
        [4215] = {8108,4,2,"主角",1,0,"玄德莫怕，我去援助子龙，寻二主母与阿斗。","玄德莫怕，我去援助子龙，寻二主母与阿斗。",1,"","","刘备",202,},
        [4216] = {8108,5,1,"刘备",202,0,"好，四弟多加小心。","好，四妹多加小心。",1,"","","主角",1,},
        [4217] = {8109,1,1,"赵云",201,0,"百姓当中，可有甘夫人否？","百姓当中，可有甘夫人否？",1,"","","",0,},
        [4218] = {8109,2,2,"甘夫人",227,0,"子龙！","子龙！",1,"","","赵云",201,},
        [4219] = {8109,3,1,"赵云",201,0,"使主母失散，云之罪也！糜夫人与小主人安在？","使主母失散，云之罪也！糜夫人与小主人安在？",1,"","","甘夫人",227,},
        [4220] = {8109,4,2,"甘夫人",227,0,"我与糜夫人被逐，弃了车仗，杂于百姓内步行，又撞见一枝军马冲散。","我与糜夫人被逐，弃了车仗，杂于百姓内步行，又撞见一枝军马冲散。",1,"","","赵云",201,},
        [4221] = {8109,5,2,"甘夫人",227,0,"糜夫人与阿斗不知何往。我独自逃生至此。","糜夫人与阿斗不知何往。我独自逃生至此。",1,"","","赵云",201,},
        [4222] = {8109,6,1,"赵云",201,0,"甘夫人上马，我杀开条血路，送你至长坂城。","甘夫人上马，我杀开条血路，送你至长坂城。",1,"","","甘夫人",227,},
        [4223] = {8110,1,2,"张飞",206,0,"子龙！你如何反我哥哥？","子龙！你如何反我哥哥？",1,"","","赵云",201,},
        [4224] = {8110,2,1,"赵云",201,0,"我寻不见主母与小主人，因此落后，何言反耶？","我寻不见主母与小主人，因此落后，何言反耶？",1,"","","张飞",206,},
        [4225] = {8110,3,2,"张飞",206,0,"若非简雍先来报信，我今见你，怎肯干休也！","若非简雍先来报信，我今见你，怎肯干休也！",1,"","","赵云",201,},
        [4226] = {8110,4,1,"赵云",201,0,"主公在何处？","主公在何处？",1,"","","张飞",206,},
        [4227] = {8110,5,2,"张飞",206,0,"只在前面不远。","只在前面不远。",1,"","","赵云",201,},
        [4228] = {8110,6,1,"赵云",201,0,"保甘夫人先行，待我仍往寻糜夫人与小主人去。","保甘夫人先行，待我仍往寻糜夫人与小主人去。",1,"","","张飞",206,},
        [4229] = {8110,7,2,"张飞",206,0,"子龙多加小心，扬威将军方才杀入敌阵之中，也寻那二主母与阿斗去了。","子龙多加小心，扬威将军方才杀入敌阵之中，也寻那二主母与阿斗去了。",1,"","","赵云",201,},
        [4230] = {8110,8,1,"赵云",201,0,"好，我这就去寻扬威将军一起搭救二主母与阿斗。","好，我这就去寻扬威将军一起搭救二主母与阿斗。",1,"","","张飞",206,},
        [4231] = {8111,1,1,"赵云",201,0,"子龙来迟。","子龙来迟。",1,"","","糜夫人",228,},
        [4232] = {8111,2,2,"糜夫人",228,0,"妾得见将军，阿斗有命矣。望将军可怜他父亲飘荡半世，只有这点骨血。","妾得见将军，阿斗有命矣。望将军可怜他父亲飘荡半世，只有这点骨血。",1,"","","赵云",201,},
        [4233] = {8111,3,2,"糜夫人",228,0,"将军可护持此子，教他得见父面，妾死无恨！","将军可护持此子，教他得见父面，妾死无恨！",1,"","","赵云",201,},
        [4234] = {8111,4,1,"赵云",201,0,"夫人受难，云之罪也。不必多言，请夫人上马。云自步行死战，保夫人透出重围。","夫人受难，云之罪也。不必多言，请夫人上马。云自步行死战，保夫人透出重围。",1,"","","糜夫人",228,},
        [4235] = {8111,5,2,"糜夫人",228,0,"不可！将军岂可无马！此子全赖将军保护。妾已重伤，死何足惜！","不可！将军岂可无马！此子全赖将军保护。妾已重伤，死何足惜！",1,"","","赵云",201,},
        [4236] = {8111,6,2,"糜夫人",228,0,"望将军速抱此子前去，勿以妾为累也。","望将军速抱此子前去，勿以妾为累也。",1,"","","赵云",201,},
        [4237] = {8111,7,1,"赵云",201,0,"喊声将近，追兵已至，请夫人速速上马。","喊声将近，追兵已至，请夫人速速上马。",1,"","","糜夫人",228,},
        [4238] = {8111,8,2,"糜夫人",228,0,"妾身委实难去。休得两误。","妾身委实难去。休得两误。",1,"","","赵云",201,},
        [4239] = {8111,9,2,"糜夫人",228,0,"此子性命全在将军身上！（糜夫人将阿斗递与赵云。）","此子性命全在将军身上！（糜夫人将阿斗递与赵云。）",1,"","","赵云",201,},
        [4240] = {8111,10,1,"赵云",201,0,"夫人不听吾言，追军若至，为之奈何？","夫人不听吾言，追军若至，为之奈何？",1,"","","糜夫人",228,},
        [4241] = {8111,11,2,"糜夫人",228,0,"阿斗就托付给你了。（糜夫人弃阿斗于地，翻身投入枯井中而死。）","阿斗就托付给你了。（糜夫人弃阿斗于地，翻身投入枯井中而死。）",1,"","","赵云",201,},
        [4242] = {8111,12,1,"赵云",201,0,"糜夫人！（赵云见夫人已死，恐曹军盗尸，便将土墙推倒掩盖枯井。解开勒甲绦，将阿斗抱护在怀，绰枪上马而去）。","糜夫人！（赵云见夫人已死，恐曹军盗尸，便将土墙推倒掩盖枯井。解开勒甲绦，将阿斗抱护在怀，绰枪上马而去）。",1,"","","糜夫人",228,},
        [4243] = {8112,1,1,"主角",1,0,"子龙！你可寻得二主母与阿斗?","子龙！你可寻得二主母与阿斗?",1,"","","赵云",201,},
        [4244] = {8112,2,2,"赵云",201,0,"甘夫人已安全渡过长板桥。","甘夫人已安全渡过长板桥。",1,"","","主角",1,},
        [4245] = {8112,3,2,"赵云",201,0,"阿斗在我怀中，糜夫人…","阿斗在我怀中，糜夫人…",1,"","","主角",1,},
        [4246] = {8112,4,1,"主角",1,0,"糜夫人怎么了？","糜夫人怎么了？",1,"","","赵云",201,},
        [4247] = {8112,5,2,"赵云",201,0,"糜夫人身受重伤，不愿拖累，翻身投入枯井之中…","糜夫人身受重伤，不愿拖累，翻身投入枯井之中…",1,"","","主角",1,},
        [4248] = {8112,6,1,"主角",1,0,"拚将一死存刘嗣，勇决还亏女丈夫。","拚将一死存刘嗣，勇决还亏女丈夫。",1,"","","赵云",201,},
        [4249] = {8112,7,1,"主角",1,0,"子龙，快！一起杀出曹阵，不枉糜夫人之托。","子龙，快！一起杀出曹阵，不枉糜夫人之托。",1,"","","赵云",201,},
        [4250] = {8201,1,1,"赵云",201,0,"后有追兵，翼德援我！","后有追兵，翼德援我！",1,"","","张飞",206,},
        [4251] = {8201,2,2,"张飞",206,0,"子龙速行，追兵我自当之。","子龙速行，追兵我自当之。",1,"","","赵云",201,},
        [4252] = {8202,1,1,"赵云",201,0,"赵云之罪，万死犹轻！糜夫人身带重伤，不肯上马，投井而死，云只得推土墙掩之。","赵云之罪，万死犹轻！糜夫人身带重伤，不肯上马，投井而死，云只得推土墙掩之。",1,"","","刘备",202,},
        [4253] = {8202,2,1,"赵云",201,0,"怀抱公子，身突重围，得扬威将军相助而脱险。","怀抱公子，身突重围，得扬威将军相助而脱险。",1,"","","刘备",202,},
        [4254] = {8202,3,1,"赵云",201,0,"幸得公子无恙！（赵云双手将阿斗递与玄德。）","幸得公子无恙！（赵云双手将阿斗递与玄德。）",1,"","","刘备",202,},
        [4255] = {8202,4,2,"刘备",202,0,"为此竖子，几损扬威将军与子龙！（玄德接过阿斗便掷之于地。）","为此竖子，几损扬威将军与子龙！（玄德接过阿斗便掷之于地。）",1,"","","赵云",201,},
        [4256] = {8202,5,1,"赵云",201,0,"云虽肝脑涂地，不能报也！（赵云连忙抱起阿斗。）","云虽肝脑涂地，不能报也！（赵云连忙抱起阿斗。）",1,"","","刘备",202,},
        [4257] = {8203,1,2,"乐进",113,0,"敌将张飞倒竖虎须，圆睁环眼，手绰蛇矛，立马长板桥上！","敌将张飞倒竖虎须，圆睁环眼，手绰蛇矛，立马长板桥上！",1,"","","曹操",103,},
        [4258] = {8203,2,2,"乐进",113,0,"桥东树林之后，尘头大起，恐是诸葛孔明之计。","桥东树林之后，尘头大起，恐是诸葛孔明之计。",1,"","","曹操",103,},
        [4259] = {8203,3,1,"曹操",103,0,"孤曾闻云长言：翼德于百万军中，取上将之首，如探囊取物。","孤曾闻云长言：翼德于百万军中，取上将之首，如探囊取物。",1,"","","乐进",113,},
        [4260] = {8203,4,1,"曹操",103,0,"今日相逢，不管是不是诸葛孔明之计，都不可轻敌。","今日相逢，不管是不是诸葛孔明之计，都不可轻敌。",1,"","","乐进",113,},
        [4261] = {8204,1,2,"张飞",206,0,"燕人张翼德在此！谁敢来决死战？","燕人张翼德在此！谁敢来决死战？",1,"","","曹操",103,},
        [4262] = {8204,2,2,"张飞",206,0,"战又不战，退又不退，却是何故！","战又不战，退又不退，却是何故！",1,"","","曹操",103,},
        [4263] = {8204,3,1,"曹操",103,0,"诸军众将一齐望西奔走，绕过长板桥。","诸军众将一齐望西奔走，绕过长板桥。",1,"","","张飞",206,},
        [4264] = {8205,1,2,"张辽",109,0,"丞相休惊。料张飞一人，何足深惧！今急回军杀去，刘备可擒也。","丞相休惊。料张飞一人，何足深惧！今急回军杀去，刘备可擒也。",1,"","","曹操",103,},
        [4265] = {8205,2,1,"曹操",103,0,"你速与许褚再至长坂桥探听消息。","你速与许褚再至长坂桥探听消息。",1,"","","张辽",109,},
        [4266] = {8206,1,2,"张辽",109,0,"张飞已拆断桥梁而去矣。","张飞已拆断桥梁而去矣。",1,"","","曹操",103,},
        [4267] = {8206,2,1,"曹操",103,0,"彼断桥而去，乃心怯也。","彼断桥而去，乃心怯也。",1,"","","张辽",109,},
        [4268] = {8206,3,1,"曹操",103,0,"传令差人速搭三座浮桥，今夜就要过河。","传令差人速搭三座浮桥，今夜就要过河。",1,"","","张辽",109,},
        [4269] = {8206,4,2,"李典",124,0,"此恐是诸葛亮之诈谋，不可轻进。","此恐是诸葛亮之诈谋，不可轻进。",1,"","","曹操",103,},
        [4270] = {8206,5,1,"曹操",103,0,"张飞一勇之夫，岂有诈谋！","张飞一勇之夫，岂有诈谋！",1,"","","李典",124,},
        [4271] = {8207,1,1,"刘备",202,0,"前有大江，后有追兵，如之奈何？","前有大江，后有追兵，如之奈何？",1,"","","主角",1,},
        [4272] = {8207,2,2,"主角",1,0,"莫忧，有我与子龙在！","莫忧，有我与子龙在！",1,"","","刘备",202,},
        [4273] = {8208,1,1,"曹操",103,0,"今刘备釜中之鱼，阱中之虎，若不就此时擒捉，如放鱼入海，纵虎归山矣。","今刘备釜中之鱼，阱中之虎，若不就此时擒捉，如放鱼入海，纵虎归山矣。",1,"","","",0,},
        [4274] = {8208,2,1,"曹操",103,0,"众将可努力向前。","众将可努力向前。",1,"","","",0,},
        [4275] = {8208,3,2,"关羽",205,0,"我在此等候多时了！（关羽去江夏借得军马一万，探知当阳长坂大战，特地从此路截出。）","我在此等候多时了！（关羽去江夏借得军马一万，探知当阳长坂大战，特地从此路截出。）",1,"","","曹操",103,},
        [4276] = {8208,4,1,"曹操",103,0,"又中诸葛亮之计也！","又中诸葛亮之计也！",1,"","","关羽",205,},
        [4277] = {8208,5,1,"曹操",103,0,"传令大军速退。","传令大军速退。",1,"","","关羽",205,},
        [4278] = {8301,1,1,"曹操",103,0,"今刘备已投江夏，恐结连东吴，是滋蔓也，当用何计破之？","今刘备已投江夏，恐结连东吴，是滋蔓也，当用何计破之？",1,"","","",0,},
        [4279] = {8301,2,2,"荀攸",120,0,"我今大振兵威，遣使驰檄江东，请孙权会猎于江夏，共擒刘备，分荆州之地，永结盟好。","我今大振兵威，遣使驰檄江东，请孙权会猎于江夏，共擒刘备，分荆州之地，永结盟好。",1,"","","曹操",103,},
        [4280] = {8301,3,2,"荀攸",120,0,"孙权必惊疑而来降，则吾事济矣。","孙权必惊疑而来降，则吾事济矣。",1,"","","曹操",103,},
        [4281] = {8301,4,1,"曹操",103,0,"好，就依你说言，速遣使赴东吴。","好，就依你说言，速遣使赴东吴。",1,"","","荀攸",120,},
        [4282] = {8302,1,2,"鲁肃",312,0,"荆州与国邻接，江山险固，士民殷富。吾若据而有之，此帝王之资也。","荆州与国邻接，江山险固，士民殷富。吾若据而有之，此帝王之资也。",1,"","","孙权",306,},
        [4283] = {8302,2,2,"鲁肃",312,0,"今刘表新亡，刘备新败，肃请奉命往江夏吊丧，因说刘备使抚刘表众将，同心一意，共破曹操。","今刘表新亡，刘备新败，肃请奉命往江夏吊丧，因说刘备使抚刘表众将，同心一意，共破曹操。",1,"","","孙权",306,},
        [4284] = {8302,3,2,"鲁肃",312,0,"备若喜而从命，则大事可定矣。","备若喜而从命，则大事可定矣。",1,"","","孙权",306,},
        [4285] = {8302,4,1,"孙权",306,0,"好，你速去江夏。","好，你速去江夏。",1,"","","鲁肃",312,},
        [4286] = {8303,1,2,"刘备",202,0,"曹军今又星夜兼道取江陵，今后该如何应对？","曹军今又星夜兼道取江陵，今后该如何应对？",1,"","","诸葛亮",203,},
        [4287] = {8303,2,1,"诸葛亮",203,0,"曹操势大，急难抵敌，不如往投东吴孙权，以为应援。使南北相持，吾等于中取利，有何不可？","曹操势大，急难抵敌，不如往投东吴孙权，以为应援。使南北相持，吾等于中取利，有何不可？",1,"","","刘备",202,},
        [4288] = {8303,3,2,"刘备",202,0,"江东人物极多，必有远谋，安肯相容耶？","江东人物极多，必有远谋，安肯相容耶？",1,"","","诸葛亮",203,},
        [4289] = {8303,4,1,"诸葛亮",203,0,"今操引百万之众，虎踞江汉，江东安得不使人来探听虚实？","今操引百万之众，虎踞江汉，江东安得不使人来探听虚实？",1,"","","刘备",202,},
        [4290] = {8303,5,1,"诸葛亮",203,0,"若有人到此，亮借一帆风，直至江东，凭三寸不烂之舌，说南北两军互相吞并。","若有人到此，亮借一帆风，直至江东，凭三寸不烂之舌，说南北两军互相吞并。",1,"","","刘备",202,},
        [4291] = {8303,6,1,"诸葛亮",203,0,"若南军胜，共诛曹操以取荆州之地，若北军胜，则我乘势以取江南可也。","若南军胜，共诛曹操以取荆州之地，若北军胜，则我乘势以取江南可也。",1,"","","刘备",202,},
        [4292] = {8303,7,2,"刘备",202,0,"此论甚高。但如何得江东人到？","此论甚高。但如何得江东人到？",1,"","","诸葛亮",203,},
        [4293] = {8303,8,1,"诸葛亮",203,0,"江东人不久后便会自来。","江东人不久后便会自来。",1,"","","刘备",202,},
        [4294] = {8304,1,2,"刘备",202,0,"江东孙权差鲁肃来吊丧，船已傍岸。","江东孙权差鲁肃来吊丧，船已傍岸。",1,"","","诸葛亮",203,},
        [4295] = {8304,2,1,"诸葛亮",203,0,"大事济矣！","大事济矣！",1,"","","刘备",202,},
        [4296] = {8304,3,1,"诸葛亮",203,0,"往日孙策亡时，襄阳曾遣人去吊丧否？","往日孙策亡时，襄阳曾遣人去吊丧否？",1,"","","刘备",202,},
        [4297] = {8304,4,2,"刘琦",128,0,"江东与我家有杀父之仇，安得通庆吊之礼！","江东与我家有杀父之仇，安得通庆吊之礼！",1,"","","诸葛亮",203,},
        [4298] = {8304,5,1,"诸葛亮",203,0,"然则鲁肃之来，非为吊丧，乃来探听军情也。","然则鲁肃之来，非为吊丧，乃来探听军情也。",1,"","","刘琦",128,},
        [4299] = {8304,6,1,"诸葛亮",203,0,"鲁肃至，若问曹操动静，主公只推不知，再三问时，主公只说可问诸葛亮。","鲁肃至，若问曹操动静，主公只推不知，再三问时，主公只说可问诸葛亮。",1,"","","刘琦",128,},
        [4300] = {8304,7,2,"刘备",202,0,"好。","好。",1,"","","诸葛亮",203,},
        [4301] = {8305,1,2,"鲁肃",312,0,"久闻皇叔大名，无缘拜会，今幸得见，实为欣慰。","久闻皇叔大名，无缘拜会，今幸得见，实为欣慰。",1,"","","刘备",202,},
        [4302] = {8305,2,2,"鲁肃",312,0,"近闻皇叔与曹操会战，必知彼虚实，敢问操军约有几何？","近闻皇叔与曹操会战，必知彼虚实，敢问操军约有几何？",1,"","","刘备",202,},
        [4303] = {8305,3,1,"刘备",202,0,"备兵微将寡，一闻操至即走，竟不知彼虚实。","备兵微将寡，一闻操至即走，竟不知彼虚实。",1,"","","鲁肃",312,},
        [4304] = {8305,4,2,"鲁肃",312,0,"闻皇叔用诸葛孔明之谋，两场火烧得曹操魂亡胆落，何言不知耶？","闻皇叔用诸葛孔明之谋，两场火烧得曹操魂亡胆落，何言不知耶？",1,"","","刘备",202,},
        [4305] = {8305,5,1,"刘备",202,0,"徐非问孔明，便知其详。","徐非问孔明，便知其详。",1,"","","鲁肃",312,},
        [4306] = {8305,6,2,"鲁肃",312,0,"孔明安在？愿求一见。","孔明安在？愿求一见。",1,"","","刘备",202,},
        [4307] = {8305,7,1,"刘备",202,0,"吾这就请孔明出来相见。","吾这就请孔明出来相见。",1,"","","鲁肃",312,},
        [4308] = {8306,1,2,"鲁肃",312,0,"向慕先生才德，未得拜晤，今幸相遇，愿闻目今安危之事。","向慕先生才德，未得拜晤，今幸相遇，愿闻目今安危之事。",1,"","","诸葛亮",203,},
        [4309] = {8306,2,1,"诸葛亮",203,0,"曹操奸计，亮已尽知，但恨力未及，故且避之。","曹操奸计，亮已尽知，但恨力未及，故且避之。",1,"","","鲁肃",312,},
        [4310] = {8306,3,2,"鲁肃",312,0,"皇叔今将止于此乎？","皇叔今将止于此乎？",1,"","","诸葛亮",203,},
        [4311] = {8306,4,1,"诸葛亮",203,0,"使君与苍梧太守吴臣有旧，将往投之。","使君与苍梧太守吴臣有旧，将往投之。",1,"","","鲁肃",312,},
        [4312] = {8306,5,2,"鲁肃",312,0,"吴臣粮少兵微，自不能保，焉能容人？","吴臣粮少兵微，自不能保，焉能容人？",1,"","","诸葛亮",203,},
        [4313] = {8306,6,1,"诸葛亮",203,0,"吴臣处虽不足久居，今且暂依之，别有良图。","吴臣处虽不足久居，今且暂依之，别有良图。",1,"","","鲁肃",312,},
        [4314] = {8306,7,2,"鲁肃",312,0,"孙将军虎踞六郡，兵精粮足，又极敬贤礼士，江表英雄，多归附之。","孙将军虎踞六郡，兵精粮足，又极敬贤礼士，江表英雄，多归附之。",1,"","","诸葛亮",203,},
        [4315] = {8306,8,2,"鲁肃",312,0,"今为君计，莫若遣心腹往结东吴，以共图大事。","今为君计，莫若遣心腹往结东吴，以共图大事。",1,"","","诸葛亮",203,},
        [4316] = {8306,9,1,"诸葛亮",203,0,"刘使君与孙将军自来无旧，恐虚费词说。且别无心腹之人可使。","刘使君与孙将军自来无旧，恐虚费词说。且别无心腹之人可使。",1,"","","鲁肃",312,},
        [4317] = {8306,10,2,"鲁肃",312,0,"先生之兄，现为江东参谋，日望与先生相见。肃不才，愿与公同见孙将军，共议大事。","先生之兄，现为江东参谋，日望与先生相见。肃不才，愿与公同见孙将军，共议大事。",1,"","","诸葛亮",203,},
        [4318] = {8306,11,1,"刘备",202,0,"孔明是吾之师，顷刻不可相离，安可去也？","孔明是吾之师，顷刻不可相离，安可去也？",1,"","","鲁肃",312,},
        [4319] = {8306,12,2,"鲁肃",312,0,"皇叔何不与孔明一同前往。","皇叔何不与孔明一同前往。",1,"","","刘备",202,},
        [4320] = {8306,13,1,"刘备",202,0,"这个…","这个…",1,"","","鲁肃",312,},
        [4321] = {8306,14,2,"主角",1,0,"此去东吴，怎能劳烦大哥，吾与孔明一同前去便是。","此去东吴，怎能劳烦大哥，吾与孔明一同前去便是。",1,"","","刘备",202,},
        [4322] = {8306,15,1,"刘备",202,0,"那劳烦四弟了。","那劳烦四妹了。",1,"","","主角",1,},
        [4323] = {8307,1,1,"孙权",306,0,"子敬往江夏，体探虚实若何？","子敬往江夏，体探虚实若何？",1,"","","鲁肃",312,},
        [4324] = {8307,2,2,"鲁肃",312,0,"已知其略，尚容徐禀。","已知其略，尚容徐禀。",1,"","","孙权",306,},
        [4325] = {8307,3,1,"孙权",306,0,"操昨遣使赍文至此，孤先发遣来使，现今会众商议未定。","操昨遣使赍文至此，孤先发遣来使，现今会众商议未定。",1,"","","鲁肃",312,},
        [4326] = {8307,4,2,"鲁肃",312,0,"曹操何意？","曹操何意？",1,"","","孙权",306,},
        [4327] = {8307,5,1,"孙权",306,0,"欲与东吴会猎于江夏，共伐刘备，同分土地，永结盟好。","欲与东吴会猎于江夏，共伐刘备，同分土地，永结盟好。",1,"","","鲁肃",312,},
        [4328] = {8307,6,2,"鲁肃",312,0,"主公尊意若何？","主公尊意若何？",1,"","","孙权",306,},
        [4329] = {8307,7,1,"孙权",306,0,"未有定论。","未有定论。",1,"","","鲁肃",312,},
        [4330] = {8307,8,2,"张昭",315,0,"曹操拥百万之众，借天子之名，以征四方，拒之不顺。且主公大势可以拒操者，长江也。","曹操拥百万之众，借天子之名，以征四方，拒之不顺。且主公大势可以拒操者，长江也。",1,"","","孙权",306,},
        [4331] = {8307,9,2,"张昭",315,0,"今操既得荆州，长江之险，已与我共之矣，势不可敌。以愚之计，不如纳降，为万安之策。","今操既得荆州，长江之险，已与我共之矣，势不可敌。以愚之计，不如纳降，为万安之策。",1,"","","孙权",306,},
        [4332] = {8307,10,1,"孙权",306,0,"这个…","这个…",1,"","","张昭",315,},
        [4333] = {8307,11,2,"张昭",315,0,"主公不必多疑。如降操，则东吴民安，江南六郡可保矣。","主公不必多疑。如降操，则东吴民安，江南六郡可保矣。",1,"","","孙权",306,},
        [4334] = {8307,12,1,"孙权",306,0,"这个…","这个…",1,"","","张昭",315,},
        [4335] = {8308,1,2,"鲁肃",312,0,"众人皆可降曹操，惟将军不可降曹操。","众人皆可降曹操，惟将军不可降曹操。",1,"","","孙权",306,},
        [4336] = {8308,2,1,"孙权",306,0,"何以言之？","何以言之？",1,"","","鲁肃",312,},
        [4337] = {8308,3,2,"鲁肃",312,0,"如肃等降操，当以肃还乡党，累官故不失州郡也。","如肃等降操，当以肃还乡党，累官故不失州郡也。",1,"","","孙权",306,},
        [4338] = {8308,4,2,"鲁肃",312,0,"将军降操，欲安所归乎？位不过封侯，车不过一乘，骑不过一匹，从不过数人，岂得南面称孤哉！","将军降操，欲安所归乎？位不过封侯，车不过一乘，骑不过一匹，从不过数人，岂得南面称孤哉！",1,"","","孙权",306,},
        [4339] = {8308,5,2,"鲁肃",312,0,"众人之意，各自为己，不可听也。将军宜早定大计。","众人之意，各自为己，不可听也。将军宜早定大计。",1,"","","孙权",306,},
        [4340] = {8308,6,1,"孙权",306,0,"诸人议论，大失孤望。子敬开说大计，正与吾见相同。","诸人议论，大失孤望。子敬开说大计，正与吾见相同。",1,"","","鲁肃",312,},
        [4341] = {8308,7,1,"孙权",306,0,"此天以子敬赐我也！但操新得袁绍之众，近又得荆州之兵，恐势大难以抵敌。","此天以子敬赐我也！但操新得袁绍之众，近又得荆州之兵，恐势大难以抵敌。",1,"","","鲁肃",312,},
        [4342] = {8308,8,2,"鲁肃",312,0,"肃至江夏，引诸葛瑾之弟诸葛亮在此，主公可问之，便知虚实。","肃至江夏，引诸葛瑾之弟诸葛亮在此，主公可问之，便知虚实。",1,"","","孙权",306,},
        [4343] = {8308,9,1,"孙权",306,0,"卧龙先生在此乎？","卧龙先生在此乎？",1,"","","鲁肃",312,},
        [4344] = {8308,10,2,"鲁肃",312,0,"现在馆驿中安歇。","现在馆驿中安歇。",1,"","","孙权",306,},
        [4345] = {8308,11,1,"孙权",306,0,"今日天晚，且未相见。来日聚文武于帐下，先教见我江东英俊，然后升堂议事。","今日天晚，且未相见。来日聚文武于帐下，先教见我江东英俊，然后升堂议事。",1,"","","鲁肃",312,},
        [4346] = {8309,1,2,"张昭",315,0,"昭乃江东微末之士，久闻先生高卧隆中，自比管、乐，此语果有之乎？","昭乃江东微末之士，久闻先生高卧隆中，自比管、乐，此语果有之乎？",1,"","","诸葛亮",203,},
        [4347] = {8309,2,1,"诸葛亮",203,0,"此亮平生小可之比也。","此亮平生小可之比也。",1,"","","张昭",315,},
        [4348] = {8309,3,2,"张昭",315,0,"近闻刘豫州三顾先生于草庐之中，幸得先生，以为如鱼得水，思欲席卷荆襄。今一旦以属曹操，未审是何主见？","近闻刘豫州三顾先生于草庐之中，幸得先生，以为如鱼得水，思欲席卷荆襄。今一旦以属曹操，未审是何主见？",1,"","","诸葛亮",203,},
        [4349] = {8309,4,1,"诸葛亮",203,0,"吾观取汉上之地，易如反掌。我主刘豫州躬行仁义，不忍夺同宗之基业，故力辞之。","吾观取汉上之地，易如反掌。我主刘豫州躬行仁义，不忍夺同宗之基业，故力辞之。",1,"","","张昭",315,},
        [4350] = {8309,5,1,"诸葛亮",203,0,"刘琮孺子，听信佞言，暗自投降，致使曹操得以猖獗。今我主屯兵江夏，别有良图，非等闲可知也。","刘琮孺子，听信佞言，暗自投降，致使曹操得以猖獗。今我主屯兵江夏，别有良图，非等闲可知也。",1,"","","张昭",315,},
        [4351] = {8309,6,2,"张昭",315,0,"若此，是先生言行相违也。今既从事刘豫州，当为生灵兴利除害，剿灭乱贼。","若此，是先生言行相违也。今既从事刘豫州，当为生灵兴利除害，剿灭乱贼。",1,"","","诸葛亮",203,},
        [4352] = {8309,7,2,"张昭",315,0,"何先生自归豫州，曹兵一出，弃甲抛戈，望风而窜，上不能报刘表以安庶民，下不能辅孤子而据疆土。","何先生自归豫州，曹兵一出，弃甲抛戈，望风而窜，上不能报刘表以安庶民，下不能辅孤子而据疆土。",1,"","","诸葛亮",203,},
        [4353] = {8309,8,2,"张昭",315,0,"弃新野，走樊城，败当阳，奔夏口，无容身之地。是豫州既得先生之后，反不如其初也。管仲、乐毅，果如是乎？","弃新野，走樊城，败当阳，奔夏口，无容身之地。是豫州既得先生之后，反不如其初也。管仲、乐毅，果如是乎？",1,"","","诸葛亮",203,},
        [4354] = {8309,9,1,"诸葛亮",203,0,"吾主刘豫州，向日军败于汝南，寄迹刘表，兵不满千，将止关、张、赵云而已，豫州不过暂借以容身，岂真将坐守于此耶？","吾主刘豫州，向日军败于汝南，寄迹刘表，兵不满千，将止关、张、赵云而已，豫州不过暂借以容身，岂真将坐守于此耶？",1,"","","张昭",315,},
        [4355] = {8309,10,1,"诸葛亮",203,0,"夫以甲兵不完，城郭不固，军不经练，粮不继日，然而博望烧屯，白河用水，使夏侯惇，曹仁辈心惊胆裂。","夫以甲兵不完，城郭不固，军不经练，粮不继日，然而博望烧屯，白河用水，使夏侯惇，曹仁辈心惊胆裂。",1,"","","张昭",315,},
        [4356] = {8309,11,1,"诸葛亮",203,0,"至于刘琮降操，豫州实出不知，且又不忍乘乱夺同宗之基业，此真大仁大义也。","至于刘琮降操，豫州实出不知，且又不忍乘乱夺同宗之基业，此真大仁大义也。",1,"","","张昭",315,},
        [4357] = {8309,12,1,"诸葛亮",203,0,"当阳之败，豫州见有数十万赴义之民，扶老携幼相随，不忍弃之，日行十里，不思进取江陵，甘与同败，此亦大仁大义也。","当阳之败，豫州见有数十万赴义之民，扶老携幼相随，不忍弃之，日行十里，不思进取江陵，甘与同败，此亦大仁大义也。",1,"","","张昭",315,},
        [4358] = {8309,13,2,"张昭",315,0,"这个…","这个…",1,"","","诸葛亮",203,},
        [4359] = {8310,1,2,"孙权",306,0,"先生在新野，佐刘豫州与曹操决战，必深知彼军虚实。曹兵共有多少？曹操部下战将还有多少？","先生在新野，佐刘豫州与曹操决战，必深知彼军虚实。曹兵共有多少？曹操部下战将还有多少？",1,"","","诸葛亮",203,},
        [4360] = {8310,2,1,"诸葛亮",203,0,"马步水军，约有一百余万。足智多谋之士，能征惯战之将，何止一二千人。","马步水军，约有一百余万。足智多谋之士，能征惯战之将，何止一二千人。",1,"","","孙权",306,},
        [4361] = {8310,3,2,"孙权",306,0,"今曹操平了荆、楚，复有远图乎？","今曹操平了荆、楚，复有远图乎？",1,"","","诸葛亮",203,},
        [4362] = {8310,4,1,"诸葛亮",203,0,"即今沿江下寨，准备战船，不欲图江东，待取何地？","即今沿江下寨，准备战船，不欲图江东，待取何地？",1,"","","孙权",306,},
        [4363] = {8310,5,2,"孙权",306,0,"若彼有吞并之意，战与不战，请足下为我一决。","若彼有吞并之意，战与不战，请足下为我一决。",1,"","","诸葛亮",203,},
        [4364] = {8310,6,1,"诸葛亮",203,0,"向者宇内大乱，故将军起江东，刘豫州收众汉南，与曹操并争天下。","向者宇内大乱，故将军起江东，刘豫州收众汉南，与曹操并争天下。",1,"","","孙权",306,},
        [4365] = {8310,7,1,"诸葛亮",203,0,"今操芟除大难，略已平矣；近又新破荆州，威震海内；纵有英雄，无用武之地：故豫州遁逃至此。","今操芟除大难，略已平矣；近又新破荆州，威震海内；纵有英雄，无用武之地：故豫州遁逃至此。",1,"","","孙权",306,},
        [4366] = {8310,8,1,"诸葛亮",203,0,"愿将军量力而处之：若能以吴、越之众，与中国抗衡，不如早与之绝；若其不能，何不从众谋士之论，按兵束甲，北面而事之？","愿将军量力而处之：若能以吴、越之众，与中国抗衡，不如早与之绝；若其不能，何不从众谋士之论，按兵束甲，北面而事之？",1,"","","孙权",306,},
        [4367] = {8310,9,1,"诸葛亮",203,0,"将军外托服从之名，内怀疑贰之见，事急而不断，祸至无日矣！","将军外托服从之名，内怀疑贰之见，事急而不断，祸至无日矣！",1,"","","孙权",306,},
        [4368] = {8310,10,2,"孙权",306,0,"诚如君言，刘豫州何不降操？","诚如君言，刘豫州何不降操？",1,"","","诸葛亮",203,},
        [4369] = {8310,11,1,"诸葛亮",203,0,"昔田横，齐之壮士耳，犹守义不辱。况刘豫州王室之胄，英才盖世，众士仰慕。事之不济，此乃天也。又安能屈处人下乎！","昔田横，齐之壮士耳，犹守义不辱。况刘豫州王室之胄，英才盖世，众士仰慕。事之不济，此乃天也。又安能屈处人下乎！",1,"","","孙权",306,},
        [4370] = {8310,12,2,"鲁肃",312,0,"大胆，先生之言藐视吾主甚矣！来日再议。","大胆，先生之言藐视吾主甚矣！来日再议。",1,"","","诸葛亮",203,},
        [4371] = {8311,1,1,"孙权",306,0,"曹操平生所恶者：吕布、刘表、袁绍、袁术、豫州与孤耳。今数雄已灭，独豫州与孤尚存。孤不能以全吴之地，受制于人。","曹操平生所恶者：吕布、刘表、袁绍、袁术、豫州与孤耳。今数雄已灭，独豫州与孤尚存。孤不能以全吴之地，受制于人。",1,"","","诸葛亮",203,},
        [4372] = {8311,2,1,"孙权",306,0,"吾计决矣。非刘豫州莫与当曹操者，然豫州新败之后，安能抗此难乎？","吾计决矣。非刘豫州莫与当曹操者，然豫州新败之后，安能抗此难乎？",1,"","","诸葛亮",203,},
        [4373] = {8311,3,2,"诸葛亮",203,0,"豫州虽新败，然关云长犹率精兵万人，刘琦领江夏战士，亦不下万人。","豫州虽新败，然关云长犹率精兵万人，刘琦领江夏战士，亦不下万人。",1,"","","孙权",306,},
        [4374] = {8311,4,2,"诸葛亮",203,0,"曹操之众，远来疲惫，近追豫州，轻骑一日夜行三百里，此所谓强弩之末，势不能穿鲁缟者也。且北方之人，不习水战。","曹操之众，远来疲惫，近追豫州，轻骑一日夜行三百里，此所谓强弩之末，势不能穿鲁缟者也。且北方之人，不习水战。",1,"","","孙权",306,},
        [4375] = {8311,5,2,"诸葛亮",203,0,"荆州士民附操者，迫于势耳，非本心也。","荆州士民附操者，迫于势耳，非本心也。",1,"","","孙权",306,},
        [4376] = {8311,6,2,"诸葛亮",203,0,"今将军诚能与豫州协力同心，破曹军必矣。操军破，必北还，则荆、吴之势强，而鼎足之形成矣。","今将军诚能与豫州协力同心，破曹军必矣。操军破，必北还，则荆、吴之势强，而鼎足之形成矣。",1,"","","孙权",306,},
        [4377] = {8311,7,2,"诸葛亮",203,0,"成败之机，在于今日。惟将军裁之。","成败之机，在于今日。惟将军裁之。",1,"","","孙权",306,},
        [4378] = {8311,8,1,"孙权",306,0,"先生之言，顿开茅塞。吾意已决，更无他疑。即日商议起兵，共灭曹操！","先生之言，顿开茅塞。吾意已决，更无他疑。即日商议起兵，共灭曹操！",1,"","","诸葛亮",203,},
        [4379] = {8312,1,2,"张昭",315,0,"中了孔明之计也！","中了孔明之计也！",1,"","","孙权",306,},
        [4380] = {8312,2,2,"张昭",315,0,"曹操向日兵微将寡，尚能一鼓克袁绍，何况今日拥百万之众南征，岂可轻敌？","曹操向日兵微将寡，尚能一鼓克袁绍，何况今日拥百万之众南征，岂可轻敌？",1,"","","孙权",306,},
        [4381] = {8312,3,2,"张昭",315,0,"若听诸葛亮之言，妄动甲兵，此所谓负薪救火也。","若听诸葛亮之言，妄动甲兵，此所谓负薪救火也。",1,"","","孙权",306,},
        [4382] = {8312,4,1,"孙权",306,0,"这个…","这个…",1,"","","张昭",315,},
        [4383] = {8312,5,2,"顾雍",321,0,"刘备因为曹操所败，故欲借我江东之兵以拒之，主公奈何为其所用乎，愿听子布之言。","刘备因为曹操所败，故欲借我江东之兵以拒之，主公奈何为其所用乎，愿听子布之言。",1,"","","孙权",306,},
        [4384] = {8312,6,1,"孙权",306,0,"这个…","这个…",1,"","","顾雍",321,},
        [4385] = {8312,7,2,"鲁肃",312,0,"适张子布等，又劝主公休动兵，力主降议，此皆全躯保妻子之臣，为自谋之计耳。原主公勿听也。","适张子布等，又劝主公休动兵，力主降议，此皆全躯保妻子之臣，为自谋之计耳。原主公勿听也。",1,"","","孙权",306,},
        [4386] = {8312,8,2,"鲁肃",312,0,"主公若迟疑，必为众人误矣。","主公若迟疑，必为众人误矣。",1,"","","孙权",306,},
        [4387] = {8312,9,1,"孙权",306,0,"卿且暂退，容我三思。","卿且暂退，容我三思。",1,"","","鲁肃",312,},
        [4388] = {8313,1,2,"吴国太",322,0,"何事在心，寝食俱废？","何事在心，寝食俱废？",1,"","","孙权",306,},
        [4389] = {8313,2,1,"孙权",306,0,"今曹操屯兵于江汉，有下江南之意。问诸文武，或欲降者，或欲战者。欲待战来，恐寡不敌众，欲待降来，又恐曹操不容。","今曹操屯兵于江汉，有下江南之意。问诸文武，或欲降者，或欲战者。欲待战来，恐寡不敌众，欲待降来，又恐曹操不容。",1,"","","吴国太",322,},
        [4390] = {8313,3,2,"吴国太",322,0,"汝何不记武烈皇后临终之语乎？","汝何不记武烈皇后临终之语乎？",1,"","","孙权",306,},
        [4391] = {8313,4,2,"吴国太",322,0,"内事不决问张昭，外事不决问周瑜。","内事不决问张昭，外事不决问周瑜。",1,"","","孙权",306,},
        [4392] = {8313,5,2,"吴国太",322,0,"今何不请公瑾问之？","今何不请公瑾问之？",1,"","","孙权",306,},
        [4393] = {8401,1,2,"张昭",315,0,"都督知江东之利害否？","都督知江东之利害否？",1,"","","周瑜",303,},
        [4394] = {8401,2,1,"周瑜",303,0,"未知也。","未知也。",1,"","","张昭",315,},
        [4395] = {8401,3,2,"张昭",315,0,"曹操拥众百万，屯于汉上，昨传檄文至此，欲请主公会猎于江夏。虽有相吞之意，尚未露其形。","曹操拥众百万，屯于汉上，昨传檄文至此，欲请主公会猎于江夏。虽有相吞之意，尚未露其形。",1,"","","周瑜",303,},
        [4396] = {8401,4,2,"张昭",315,0,"昭等劝主公且降之，庶免江东之祸。不想鲁子敬从江夏带刘备军师诸葛亮至此，彼因自欲雪愤，特下说词以激主公。","昭等劝主公且降之，庶免江东之祸。不想鲁子敬从江夏带刘备军师诸葛亮至此，彼因自欲雪愤，特下说词以激主公。",1,"","","周瑜",303,},
        [4397] = {8401,5,2,"张昭",315,0,"子敬却执迷不悟。正欲待都督一决。","子敬却执迷不悟。正欲待都督一决。",1,"","","周瑜",303,},
        [4398] = {8401,6,1,"周瑜",303,0,"公等之见皆同否？","公等之见皆同否？",1,"","","张昭",315,},
        [4399] = {8401,7,2,"顾雍",321,0,"所议皆同。","所议皆同。",1,"","","周瑜",303,},
        [4400] = {8401,8,1,"周瑜",303,0,"吾亦欲降久矣。公等请回，明早见主公，自有定议。","吾亦欲降久矣。公等请回，明早见主公，自有定议。",1,"","","顾雍",321,},
        [4401] = {8402,1,2,"程普",320,0,"都督知江东早晚属他人否？","都督知江东早晚属他人否？",1,"","","周瑜",303,},
        [4402] = {8402,2,1,"周瑜",303,0,"未知也。","未知也。",1,"","","程普",320,},
        [4403] = {8402,3,2,"程普",320,0,"吾等自随孙将军开基创业，大小数百战，方才战得六郡城池。今主公听谋士之言，欲降曹操，此真可耻可惜之事！","吾等自随孙将军开基创业，大小数百战，方才战得六郡城池。今主公听谋士之言，欲降曹操，此真可耻可惜之事！",1,"","","周瑜",303,},
        [4404] = {8402,4,2,"程普",320,0,"吾等宁死不辱。望都督劝主公决计兴兵，吾等愿效死战。","吾等宁死不辱。望都督劝主公决计兴兵，吾等愿效死战。",1,"","","周瑜",303,},
        [4405] = {8402,5,1,"周瑜",303,0,"将军等所见皆同否？","将军等所见皆同否？",1,"","","程普",320,},
        [4406] = {8402,6,2,"黄盖",314,0,"吾头可断，誓不降曹！","吾头可断，誓不降曹！",1,"","","周瑜",303,},
        [4407] = {8402,7,1,"周瑜",303,0,"吾正欲与曹操决战，安肯投降！将军等请回。瑜见主公，自有定议。","吾正欲与曹操决战，安肯投降！将军等请回。瑜见主公，自有定议。",1,"","","黄盖",314,},
        [4408] = {8403,1,2,"诸葛瑾",319,0,"诸葛亮自汉上来，言刘豫州欲结东吴，共伐曹操，文武商议未定。瑾不敢多言，专候都督来决此事。","诸葛亮自汉上来，言刘豫州欲结东吴，共伐曹操，文武商议未定。瑾不敢多言，专候都督来决此事。",1,"","","周瑜",303,},
        [4409] = {8403,2,1,"周瑜",303,0,"以公论之若何？","以公论之若何？",1,"","","诸葛瑾",319,},
        [4410] = {8403,3,2,"诸葛瑾",319,0,"降者易安，战者难保。","降者易安，战者难保。",1,"","","周瑜",303,},
        [4411] = {8403,4,1,"周瑜",303,0,"瑜自有主张。来日同至府下定议。","瑜自有主张。来日同至府下定议。",1,"","","诸葛瑾",319,},
        [4412] = {8404,1,2,"鲁肃",312,0,"今曹操驱众南侵，和与战二策，主公不能决，一听于将军。将军之意若何？","今曹操驱众南侵，和与战二策，主公不能决，一听于将军。将军之意若何？",1,"","","周瑜",303,},
        [4413] = {8404,2,1,"周瑜",303,0,"曹操以天子为名，其师不可拒。且其势大，未可轻敌。战则必败，降则易安。吾意已决。来日见主公，便当遣使纳降。","曹操以天子为名，其师不可拒。且其势大，未可轻敌。战则必败，降则易安。吾意已决。来日见主公，便当遣使纳降。",1,"","","鲁肃",312,},
        [4414] = {8404,3,2,"鲁肃",312,0,"君言差矣！江东基业，已历三世，岂可一旦弃于他人？伯符遗言，外事付托将军。","君言差矣！江东基业，已历三世，岂可一旦弃于他人？伯符遗言，外事付托将军。",1,"","","周瑜",303,},
        [4415] = {8404,4,2,"鲁肃",312,0,"今正欲仗将军保全国家，为泰山之靠，奈何从懦夫之议耶？","今正欲仗将军保全国家，为泰山之靠，奈何从懦夫之议耶？",1,"","","周瑜",303,},
        [4416] = {8404,5,1,"周瑜",303,0,"江东六郡，主灵无限，若罹兵革之祸，必有归怨于我，故决计请降耳。","江东六郡，主灵无限，若罹兵革之祸，必有归怨于我，故决计请降耳。",1,"","","鲁肃",312,},
        [4417] = {8404,6,2,"鲁肃",312,0,"不然。以将军之英雄，东吴之险固，操未必便能得志也。","不然。以将军之英雄，东吴之险固，操未必便能得志也。",1,"","","周瑜",303,},
        [4418] = {8404,7,2,"诸葛亮",203,0,"哈哈哈哈哈。","哈哈哈哈哈。",1,"","","周瑜",303,},
        [4419] = {8404,8,1,"周瑜",303,0,"先生何故哂笑？","先生何故哂笑？",1,"","","诸葛亮",203,},
        [4420] = {8404,9,2,"诸葛亮",203,0,"亮不笑别人，笑子敬不识时务耳。","亮不笑别人，笑子敬不识时务耳。",1,"","","周瑜",303,},
        [4421] = {8405,1,2,"鲁肃",312,0,"方才先生如何反笑我不识时务？","方才先生如何反笑我不识时务？",1,"","","诸葛亮",203,},
        [4422] = {8405,2,1,"诸葛亮",203,0,"公瑾主意欲降操，甚为合理。","公瑾主意欲降操，甚为合理。",1,"","","鲁肃",312,},
        [4423] = {8405,3,2,"周瑜",303,0,"孔明乃识时务之士，必与吾有同心。","孔明乃识时务之士，必与吾有同心。",1,"","","诸葛亮",203,},
        [4424] = {8405,4,2,"鲁肃",312,0,"孔明，你也如何说此？","孔明，你也如何说此？",1,"","","诸葛亮",203,},
        [4425] = {8405,5,1,"诸葛亮",203,0,"操极善用兵，天下莫敢当。向只有吕布、袁绍、袁术、刘表敢与对敌。今数人皆被操灭，天下无人矣。","操极善用兵，天下莫敢当。向只有吕布、袁绍、袁术、刘表敢与对敌。今数人皆被操灭，天下无人矣。",1,"","","鲁肃",312,},
        [4426] = {8405,6,1,"诸葛亮",203,0,"独有刘豫州不识时务，强与争衡，存亡未保。将军降曹，可以保妻子，可以全富贵。国祚迁移，付之天命，何足惜哉！","独有刘豫州不识时务，强与争衡，存亡未保。将军降曹，可以保妻子，可以全富贵。国祚迁移，付之天命，何足惜哉！",1,"","","鲁肃",312,},
        [4427] = {8405,7,2,"鲁肃",312,0,"汝教吾主屈膝受辱于国贼乎！","汝教吾主屈膝受辱于国贼乎！",1,"","","诸葛亮",203,},
        [4428] = {8405,8,1,"诸葛亮",203,0,"愚有一计，只须遣一介之使，扁舟送两个人到江上。操一得此两人，百万之众，皆卸甲卷旗而退矣。","愚有一计，只须遣一介之使，扁舟送两个人到江上。操一得此两人，百万之众，皆卸甲卷旗而退矣。",1,"","","鲁肃",312,},
        [4429] = {8405,9,2,"周瑜",303,0,"用何二人，可退操兵？","用何二人，可退操兵？",1,"","","诸葛亮",203,},
        [4430] = {8405,10,1,"诸葛亮",203,0,"江东乔公二女！","江东乔公二女！",1,"","","周瑜",303,},
        [4431] = {8406,1,2,"周瑜",303,0,"此前先生所说退曹之计，为何用江东乔公二女？","此前先生所说退曹之计，为何用江东乔公二女？",1,"","","诸葛亮",203,},
        [4432] = {8406,2,1,"诸葛亮",203,0,"亮居隆中时，即闻操于漳河新造一台，名曰铜雀，极其壮丽，广选天下美女以实其中。","亮居隆中时，即闻操于漳河新造一台，名曰铜雀，极其壮丽，广选天下美女以实其中。",1,"","","周瑜",303,},
        [4433] = {8406,3,1,"诸葛亮",203,0,"操本好色之徒，久闻江东乔公有二女，长曰大乔，次曰小乔，有沉鱼落雁之容，闭月羞花之貌。","操本好色之徒，久闻江东乔公有二女，长曰大乔，次曰小乔，有沉鱼落雁之容，闭月羞花之貌。",1,"","","周瑜",303,},
        [4434] = {8406,4,1,"诸葛亮",203,0,"操曾发誓，一愿扫平四海，以成帝业，一愿得江东二乔，置之铜雀台，以乐晚年，虽死无恨矣。","操曾发誓，一愿扫平四海，以成帝业，一愿得江东二乔，置之铜雀台，以乐晚年，虽死无恨矣。",1,"","","周瑜",303,},
        [4435] = {8406,5,1,"诸葛亮",203,0,"今虽引百万之众，虎视江南，其实为此二女也。将军何不去寻乔公，以千金买此二女，差人送与曹操。","今虽引百万之众，虎视江南，其实为此二女也。将军何不去寻乔公，以千金买此二女，差人送与曹操。",1,"","","周瑜",303,},
        [4436] = {8406,6,1,"诸葛亮",203,0,"此范蠡献西施之计，操得二女，称心满意，必班师矣。","此范蠡献西施之计，操得二女，称心满意，必班师矣。",1,"","","周瑜",303,},
        [4437] = {8406,7,2,"周瑜",303,0,"操欲得二乔，有何证验？","操欲得二乔，有何证验？",1,"","","诸葛亮",203,},
        [4438] = {8406,8,1,"主角",1,0,"曹操幼子曹植，字子建，下笔成文。操尝命作一赋，名曰《铜雀台赋》。赋中之意，单道他家合为天子，誓取二乔。","曹操幼子曹植，字子建，下笔成文。操尝命作一赋，名曰《铜雀台赋》。赋中之意，单道他家合为天子，誓取二乔。",1,"","","周瑜",303,},
        [4439] = {8406,9,1,"主角",1,0,"今日特携此赋前来，烦请过目。","今日特携此赋前来，烦请过目。",1,"","","周瑜",303,},
        [4440] = {8406,10,2,"周瑜",303,0,"老贼欺吾太甚！","老贼欺吾太甚！",1,"","","主角",1,},
        [4441] = {8406,11,1,"诸葛亮",203,0,"昔单于屡侵疆界，汉天子许以公主和亲，今何惜民间二女乎？","昔单于屡侵疆界，汉天子许以公主和亲，今何惜民间二女乎？",1,"","","周瑜",303,},
        [4442] = {8406,12,2,"周瑜",303,0,"公有所不知：大乔是孙伯符将军主妇，小乔乃瑜之妻也。","公有所不知：大乔是孙伯符将军主妇，小乔乃瑜之妻也。",1,"","","诸葛亮",203,},
        [4443] = {8406,13,1,"诸葛亮",203,0,"亮实不知。失口乱言，死罪！死罪！","亮实不知。失口乱言，死罪！死罪！",1,"","","周瑜",303,},
        [4444] = {8406,14,2,"周瑜",303,0,"吾与老贼誓不两立！来日入见主公，便议起兵。","吾与老贼誓不两立！来日入见主公，便议起兵。",1,"","","诸葛亮",203,},
        [4445] = {8407,1,2,"孙权",306,0,"连日议此事，有劝我降者，有劝我战者。吾意未定，故请公瑾一决。","连日议此事，有劝我降者，有劝我战者。吾意未定，故请公瑾一决。",1,"","","周瑜",303,},
        [4446] = {8407,2,1,"周瑜",303,0,"谁劝主公降？","谁劝主公降？",1,"","","孙权",306,},
        [4447] = {8407,3,2,"孙权",306,0,"张子布等皆主其意。","张子布等皆主其意。",1,"","","周瑜",303,},
        [4448] = {8407,4,1,"周瑜",303,0,"愿闻子布主降之意。","愿闻子布主降之意。",1,"","","孙权",306,},
        [4449] = {8407,5,2,"张昭",315,0,"曹操挟天子而征四方，动以朝廷为名，近又得荆州，威势越大。吾江东可以拒操者，长江耳。","曹操挟天子而征四方，动以朝廷为名，近又得荆州，威势越大。吾江东可以拒操者，长江耳。",1,"","","周瑜",303,},
        [4450] = {8407,6,2,"张昭",315,0,"今操艨艟战舰，何止千百？水陆并进，何可当之？不如且降，更图后计。","今操艨艟战舰，何止千百？水陆并进，何可当之？不如且降，更图后计。",1,"","","周瑜",303,},
        [4451] = {8407,7,1,"周瑜",303,0,"此迂儒之论也！江东自开国以来，今历三世，安忍一旦废弃？","此迂儒之论也！江东自开国以来，今历三世，安忍一旦废弃？",1,"","","张昭",315,},
        [4452] = {8407,8,2,"孙权",306,0,"若此，计将安出？","若此，计将安出？",1,"","","周瑜",303,},
        [4453] = {8407,9,1,"周瑜",303,0,"操虽托名汉相，实为汉贼。将军以神武雄才，仗父兄余业，据有江东，兵精粮足，为国家除残去暴，奈何降贼耶？","操虽托名汉相，实为汉贼。将军以神武雄才，仗父兄余业，据有江东，兵精粮足，为国家除残去暴，奈何降贼耶？",1,"","","孙权",306,},
        [4454] = {8407,10,1,"周瑜",303,0,"且操今此来，北土未平，久于南征，一忌也，北军不熟水战，操舍鞍马，仗舟楫，与东吴争衡，二忌也。","且操今此来，北土未平，久于南征，一忌也，北军不熟水战，操舍鞍马，仗舟楫，与东吴争衡，二忌也。",1,"","","孙权",306,},
        [4455] = {8407,11,1,"周瑜",303,0,"又时值隆冬盛寒，马无藁草，三忌也，远涉江湖，不服水土，多生疾病，四忌也。","又时值隆冬盛寒，马无藁草，三忌也，远涉江湖，不服水土，多生疾病，四忌也。",1,"","","孙权",306,},
        [4456] = {8407,12,1,"周瑜",303,0,"操兵犯此数忌，虽多必败。将军擒操，正在今日。瑜请得精兵数万人，进屯夏口，为将军破之！","操兵犯此数忌，虽多必败。将军擒操，正在今日。瑜请得精兵数万人，进屯夏口，为将军破之！",1,"","","孙权",306,},
        [4457] = {8407,13,2,"孙权",306,0,"封汝为大都督，程普为副都督，鲁肃为赞军校尉，起兵伐曹。诸官将有再言降操者，斩！","封汝为大都督，程普为副都督，鲁肃为赞军校尉，起兵伐曹。诸官将有再言降操者，斩！",1,"","","周瑜",303,},
        [4458] = {8501,1,2,"周瑜",303,0,"今日府下公议已定，愿求破曹良策。","今日府下公议已定，愿求破曹良策。",1,"","","诸葛亮",203,},
        [4459] = {8501,2,1,"诸葛亮",203,0,"孙将军心尚未稳，不可以决策也。","孙将军心尚未稳，不可以决策也。",1,"","","周瑜",303,},
        [4460] = {8501,3,2,"周瑜",303,0,"何谓心不稳？","何谓心不稳？",1,"","","诸葛亮",203,},
        [4461] = {8501,4,1,"诸葛亮",203,0,"心怯曹兵之多，怀寡不敌众之意。将军能以军数开解，使其了然无疑，然后大事可成。","心怯曹兵之多，怀寡不敌众之意。将军能以军数开解，使其了然无疑，然后大事可成。",1,"","","周瑜",303,},
        [4462] = {8501,5,2,"周瑜",303,0,"先生之论甚善。","先生之论甚善。",1,"","","诸葛亮",203,},
        [4463] = {8502,1,2,"孙权",306,0,"公瑾夜至，必有事故。","公瑾夜至，必有事故。",1,"","","周瑜",303,},
        [4464] = {8502,2,1,"周瑜",303,0,"来日调拨军马，主公心有疑否？","来日调拨军马，主公心有疑否？",1,"","","孙权",306,},
        [4465] = {8502,3,2,"孙权",306,0,"但忧曹操兵多，寡不敌众耳。他无所疑。","但忧曹操兵多，寡不敌众耳。他无所疑。",1,"","","周瑜",303,},
        [4466] = {8502,4,1,"周瑜",303,0,"瑜特为此来开解主公。主公因见操檄文，言水陆大军百万，故怀疑惧，不复料其虚实。","瑜特为此来开解主公。主公因见操檄文，言水陆大军百万，故怀疑惧，不复料其虚实。",1,"","","孙权",306,},
        [4467] = {8502,5,1,"周瑜",303,0,"然，夫以久疲之卒，御狐疑之众，其数虽多，不足畏也。瑜得五万兵，自足破之。愿主公勿以为虑。","然，夫以久疲之卒，御狐疑之众，其数虽多，不足畏也。瑜得五万兵，自足破之。愿主公勿以为虑。",1,"","","孙权",306,},
        [4468] = {8502,6,2,"孙权",306,0,"公瑾此言，足释吾疑。子布无谋，深失孤望，独卿及子敬，与孤同心耳。","公瑾此言，足释吾疑。子布无谋，深失孤望，独卿及子敬，与孤同心耳。",1,"","","周瑜",303,},
        [4469] = {8503,1,1,"周瑜",303,0,"孔明早已料着吴侯之心。其计画又高我一头。久必为江东之患，不如杀之。","孔明早已料着吴侯之心。其计画又高我一头。久必为江东之患，不如杀之。",1,"","","鲁肃",312,},
        [4470] = {8503,2,2,"鲁肃",312,0,"不可。今操贼未破，先杀贤士，是自去其助也。","不可。今操贼未破，先杀贤士，是自去其助也。",1,"","","周瑜",303,},
        [4471] = {8503,3,1,"周瑜",303,0,"此人助刘备，必为江东之患。","此人助刘备，必为江东之患。",1,"","","鲁肃",312,},
        [4472] = {8503,4,2,"鲁肃",312,0,"诸葛瑾乃其亲兄，可令招此人同事东吴，岂不妙哉？","诸葛瑾乃其亲兄，可令招此人同事东吴，岂不妙哉？",1,"","","周瑜",303,},
        [4473] = {8503,5,1,"周瑜",303,0,"嗯，言之有理。","嗯，言之有理。",1,"","","鲁肃",312,},
        [4474] = {8504,1,1,"周瑜",303,0,"孔明有王佐之才，如何屈身事刘备？","孔明有王佐之才，如何屈身事刘备？",1,"","","诸葛瑾",319,},
        [4475] = {8504,2,1,"周瑜",303,0,"今幸至江东，欲烦先生不惜齿牙余论，使令弟弃刘备而事东吴，则主公既得良辅，而先生兄弟又得相见，岂不美哉？","今幸至江东，欲烦先生不惜齿牙余论，使令弟弃刘备而事东吴，则主公既得良辅，而先生兄弟又得相见，岂不美哉？",1,"","","诸葛瑾",319,},
        [4476] = {8504,3,2,"诸葛瑾",319,0,"瑾自至江东，愧无寸功。今都督有命，敢不效力。","瑾自至江东，愧无寸功。今都督有命，敢不效力。",1,"","","周瑜",303,},
        [4477] = {8505,1,2,"诸葛瑾",319,0,"汝是否愿弃刘备而事东吴？","汝是否愿弃刘备而事东吴？",1,"","","诸葛亮",203,},
        [4478] = {8505,2,1,"诸葛亮",203,0,"此必周郎教来说我也。","此必周郎教来说我也。",1,"","","诸葛瑾",319,},
        [4479] = {8505,3,2,"诸葛瑾",319,0,"知伯夷、叔齐乎？","知伯夷、叔齐乎？",1,"","","诸葛亮",203,},
        [4480] = {8505,4,2,"诸葛瑾",319,0,"夷、齐虽至饿死首阳山下，兄弟二人亦在一处。我今与你同胞共乳，乃各事其主，不能旦暮相聚。视夷、齐之为人，能无愧乎？","夷、齐虽至饿死首阳山下，兄弟二人亦在一处。我今与你同胞共乳，乃各事其主，不能旦暮相聚。视夷、齐之为人，能无愧乎？",1,"","","诸葛亮",203,},
        [4481] = {8505,5,1,"诸葛亮",203,0,"兄所言者，情也，弟所守者，义也。弟与兄皆汉人。","兄所言者，情也，弟所守者，义也。弟与兄皆汉人。",1,"","","诸葛瑾",319,},
        [4482] = {8505,6,1,"诸葛亮",203,0,"今刘皇叔乃汉室之胄，兄若能去东吴，而与弟同事刘皇叔，则上不愧为汉臣，而骨肉又得相聚，此情义两全之策也。","今刘皇叔乃汉室之胄，兄若能去东吴，而与弟同事刘皇叔，则上不愧为汉臣，而骨肉又得相聚，此情义两全之策也。",1,"","","诸葛瑾",319,},
        [4483] = {8505,7,1,"诸葛亮",203,0,"不识兄意以为何如？","不识兄意以为何如？",1,"","","诸葛瑾",319,},
        [4484] = {8505,8,2,"诸葛瑾",319,0,"已明汝意，吾告辞。","已明汝意，吾告辞。",1,"","","诸葛亮",203,},
        [4485] = {8506,1,2,"周瑜",303,0,"昔曹操兵少，袁绍兵多，而操反胜绍者，因用许攸之谋，先断乌巢之粮也。","昔曹操兵少，袁绍兵多，而操反胜绍者，因用许攸之谋，先断乌巢之粮也。",1,"","","诸葛亮",203,},
        [4486] = {8506,2,2,"周瑜",303,0,"今操兵八十三万，我兵只五六万，安能拒之？亦必须先断操之粮，然后可破。我已探知操军粮草，俱屯于聚铁山。","今操兵八十三万，我兵只五六万，安能拒之？亦必须先断操之粮，然后可破。我已探知操军粮草，俱屯于聚铁山。",1,"","","诸葛亮",203,},
        [4487] = {8506,3,2,"周瑜",303,0,"先生久居汉上，熟知地理。敢烦先生星夜往聚铁山断操粮道。彼此各为主人之事，幸勿推调。","先生久居汉上，熟知地理。敢烦先生星夜往聚铁山断操粮道。彼此各为主人之事，幸勿推调。",1,"","","诸葛亮",203,},
        [4488] = {8506,4,1,"诸葛亮",203,0,"好。","好。",1,"","","周瑜",303,},
        [4489] = {8507,1,1,"主角",1,0,"周瑜因说不动先生，故借曹操之手杀之，以绝后患耳。","周瑜因说不动先生，故借曹操之手杀之，以绝后患耳。",1,"","","诸葛亮",203,},
        [4490] = {8507,2,2,"诸葛亮",203,0,"我知晓，但我若推调，必为所笑。不如应之，别有计议。","我知晓，但我若推调，必为所笑。不如应之，别有计议。",1,"","","主角",1,},
        [4491] = {8507,3,1,"主角",1,0,"先生厉害。","先生厉害。",1,"","","诸葛亮",203,},
        [4492] = {8508,1,2,"鲁肃",312,0,"先生此去劫粮可成功否？","先生此去劫粮可成功否？",1,"","","诸葛亮",203,},
        [4493] = {8508,2,1,"诸葛亮",203,0,"吾水战、步战、马战、车战，各尽其妙，何愁功绩不成，非比江东公与周郎辈止一能也。","吾水战、步战、马战、车战，各尽其妙，何愁功绩不成，非比江东公与周郎辈止一能也。",1,"","","鲁肃",312,},
        [4494] = {8508,3,2,"鲁肃",312,0,"吾与公瑾何谓一能？","吾与公瑾何谓一能？",1,"","","诸葛亮",203,},
        [4495] = {8508,4,1,"诸葛亮",203,0,"江南小儿谣言云，伏路把关饶子敬，临江水战有周郎。","江南小儿谣言云，伏路把关饶子敬，临江水战有周郎。",1,"","","鲁肃",312,},
        [4496] = {8508,5,1,"诸葛亮",203,0,"公等于陆地但能伏路把关，周公瑾但堪水战，不能陆战耳。","公等于陆地但能伏路把关，周公瑾但堪水战，不能陆战耳。",1,"","","鲁肃",312,},
        [4497] = {8509,1,2,"鲁肃",312,0,"我前去打探，见那孔明言汝只识水战，不能陆战耳。","我前去打探，见那孔明言汝只识水战，不能陆战耳。",1,"","","周瑜",303,},
        [4498] = {8509,2,1,"周瑜",303,0,"何欺我不能陆战耶！不用他去！我自引一万马军，往聚铁山断操粮道。","何欺我不能陆战耶！不用他去！我自引一万马军，往聚铁山断操粮道。",1,"","","鲁肃",312,},
        [4499] = {8510,1,1,"刘备",202,0,"孔明一去东吴，杳无音信，不知如何？","孔明一去东吴，杳无音信，不知如何？",1,"","","",0,},
        [4500] = {8510,2,2,"糜竺",243,0,"此去探听虚实，东吴言孔明与其同谋破曹，不可暂离。","此去探听虚实，东吴言孔明与其同谋破曹，不可暂离。",1,"","","刘备",202,},
        [4501] = {8510,3,2,"糜竺",243,0,"且周郎亦欲见汝，共议良策。","且周郎亦欲见汝，共议良策。",1,"","","刘备",202,},
        [4502] = {8510,4,2,"关羽",205,0,"周瑜多谋之士，又无孔明书信，恐其中有诈，不可轻去。","周瑜多谋之士，又无孔明书信，恐其中有诈，不可轻去。",1,"","","刘备",202,},
        [4503] = {8510,5,1,"刘备",202,0,"我今结东吴以共破曹操，周郎欲见我，我若不往，非同盟之意。两相猜忌，事不谐矣。","我今结东吴以共破曹操，周郎欲见我，我若不往，非同盟之意。两相猜忌，事不谐矣。",1,"","","关羽",205,},
        [4504] = {8510,6,2,"关羽",205,0,"兄长若坚意要去，弟愿同往。","兄长若坚意要去，弟愿同往。",1,"","","刘备",202,},
        [4505] = {8510,7,2,"张飞",206,0,"我也跟去。","我也跟去。",1,"","","刘备",202,},
        [4506] = {8510,8,1,"刘备",202,0,"只云长随我去即可，况且孔明身边还有四弟在。","只云长随我去即可，况且孔明身边还有四妹在。",1,"","","张飞",206,},
        [4507] = {8511,1,2,"鲁肃",312,0,"刘豫州来了。","刘豫州来了。",1,"","","周瑜",303,},
        [4508] = {8511,2,1,"周瑜",303,0,"带多少船只来？","带多少船只来？",1,"","","鲁肃",312,},
        [4509] = {8511,3,2,"鲁肃",312,0,"只有一只船，二十余从人。","只有一只船，二十余从人。",1,"","","周瑜",303,},
        [4510] = {8511,4,1,"周瑜",303,0,"此人命合体矣！命刀斧手先埋伏定。","此人命合体矣！命刀斧手先埋伏定。",1,"","","鲁肃",312,},
        [4511] = {8512,1,2,"周瑜",303,0,"玄德请上坐。","玄德请上坐。",1,"","","刘备",202,},
        [4512] = {8512,2,1,"刘备",202,0,"将军名传天下，备不才，何烦将军重礼？","将军名传天下，备不才，何烦将军重礼？",1,"","","周瑜",303,},
        [4513] = {8512,3,2,"周瑜",303,0,"言重言重。","言重言重。",1,"","","刘备",202,},
        [4514] = {8512,4,2,"周瑜",303,0,"敢问玄德身后，站是何人。","敢问玄德身后，站是何人。",1,"","","刘备",202,},
        [4515] = {8512,5,1,"刘备",202,0,"吾弟关云长也。","吾弟关云长也。",1,"","","周瑜",303,},
        [4516] = {8512,6,2,"周瑜",303,0,"非向日斩颜良、文丑者乎？","非向日斩颜良、文丑者乎？",1,"","","刘备",202,},
        [4517] = {8512,7,1,"刘备",202,0,"然也。","然也。",1,"","","周瑜",303,},
        [4518] = {8512,8,2,"周瑜",303,0,"久闻大名，来来来，也敬云长一盏酒。","久闻大名，来来来，也敬云长一盏酒。",1,"","","刘备",202,},
        [4519] = {8513,1,2,"鲁肃",312,0,"公既诱玄德至此，为何又不下手？","公既诱玄德至此，为何又不下手？",1,"","","周瑜",303,},
        [4520] = {8513,2,1,"周瑜",303,0,"关云长，世之虎将也，与玄德行坐相随，吾若下手，他必来害我。","关云长，世之虎将也，与玄德行坐相随，吾若下手，他必来害我。",1,"","","鲁肃",312,},
        [4521] = {8513,3,2,"鲁肃",312,0,"言之有理。","言之有理。",1,"","","周瑜",303,},
        [4522] = {8514,1,2,"诸葛亮",203,0,"主公知今日之危乎？","主公知今日之危乎？",1,"","","刘备",202,},
        [4523] = {8514,2,1,"刘备",202,0,"不知也。","不知也。",1,"","","诸葛亮",203,},
        [4524] = {8514,3,2,"主角",1,0,"若无云长，大哥必遭周郎所害。","若无云长，大哥必遭周郎所害。",1,"","","刘备",202,},
        [4525] = {8514,4,1,"刘备",202,0,"方才真是凶险。既然如此，你们速与我一起回去。","方才真是凶险。既然如此，你们速与我一起回去。",1,"","","主角",1,},
        [4526] = {8514,5,2,"诸葛亮",203,0,"虽居虎口，安如泰山。今主公但收拾船只军马候用。","虽居虎口，安如泰山。今主公但收拾船只军马候用。",1,"","","刘备",202,},
        [4527] = {8514,6,2,"诸葛亮",203,0,"以十一月二十甲子日后为期，可令子龙驾小舟来南岸边等候。切勿有误。","以十一月二十甲子日后为期，可令子龙驾小舟来南岸边等候。切勿有误。",1,"","","刘备",202,},
        [4528] = {8514,7,2,"诸葛亮",203,0,"但看东南风起，亮必还矣。","但看东南风起，亮必还矣。",1,"","","刘备",202,},
        [4529] = {8514,8,1,"刘备",202,0,"好，先生与四弟小心。","好，先生与四妹小心。",1,"","","诸葛亮",203,},
        [4530] = {8601,1,2,"鲁肃",312,0,"曹操遣使送书至。","曹操遣使送书至。",1,"","","周瑜",303,},
        [4531] = {8601,2,1,"周瑜",303,0,"呈上书信。","呈上书信。",1,"","","鲁肃",312,},
        [4532] = {8601,3,2,"鲁肃",312,0,"是。","是。",1,"","","周瑜",303,},
        [4533] = {8601,4,1,"周瑜",303,0,"把来使给我斩了。","把来使给我斩了。",1,"","","鲁肃",312,},
        [4534] = {8601,5,2,"鲁肃",312,0,"为何？","为何？",1,"","","周瑜",303,},
        [4535] = {8601,6,1,"周瑜",303,0,"封面竟写，汉大丞相付周都督开拆。","封面竟写，汉大丞相付周都督开拆。",1,"","","鲁肃",312,},
        [4536] = {8601,7,2,"鲁肃",312,0,"两国相争，不斩来使。","两国相争，不斩来使。",1,"","","周瑜",303,},
        [4537] = {8601,8,1,"周瑜",303,0,"斩使以示威！将首级付从人持回。","斩使以示威！将首级付从人持回。",1,"","","鲁肃",312,},
        [4538] = {8601,9,1,"周瑜",303,0,"令甘宁为先锋，韩当为左翼，蒋钦为右翼。吾自部领诸将接应。来日四更造饭，五更开船，鸣鼓呐喊而进。","令甘宁为先锋，韩当为左翼，蒋钦为右翼。吾自部领诸将接应。来日四更造饭，五更开船，鸣鼓呐喊而进。",1,"","","鲁肃",312,},
        [4539] = {8602,1,1,"曹操",103,0,"周瑜竟毁书斩使！","周瑜竟毁书斩使！",1,"","","",0,},
        [4540] = {8602,2,1,"曹操",103,0,"蔡瑁、张允为前部，孤自为后军，催督战船，行三江口。","蔡瑁、张允为前部，孤自为后军，催督战船，行三江口。",1,"","","",0,},
        [4541] = {8603,1,1,"曹操",103,0,"蔡壎战死，曹军中箭着炮者，不计其数。","蔡壎战死，曹军中箭着炮者，不计其数。",1,"","","蔡瑁",443,},
        [4542] = {8603,2,1,"曹操",103,0,"东吴兵少，反为所败，是汝等不用心耳！","东吴兵少，反为所败，是汝等不用心耳！",1,"","","蔡瑁",443,},
        [4543] = {8603,3,2,"蔡瑁",443,0,"荆州水军，久不操练，青、徐之军，又素不习水战。故尔致败。","荆州水军，久不操练，青、徐之军，又素不习水战。故尔致败。",1,"","","曹操",103,},
        [4544] = {8603,4,2,"蔡瑁",443,0,"今当先立水寨，令青、徐军在中，荆州军在外，每日教习精熟，方可用之。","今当先立水寨，令青、徐军在中，荆州军在外，每日教习精熟，方可用之。",1,"","","曹操",103,},
        [4545] = {8603,5,1,"曹操",103,0,"汝既为水军都督，可以便宜从事，何必禀我！","汝既为水军都督，可以便宜从事，何必禀我！",1,"","","蔡瑁",443,},
        [4546] = {8604,1,1,"主角",1,0,"昨夜登高观望，只见西边火光接天，皆北军灯火之光也。","昨夜登高观望，只见西边火光接天，皆北军灯火之光也。",1,"","","周瑜",303,},
        [4547] = {8604,2,1,"主角",1,0,"便使一扁舟暗窥曹操水寨，此深得水军之妙也！","便使一扁舟暗窥曹操水寨，此深得水军之妙也！",1,"","","周瑜",303,},
        [4548] = {8604,3,2,"周瑜",303,0,"水军都督是谁？","水军都督是谁？",1,"","","主角",1,},
        [4549] = {8604,4,1,"主角",1,0,"蔡瑁、涨允。","蔡瑁、涨允。",1,"","","周瑜",303,},
        [4550] = {8604,5,2,"诸葛亮",203,0,"二人久居江东，谙习水战，吾必设计先除此二人，然后可以破曹。","二人久居江东，谙习水战，吾必设计先除此二人，然后可以破曹。",1,"","","主角",1,},
        [4551] = {8605,1,1,"曹操",103,0,"昨日输了一阵，挫动锐气，今又被他深窥吾寨。吾当作何计破之？","昨日输了一阵，挫动锐气，今又被他深窥吾寨。吾当作何计破之？",1,"","","",0,},
        [4552] = {8605,2,2,"蒋干",143,0,"某自幼与周郎同窗交契，愿凭三寸不烂之舌，往江东说此人来降。","某自幼与周郎同窗交契，愿凭三寸不烂之舌，往江东说此人来降。",1,"","","曹操",103,},
        [4553] = {8605,3,1,"曹操",103,0,"子翼与周公瑾相厚乎？","子翼与周公瑾相厚乎？",1,"","","蒋干",143,},
        [4554] = {8605,4,2,"蒋干",143,0,"丞相放心。干到江左，必要成功。","丞相放心。干到江左，必要成功。",1,"","","曹操",103,},
        [4555] = {8605,5,1,"曹操",103,0,"要将何物去？","要将何物去？",1,"","","蒋干",143,},
        [4556] = {8605,6,2,"蒋干",143,0,"只消一童随往，二仆驾舟，其余不用。","只消一童随往，二仆驾舟，其余不用。",1,"","","曹操",103,},
        [4557] = {8605,7,1,"曹操",103,0,"好！","好！",1,"","","蒋干",143,},
        [4558] = {8606,1,2,"鲁肃",312,0,"故人蒋干驾一只小舟前来相访。","故人蒋干驾一只小舟前来相访。",1,"","","周瑜",303,},
        [4559] = {8606,2,1,"周瑜",303,0,"说客至矣！","说客至矣！",1,"","","鲁肃",312,},
        [4560] = {8606,3,2,"主角",1,0,"来得正好，曹操疑心重，可行反间计。","来得正好，曹操疑心重，可行反间计。",1,"","","周瑜",303,},
        [4561] = {8606,4,1,"周瑜",303,0,"怎讲？","怎讲？",1,"","","主角",1,},
        [4562] = {8606,5,2,"主角",1,0,"烦请周郎附耳，我道之。","烦请周郎附耳，我道之。",1,"","","周瑜",303,},
        [4563] = {8606,6,1,"周瑜",303,0,"好，就依此计行事。","好，就依此计行事。",1,"","","主角",1,},
        [4564] = {8607,1,2,"蒋干",143,0,"公瑾别来无恙！","公瑾别来无恙！",1,"","","周瑜",303,},
        [4565] = {8607,2,1,"周瑜",303,0,"子翼良苦，远涉江湖，为曹氏作说客耶？","子翼良苦，远涉江湖，为曹氏作说客耶？",1,"","","蒋干",143,},
        [4566] = {8607,3,2,"蒋干",143,0,"吾久别足下，特来叙旧，奈何疑我作说客也？","吾久别足下，特来叙旧，奈何疑我作说客也？",1,"","","周瑜",303,},
        [4567] = {8607,4,1,"周瑜",303,0,"吾虽不及师旷之聪，闻弦歌而知雅意。","吾虽不及师旷之聪，闻弦歌而知雅意。",1,"","","蒋干",143,},
        [4568] = {8607,5,2,"蒋干",143,0,"足下待故人如此，便请告退。","足下待故人如此，便请告退。",1,"","","周瑜",303,},
        [4569] = {8607,6,1,"周瑜",303,0,"吾但恐兄为曹氏作说客耳。既无此心，何速去也？","吾但恐兄为曹氏作说客耳。既无此心，何速去也？",1,"","","蒋干",143,},
        [4570] = {8607,7,1,"周瑜",303,0,"随我入帐共饮。","随我入帐共饮。",1,"","","蒋干",143,},
        [4571] = {8608,1,2,"蒋干",143,0,"不胜酒力矣。","不胜酒力矣。",1,"","","周瑜",303,},
        [4572] = {8608,2,1,"周瑜",303,0,"也罢也罢，撤席。","也罢也罢，撤席。",1,"","","蒋干",143,},
        [4573] = {8608,3,1,"周瑜",303,0,"久不与子翼同榻，今宵抵足而眠。","久不与子翼同榻，今宵抵足而眠。",1,"","","蒋干",143,},
        [4574] = {8608,4,2,"蒋干",143,0,"好，随周郎之意。","好，随周郎之意。",1,"","","周瑜",303,},
        [4575] = {8609,1,1,"曹操",103,0,"子翼干事若何？","子翼干事若何？",1,"","","蒋干",143,},
        [4576] = {8609,2,2,"蒋干",143,0,"周瑜雅量高致，非言词所能动也。","周瑜雅量高致，非言词所能动也。",1,"","","曹操",103,},
        [4577] = {8609,3,1,"曹操",103,0,"事又不济，反为所笑！","事又不济，反为所笑！",1,"","","蒋干",143,},
        [4578] = {8609,4,2,"蒋干",143,0,"虽不能说周瑜，却与丞相打听得一件事。","虽不能说周瑜，却与丞相打听得一件事。",1,"","","曹操",103,},
        [4579] = {8609,5,1,"曹操",103,0,"速速道来。","速速道来。",1,"","","蒋干",143,},
        [4580] = {8609,6,2,"蒋干",143,0,"烦请乞退左右。","烦请乞退左右。",1,"","","曹操",103,},
        [4581] = {8610,1,1,"曹操",103,0,"只剩你我二人，子翼请讲。","只剩你我二人，子翼请讲。",1,"","","蒋干",143,},
        [4582] = {8610,2,2,"蒋干",143,0,"昨夜周郎不胜酒力，吾诈醉与其共眠。","昨夜周郎不胜酒力，吾诈醉与其共眠。",1,"","","曹操",103,},
        [4583] = {8610,3,2,"蒋干",143,0,"二更天，吾起床翻看帐内桌上文书，都是往来书信。","二更天，吾起床翻看帐内桌上文书，都是往来书信。",1,"","","曹操",103,},
        [4584] = {8610,4,2,"蒋干",143,0,"内有一封，上写蔡瑁张允谨封。","内有一封，上写蔡瑁张允谨封。",1,"","","曹操",103,},
        [4585] = {8610,5,2,"蒋干",143,0,"书信上写着，某等降曹，非图仕禄，迫于势耳。今已赚北军困于寨中，但得其便，即将操贼之首，献于麾下。","书信上写着，某等降曹，非图仕禄，迫于势耳。今已赚北军困于寨中，但得其便，即将操贼之首，献于麾下。",1,"","","曹操",103,},
        [4586] = {8610,6,2,"蒋干",143,0,"早晚人到，便有关报。幸勿见疑。先此敬覆。","早晚人到，便有关报。幸勿见疑。先此敬覆。",1,"","","曹操",103,},
        [4587] = {8610,7,1,"曹操",103,0,"二贼如此无礼耶！","二贼如此无礼耶！",1,"","","蒋干",143,},
        [4588] = {8611,1,1,"曹操",103,0,"我欲使汝二人进兵。","我欲使汝二人进兵。",1,"","","蔡瑁",443,},
        [4589] = {8611,2,2,"蔡瑁",443,0,"军尚未曾练熟，不可轻进。","军尚未曾练熟，不可轻进。",1,"","","曹操",103,},
        [4590] = {8611,3,1,"曹操",103,0,"军若练熟，吾首级献于周郎矣！","军若练熟，吾首级献于周郎矣！",1,"","","蔡瑁",443,},
        [4591] = {8611,4,2,"蔡瑁",443,0,"实不知丞相何意？","实不知丞相何意？",1,"","","曹操",103,},
        [4592] = {8611,5,1,"曹操",103,0,"将两人推出斩了！","将两人推出斩了！",1,"","","蔡瑁",443,},
        [4593] = {8612,1,1,"曹操",103,0,"来人，蔡瑁张允斩了嘛？","来人，蔡瑁张允斩了嘛？",1,"","","",0,},
        [4594] = {8612,2,2,"鲁肃",312,0,"回丞相，两人已经就地正法。","回丞相，两人已经就地正法。",1,"","","曹操",103,},
        [4595] = {8612,3,1,"曹操",103,0,"不好，吾中计矣！","不好，吾中计矣！",1,"","","鲁肃",312,},
        [4596] = {8612,4,2,"鲁肃",312,0,"丞相何意，子敬不懂。","丞相何意，子敬不懂。",1,"","","曹操",103,},
        [4597] = {8612,5,1,"曹操",103,0,"为时已晚为时已晚。","为时已晚为时已晚。",1,"","","鲁肃",312,},
        [4598] = {8701,1,1,"周瑜",303,0,"孔明此人决不可留！吾决意斩之！","孔明此人决不可留！吾决意斩之！",1,"","","鲁肃",312,},
        [4599] = {8701,2,2,"鲁肃",312,0,"若杀孔明，却被曹操笑也。","若杀孔明，却被曹操笑也。",1,"","","周瑜",303,},
        [4600] = {8701,3,1,"周瑜",303,0,"吾自有公道斩之，教他死而无怨。","吾自有公道斩之，教他死而无怨。",1,"","","鲁肃",312,},
        [4601] = {8701,4,2,"鲁肃",312,0,"何以公道斩之？","何以公道斩之？",1,"","","周瑜",303,},
        [4602] = {8701,5,1,"周瑜",303,0,"子敬休问，来日便见。","子敬休问，来日便见。",1,"","","鲁肃",312,},
        [4603] = {8702,1,2,"周瑜",303,0,"即日将与曹军交战，水路交兵，当以何兵器为先？","即日将与曹军交战，水路交兵，当以何兵器为先？",1,"","","诸葛亮",203,},
        [4604] = {8702,2,1,"诸葛亮",203,0,"大江之上，以弓箭为先。","大江之上，以弓箭为先。",1,"","","周瑜",303,},
        [4605] = {8702,3,2,"周瑜",303,0,"先生之言，甚合愚意。但今军中正缺箭用，敢烦先生监造十万枝箭，以为应敌之具。此系公事，先生幸勿推却。","先生之言，甚合愚意。但今军中正缺箭用，敢烦先生监造十万枝箭，以为应敌之具。此系公事，先生幸勿推却。",1,"","","诸葛亮",203,},
        [4606] = {8702,4,1,"诸葛亮",203,0,"都督见委，自当效劳。敢问十万枝箭，何时要用？","都督见委，自当效劳。敢问十万枝箭，何时要用？",1,"","","周瑜",303,},
        [4607] = {8702,5,2,"周瑜",303,0,"十日之内，可完办否？","十日之内，可完办否？",1,"","","诸葛亮",203,},
        [4608] = {8702,6,1,"诸葛亮",203,0,"操军即日将至，若候十日，必误大事。","操军即日将至，若候十日，必误大事。",1,"","","周瑜",303,},
        [4609] = {8702,7,2,"周瑜",303,0,"先生料几日可完办？","先生料几日可完办？",1,"","","诸葛亮",203,},
        [4610] = {8702,8,1,"诸葛亮",203,0,"只消三日，便可拜纳十万枝箭。","只消三日，便可拜纳十万枝箭。",1,"","","周瑜",303,},
        [4611] = {8702,9,2,"周瑜",303,0,"军中无戏言。","军中无戏言。",1,"","","诸葛亮",203,},
        [4612] = {8702,10,1,"诸葛亮",203,0,"怎敢戏都督！愿纳军令状，三日不办，甘当重罚。","怎敢戏都督！愿纳军令状，三日不办，甘当重罚。",1,"","","周瑜",303,},
        [4613] = {8702,11,2,"周瑜",303,0,"待军事毕后，自有酬劳。","待军事毕后，自有酬劳。",1,"","","诸葛亮",203,},
        [4614] = {8702,12,1,"诸葛亮",203,0,"今日已不及，来日造起。至第三日，可差五百小军到江边搬箭。","今日已不及，来日造起。至第三日，可差五百小军到江边搬箭。",1,"","","周瑜",303,},
        [4615] = {8703,1,2,"鲁肃",312,0,"此人莫非诈乎？","此人莫非诈乎？",1,"","","周瑜",303,},
        [4616] = {8703,2,1,"周瑜",303,0,"他自送死，非我逼他。今明白对众要了文书，他便两胁生翅，也飞不去。","他自送死，非我逼他。今明白对众要了文书，他便两胁生翅，也飞不去。",1,"","","鲁肃",312,},
        [4617] = {8703,3,1,"周瑜",303,0,"我只分付军匠人等，教他故意迟延，凡应用物件，都不与齐备。","我只分付军匠人等，教他故意迟延，凡应用物件，都不与齐备。",1,"","","鲁肃",312,},
        [4618] = {8703,4,1,"周瑜",303,0,"如此，必然误了日期。那时定罪，有何理说？公今可去探他虚实，却来回报。","如此，必然误了日期。那时定罪，有何理说？公今可去探他虚实，却来回报。",1,"","","鲁肃",312,},
        [4619] = {8704,1,1,"诸葛亮",203,0,"吾曾告子敬，休对公瑾说，他必要害我。不想子敬不肯为我隐讳，今日果然又弄出事来。","吾曾告子敬，休对公瑾说，他必要害我。不想子敬不肯为我隐讳，今日果然又弄出事来。",1,"","","鲁肃",312,},
        [4620] = {8704,2,1,"诸葛亮",203,0,"三日内如何造得十万箭？子敬只得救我！","三日内如何造得十万箭？子敬只得救我！",1,"","","鲁肃",312,},
        [4621] = {8704,3,2,"鲁肃",312,0,"公自取其祸，我如何救得你？","公自取其祸，我如何救得你？",1,"","","诸葛亮",203,},
        [4622] = {8704,4,1,"诸葛亮",203,0,"望子敬借我二十只船，每船要军士三十人，船上皆用青布为幔，各束草千余个，分布两边。","望子敬借我二十只船，每船要军士三十人，船上皆用青布为幔，各束草千余个，分布两边。",1,"","","鲁肃",312,},
        [4623] = {8704,5,1,"诸葛亮",203,0,"吾别有妙用。第三日包管有十万枝箭。只不可又教公瑾得知，若彼知之，吾计败矣。","吾别有妙用。第三日包管有十万枝箭。只不可又教公瑾得知，若彼知之，吾计败矣。",1,"","","鲁肃",312,},
        [4624] = {8704,6,2,"鲁肃",312,0,"虽不解汝意，但我允诺便是。","虽不解汝意，但我允诺便是。",1,"","","诸葛亮",203,},
        [4625] = {8705,1,2,"鲁肃",312,0,"公召我来何意？","公召我来何意？",1,"","","诸葛亮",203,},
        [4626] = {8705,2,1,"诸葛亮",203,0,"特请子敬同往取箭。","特请子敬同往取箭。",1,"","","鲁肃",312,},
        [4627] = {8705,3,2,"鲁肃",312,0,"何处去取？","何处去取？",1,"","","诸葛亮",203,},
        [4628] = {8705,4,1,"诸葛亮",203,0,"子敬休问，前去便见。","子敬休问，前去便见。",1,"","","鲁肃",312,},
        [4629] = {8706,1,2,"鲁肃",312,0,"已五更，船为何驶向曹操水寨。","已五更，船为何驶向曹操水寨。",1,"","","诸葛亮",203,},
        [4630] = {8706,2,1,"诸葛亮",203,0,"莫急，将船只头西尾东，一带摆开，就船上擂鼓呐喊。","莫急，将船只头西尾东，一带摆开，就船上擂鼓呐喊。",1,"","","鲁肃",312,},
        [4631] = {8706,3,2,"鲁肃",312,0,"倘曹兵齐出，如之奈何？","倘曹兵齐出，如之奈何？",1,"","","诸葛亮",203,},
        [4632] = {8706,4,1,"诸葛亮",203,0,"吾料曹操于重雾中必不敢出。吾等只顾酌酒取乐，待雾散便回。","吾料曹操于重雾中必不敢出。吾等只顾酌酒取乐，待雾散便回。",1,"","","鲁肃",312,},
        [4633] = {8707,1,2,"于禁",119,0,"丞相，东吴使船江中擂鼓呐喊。","丞相，东吴使船江中擂鼓呐喊。",1,"","","曹操",103,},
        [4634] = {8707,2,1,"曹操",103,0,"重雾迷江，彼军忽至，必有埋伏，切不可轻动。可拨水军弓弩手乱箭射之。","重雾迷江，彼军忽至，必有埋伏，切不可轻动。可拨水军弓弩手乱箭射之。",1,"","","于禁",119,},
        [4635] = {8707,3,1,"曹操",103,0,"张辽、徐晃各带弓弩军三千，火速到江边助射。","张辽、徐晃各带弓弩军三千，火速到江边助射。",1,"","","于禁",119,},
        [4636] = {8707,4,2,"张辽",109,0,"是。","是。",1,"","","曹操",103,},
        [4637] = {8708,1,2,"诸葛亮",203,0,"谢丞相箭！","谢丞相箭！",1,"","","",0,},
        [4638] = {8708,2,1,"曹操",103,0,"什么！快使船去追。","什么！快使船去追。",1,"","","诸葛亮",203,},
        [4639] = {8708,3,2,"于禁",119,0,"船轻水急，已放回二十余里，追之不及。","船轻水急，已放回二十余里，追之不及。",1,"","","曹操",103,},
        [4640] = {8708,4,1,"曹操",103,0,"可恶，好个诸葛孔明！","可恶，好个诸葛孔明！",1,"","","于禁",119,},
        [4641] = {8709,1,1,"诸葛亮",203,0,"每船上箭约五六千矣。不费江东半分之力，已得十万余箭。明日即将来射曹军，却不甚便！","每船上箭约五六千矣。不费江东半分之力，已得十万余箭。明日即将来射曹军，却不甚便！",1,"","","鲁肃",312,},
        [4642] = {8709,2,2,"鲁肃",312,0,"先生真神人也！何以知今日如此大雾？","先生真神人也！何以知今日如此大雾？",1,"","","诸葛亮",203,},
        [4643] = {8709,3,1,"诸葛亮",203,0,"为将而不通天文，不识地利，不知奇门，不晓阴阳，不看阵图，不明兵势，是庸才也。","为将而不通天文，不识地利，不知奇门，不晓阴阳，不看阵图，不明兵势，是庸才也。",1,"","","鲁肃",312,},
        [4644] = {8709,4,1,"诸葛亮",203,0,"亮于三日前已算定今日有大雾，因此敢任三日之限。","亮于三日前已算定今日有大雾，因此敢任三日之限。",1,"","","鲁肃",312,},
        [4645] = {8709,5,1,"诸葛亮",203,0,"公瑾教我十日完办，工匠料物，都不应手，将这一件风流罪过，明白要杀我。我命系于天，公瑾焉能害我哉！","公瑾教我十日完办，工匠料物，都不应手，将这一件风流罪过，明白要杀我。我命系于天，公瑾焉能害我哉！",1,"","","鲁肃",312,},
        [4646] = {8709,6,2,"鲁肃",312,0,"子敬拜服。","子敬拜服。",1,"","","诸葛亮",203,},
        [4647] = {8710,1,2,"周瑜",303,0,"先生神算，使人敬服。","先生神算，使人敬服。",1,"","","诸葛亮",203,},
        [4648] = {8710,2,1,"诸葛亮",203,0,"诡谲小计，何足为奇。","诡谲小计，何足为奇。",1,"","","周瑜",303,},
        [4649] = {8710,3,2,"周瑜",303,0,"昨吾主遣使来催督进军，瑜未有奇计，愿先生教我。","昨吾主遣使来催督进军，瑜未有奇计，愿先生教我。",1,"","","诸葛亮",203,},
        [4650] = {8710,4,1,"诸葛亮",203,0,"亮乃碌碌庸才，安有妙计？","亮乃碌碌庸才，安有妙计？",1,"","","周瑜",303,},
        [4651] = {8710,5,2,"周瑜",303,0,"某昨观曹操水寨，极是严整有法，非等闲可攻。思得一计，不知可否。先生幸为我一决之。","某昨观曹操水寨，极是严整有法，非等闲可攻。思得一计，不知可否。先生幸为我一决之。",1,"","","诸葛亮",203,},
        [4652] = {8710,6,1,"诸葛亮",203,0,"都督且休言。各自写于手内，看同也不同。","都督且休言。各自写于手内，看同也不同。",1,"","","周瑜",303,},
        [4653] = {8710,7,2,"周瑜",303,0,"吾乃一火字。","吾乃一火字。",1,"","","诸葛亮",203,},
        [4654] = {8710,8,1,"诸葛亮",203,0,"吾亦一火字。","吾亦一火字。",1,"","","周瑜",303,},
        [4655] = {8710,9,2,"周瑜",303,0,"既我两人所见相同，更无疑矣。幸勿漏泄。","既我两人所见相同，更无疑矣。幸勿漏泄。",1,"","","诸葛亮",203,},
        [4656] = {8710,10,1,"诸葛亮",203,0,"两家公事，岂有漏泄之理。吾料曹操虽两番经我这条计，然必不为备。今都督尽行之可也。","两家公事，岂有漏泄之理。吾料曹操虽两番经我这条计，然必不为备。今都督尽行之可也。",1,"","","周瑜",303,},
        [4657] = {8801,1,2,"荀攸",120,0,"江东有周瑜、诸葛亮二人用计，急切难破。可差人去东吴诈降，为奸细内应，以通消息，方可图也。","江东有周瑜、诸葛亮二人用计，急切难破。可差人去东吴诈降，为奸细内应，以通消息，方可图也。",1,"","","曹操",103,},
        [4658] = {8801,2,1,"曹操",103,0,"此言正合吾意。汝料军中谁可行此计？","此言正合吾意。汝料军中谁可行此计？",1,"","","荀攸",120,},
        [4659] = {8801,3,2,"荀攸",120,0,"蔡瑁被诛，蔡氏宗族，皆在军中。瑁之族弟蔡中、蔡和现为副将。丞相可以恩结之，差往诈降东吴，必不见疑。","蔡瑁被诛，蔡氏宗族，皆在军中。瑁之族弟蔡中、蔡和现为副将。丞相可以恩结之，差往诈降东吴，必不见疑。",1,"","","曹操",103,},
        [4660] = {8801,4,1,"曹操",103,0,"好计。","好计。",1,"","","荀攸",120,},
        [4661] = {8802,1,1,"曹操",103,0,"蔡中、蔡和，汝二人可引些少军士，去东吴诈降。但有动静，使人密报，事成之后，重加封赏。休怀二心！","蔡中、蔡和，汝二人可引些少军士，去东吴诈降。但有动静，使人密报，事成之后，重加封赏。休怀二心！",1,"","","蔡和",138,},
        [4662] = {8802,2,2,"蔡和",138,0,"吾等妻子俱在荆州，安敢怀二心，丞相勿疑。某二人必取周瑜、诸葛亮之首，献于麾下。","吾等妻子俱在荆州，安敢怀二心，丞相勿疑。某二人必取周瑜、诸葛亮之首，献于麾下。",1,"","","曹操",103,},
        [4663] = {8802,3,1,"曹操",103,0,"好，孤现在就厚赏汝二人。","好，孤现在就厚赏汝二人。",1,"","","蔡和",138,},
        [4664] = {8802,4,1,"曹操",103,0,"次日，汝二人带五百军士，驾船数只，顺风望着南岸来。","次日，汝二人带五百军士，驾船数只，顺风望着南岸来。",1,"","","蔡和",138,},
        [4665] = {8802,5,2,"蔡和",138,0,"是。","是。",1,"","","曹操",103,},
        [4666] = {8803,1,2,"蔡和",138,0,"吾兄无罪，被操贼所杀。吾二人欲报兄仇，特来投降。望赐收录，愿为前部。","吾兄无罪，被操贼所杀。吾二人欲报兄仇，特来投降。望赐收录，愿为前部。",1,"","","周瑜",303,},
        [4667] = {8803,2,1,"周瑜",303,0,"欢迎二人加入东吴，重赏！与甘宁引军为前部。","欢迎二人加入东吴，重赏！与甘宁引军为前部。",1,"","","蔡和",138,},
        [4668] = {8803,3,2,"蔡和",138,0,"谢周郎！","谢周郎！",1,"","","周瑜",303,},
        [4669] = {8804,1,1,"周瑜",303,0,"蔡中、蔡和二人不带家小，非真投降，乃曹操使来为奸细者。吾今欲将计就计，教他通报消息。","蔡中、蔡和二人不带家小，非真投降，乃曹操使来为奸细者。吾今欲将计就计，教他通报消息。",1,"","","甘宁",308,},
        [4670] = {8804,2,1,"周瑜",303,0,"汝可殷勤相待，就里提防。至出兵之日，先要杀他两个祭旗。汝切须小心，不可有误。","汝可殷勤相待，就里提防。至出兵之日，先要杀他两个祭旗。汝切须小心，不可有误。",1,"","","甘宁",308,},
        [4671] = {8804,3,2,"甘宁",308,0,"是！","是！",1,"","","周瑜",303,},
        [4672] = {8805,1,2,"黄盖",314,0,"见过周郎。","见过周郎。",1,"","","周瑜",303,},
        [4673] = {8805,2,1,"周瑜",303,0,"公覆夜至，必有良谋见教？","公覆夜至，必有良谋见教？",1,"","","黄盖",314,},
        [4674] = {8805,3,2,"黄盖",314,0,"彼众我寡，不宜久持，何不用火攻之？","彼众我寡，不宜久持，何不用火攻之？",1,"","","周瑜",303,},
        [4675] = {8805,4,1,"周瑜",303,0,"谁教公献此计？","谁教公献此计？",1,"","","黄盖",314,},
        [4676] = {8805,5,2,"黄盖",314,0,"某出自己意，非他人之所教也。","某出自己意，非他人之所教也。",1,"","","周瑜",303,},
        [4677] = {8805,6,1,"周瑜",303,0,"吾正欲如此，故留蔡中、蔡和诈降之人，以通消息，但恨无一人为我行诈降计耳。","吾正欲如此，故留蔡中、蔡和诈降之人，以通消息，但恨无一人为我行诈降计耳。",1,"","","黄盖",314,},
        [4678] = {8805,7,2,"黄盖",314,0,"某愿行此计。","某愿行此计。",1,"","","周瑜",303,},
        [4679] = {8805,8,1,"周瑜",303,0,"不受些苦，彼如何肯信？","不受些苦，彼如何肯信？",1,"","","黄盖",314,},
        [4680] = {8805,9,2,"黄盖",314,0,"某受孙氏厚恩，虽肝脑涂地，亦无怨悔。","某受孙氏厚恩，虽肝脑涂地，亦无怨悔。",1,"","","周瑜",303,},
        [4681] = {8805,10,1,"周瑜",303,0,"君若肯行此苦肉计，则江东之万幸也。","君若肯行此苦肉计，则江东之万幸也。",1,"","","黄盖",314,},
        [4682] = {8805,11,2,"黄盖",314,0,"某死亦无怨。","某死亦无怨。",1,"","","周瑜",303,},
        [4683] = {8806,1,1,"周瑜",303,0,"操引百万之众，连络三百余里，非一日可破。今令诸将各领三个月粮草，准备御敌。","操引百万之众，连络三百余里，非一日可破。今令诸将各领三个月粮草，准备御敌。",1,"","","",0,},
        [4684] = {8806,2,2,"黄盖",314,0,"莫说三个月，便支三十个月粮草，也不济事！若是这个月破的便破。","莫说三个月，便支三十个月粮草，也不济事！若是这个月破的便破。",1,"","","周瑜",303,},
        [4685] = {8806,3,2,"黄盖",314,0,"若是这个月破不的，只可依张子布之言，弃甲倒戈，北面而降之耳！","若是这个月破不的，只可依张子布之言，弃甲倒戈，北面而降之耳！",1,"","","周瑜",303,},
        [4686] = {8806,4,1,"周瑜",303,0,"吾奉主公之命，督兵破曹，敢有再言降者必斩。今两军相敌之际，汝敢出此言，慢我军心，不斩汝首，难以服众！","吾奉主公之命，督兵破曹，敢有再言降者必斩。今两军相敌之际，汝敢出此言，慢我军心，不斩汝首，难以服众！",1,"","","黄盖",314,},
        [4687] = {8806,5,2,"黄盖",314,0,"吾自随破虏将军，纵横东南，已历三世，那有你来？","吾自随破虏将军，纵横东南，已历三世，那有你来？",1,"","","周瑜",303,},
        [4688] = {8806,6,1,"周瑜",303,0,"速速拖下去斩了！","速速拖下去斩了！",1,"","","黄盖",314,},
        [4689] = {8806,7,2,"甘宁",308,0,"公覆乃东吴旧臣，望宽恕之。","公覆乃东吴旧臣，望宽恕之。",1,"","","周瑜",303,},
        [4690] = {8806,8,1,"周瑜",303,0,"汝何敢多言，乱吾法度！将甘宁拖出去杖责。","汝何敢多言，乱吾法度！将甘宁拖出去杖责。",1,"","","甘宁",308,},
        [4691] = {8806,9,2,"鲁肃",312,0,"黄盖罪固当诛，但于军不利。望都督宽恕，权且记罪。破曹之后，斩亦未迟。","黄盖罪固当诛，但于军不利。望都督宽恕，权且记罪。破曹之后，斩亦未迟。",1,"","","周瑜",303,},
        [4692] = {8806,10,1,"周瑜",303,0,"若不看众官面皮，决须斩首！今且免死！拖翻打一百脊杖，以正其罪！","若不看众官面皮，决须斩首！今且免死！拖翻打一百脊杖，以正其罪！",1,"","","鲁肃",312,},
        [4693] = {8807,1,2,"鲁肃",312,0,"今日公瑾怒责公覆，我等皆是他部下，不敢犯颜苦谏。先生是客，何故袖手旁观，不发一语？","今日公瑾怒责公覆，我等皆是他部下，不敢犯颜苦谏。先生是客，何故袖手旁观，不发一语？",1,"","","诸葛亮",203,},
        [4694] = {8807,2,1,"诸葛亮",203,0,"子敬欺我。","子敬欺我。",1,"","","鲁肃",312,},
        [4695] = {8807,3,2,"鲁肃",312,0,"肃与先生渡江以来，未尝一事相欺。今何出此言？","肃与先生渡江以来，未尝一事相欺。今何出此言？",1,"","","诸葛亮",203,},
        [4696] = {8807,4,1,"诸葛亮",203,0,"子敬岂不知公瑾今日毒打黄公覆，乃其计耶？如何要我劝他？","子敬岂不知公瑾今日毒打黄公覆，乃其计耶？如何要我劝他？",1,"","","鲁肃",312,},
        [4697] = {8807,5,2,"鲁肃",312,0,"真不知也，这才知晓！","真不知也，这才知晓！",1,"","","诸葛亮",203,},
        [4698] = {8807,6,1,"诸葛亮",203,0,"不用苦肉计，何能瞒过曹操？今必令黄公覆去诈降，却教蔡中、蔡和报知其事矣。","不用苦肉计，何能瞒过曹操？今必令黄公覆去诈降，却教蔡中、蔡和报知其事矣。",1,"","","鲁肃",312,},
        [4699] = {8808,1,2,"阚泽",330,0,"将军莫非与都督有仇？","将军莫非与都督有仇？",1,"","","黄盖",314,},
        [4700] = {8808,2,1,"黄盖",314,0,"非也。","非也。",1,"","","阚泽",330,},
        [4701] = {8808,3,2,"阚泽",330,0,"然则公之受责，莫非苦肉计乎？","然则公之受责，莫非苦肉计乎？",1,"","","黄盖",314,},
        [4702] = {8808,4,1,"黄盖",314,0,"何以知之？","何以知之？",1,"","","阚泽",330,},
        [4703] = {8808,5,2,"阚泽",330,0,"某观公瑾举动，已料着八九分。","某观公瑾举动，已料着八九分。",1,"","","黄盖",314,},
        [4704] = {8808,6,1,"黄盖",314,0,"某受吴侯三世厚恩，无以为报，故献此计，以破曹操。吾虽受苦，亦无所恨。","某受吴侯三世厚恩，无以为报，故献此计，以破曹操。吾虽受苦，亦无所恨。",1,"","","阚泽",330,},
        [4705] = {8808,7,1,"黄盖",314,0,"吾遍观军中，无一人可为心腹者。惟公素有忠义之心，敢以心腹相告。","吾遍观军中，无一人可为心腹者。惟公素有忠义之心，敢以心腹相告。",1,"","","阚泽",330,},
        [4706] = {8808,8,2,"阚泽",330,0,"公之告我，无非要我献诈降书耳。","公之告我，无非要我献诈降书耳。",1,"","","黄盖",314,},
        [4707] = {8808,9,1,"黄盖",314,0,"实有此意。未知肯否？","实有此意。未知肯否？",1,"","","阚泽",330,},
        [4708] = {8808,10,2,"阚泽",330,0,"大丈夫处世，不能立功建业，不几与草木同腐乎！公既捐躯报主，泽又何惜微生！","大丈夫处世，不能立功建业，不几与草木同腐乎！公既捐躯报主，泽又何惜微生！",1,"","","黄盖",314,},
        [4709] = {8808,11,1,"黄盖",314,0,"不知如何感谢，唯有一拜。","不知如何感谢，唯有一拜。",1,"","","阚泽",330,},
        [4710] = {8808,12,2,"阚泽",330,0,"事不可缓，即今便行。","事不可缓，即今便行。",1,"","","黄盖",314,},
        [4711] = {8808,13,1,"黄盖",314,0,"书已修下了。","书已修下了。",1,"","","阚泽",330,},
        [4712] = {8808,14,2,"阚泽",330,0,"吾这就扮作渔翁，驾小舟望北岸而去。","吾这就扮作渔翁，驾小舟望北岸而去。",1,"","","黄盖",314,},
        [4713] = {8809,1,2,"曹操",103,0,"汝既是东吴参谋，来此何干？","汝既是东吴参谋，来此何干？",1,"","","阚泽",330,},
        [4714] = {8809,2,1,"阚泽",330,0,"黄公覆乃东吴三世旧臣，今被周瑜于众将之前，无端毒打，不胜忿恨。因欲投降丞相，特谋之于我。未知丞相肯容纳否？","黄公覆乃东吴三世旧臣，今被周瑜于众将之前，无端毒打，不胜忿恨。因欲投降丞相，特谋之于我。未知丞相肯容纳否？",1,"","","曹操",103,},
        [4715] = {8809,3,2,"曹操",103,0,"黄盖用苦肉计，令汝下诈降书，就中取事，却敢来戏侮我耶！推出斩之。","黄盖用苦肉计，令汝下诈降书，就中取事，却敢来戏侮我耶！推出斩之。",1,"","","阚泽",330,},
        [4716] = {8809,4,1,"阚泽",330,0,"哈哈哈哈哈。","哈哈哈哈哈。",1,"","","曹操",103,},
        [4717] = {8809,5,2,"曹操",103,0,"吾已识破奸计，汝何故哂笑？","吾已识破奸计，汝何故哂笑？",1,"","","阚泽",330,},
        [4718] = {8809,6,1,"阚泽",330,0,"吾不笑你。吾笑黄公覆不识人耳。","吾不笑你。吾笑黄公覆不识人耳。",1,"","","曹操",103,},
        [4719] = {8809,7,2,"曹操",103,0,"吾自幼熟读兵书，深知奸伪之道。汝这条计，只好瞒别人，如何瞒得我！","吾自幼熟读兵书，深知奸伪之道。汝这条计，只好瞒别人，如何瞒得我！",1,"","","阚泽",330,},
        [4720] = {8809,8,1,"阚泽",330,0,"亏汝不惶恐，敢自夸熟读兵书！还不及早收兵回去！倘若交战，必被周瑜擒矣！无学之辈！可惜吾屈死汝手！","亏汝不惶恐，敢自夸熟读兵书！还不及早收兵回去！倘若交战，必被周瑜擒矣！无学之辈！可惜吾屈死汝手！",1,"","","曹操",103,},
        [4721] = {8809,9,2,"曹操",103,0,"何谓我无学？","何谓我无学？",1,"","","阚泽",330,},
        [4722] = {8809,10,1,"阚泽",330,0,"岂不闻背主作窃，不可定期？倘今约定日期，急切下不得手，这里反来接应，事必泄漏。","岂不闻背主作窃，不可定期？倘今约定日期，急切下不得手，这里反来接应，事必泄漏。",1,"","","曹操",103,},
        [4723] = {8809,11,1,"阚泽",330,0,"但可觑便而行，岂可预期相订乎？汝不明此理，欲屈杀好人，真无学之辈也！","但可觑便而行，岂可预期相订乎？汝不明此理，欲屈杀好人，真无学之辈也！",1,"","","曹操",103,},
        [4724] = {8809,12,2,"曹操",103,0,"某见事不明，误犯尊威，幸勿挂怀。","某见事不明，误犯尊威，幸勿挂怀。",1,"","","阚泽",330,},
        [4725] = {8809,13,1,"阚泽",330,0,"吾与黄公覆，倾心投降，如婴儿之望父母，岂有诈乎！","吾与黄公覆，倾心投降，如婴儿之望父母，岂有诈乎！",1,"","","曹操",103,},
        [4726] = {8809,14,2,"曹操",103,0,"若二人能建大功，他日受爵，必在诸人之上。","若二人能建大功，他日受爵，必在诸人之上。",1,"","","阚泽",330,},
        [4727] = {8809,15,1,"阚泽",330,0,"某等非为爵禄而来，实应天顺人耳。","某等非为爵禄而来，实应天顺人耳。",1,"","","曹操",103,},
        [4728] = {8810,1,2,"阚泽",330,0,"将军昨为救黄公覆，被周公瑾所辱，吾甚不平。","将军昨为救黄公覆，被周公瑾所辱，吾甚不平。",1,"","","甘宁",308,},
        [4729] = {8810,2,1,"甘宁",308,0,"周公瑾只自恃其能，全不以我等为念。我今被辱，羞见江左诸人！","周公瑾只自恃其能，全不以我等为念。我今被辱，羞见江左诸人！",1,"","","阚泽",330,},
        [4730] = {8810,3,2,"蔡和",138,0,"将军何故烦恼？先生有何不平？","将军何故烦恼？先生有何不平？",1,"","","甘宁",308,},
        [4731] = {8810,4,1,"甘宁",308,0,"吾等腹中之苦，汝岂知耶！","吾等腹中之苦，汝岂知耶！",1,"","","蔡和",138,},
        [4732] = {8810,5,2,"蔡和",138,0,"莫非欲背吴投曹耶？","莫非欲背吴投曹耶？",1,"","","甘宁",308,},
        [4733] = {8810,6,1,"甘宁",308,0,"吾事已为窥破，不可不杀之以灭口！","吾事已为窥破，不可不杀之以灭口！",1,"","","蔡和",138,},
        [4734] = {8810,7,2,"蔡和",138,0,"二公勿忧。吾亦当以心腹之事相告。","二公勿忧。吾亦当以心腹之事相告。",1,"","","甘宁",308,},
        [4735] = {8810,8,1,"甘宁",308,0,"可速言之！","可速言之！",1,"","","蔡和",138,},
        [4736] = {8810,9,2,"蔡和",138,0,"吾二人乃曹公使来诈降者。二公若有归顺之心，吾当引进。","吾二人乃曹公使来诈降者。二公若有归顺之心，吾当引进。",1,"","","甘宁",308,},
        [4737] = {8810,10,1,"甘宁",308,0,"汝言果真？","汝言果真？",1,"","","蔡和",138,},
        [4738] = {8810,11,2,"蔡和",138,0,"安敢相欺！","安敢相欺！",1,"","","甘宁",308,},
        [4739] = {8810,12,1,"甘宁",308,0,"若如此，是天赐其便也！","若如此，是天赐其便也！",1,"","","蔡和",138,},
        [4740] = {8810,13,2,"蔡和",138,0,"黄公覆与将军被辱之事，吾已报知丞相矣。","黄公覆与将军被辱之事，吾已报知丞相矣。",1,"","","甘宁",308,},
        [4741] = {8810,14,1,"阚泽",330,0,"吾已为黄公覆献书丞相，今特来见兴霸，相约同降耳。","吾已为黄公覆献书丞相，今特来见兴霸，相约同降耳。",1,"","","蔡和",138,},
        [4742] = {8810,15,1,"甘宁",308,0,"大丈夫既遇明主，自当倾心相投。","大丈夫既遇明主，自当倾心相投。",1,"","","蔡和",138,},
        [4743] = {8811,1,1,"曹操",103,0,"江左甘宁，被周瑜所辱，愿为内应，黄盖受责，令阚泽来纳降，俱未可深信。","江左甘宁，被周瑜所辱，愿为内应，黄盖受责，令阚泽来纳降，俱未可深信。",1,"","","",0,},
        [4744] = {8811,2,1,"曹操",103,0,"谁敢直入周瑜寨中，探听实信？","谁敢直入周瑜寨中，探听实信？",1,"","","",0,},
        [4745] = {8811,3,2,"蒋干",143,0,"某前日空往东吴，未得成功，深怀惭愧。今愿舍身再往，务得实信，回报丞相。","某前日空往东吴，未得成功，深怀惭愧。今愿舍身再往，务得实信，回报丞相。",1,"","","曹操",103,},
        [4746] = {8812,1,1,"周瑜",303,0,"子翼何故欺吾太甚？","子翼何故欺吾太甚？",1,"","","蒋干",143,},
        [4747] = {8812,2,2,"蒋干",143,0,"吾想与你乃旧日弟兄，特来吐心腹事，何言相欺也？","吾想与你乃旧日弟兄，特来吐心腹事，何言相欺也？",1,"","","周瑜",303,},
        [4748] = {8812,3,1,"周瑜",303,0,"汝要说我降，除非海枯石烂！前番吾念旧日交情，请你痛饮一醉，留你共榻。","汝要说我降，除非海枯石烂！前番吾念旧日交情，请你痛饮一醉，留你共榻。",1,"","","蒋干",143,},
        [4749] = {8812,4,1,"周瑜",303,0,"你却盗吾私书，不辞而去，归报曹操，杀了蔡瑁、张允，致使吾事不成。今日无故又来，必不怀好意！","你却盗吾私书，不辞而去，归报曹操，杀了蔡瑁、张允，致使吾事不成。今日无故又来，必不怀好意！",1,"","","蒋干",143,},
        [4750] = {8812,5,1,"周瑜",303,0,"吾不看旧日之情，一刀两段！本待送你过去，争奈吾一二日间，便要破曹贼，待留你在军中，又必有泄漏。","吾不看旧日之情，一刀两段！本待送你过去，争奈吾一二日间，便要破曹贼，待留你在军中，又必有泄漏。",1,"","","蒋干",143,},
        [4751] = {8812,6,1,"周瑜",303,0,"送子翼往西山庵中歇息。待吾破了曹操，那时渡你过江未迟。","送子翼往西山庵中歇息。待吾破了曹操，那时渡你过江未迟。",1,"","","蒋干",143,},
        [4752] = {8812,7,2,"蒋干",143,0,"这个…","这个…",1,"","","周瑜",303,},
        [4753] = {8813,1,1,"周瑜",303,0,"庞士元是否已于西山静候。","庞士元是否已于西山静候。",1,"","","鲁肃",312,},
        [4754] = {8813,2,2,"鲁肃",312,0,"已至西山。","已至西山。",1,"","","周瑜",303,},
        [4755] = {8813,3,1,"周瑜",303,0,"好，就静待蒋干中计吧。","好，就静待蒋干中计吧。",1,"","","鲁肃",312,},
        [4756] = {8813,4,2,"主角",1,0,"庞士元势必深入曹营，为其安全，我愿假扮侍从随行。","庞士元势必深入曹营，为其安全，我愿假扮侍从随行。",1,"","","周瑜",303,},
        [4757] = {8813,5,1,"周瑜",303,0,"有劳扬威将军了。","有劳扬威将军了。",1,"","","主角",1,},
        [4758] = {8901,1,1,"蒋干",143,0,"汝何人，居于这西山之中，夜星读书。","汝何人，居于这西山之中，夜星读书。",1,"","","庞统",211,},
        [4759] = {8901,2,2,"庞统",211,0,"姓庞，名统，字士元。","姓庞，名统，字士元。",1,"","","蒋干",143,},
        [4760] = {8901,3,1,"蒋干",143,0,"莫非凤雏先生否？","莫非凤雏先生否？",1,"","","庞统",211,},
        [4761] = {8901,4,2,"庞统",211,0,"然也。","然也。",1,"","","蒋干",143,},
        [4762] = {8901,5,1,"蒋干",143,0,"久闻大名，今何僻居此地？","久闻大名，今何僻居此地？",1,"","","庞统",211,},
        [4763] = {8901,6,2,"庞统",211,0,"周瑜自恃才高，不能容物，吾故隐居于此。公乃何人？","周瑜自恃才高，不能容物，吾故隐居于此。公乃何人？",1,"","","蒋干",143,},
        [4764] = {8901,7,1,"蒋干",143,0,"吾蒋干也。以公之才，何往不利？如肯归曹，干当引进。","吾蒋干也。以公之才，何往不利？如肯归曹，干当引进。",1,"","","庞统",211,},
        [4765] = {8901,8,2,"庞统",211,0,"吾亦欲离江东久矣。公既有引进之心，即今便当一行。如迟则周瑜闻之，必将见害。","吾亦欲离江东久矣。公既有引进之心，即今便当一行。如迟则周瑜闻之，必将见害。",1,"","","蒋干",143,},
        [4766] = {8901,9,1,"蒋干",143,0,"你我现在就下山，寻船只，飞棹投江北。","你我现在就下山，寻船只，飞棹投江北。",1,"","","庞统",211,},
        [4767] = {8902,1,1,"曹操",103,0,"周瑜年幼，恃才欺众，不用良谋。操久闻先生大名，今得惠顾，乞不吝教诲。","周瑜年幼，恃才欺众，不用良谋。操久闻先生大名，今得惠顾，乞不吝教诲。",1,"","","庞统",211,},
        [4768] = {8902,2,2,"庞统",211,0,"某素闻丞相用兵有法，今一睹军容。傍山依林，前后顾盼，出入有门，进退曲折，虽孙、吴再生，穰苴复出，亦不过此矣。","某素闻丞相用兵有法，今一睹军容。傍山依林，前后顾盼，出入有门，进退曲折，虽孙、吴再生，穰苴复出，亦不过此矣。",1,"","","曹操",103,},
        [4769] = {8902,3,1,"曹操",103,0,"先生勿得过誉。","先生勿得过誉。",1,"","","庞统",211,},
        [4770] = {8902,4,2,"庞统",211,0,"敢问军中有良医否？","敢问军中有良医否？",1,"","","曹操",103,},
        [4771] = {8902,5,1,"曹操",103,0,"为何有此一问？","为何有此一问？",1,"","","庞统",211,},
        [4772] = {8902,6,2,"庞统",211,0,"水军多疾，须用良医治之。","水军多疾，须用良医治之。",1,"","","曹操",103,},
        [4773] = {8902,7,1,"曹操",103,0,"因不服水土，俱生呕吐之疾，多有死者，孤正虑此事。","因不服水土，俱生呕吐之疾，多有死者，孤正虑此事。",1,"","","庞统",211,},
        [4774] = {8902,8,2,"庞统",211,0,"丞相教练水军之法甚妙，但可惜不全。某有一策，使大小水军，并无疾病，安稳成功。","丞相教练水军之法甚妙，但可惜不全。某有一策，使大小水军，并无疾病，安稳成功。",1,"","","曹操",103,},
        [4775] = {8902,9,1,"曹操",103,0,"是何计策？","是何计策？",1,"","","庞统",211,},
        [4776] = {8902,10,2,"庞统",211,0,"大江之中，潮生潮落，风浪不息，北兵不惯乘舟，受此颠播，便生疾病。","大江之中，潮生潮落，风浪不息，北兵不惯乘舟，受此颠播，便生疾病。",1,"","","曹操",103,},
        [4777] = {8902,11,2,"庞统",211,0,"若以大船小船各皆配搭，或三十为一排，或五十为一排，首尾用铁环连锁，上铺阔板。","若以大船小船各皆配搭，或三十为一排，或五十为一排，首尾用铁环连锁，上铺阔板。",1,"","","曹操",103,},
        [4778] = {8902,12,2,"庞统",211,0,"休言人可渡，马亦可走矣，乘此而行，任他风浪潮水上下，复何惧哉？","休言人可渡，马亦可走矣，乘此而行，任他风浪潮水上下，复何惧哉？",1,"","","曹操",103,},
        [4779] = {8902,13,1,"曹操",103,0,"非先生良谋，安能破东吴耶！","非先生良谋，安能破东吴耶！",1,"","","庞统",211,},
        [4780] = {8902,14,2,"庞统",211,0,"愚浅之见，丞相自裁之。","愚浅之见，丞相自裁之。",1,"","","曹操",103,},
        [4781] = {8902,15,1,"曹操",103,0,"速唤军中铁匠，连夜打造连环大钉，锁住船只。","速唤军中铁匠，连夜打造连环大钉，锁住船只。",1,"","","庞统",211,},
        [4782] = {8903,1,1,"徐庶",212,0,"你好大胆！黄盖用苦肉计，阚泽下诈降书，你又来献连环计，只恐烧不尽绝！","你好大胆！黄盖用苦肉计，阚泽下诈降书，你又来献连环计，只恐烧不尽绝！",1,"","","庞统",211,},
        [4783] = {8903,2,1,"徐庶",212,0,"你们把出这等毒手来，只好瞒曹操，也须瞒我不得！","你们把出这等毒手来，只好瞒曹操，也须瞒我不得！",1,"","","庞统",211,},
        [4784] = {8903,3,2,"庞统",211,0,"你若说破我计，可惜江南八十一州百姓，皆是你送了也！","你若说破我计，可惜江南八十一州百姓，皆是你送了也！",1,"","","徐庶",212,},
        [4785] = {8903,4,1,"徐庶",212,0,"此间八十三万人马，性命如何？","此间八十三万人马，性命如何？",1,"","","庞统",211,},
        [4786] = {8903,5,2,"主角",1,0,"元直真欲破之计耶？","元直真欲破之计耶？",1,"","","徐庶",212,},
        [4787] = {8903,6,1,"徐庶",212,0,"吾感刘皇叔厚恩，未尝忘报。曹操送死吾母，吾已说过终身不设一谋，今安肯破兄良策？","吾感刘皇叔厚恩，未尝忘报。曹操送死吾母，吾已说过终身不设一谋，今安肯破兄良策？",1,"","","主角",1,},
        [4788] = {8903,7,1,"徐庶",212,0,"只是我亦随军在此，兵败之后，玉石不分，岂能免难？君当教我脱身之术，我即缄口远避矣。","只是我亦随军在此，兵败之后，玉石不分，岂能免难？君当教我脱身之术，我即缄口远避矣。",1,"","","主角",1,},
        [4789] = {8903,8,2,"主角",1,0,"元直如此高见远识，谅此有何难哉！","元直如此高见远识，谅此有何难哉！",1,"","","徐庶",212,},
        [4790] = {8903,9,1,"徐庶",212,0,"愿将军赐教。","愿将军赐教。",1,"","","主角",1,},
        [4791] = {8903,10,2,"主角",1,0,"烦请元直附耳。","烦请元直附耳。",1,"","","徐庶",212,},
        [4792] = {8903,11,1,"徐庶",212,0,"哈哈哈，此乃妙计，谢扬威将军。","哈哈哈，此乃妙计，谢扬威将军。",1,"","","主角",1,},
        [4793] = {8904,1,1,"曹操",103,0,"军中传言西凉州韩遂、马腾谋反，杀奔许都来。虽未辨虚实，然不可不防。","军中传言西凉州韩遂、马腾谋反，杀奔许都来。虽未辨虚实，然不可不防。",1,"","","",0,},
        [4794] = {8904,2,2,"徐庶",212,0,"庶蒙丞相收录，恨无寸功报效。请得三千人马，星夜往散关把住隘口，如有紧急，再行告报。","庶蒙丞相收录，恨无寸功报效。请得三千人马，星夜往散关把住隘口，如有紧急，再行告报。",1,"","","曹操",103,},
        [4795] = {8904,3,1,"曹操",103,0,"若得元直去，吾无忧矣！散关之上，亦有军兵，公统领之。目下拨三千马步军，命臧霸为先锋，星夜前去，不可稽迟。","若得元直去，吾无忧矣！散关之上，亦有军兵，公统领之。目下拨三千马步军，命臧霸为先锋，星夜前去，不可稽迟。",1,"","","徐庶",212,},
        [4796] = {8904,4,2,"徐庶",212,0,"徐庶这就与臧霸出发。","徐庶这就与臧霸出发。",1,"","","曹操",103,},
        [4797] = {9001,1,1,"曹操",103,0,"周瑜、鲁肃，不识天时！今幸有投降之人，为彼心腹之患，此天助吾也。","周瑜、鲁肃，不识天时！今幸有投降之人，为彼心腹之患，此天助吾也。",1,"","","",0,},
        [4798] = {9001,2,2,"荀攸",120,0,"丞相勿言，恐有泄漏。","丞相勿言，恐有泄漏。",1,"","","曹操",103,},
        [4799] = {9001,3,1,"曹操",103,0,"座上诸公，与近侍左右，皆吾心腹之人也，言之何碍！","座上诸公，与近侍左右，皆吾心腹之人也，言之何碍！",1,"","","荀攸",120,},
        [4800] = {9001,4,1,"曹操",103,0,"刘备、诸葛亮，汝不料蝼蚁之力，欲撼泰山，何其愚耶！","刘备、诸葛亮，汝不料蝼蚁之力，欲撼泰山，何其愚耶！",1,"","","荀攸",120,},
        [4801] = {9002,1,1,"曹操",103,0,"孤闻鸦声望南飞鸣而去，此鸦缘何夜鸣？","孤闻鸦声望南飞鸣而去，此鸦缘何夜鸣？",1,"","","",0,},
        [4802] = {9002,2,2,"程昱",121,0,"鸦见月明，疑是天晓，故离树而鸣也。","鸦见月明，疑是天晓，故离树而鸣也。",1,"","","曹操",103,},
        [4803] = {9002,3,1,"曹操",103,0,"我持此槊，破黄巾、擒吕布、灭袁术、收袁绍，深入塞北，直抵辽东，纵横天下，颇不负大丈夫之志也。","我持此槊，破黄巾、擒吕布、灭袁术、收袁绍，深入塞北，直抵辽东，纵横天下，颇不负大丈夫之志也。",1,"","","程昱",121,},
        [4804] = {9002,4,1,"曹操",103,0,"今对此景，甚有慷慨。吾当作歌，汝等和之。","今对此景，甚有慷慨。吾当作歌，汝等和之。",1,"","","程昱",121,},
        [4805] = {9002,5,1,"曹操",103,0,"对酒当歌，人生几何，譬如朝露，去日苦多。","对酒当歌，人生几何，譬如朝露，去日苦多。",1,"","","程昱",121,},
        [4806] = {9002,6,1,"曹操",103,0,"慨当以慷，忧思难忘，何以解忧，惟有杜康。","慨当以慷，忧思难忘，何以解忧，惟有杜康。",1,"","","程昱",121,},
        [4807] = {9002,7,1,"曹操",103,0,"青青子衿，悠悠我心，但为君故，沉吟至今。","青青子衿，悠悠我心，但为君故，沉吟至今。",1,"","","程昱",121,},
        [4808] = {9003,1,1,"曹操",103,0,"若非天命助吾，安得凤雏妙计？铁索连舟，果然渡江如履平地。","若非天命助吾，安得凤雏妙计？铁索连舟，果然渡江如履平地。",1,"","","",0,},
        [4809] = {9003,2,2,"程昱",121,0,"船皆连锁，固是平稳，但彼若用火攻，难以回避。不可不防。","船皆连锁，固是平稳，但彼若用火攻，难以回避。不可不防。",1,"","","曹操",103,},
        [4810] = {9003,3,1,"曹操",103,0,"程仲德虽有远虑，却还有见不到处。","程仲德虽有远虑，却还有见不到处。",1,"","","程昱",121,},
        [4811] = {9003,4,2,"荀攸",120,0,"仲德之言甚是。丞相何故笑之？","仲德之言甚是。丞相何故笑之？",1,"","","曹操",103,},
        [4812] = {9003,5,1,"曹操",103,0,"凡用火攻，必藉风力。方今隆冬之际，但有西风北风，安有东风南风耶？","凡用火攻，必藉风力。方今隆冬之际，但有西风北风，安有东风南风耶？",1,"","","荀攸",120,},
        [4813] = {9003,6,1,"曹操",103,0,"吾居于西北之上，彼兵皆在南岸，彼若用火，是烧自己之兵也，吾何惧哉？若是十月小春之时，吾早已提备矣。","吾居于西北之上，彼兵皆在南岸，彼若用火，是烧自己之兵也，吾何惧哉？若是十月小春之时，吾早已提备矣。",1,"","","荀攸",120,},
        [4814] = {9003,7,2,"程昱",121,0,"丞相高见，众人不及。","丞相高见，众人不及。",1,"","","曹操",103,},
        [4815] = {9004,1,1,"主角",1,0,"士元，计已成，曹操明日便会出征，我们赶紧撤离。","士元，计已成，曹操明日便会出征，我们赶紧撤离。",1,"","","庞统",211,},
        [4816] = {9004,2,2,"庞统",211,0,"好，我们寻一扁舟星夜悄行。","好，我们寻一扁舟星夜悄行。",1,"","","主角",1,},
        [4817] = {9101,1,1,"周瑜",303,0,"狂风大作，风折寨中黄旗，飘入江中。此不祥之兆也！","狂风大作，风折寨中黄旗，飘入江中。此不祥之兆也！",1,"","","鲁肃",312,},
        [4818] = {9101,2,1,"周瑜",303,0,"如何行火计！此前所行计策全部泡汤。","如何行火计！此前所行计策全部泡汤。",1,"","","鲁肃",312,},
        [4819] = {9101,3,2,"鲁肃",312,0,"这个…","这个…",1,"","","周瑜",303,},
        [4820] = {9101,4,1,"周瑜",303,0,"啊啊啊啊！","啊啊啊啊！",1,"","","鲁肃",312,},
        [4821] = {9101,5,2,"鲁肃",312,0,"快来人，都督口吐鲜血，快传军医。","快来人，都督口吐鲜血，快传军医。",1,"","","周瑜",303,},
        [4822] = {9102,1,2,"鲁肃",312,0,"狂风大作，无法行火计，周郎卒病。","狂风大作，无法行火计，周郎卒病。",1,"","","诸葛亮",203,},
        [4823] = {9102,2,1,"诸葛亮",203,0,"公以为何如？","公以为何如？",1,"","","鲁肃",312,},
        [4824] = {9102,3,2,"鲁肃",312,0,"此乃曹操之福，江东之祸也。","此乃曹操之福，江东之祸也。",1,"","","诸葛亮",203,},
        [4825] = {9102,4,1,"诸葛亮",203,0,"公瑾之病，亮亦能医。","公瑾之病，亮亦能医。",1,"","","鲁肃",312,},
        [4826] = {9102,5,2,"鲁肃",312,0,"诚如此，则国家万幸！速与我同去。","诚如此，则国家万幸！速与我同去。",1,"","","诸葛亮",203,},
        [4827] = {9103,1,2,"鲁肃",312,0,"都督病势若何？","都督病势若何？",1,"","","周瑜",303,},
        [4828] = {9103,2,1,"周瑜",303,0,"心腹搅痛，时复昏迷。","心腹搅痛，时复昏迷。",1,"","","鲁肃",312,},
        [4829] = {9103,3,2,"鲁肃",312,0,"曾服何药饵？","曾服何药饵？",1,"","","周瑜",303,},
        [4830] = {9103,4,1,"周瑜",303,0,"心中呕逆，药不能下。","心中呕逆，药不能下。",1,"","","鲁肃",312,},
        [4831] = {9103,5,2,"鲁肃",312,0,"适来去望孔明，言能医都督之病。现在帐外，烦来医治，何如？","适来去望孔明，言能医都督之病。现在帐外，烦来医治，何如？",1,"","","周瑜",303,},
        [4832] = {9103,6,1,"周瑜",303,0,"然。","然。",1,"","","鲁肃",312,},
        [4833] = {9104,1,2,"诸葛亮",203,0,"连日不晤君颜，何期贵体不安！","连日不晤君颜，何期贵体不安！",1,"","","周瑜",303,},
        [4834] = {9104,2,1,"周瑜",303,0,"人有旦夕祸福，岂能自保？","人有旦夕祸福，岂能自保？",1,"","","诸葛亮",203,},
        [4835] = {9104,3,2,"诸葛亮",203,0,"天有不测风云，人又岂能料乎？都督心中似觉烦积否？","天有不测风云，人又岂能料乎？都督心中似觉烦积否？",1,"","","周瑜",303,},
        [4836] = {9104,4,1,"周瑜",303,0,"然。","然。",1,"","","诸葛亮",203,},
        [4837] = {9104,5,2,"诸葛亮",203,0,"亮有一方，便教都督气顺。","亮有一方，便教都督气顺。",1,"","","周瑜",303,},
        [4838] = {9104,6,1,"周瑜",303,0,"愿先生赐教。","愿先生赐教。",1,"","","诸葛亮",203,},
        [4839] = {9104,7,2,"诸葛亮",203,0,"欲破曹公，宜用火攻，万事俱备，只欠东风。此都督病源也。","欲破曹公，宜用火攻，万事俱备，只欠东风。此都督病源也。",1,"","","周瑜",303,},
        [4840] = {9104,8,1,"周瑜",303,0,"先生已知我病源，将用何药治之？事在危急，望即赐教。","先生已知我病源，将用何药治之？事在危急，望即赐教。",1,"","","诸葛亮",203,},
        [4841] = {9104,9,2,"诸葛亮",203,0,"亮虽不才，曾遇异人，传授奇门遁甲天书，可以呼风唤雨。","亮虽不才，曾遇异人，传授奇门遁甲天书，可以呼风唤雨。",1,"","","周瑜",303,},
        [4842] = {9104,10,2,"诸葛亮",203,0,"都督若要东南风时，可于南屏山建一台，名曰七星坛：高九尺，作三层，用一百二十人，手执旗幡围绕。","都督若要东南风时，可于南屏山建一台，名曰七星坛：高九尺，作三层，用一百二十人，手执旗幡围绕。",1,"","","周瑜",303,},
        [4843] = {9104,11,2,"诸葛亮",203,0,"亮于台上作法，借三日三夜东南大风，助都督用兵，何如？”","亮于台上作法，借三日三夜东南大风，助都督用兵，何如？”",1,"","","周瑜",303,},
        [4844] = {9104,12,1,"周瑜",303,0,"休道三日三夜，只一夜大风，大事可成矣。只是事在目前，不可迟缓。","休道三日三夜，只一夜大风，大事可成矣。只是事在目前，不可迟缓。",1,"","","诸葛亮",203,},
        [4845] = {9104,13,2,"诸葛亮",203,0,"十一月二十日甲子祭风，至二十二日丙寅风息，如何？","十一月二十日甲子祭风，至二十二日丙寅风息，如何？",1,"","","周瑜",303,},
        [4846] = {9104,14,1,"周瑜",303,0,"速传令差五百精壮军士，往南屏山筑坛，拨一百二十人，执旗守坛，听候使令。","速传令差五百精壮军士，往南屏山筑坛，拨一百二十人，执旗守坛，听候使令。",1,"","","诸葛亮",203,},
        [4847] = {9105,1,1,"周瑜",303,0,"近夜，天色清明，微风不动。孔明之言谬矣。隆冬之时，怎得东南风乎？","近夜，天色清明，微风不动。孔明之言谬矣。隆冬之时，怎得东南风乎？",1,"","","鲁肃",312,},
        [4848] = {9105,2,2,"鲁肃",312,0,"吾料孔明必不谬谈。","吾料孔明必不谬谈。",1,"","","周瑜",303,},
        [4849] = {9105,3,2,"鲁肃",312,0,"都督，你看，旗幡转动，旗脚竟飘西北，霎时间东南风大起。","都督，你看，旗幡转动，旗脚竟飘西北，霎时间东南风大起。",1,"","","周瑜",303,},
        [4850] = {9105,4,1,"周瑜",303,0,"此人有夺天地造化之法、鬼神不测之术！若留此人，乃东吴祸根也。及早杀却，免生他日之忧。","此人有夺天地造化之法、鬼神不测之术！若留此人，乃东吴祸根也。及早杀却，免生他日之忧。",1,"","","鲁肃",312,},
        [4851] = {9105,5,1,"周瑜",303,0,"丁奉、徐盛，汝二人各带一百人。徐盛从江内去，丁奉从旱路去，","丁奉、徐盛，汝二人各带一百人。徐盛从江内去，丁奉从旱路去，",1,"","","鲁肃",312,},
        [4852] = {9105,6,1,"周瑜",303,0,"都到南屏山七星坛前，休问长短，拿住诸葛亮便行斩首，将首级来请功。","都到南屏山七星坛前，休问长短，拿住诸葛亮便行斩首，将首级来请功。",1,"","","鲁肃",312,},
        [4853] = {9105,7,2,"徐盛",328,0,"是，都督。","是，都督。",1,"","","周瑜",303,},
        [4854] = {9106,1,2,"主角",1,0,"周郎心胸狭窄，今东南风已起，他必命人来抓你。","周郎心胸狭窄，今东南风已起，他必命人来抓你。",1,"","","诸葛亮",203,},
        [4855] = {9106,2,1,"诸葛亮",203,0,"吾已料到，已让子龙江中接应。","吾已料到，已让子龙江中接应。",1,"","","主角",1,},
        [4856] = {9106,3,2,"主角",1,0,"昨晚我已将一只快船停在前面滩口，我们速速与子龙汇合。","昨晚我已将一只快船停在前面滩口，我们速速与子龙汇合。",1,"","","诸葛亮",203,},
        [4857] = {9107,1,2,"徐盛",328,0,"军师休去！都督有请！","军师休去！都督有请！",1,"","","诸葛亮",203,},
        [4858] = {9107,2,1,"诸葛亮",203,0,"上覆都督，好好用兵，诸葛亮暂回夏口，异日再容相见。","上覆都督，好好用兵，诸葛亮暂回夏口，异日再容相见。",1,"","","徐盛",328,},
        [4859] = {9107,3,2,"徐盛",328,0,"请暂少住，有紧话说。","请暂少住，有紧话说。",1,"","","诸葛亮",203,},
        [4860] = {9107,4,1,"诸葛亮",203,0,"吾已料定都督不能容我，必来加害，预先教赵子龙来相接。将军不必追赶。","吾已料定都督不能容我，必来加害，预先教赵子龙来相接。将军不必追赶。",1,"","","徐盛",328,},
        [4861] = {9107,5,1,"赵云",201,0,"吾乃常山赵子龙也！奉令特来接军师。你如何来追赶？本待一箭射死你来，显得两家失了和气。","吾乃常山赵子龙也！奉令特来接军师。你如何来追赶？本待一箭射死你来，显得两家失了和气。",1,"","","徐盛",328,},
        [4862] = {9107,6,1,"丁奉",339,0,"诸葛亮神机妙算，人不可及。更兼赵云有万夫不当之勇，汝知他当阳长坂时否？","诸葛亮神机妙算，人不可及。更兼赵云有万夫不当之勇，汝知他当阳长坂时否？",1,"","","徐盛",328,},
        [4863] = {9107,7,1,"丁奉",339,0,"吾等只索回报便了。","吾等只索回报便了。",1,"","","徐盛",328,},
        [4864] = {9107,8,2,"徐盛",328,0,"然。","然。",1,"","","丁奉",339,},
        [4865] = {9108,1,2,"徐盛",328,0,"孔明预先约赵云迎接去了，尔等追之不及。","孔明预先约赵云迎接去了，尔等追之不及。",1,"","","周瑜",303,},
        [4866] = {9108,2,1,"周瑜",303,0,"此人如此多谋，使我晓夜不安矣！","此人如此多谋，使我晓夜不安矣！",1,"","","徐盛",328,},
        [4867] = {9108,3,2,"鲁肃",312,0,"且待破曹之后，却再图之。","且待破曹之后，却再图之。",1,"","","周瑜",303,},
        [4868] = {9108,4,1,"周瑜",303,0,"好。","好。",1,"","","鲁肃",312,},
        [4869] = {9201,1,2,"鲁肃",312,0,"江南隐隐一簇帆幔，使风而来。皆插青龙牙旗。内中有大旗，上书先锋黄盖名字。","江南隐隐一簇帆幔，使风而来。皆插青龙牙旗。内中有大旗，上书先锋黄盖名字。",1,"","","曹操",103,},
        [4870] = {9201,2,1,"曹操",103,0,"公覆来降，此天助我也！","公覆来降，此天助我也！",1,"","","鲁肃",312,},
        [4871] = {9201,3,2,"程昱",121,0,"来船必诈。且休教近寨。","来船必诈。且休教近寨。",1,"","","曹操",103,},
        [4872] = {9201,4,1,"曹操",103,0,"何以知之！","何以知之！",1,"","","程昱",121,},
        [4873] = {9201,5,2,"程昱",121,0,"粮在船中，船必稳重，今观来船，轻而且浮。更兼今夜东南风甚紧，倘有诈谋，何以当之？","粮在船中，船必稳重，今观来船，轻而且浮。更兼今夜东南风甚紧，倘有诈谋，何以当之？",1,"","","曹操",103,},
        [4874] = {9201,6,1,"曹操",103,0,"谁去止之？","谁去止之？",1,"","","程昱",121,},
        [4875] = {9201,7,2,"文聘",122,0,"某在水上颇熟，愿请一往。","某在水上颇熟，愿请一往。",1,"","","曹操",103,},
        [4876] = {9201,8,1,"曹操",103,0,"好。","好。",1,"","","文聘",122,},
        [4877] = {9202,1,1,"文聘",122,0,"丞相钧旨，南船且休近寨，就江心抛住。","丞相钧旨，南船且休近寨，就江心抛住。",1,"","","黄盖",314,},
        [4878] = {9202,2,1,"文聘",122,0,"快下了篷！","快下了篷！",1,"","","黄盖",314,},
        [4879] = {9202,3,2,"黄盖",314,0,"火箭齐发！","火箭齐发！",1,"","","文聘",122,},
        [4880] = {9202,4,2,"黄盖",314,0,"火船，撞入水寨！","火船，撞入水寨！",1,"","","文聘",122,},
        [4881] = {9202,5,1,"文聘",122,0,"不能让他们驶船进去。","不能让他们驶船进去。",1,"","","黄盖",314,},
        [4882] = {9203,1,2,"黄盖",314,0,"黄盖在此！曹贼休走！","黄盖在此！曹贼休走！",1,"","","曹操",0,},
        [4883] = {9203,2,1,"曹操",103,0,"快来人保护孤。","快来人保护孤。",1,"","","黄盖",314,},
        [4884] = {9203,3,2,"张辽",109,0,"船只被铁环锁住，一着皆着，三江面上，火逐风飞。","船只被铁环锁住，一着皆着，三江面上，火逐风飞。",1,"","","曹操",103,},
        [4885] = {9203,4,2,"张辽",109,0,"丞相速上小脚船往岸口去。","丞相速上小脚船往岸口去。",1,"","","曹操",103,},
        [4886] = {9203,5,1,"黄盖",314,0,"哪里跑！","哪里跑！",1,"","","张辽",109,},
        [4887] = {9203,6,2,"张辽",109,0,"黄盖看箭！","黄盖看箭！",1,"","","黄盖",314,},
        [4888] = {9301,1,1,"诸葛亮",203,0,"子龙可带三千军马，渡江径取乌林小路，拣树木芦苇密处埋伏。今夜四更已后，曹操必然从那条路奔走。","子龙可带三千军马，渡江径取乌林小路，拣树木芦苇密处埋伏。今夜四更已后，曹操必然从那条路奔走。",1,"","","赵云",201,},
        [4889] = {9301,2,1,"诸葛亮",203,0,"等他军马过，就半中间放起火来。虽然不杀他尽绝，也杀一半。","等他军马过，就半中间放起火来。虽然不杀他尽绝，也杀一半。",1,"","","赵云",201,},
        [4890] = {9301,3,2,"赵云",201,0,"乌林有两条路：一条通南郡，一条取荆州。不知向那条路来？","乌林有两条路：一条通南郡，一条取荆州。不知向那条路来？",1,"","","诸葛亮",203,},
        [4891] = {9301,4,1,"诸葛亮",203,0,"南郡势迫，曹操不敢往，必来荆州，然后大军投许昌而去。","南郡势迫，曹操不敢往，必来荆州，然后大军投许昌而去。",1,"","","赵云",201,},
        [4892] = {9301,5,2,"赵云",201,0,"子龙这便出发。","子龙这便出发。",1,"","","诸葛亮",203,},
        [4893] = {9301,6,1,"诸葛亮",203,0,"翼德可领三千兵渡江，截断彝陵这条路，去葫芦谷口埋伏。曹操不敢走南彝陵，必望北彝陵去。","翼德可领三千兵渡江，截断彝陵这条路，去葫芦谷口埋伏。曹操不敢走南彝陵，必望北彝陵去。",1,"","","赵云",201,},
        [4894] = {9301,7,1,"诸葛亮",203,0,"来日雨过，必然来埋锅造饭。只看烟起，便就山边放起火来。虽然不捉得曹操，翼德这场功料也不小。","来日雨过，必然来埋锅造饭。只看烟起，便就山边放起火来。虽然不捉得曹操，翼德这场功料也不小。",1,"","","赵云",201,},
        [4895] = {9301,8,2,"张飞",206,0,"翼德这便出发。","翼德这便出发。",1,"","","诸葛亮",203,},
        [4896] = {9301,9,1,"诸葛亮",203,0,"糜竺、糜芳、刘封三人各驾船只，绕江剿擒败军，夺取器械。","糜竺、糜芳、刘封三人各驾船只，绕江剿擒败军，夺取器械。",1,"","","张飞",206,},
        [4897] = {9301,10,2,"糜竺",243,0,"好。","好。",1,"","","诸葛亮",203,},
        [4898] = {9301,11,1,"诸葛亮",203,0,"武昌一望之地。最为紧要。公子便请回，率领所部之兵，陈于岸口。操一败必有逃来者，就而擒之，却不可轻离城郭。","武昌一望之地。最为紧要。公子便请回，率领所部之兵，陈于岸口。操一败必有逃来者，就而擒之，却不可轻离城郭。",1,"","","糜竺",243,},
        [4899] = {9301,12,2,"刘琦",128,0,"我这就动身。","我这就动身。",1,"","","诸葛亮",203,},
        [4900] = {9301,13,1,"诸葛亮",203,0,"主公可于樊口屯兵，凭高而望，坐看今夜周郎成大功也。","主公可于樊口屯兵，凭高而望，坐看今夜周郎成大功也。",1,"","","刘琦",128,},
        [4901] = {9301,14,2,"刘备",202,0,"先生英明。","先生英明。",1,"","","诸葛亮",203,},
        [4902] = {9302,1,1,"关羽",205,0,"关某自随兄长征战，许多年来，未尝落后。今日逢大敌，军师却不委用，此是何意？","关某自随兄长征战，许多年来，未尝落后。今日逢大敌，军师却不委用，此是何意？",1,"","","诸葛亮",203,},
        [4903] = {9302,2,2,"诸葛亮",203,0,"云长勿怪！某本欲烦足下把一个最紧要的隘口，怎奈有些违碍，不敢教去。","云长勿怪！某本欲烦足下把一个最紧要的隘口，怎奈有些违碍，不敢教去。",1,"","","关羽",205,},
        [4904] = {9302,3,1,"关羽",205,0,"有何违碍？愿即见谕。","有何违碍？愿即见谕。",1,"","","诸葛亮",203,},
        [4905] = {9302,4,2,"诸葛亮",203,0,"昔日曹操待足下甚厚，足下当有以报之。今日操兵败，必走华容道，若令足下去时，必然放他过去。因此不敢教去。","昔日曹操待足下甚厚，足下当有以报之。今日操兵败，必走华容道，若令足下去时，必然放他过去。因此不敢教去。",1,"","","关羽",205,},
        [4906] = {9302,5,1,"关羽",205,0,"军师好心多！当日曹操果是重待某，某已斩颜良，诛文丑，解白马之围，报过他了。今日撞见，岂肯放过！","军师好心多！当日曹操果是重待某，某已斩颜良，诛文丑，解白马之围，报过他了。今日撞见，岂肯放过！",1,"","","诸葛亮",203,},
        [4907] = {9302,6,2,"诸葛亮",203,0,"倘若放了时，却如何？","倘若放了时，却如何？",1,"","","关羽",205,},
        [4908] = {9302,7,1,"关羽",205,0,"愿依军法！","愿依军法！",1,"","","诸葛亮",203,},
        [4909] = {9302,8,2,"诸葛亮",203,0,"如此，立下文书。","如此，立下文书。",1,"","","关羽",205,},
        [4910] = {9302,9,1,"关羽",205,0,"若曹操不从那条路上来，如何？","若曹操不从那条路上来，如何？",1,"","","诸葛亮",203,},
        [4911] = {9302,10,2,"诸葛亮",203,0,"我亦与你军令状。","我亦与你军令状。",1,"","","关羽",205,},
        [4912] = {9302,11,2,"诸葛亮",203,0,"云长可于华容小路高山之处，堆积柴草，放起一把火烟，引曹操来。","云长可于华容小路高山之处，堆积柴草，放起一把火烟，引曹操来。",1,"","","关羽",205,},
        [4913] = {9302,12,1,"关羽",205,0,"曹操望见烟，知有埋伏，如何肯来？","曹操望见烟，知有埋伏，如何肯来？",1,"","","诸葛亮",203,},
        [4914] = {9302,13,2,"诸葛亮",203,0,"岂不闻兵法虚虚实实之论？操虽能用兵，只此可以瞒过他也。他见烟起，将谓虚张声势，必然投这条路来。将军休得容情。","岂不闻兵法虚虚实实之论？操虽能用兵，只此可以瞒过他也。他见烟起，将谓虚张声势，必然投这条路来。将军休得容情。",1,"","","关羽",205,},
        [4915] = {9303,1,2,"刘备",202,0,"吾弟义气深重，若曹操果然投华容道去时，只恐端的放了。","吾弟义气深重，若曹操果然投华容道去时，只恐端的放了。",1,"","","诸葛亮",203,},
        [4916] = {9303,2,1,"诸葛亮",203,0,"亮夜观乾象，操贼未合身亡。留这人情，教云长做了，亦是美事。","亮夜观乾象，操贼未合身亡。留这人情，教云长做了，亦是美事。",1,"","","刘备",202,},
        [4917] = {9303,3,2,"刘备",202,0,"先生神算，世所罕及！","先生神算，世所罕及！",1,"","","诸葛亮",203,},
        [4918] = {9401,1,1,"曹操",103,0,"前面是哪？","前面是哪？",1,"","","许褚",108,},
        [4919] = {9401,2,2,"许褚",108,0,"一边是南彝陵大路，一边是北彝陵山路。","一边是南彝陵大路，一边是北彝陵山路。",1,"","","曹操",103,},
        [4920] = {9401,3,1,"曹操",103,0,"哪里投南郡江陵去近？","哪里投南郡江陵去近？",1,"","","许褚",108,},
        [4921] = {9401,4,2,"许褚",108,0,"取南彝陵过葫芦口去最便。","取南彝陵过葫芦口去最便。",1,"","","曹操",103,},
        [4922] = {9401,5,1,"曹操",103,0,"走南彝陵。","走南彝陵。",1,"","","许褚",108,},
        [4923] = {9402,1,1,"曹操",103,0,"哈哈哈哈哈。","哈哈哈哈哈。",1,"","","",0,},
        [4924] = {9402,2,2,"张辽",109,0,"丞相为何大笑？","丞相为何大笑？",1,"","","曹操",103,},
        [4925] = {9402,3,1,"曹操",103,0,"吾笑诸葛亮、周瑜毕竟智谋不足。若是我用兵时，就这个去处，也埋伏一彪军马，以逸待劳。","吾笑诸葛亮、周瑜毕竟智谋不足。若是我用兵时，就这个去处，也埋伏一彪军马，以逸待劳。",1,"","","张辽",109,},
        [4926] = {9402,4,1,"曹操",103,0,"我等纵然脱得性命，也不免重伤矣。彼见不到此，我是以笑之。","我等纵然脱得性命，也不免重伤矣。彼见不到此，我是以笑之。",1,"","","张辽",109,},
        [4927] = {9402,5,2,"张飞",206,0,"谁说孔明智谋不足的，翼德在此恭候多时！","谁说孔明智谋不足的，翼德在此恭候多时！",1,"","","曹操",103,},
        [4928] = {9402,6,1,"张辽",109,0,"丞相先走，我来掩护。","丞相先走，我来掩护。",1,"","","张飞",206,},
        [4929] = {9403,1,2,"张辽",109,0,"前面有两条路，请问丞相从那条路去？","前面有两条路，请问丞相从那条路去？",1,"","","曹操",103,},
        [4930] = {9403,2,1,"曹操",103,0,"哪条路近？","哪条路近？",1,"","","张辽",109,},
        [4931] = {9403,3,2,"张辽",109,0,"大路稍平，却远五十余里。小路投华容道，却近五十余里，只是地窄路险，坑坎难行。","大路稍平，却远五十余里。小路投华容道，却近五十余里，只是地窄路险，坑坎难行。",1,"","","曹操",103,},
        [4932] = {9403,4,2,"张辽",109,0,"且小路山边有数处烟起，大路并无动静。","且小路山边有数处烟起，大路并无动静。",1,"","","曹操",103,},
        [4933] = {9403,5,1,"曹操",103,0,"走华容道小路。","走华容道小路。",1,"","","张辽",109,},
        [4934] = {9403,6,2,"张辽",109,0,"烽烟起处，必有军马，何故反走这条路？","烽烟起处，必有军马，何故反走这条路？",1,"","","曹操",103,},
        [4935] = {9403,7,1,"曹操",103,0,"岂不闻兵书有云：虚则实之，实则虚之。","岂不闻兵书有云：虚则实之，实则虚之。",1,"","","张辽",109,},
        [4936] = {9403,8,1,"曹操",103,0,"诸葛亮多谋，故使人于山僻烧烟，使我军不敢从这条山路走，他却伏兵于大路等着。吾料已定，偏不教中他计！","诸葛亮多谋，故使人于山僻烧烟，使我军不敢从这条山路走，他却伏兵于大路等着。吾料已定，偏不教中他计！",1,"","","张辽",109,},
        [4937] = {9403,9,2,"张辽",109,0,"丞相妙算，人不可及。","丞相妙算，人不可及。",1,"","","曹操",103,},
        [4938] = {9404,1,2,"张辽",109,0,"不好，两边五百校刀手摆开，为首大将关云长，提青龙刀，跨赤兔马，截住去路。","不好，两边五百校刀手摆开，为首大将关云长，提青龙刀，跨赤兔马，截住去路。",1,"","","曹操",103,},
        [4939] = {9404,2,1,"曹操",103,0,"既到此处，只得决一死战！","既到此处，只得决一死战！",1,"","","张辽",109,},
        [4940] = {9404,3,2,"程昱",121,0,"某素知云长傲上而不忍下，欺强而不凌弱，恩怨分明，信义素著。","某素知云长傲上而不忍下，欺强而不凌弱，恩怨分明，信义素著。",1,"","","曹操",103,},
        [4941] = {9404,4,2,"程昱",121,0,"丞相旧日有恩于彼，今只亲自告之，可脱此难。","丞相旧日有恩于彼，今只亲自告之，可脱此难。",1,"","","曹操",103,},
        [4942] = {9405,1,1,"曹操",103,0,"将军别来无恙！","将军别来无恙！",1,"","","关羽",205,},
        [4943] = {9405,2,2,"关羽",205,0,"关某奉军师将令，等候丞相多时。","关某奉军师将令，等候丞相多时。",1,"","","曹操",103,},
        [4944] = {9405,3,1,"曹操",103,0,"曹操兵败势危，到此无路，望将军以昔日之情为重。","曹操兵败势危，到此无路，望将军以昔日之情为重。",1,"","","关羽",205,},
        [4945] = {9405,4,2,"关羽",205,0,"昔日关某虽蒙丞相厚恩，然已斩颜良，诛文丑，解白马之围，以奉报矣。今日之事，岂敢以私废公？","昔日关某虽蒙丞相厚恩，然已斩颜良，诛文丑，解白马之围，以奉报矣。今日之事，岂敢以私废公？",1,"","","曹操",103,},
        [4946] = {9405,5,1,"曹操",103,0,"五关斩将之时，还能记否？大丈夫以信义为重。将军深明《春秋》，岂不知庾公之斯追子濯孺子之事乎？","五关斩将之时，还能记否？大丈夫以信义为重。将军深明《春秋》，岂不知庾公之斯追子濯孺子之事乎？",1,"","","关羽",205,},
        [4947] = {9405,6,2,"关羽",205,0,"将士们四散摆开吧。","将士们四散摆开吧。",1,"","","曹操",103,},
        [4948] = {9405,7,1,"曹操",103,0,"谢将军。","谢将军。",1,"","","关羽",205,},
        [4949] = {9501,1,1,"曹操",103,0,"南方献来一只山鸡，山鸡爱其毛羽，看到水里的倒影就会起舞。","南方献来一只山鸡，山鸡爱其毛羽，看到水里的倒影就会起舞。",1,"","","",0,},
        [4950] = {9501,2,1,"曹操",103,0,"孤想要让它鸣舞，却毫无办法。","孤想要让它鸣舞，却毫无办法。",1,"","","",0,},
        [4951] = {9501,3,2,"曹冲",115,0,"不如将山鸡置于铜镜之前试试。","不如将山鸡置于铜镜之前试试。",1,"","","曹操",103,},
        [4952] = {9501,4,1,"曹操",103,0,"嗯，是个办法。","嗯，是个办法。",1,"","","曹冲",115,},
        [4953] = {9502,1,1,"曹操",103,0,"孤放置一面大铜镜于山鸡前，鸡看到大镜竟开始起舞。","孤放置一面大铜镜于山鸡前，鸡看到大镜竟开始起舞。",1,"","","曹冲",115,},
        [4954] = {9502,2,1,"曹操",103,0,"但山鸡却不知道停止，最后累死了。","但山鸡却不知道停止，最后累死了。",1,"","","曹冲",115,},
        [4955] = {9502,3,2,"曹冲",115,0,"这也许就是所谓的顾影自怜吧。","这也许就是所谓的顾影自怜吧。",1,"","","曹操",103,},
        [4956] = {9503,1,2,"环夫人",118,0,"孟德，曹冲突染重病，你快去看看。","孟德，曹冲突染重病，你快去看看。",1,"","","曹操",103,},
        [4957] = {9503,2,1,"曹操",103,0,"什么？我这就回许都。","什么？我这就回许都。",1,"","","环夫人",118,},
        [4958] = {9504,1,1,"曹操",103,0,"曹冲这是患了何病，是否请大夫看过？","曹冲这是患了何病，是否请大夫看过？",1,"","","环夫人",118,},
        [4959] = {9504,2,2,"环夫人",118,0,"已经让大夫看过了，不知是何疾病。","已经让大夫看过了，不知是何疾病。",1,"","","曹操",103,},
        [4960] = {9504,3,1,"曹操",103,0,"放心，我寻遍天下名医也要为曹冲治病。","放心，我寻遍天下名医也要为曹冲治病。",1,"","","环夫人",118,},
        [4961] = {9504,4,1,"曹操",103,0,"快，给我去寻名医！快！","快，给我去寻名医！快！",1,"","","环夫人",118,},
        [4962] = {9504,5,2,"曹仁",106,0,"是。","是。",1,"","","曹操",103,},
        [4963] = {9505,1,2,"曹丕",111,0,"曹冲病逝，实属不幸，请节哀。","曹冲病逝，实属不幸，请节哀。",1,"","","曹操",103,},
        [4964] = {9505,2,1,"曹操",103,0,"建安二年，曹昂随孤出征张绣，负责断後而亡。","建安二年，曹昂随孤出征张绣，负责断後而亡。",1,"","","曹丕",111,},
        [4965] = {9505,3,1,"曹操",103,0,"曹冲十分聪慧，年仅十三便逝世，让孤怎能不伤心！","曹冲十分聪慧，年仅十三便逝世，让孤怎能不伤心！",1,"","","曹丕",111,},
        [4966] = {9505,4,1,"曹操",103,0,"此我之不幸，而汝曹之幸也。","此我之不幸，而汝曹之幸也。",1,"","","曹丕",111,},
        [4967] = {9601,1,2,"周瑜",303,0,"豫州移兵在此，莫非有取南郡之意否？","豫州移兵在此，莫非有取南郡之意否？",1,"","","刘备",202,},
        [4968] = {9601,2,1,"刘备",202,0,"闻都督欲取南郡，故来相助。若都督不取，备必取之。","闻都督欲取南郡，故来相助。若都督不取，备必取之。",1,"","","周瑜",303,},
        [4969] = {9601,3,2,"周瑜",303,0,"吾东吴久欲吞并汉江，今南郡已在掌中，如何不取？","吾东吴久欲吞并汉江，今南郡已在掌中，如何不取？",1,"","","刘备",202,},
        [4970] = {9601,4,1,"刘备",202,0,"胜负不可预定。曹操临归，令曹仁守南郡等处，必有奇计，更兼曹仁勇不可当，但恐都督不能取耳。","胜负不可预定。曹操临归，令曹仁守南郡等处，必有奇计，更兼曹仁勇不可当，但恐都督不能取耳。",1,"","","周瑜",303,},
        [4971] = {9601,5,2,"周瑜",303,0,"吾若取不得，那时任从公取。","吾若取不得，那时任从公取。",1,"","","刘备",202,},
        [4972] = {9601,6,1,"刘备",202,0,"子敬、孔明在此为证，都督休悔。","子敬、孔明在此为证，都督休悔。",1,"","","周瑜",303,},
        [4973] = {9601,7,2,"周瑜",303,0,"大丈夫一言既出，何悔之有！","大丈夫一言既出，何悔之有！",1,"","","刘备",202,},
        [4974] = {9601,8,1,"刘备",202,0,"都督此言，甚是公论。先让东吴去取，若不下，主公取之，有何不可！","都督此言，甚是公论。先让东吴去取，若不下，主公取之，有何不可！",1,"","","周瑜",303,},
        [4975] = {9602,1,1,"刘备",202,0,"却才先生教备如此回答，虽一时说了，展转寻思，于理未然。我今孤穷一身，无置足之地，欲得南郡，权且容身。","却才先生教备如此回答，虽一时说了，展转寻思，于理未然。我今孤穷一身，无置足之地，欲得南郡，权且容身。",1,"","","诸葛亮",203,},
        [4976] = {9602,2,1,"刘备",202,0,"若先教周瑜取了，城池已属东吴矣，却如何得住？","若先教周瑜取了，城池已属东吴矣，却如何得住？",1,"","","诸葛亮",203,},
        [4977] = {9602,3,2,"诸葛亮",203,0,"当初亮劝主公取荆州，主公不听，今日却想耶？","当初亮劝主公取荆州，主公不听，今日却想耶？",1,"","","刘备",202,},
        [4978] = {9602,4,1,"刘备",202,0,"前为景升之地，故不忍取，今为曹操之地，理合取之。","前为景升之地，故不忍取，今为曹操之地，理合取之。",1,"","","诸葛亮",203,},
        [4979] = {9602,5,2,"诸葛亮",203,0,"不须主公忧虑。尽着周瑜去厮杀，早晚教主公在南郡城中高坐。","不须主公忧虑。尽着周瑜去厮杀，早晚教主公在南郡城中高坐。",1,"","","刘备",202,},
        [4980] = {9602,6,1,"刘备",202,0,"计将安出？","计将安出？",1,"","","诸葛亮",203,},
        [4981] = {9602,7,2,"诸葛亮",203,0,"请附耳。","请附耳。",1,"","","刘备",202,},
        [4982] = {9603,1,2,"鲁肃",312,0,"都督如何亦许玄德取南郡？","都督如何亦许玄德取南郡？",1,"","","周瑜",303,},
        [4983] = {9603,2,1,"周瑜",303,0,"吾弹指可得南郡，落得虚做人情。","吾弹指可得南郡，落得虚做人情。",1,"","","鲁肃",312,},
        [4984] = {9603,3,1,"周瑜",303,0,"谁敢先取南郡？","谁敢先取南郡？",1,"","","鲁肃",312,},
        [4985] = {9603,4,2,"蒋钦",335,0,"我！","我！",1,"","","周瑜",303,},
        [4986] = {9603,5,1,"周瑜",303,0,"汝为先锋，徐盛、丁奉为副将，拨五千精锐军马，先渡江。吾随后引兵接应。","汝为先锋，徐盛、丁奉为副将，拨五千精锐军马，先渡江。吾随后引兵接应。",1,"","","蒋钦",335,},
        [4987] = {9604,1,2,"牛金",135,0,"吴兵已渡汉江。","吴兵已渡汉江。",1,"","","曹仁",106,},
        [4988] = {9604,2,1,"曹仁",106,0,"坚守勿战为上。","坚守勿战为上。",1,"","","牛金",135,},
        [4989] = {9604,3,2,"牛金",135,0,"兵临城下而不出战，是怯也。况吾兵新败，正当重振锐气。某愿借精兵五百，决一死战。","兵临城下而不出战，是怯也。况吾兵新败，正当重振锐气。某愿借精兵五百，决一死战。",1,"","","曹仁",106,},
        [4990] = {9604,4,1,"曹仁",106,0,"好，汝牛金引五百军出战。","好，汝牛金引五百军出战。",1,"","","牛金",135,},
        [4991] = {9605,1,1,"周瑜",303,0,"吾点兵，要亲与曹仁决战。","吾点兵，要亲与曹仁决战。",1,"","","甘宁",308,},
        [4992] = {9605,2,2,"甘宁",308,0,"都督未可造次。今曹仁令曹洪据守彝陵，为掎角之势，某愿以精兵三千，径取彝陵，都督然后可取南郡。","都督未可造次。今曹仁令曹洪据守彝陵，为掎角之势，某愿以精兵三千，径取彝陵，都督然后可取南郡。",1,"","","周瑜",303,},
        [4993] = {9605,3,1,"周瑜",303,0,"然，汝领三千兵攻打彝陵。","然，汝领三千兵攻打彝陵。",1,"","","甘宁",308,},
        [4994] = {9606,1,1,"曹仁",106,0,"细作报，东吴甘宁领三千精兵攻彝陵。","细作报，东吴甘宁领三千精兵攻彝陵。",1,"","","牛金",135,},
        [4995] = {9606,2,1,"曹仁",106,0,"彝陵有失，南郡亦不可守矣。宜速救之。","彝陵有失，南郡亦不可守矣。宜速救之。",1,"","","牛金",135,},
        [4996] = {9606,3,1,"曹仁",106,0,"先差人飞奔报知曹洪，令洪出城诱敌。","先差人飞奔报知曹洪，令洪出城诱敌。",1,"","","牛金",135,},
        [4997] = {9606,4,1,"曹仁",106,0,"汝与曹纯暗地引兵救曹洪。","汝与曹纯暗地引兵救曹洪。",1,"","","牛金",135,},
        [4998] = {9606,5,2,"牛金",135,0,"好。","好。",1,"","","曹仁",106,},
        [4999] = {9607,1,2,"甘宁",308,0,"兴霸在此，速弃彝陵来降。","兴霸在此，速弃彝陵来降。",1,"","","曹洪",142,},
        [5000] = {9607,2,1,"曹洪",142,0,"吾曹洪岂会怕你个锦帆贼！这就与你一战。","吾曹洪岂会怕你个锦帆贼！这就与你一战。",1,"","","甘宁",308,},
        [5001] = {9608,1,1,"甘宁",308,0,"没想你曹洪这么弱，只二十回合便败走。","没想你曹洪这么弱，只二十回合便败走。",1,"","","",0,},
        [5002] = {9608,2,1,"甘宁",308,0,"这彝陵我拿下了。","这彝陵我拿下了。",1,"","","",0,},
        [5003] = {9609,1,2,"曹洪",142,0,"锦帆贼，你以为这彝陵这么好夺？","锦帆贼，你以为这彝陵这么好夺？",1,"","","甘宁",308,},
        [5004] = {9609,2,2,"牛金",135,0,"你已被团团围在城中。","你已被团团围在城中。",1,"","","甘宁",308,},
        [5005] = {9609,3,2,"曹仁",106,0,"速提头来降！","速提头来降！",1,"","","甘宁",308,},
        [5006] = {9609,4,1,"甘宁",308,0,"可恶，中计了！","可恶，中计了！",1,"","","曹仁",106,},
        [5007] = {9701,1,1,"周瑜",303,0,"甘宁中计，被困于彝陵城中！","甘宁中计，被困于彝陵城中！",1,"","","",0,},
        [5008] = {9701,2,2,"程普",320,0,"可急分兵救之。","可急分兵救之。",1,"","","周瑜",303,},
        [5009] = {9701,3,1,"周瑜",303,0,"此地正当冲要之处，若分兵去救，倘曹仁引兵来袭，奈何？","此地正当冲要之处，若分兵去救，倘曹仁引兵来袭，奈何？",1,"","","程普",320,},
        [5010] = {9701,4,2,"吕蒙",307,0,"甘兴霸乃江东大将，岂可不救？","甘兴霸乃江东大将，岂可不救？",1,"","","周瑜",303,},
        [5011] = {9701,5,1,"周瑜",303,0,"吾欲自往救之，但留何人在此，代当吾任？","吾欲自往救之，但留何人在此，代当吾任？",1,"","","吕蒙",307,},
        [5012] = {9701,6,2,"吕蒙",307,0,"留凌公绩当之。蒙为前驱，都督断后，不须十日，必奏凯歌。","留凌公绩当之。蒙为前驱，都督断后，不须十日，必奏凯歌。",1,"","","周瑜",303,},
        [5013] = {9701,7,1,"周瑜",303,0,"未知凌公绩肯暂代吾任否？","未知凌公绩肯暂代吾任否？",1,"","","吕蒙",307,},
        [5014] = {9701,8,2,"凌统",313,0,"若十日为期，可当之，十日之外，不胜其任矣。","若十日为期，可当之，十日之外，不胜其任矣。",1,"","","周瑜",303,},
        [5015] = {9701,9,1,"周瑜",303,0,"留兵万余，付与汝凌统，即日起大兵投彝陵来。","留兵万余，付与汝凌统，即日起大兵投彝陵来。",1,"","","凌统",313,},
        [5016] = {9701,10,2,"吕蒙",307,0,"彝陵南僻小路，取南郡极便。可差五百军去砍倒树木，以断其路。","彝陵南僻小路，取南郡极便。可差五百军去砍倒树木，以断其路。",1,"","","周瑜",303,},
        [5017] = {9701,11,2,"吕蒙",307,0,"彼军若败，必走此路，马不能行，必弃马而走，吾可得其马也。","彼军若败，必走此路，马不能行，必弃马而走，吾可得其马也。",1,"","","周瑜",303,},
        [5018] = {9701,12,1,"周瑜",303,0,"好！","好！",1,"","","吕蒙",307,},
        [5019] = {9702,1,1,"周瑜",303,0,"兵将至彝陵，谁可突围而入，以救甘宁？","兵将至彝陵，谁可突围而入，以救甘宁？",1,"","","",0,},
        [5020] = {9702,2,2,"周泰",316,0,"吾愿往！","吾愿往！",1,"","","周瑜",303,},
        [5021] = {9702,3,1,"周瑜",303,0,"好！","好！",1,"","","周泰",316,},
        [5022] = {9703,1,1,"周泰",316,0,"都督自提兵至。","都督自提兵至。",1,"","","甘宁",308,},
        [5023] = {9703,2,2,"甘宁",308,0,"吾这就传令教军士严装饱食，准备内应。","吾这就传令教军士严装饱食，准备内应。",1,"","","周泰",316,},
        [5024] = {9704,1,2,"曹洪",142,0,"目今失了彝陵，势已危急，何不拆丞相遗计观之，以解此危？","目今失了彝陵，势已危急，何不拆丞相遗计观之，以解此危？",1,"","","曹仁",106,},
        [5025] = {9704,2,1,"曹仁",106,0,"汝言正合吾意。","汝言正合吾意。",1,"","","曹洪",142,},
        [5026] = {9704,3,1,"曹仁",106,0,"哈哈哈哈哈。","哈哈哈哈哈。",1,"","","曹洪",142,},
        [5027] = {9704,4,1,"曹仁",106,0,"丞相命五更造饭，平明，大小军马，尽皆弃城，城上遍插旌旗，虚张声势。","丞相命五更造饭，平明，大小军马，尽皆弃城，城上遍插旌旗，虚张声势。",1,"","","曹洪",142,},
        [5028] = {9704,5,2,"曹洪",142,0,"好计好计。","好计好计。",1,"","","曹仁",106,},
        [5029] = {9705,1,1,"曹仁",106,0,"丞相妙策如神！周瑜甘宁中计。","丞相妙策如神！周瑜甘宁中计。",1,"","","",0,},
        [5030] = {9705,2,1,"曹仁",106,0,"吴兵争先入空城的，都颠入陷坑内，两边弓弩齐发，势如骤雨，死伤无数。","吴兵争先入空城的，都颠入陷坑内，两边弓弩齐发，势如骤雨，死伤无数。",1,"","","",0,},
        [5031] = {9705,3,1,"曹仁",106,0,"周瑜急勒马回时，被一弩箭击中，翻身落马。","周瑜急勒马回时，被一弩箭击中，翻身落马。",1,"","","",0,},
        [5032] = {9706,1,1,"周瑜",303,0,"何处鼓噪呐喊？","何处鼓噪呐喊？",1,"","","",0,},
        [5033] = {9706,2,2,"甘宁",308,0,"军中教演士卒。","军中教演士卒。",1,"","","周瑜",303,},
        [5034] = {9706,3,1,"周瑜",303,0,"何欺我也！吾已知曹兵常来寨前辱骂。程德谋既同掌兵权，何故坐视？","何欺我也！吾已知曹兵常来寨前辱骂。程德谋既同掌兵权，何故坐视？",1,"","","甘宁",308,},
        [5035] = {9706,4,2,"程普",320,0,"吾见公瑾病疮，医者言勿触怒，故曹兵搦战，不敢报知。","吾见公瑾病疮，医者言勿触怒，故曹兵搦战，不敢报知。",1,"","","周瑜",303,},
        [5036] = {9706,5,1,"周瑜",303,0,"公等不战，主意若何？","公等不战，主意若何？",1,"","","程普",320,},
        [5037] = {9706,6,2,"程普",320,0,"众将皆欲收兵暂回江东。待公箭疮平复，再作区处。","众将皆欲收兵暂回江东。待公箭疮平复，再作区处。",1,"","","周瑜",303,},
        [5038] = {9706,7,1,"周瑜",303,0,"大丈夫既食君禄，当死于战场，以马革裹尸还，幸也！岂可为我一人，而废国家大事乎？","大丈夫既食君禄，当死于战场，以马革裹尸还，幸也！岂可为我一人，而废国家大事乎？",1,"","","程普",320,},
        [5039] = {9707,1,1,"曹仁",106,0,"周瑜孺子，料必横夭，再不敢正觑我兵！","周瑜孺子，料必横夭，再不敢正觑我兵！",1,"","","周瑜",303,},
        [5040] = {9707,2,2,"周瑜",303,0,"曹仁匹夫！见周郎否！","曹仁匹夫！见周郎否！",1,"","","曹仁",106,},
        [5041] = {9707,3,1,"曹仁",106,0,"敢否一战！","敢否一战！",1,"","","周瑜",303,},
        [5042] = {9707,4,2,"周瑜",303,0,"潘璋出战。","潘璋出战。",1,"","","曹仁",106,},
        [5043] = {9708,1,1,"周瑜",303,0,"啊！（周瑜忽大叫一声，口中喷血，坠于马下。）","啊！（周瑜忽大叫一声，口中喷血，坠于马下。）",1,"","","",0,},
        [5044] = {9708,2,2,"程普",320,0,"都督贵体若何？","都督贵体若何？",1,"","","周瑜",303,},
        [5045] = {9708,3,1,"周瑜",303,0,"此吾之计也。","此吾之计也。",1,"","","程普",320,},
        [5046] = {9708,4,2,"程普",320,0,"计将安出？","计将安出？",1,"","","周瑜",303,},
        [5047] = {9708,5,1,"周瑜",303,0,"吾身本无甚痛楚，吾所以为此者，欲令曹兵知我病危，必然欺敌。","吾身本无甚痛楚，吾所以为此者，欲令曹兵知我病危，必然欺敌。",1,"","","程普",320,},
        [5048] = {9708,6,1,"周瑜",303,0,"可使心腹军士去城中诈降，说吾已死。今夜曹仁必来劫寨。吾却于四下埋伏以应之，则曹仁可一鼓而擒也。","可使心腹军士去城中诈降，说吾已死。今夜曹仁必来劫寨。吾却于四下埋伏以应之，则曹仁可一鼓而擒也。",1,"","","程普",320,},
        [5049] = {9708,7,2,"程普",320,0,"此计大妙！","此计大妙！",1,"","","周瑜",303,},
        [5050] = {9709,1,1,"曹仁",106,0,"听闻周瑜阵前金疮碎裂，归寨即死，众将皆已挂孝举哀。","听闻周瑜阵前金疮碎裂，归寨即死，众将皆已挂孝举哀。",1,"","","",0,},
        [5051] = {9709,2,1,"曹仁",106,0,"今晚便去劫寨，夺周瑜之尸，斩其首级，送赴许都。","今晚便去劫寨，夺周瑜之尸，斩其首级，送赴许都。",1,"","","",0,},
        [5052] = {9709,3,1,"曹仁",106,0,"牛金为先锋，自为中军，曹洪、曹纯为合后，只留陈矫领些少军士守城，其余军兵尽起。","牛金为先锋，自为中军，曹洪、曹纯为合后，只留陈矫领些少军士守城，其余军兵尽起。",1,"","","",0,},
        [5053] = {9710,1,2,"曹洪",142,0,"寨中不见一人，虚插旗枪而已。","寨中不见一人，虚插旗枪而已。",1,"","","曹仁",106,},
        [5054] = {9710,2,1,"曹仁",106,0,"不好，中计也，速速退军投襄阳。","不好，中计也，速速退军投襄阳。",1,"","","曹洪",142,},
        [5055] = {9801,1,2,"程普",320,0,"南郡城下，旌旗布满，常山赵子龙乘虚夺了城池。","南郡城下，旌旗布满，常山赵子龙乘虚夺了城池。",1,"","","周瑜",303,},
        [5056] = {9801,2,1,"周瑜",303,0,"什么！整顿兵马攻城。","什么！整顿兵马攻城。",1,"","","程普",320,},
        [5057] = {9801,3,2,"程普",320,0,"城上乱箭射下，暂无法攻城。","城上乱箭射下，暂无法攻城。",1,"","","周瑜",303,},
        [5058] = {9801,4,1,"周瑜",303,0,"甘宁引数千军马，径取荆州。","甘宁引数千军马，径取荆州。",1,"","","程普",320,},
        [5059] = {9801,5,1,"周瑜",303,0,"凌统引数千军马，径取襄阳，然后再取南郡未迟。","凌统引数千军马，径取襄阳，然后再取南郡未迟。",1,"","","程普",320,},
        [5060] = {9802,1,2,"程普",320,0,"报！诸葛亮自得了南郡，遂用兵符，星夜诈调荆州守城军马来救。","诸葛亮自得了南郡，遂用兵符，星夜诈调荆州守城军马来救。",1,"","","周瑜",303,},
        [5061] = {9802,2,2,"程普",320,0,"张飞袭了荆州。","张飞袭了荆州。",1,"","","周瑜",303,},
        [5062] = {9802,3,1,"周瑜",303,0,"什么！","什么！",1,"","","程普",320,},
        [5063] = {9803,1,2,"程普",320,0,"报！夏侯惇在襄阳，被诸葛亮差人赍兵符，诈称曹仁求救，诱惇引兵出，却教云长袭取了襄阳。","夏侯惇在襄阳，被诸葛亮差人赍兵符，诈称曹仁求救，诱惇引兵出，却教云长袭取了襄阳。",1,"","","周瑜",303,},
        [5064] = {9803,2,2,"程普",320,0,"二处城池，全不费力，皆属刘玄德矣。","二处城池，全不费力，皆属刘玄德矣。",1,"","","周瑜",303,},
        [5065] = {9803,3,1,"周瑜",303,0,"诸葛亮怎得兵符？","诸葛亮怎得兵符？",1,"","","程普",320,},
        [5066] = {9803,4,2,"程普",320,0,"他拿住陈矫，兵符自然尽属之矣。","他拿住陈矫，兵符自然尽属之矣。",1,"","","周瑜",303,},
        [5067] = {9803,5,1,"周瑜",303,0,"啊！（周瑜大叫一声，金疮迸裂。）","啊！（周瑜大叫一声，金疮迸裂。）",1,"","","程普",320,},
        [5068] = {9803,6,2,"程普",320,0,"快来人，扶都督回营。","快来人，扶都督回营。",1,"","","周瑜",303,},
        [5069] = {9804,1,1,"周瑜",303,0,"若不杀诸葛村夫，怎息我心中怨气！","若不杀诸葛村夫，怎息我心中怨气！",1,"","","鲁肃",312,},
        [5070] = {9804,2,1,"周瑜",303,0,"吾欲起兵与刘备、诸葛亮共决雌雄，复夺城池。子敬幸助我。","吾欲起兵与刘备、诸葛亮共决雌雄，复夺城池。子敬幸助我。",1,"","","鲁肃",312,},
        [5071] = {9804,3,2,"鲁肃",312,0,"不可。方今与曹操相持，尚未分成败，主公现攻合淝不下。不争自家互相吞并，倘曹兵乘虚而来，其势危矣。","不可。方今与曹操相持，尚未分成败，主公现攻合淝不下。不争自家互相吞并，倘曹兵乘虚而来，其势危矣。",1,"","","周瑜",303,},
        [5072] = {9804,4,2,"鲁肃",312,0,"况刘玄德旧曾与曹操相厚，若逼得紧急，献了城池，一同攻打东吴，如之奈何？","况刘玄德旧曾与曹操相厚，若逼得紧急，献了城池，一同攻打东吴，如之奈何？",1,"","","周瑜",303,},
        [5073] = {9804,5,1,"周瑜",303,0,"吾等用计策，损兵马，费钱粮，他去图现成，岂不可恨！","吾等用计策，损兵马，费钱粮，他去图现成，岂不可恨！",1,"","","鲁肃",312,},
        [5074] = {9804,6,2,"鲁肃",312,0,"公瑾且耐。容某亲见玄德，将理来说他。若说不通，那时动兵未迟。","公瑾且耐。容某亲见玄德，将理来说他。若说不通，那时动兵未迟。",1,"","","周瑜",303,},
        [5075] = {9901,1,2,"鲁肃",312,0,"吾主吴侯，与都督公瑾，教某再三申意皇叔，前者，操引百万之众，名下江南，实欲来图皇叔。","吾主吴侯，与都督公瑾，教某再三申意皇叔，前者，操引百万之众，名下江南，实欲来图皇叔。",1,"","","诸葛亮",203,},
        [5076] = {9901,2,2,"鲁肃",312,0,"幸得东吴杀退曹兵，救了皇叔。所有荆州九郡，合当归于东吴。","幸得东吴杀退曹兵，救了皇叔。所有荆州九郡，合当归于东吴。",1,"","","诸葛亮",203,},
        [5077] = {9901,3,2,"鲁肃",312,0,"今皇叔用诡计，夺占荆襄，使江东空费钱粮军马，而皇叔安受其利，恐于理未顺。","今皇叔用诡计，夺占荆襄，使江东空费钱粮军马，而皇叔安受其利，恐于理未顺。",1,"","","诸葛亮",203,},
        [5078] = {9901,4,1,"诸葛亮",203,0,"子敬乃高明之士，何故亦出此言？常言道：物必归主。荆襄九郡，非东吴之地，乃刘景升之基业。","子敬乃高明之士，何故亦出此言？常言道：物必归主。荆襄九郡，非东吴之地，乃刘景升之基业。",1,"","","鲁肃",312,},
        [5079] = {9901,5,1,"诸葛亮",203,0,"吾主固景升之弟也。景升虽亡，其子尚在，以叔辅侄，而取荆州，有何不可？","吾主固景升之弟也。景升虽亡，其子尚在，以叔辅侄，而取荆州，有何不可？",1,"","","鲁肃",312,},
        [5080] = {9901,6,2,"鲁肃",312,0,"若果系公子刘琦占据，尚有可解，今公子在江夏，须不在这里！","若果系公子刘琦占据，尚有可解，今公子在江夏，须不在这里！",1,"","","诸葛亮",203,},
        [5081] = {9901,7,1,"诸葛亮",203,0,"子敬欲见公子乎？","子敬欲见公子乎？",1,"","","鲁肃",312,},
        [5082] = {9901,8,2,"鲁肃",312,0,"好！","好！",1,"","","诸葛亮",203,},
        [5083] = {9901,9,1,"诸葛亮",203,0,"孔明这就带你见他。","孔明这就带你见他。",1,"","","鲁肃",312,},
        [5084] = {9902,1,1,"刘琦",128,0,"病躯不能施礼，子敬勿罪。（刘琦躺于床榻之上。）","病躯不能施礼，子敬勿罪。（刘琦躺于床榻之上。）",1,"","","鲁肃",312,},
        [5085] = {9902,2,2,"鲁肃",312,0,"公子若不在，便如何？","公子若不在，便如何？",1,"","","刘琦",128,},
        [5086] = {9902,3,1,"诸葛亮",203,0,"公子在一日，守一日，若不在，别有商议。","公子在一日，守一日，若不在，别有商议。",1,"","","鲁肃",312,},
        [5087] = {9902,4,2,"鲁肃",312,0,"若公子不在，须将城池还我东吴。","若公子不在，须将城池还我东吴。",1,"","","诸葛亮",203,},
        [5088] = {9902,5,1,"诸葛亮",203,0,"子敬之言是也。","子敬之言是也。",1,"","","鲁肃",312,},
        [5089] = {9903,1,2,"鲁肃",312,0,"刘琦死后，城池便还我东吴。","刘琦死后，城池便还我东吴。",1,"","","周瑜",303,},
        [5090] = {9903,2,1,"周瑜",303,0,"刘琦正青春年少，如何便得他死？这荆州何日得还？","刘琦正青春年少，如何便得他死？这荆州何日得还？",1,"","","鲁肃",312,},
        [5091] = {9903,3,2,"鲁肃",312,0,"都督放心。只在鲁肃身上，务要讨荆襄还东吴。","都督放心。只在鲁肃身上，务要讨荆襄还东吴。",1,"","","周瑜",303,},
        [5092] = {9903,4,1,"周瑜",303,0,"子敬有何高见？","子敬有何高见？",1,"","","鲁肃",312,},
        [5093] = {9903,5,2,"鲁肃",312,0,"吾观刘琦过于酒色，病入膏肓，现今面色羸瘦，气喘呕血，不过半年，其人必死。","吾观刘琦过于酒色，病入膏肓，现今面色羸瘦，气喘呕血，不过半年，其人必死。",1,"","","周瑜",303,},
        [5094] = {9903,6,2,"鲁肃",312,0,"那时往取荆州，刘备须无得推故。","那时往取荆州，刘备须无得推故。",1,"","","周瑜",303,},
        [5095] = {9903,7,1,"周瑜",303,0,"好。","好。",1,"","","鲁肃",312,},
        [5096] = {9904,1,2,"伊籍",121,0,"要知荆州久远之计，何不求贤士以问之？","要知荆州久远之计，何不求贤士以问之？",1,"","","刘备",202,},
        [5097] = {9904,2,1,"刘备",202,0,"贤士安在？","贤士安在？",1,"","","伊籍",121,},
        [5098] = {9904,3,2,"伊籍",121,0,"荆襄马氏，兄弟五人并有才名：幼者名谡，字幼常，其最贤者，眉间有白毛，名良，字季常。","荆襄马氏，兄弟五人并有才名：幼者名谡，字幼常，其最贤者，眉间有白毛，名良，字季常。",1,"","","刘备",202,},
        [5099] = {9904,4,2,"伊籍",121,0,"乡里为之谚曰：马氏五常，白眉最良。公何不求此人而与之谋？","乡里为之谚曰：马氏五常，白眉最良。公何不求此人而与之谋？",1,"","","刘备",202,},
        [5100] = {9904,5,1,"刘备",202,0,"速速请之。","速速请之。",1,"","","伊籍",121,},
        [5101] = {9905,1,1,"刘备",202,0,"先生可有保荆襄之策。","先生可有保荆襄之策。",1,"","","马良",232,},
        [5102] = {9905,2,2,"马良",232,0,"荆襄四面受敌之地，恐不可久守，可令公子刘琦于此养病，招谕旧人以守之，就表奏公子为荆州刺史，以安民心。","荆襄四面受敌之地，恐不可久守，可令公子刘琦于此养病，招谕旧人以守之，就表奏公子为荆州刺史，以安民心。",1,"","","刘备",202,},
        [5103] = {9905,3,2,"马良",232,0,"然后南征武陵、长沙、桂阳、零陵四郡，积收钱粮，以为根本。此久远之计也。","然后南征武陵、长沙、桂阳、零陵四郡，积收钱粮，以为根本。此久远之计也。",1,"","","刘备",202,},
        [5104] = {9905,4,1,"刘备",202,0,"四郡当先取何郡？","四郡当先取何郡？",1,"","","马良",232,},
        [5105] = {9905,5,2,"马良",232,0,"湘江之西，零陵最近，可先取之，次取武陵。然后湘江之东取桂阳，长沙为后。","湘江之西，零陵最近，可先取之，次取武陵。然后湘江之东取桂阳，长沙为后。",1,"","","刘备",202,},
        [5106] = {9905,6,1,"刘备",202,0,"好！汝为从事，伊籍副之。送刘琦回襄阳，替云长回荆州。","好！汝为从事，伊籍副之。送刘琦回襄阳，替云长回荆州。",1,"","","马良",232,},
        [5107] = {9905,7,1,"刘备",202,0,"调兵取零陵，差张飞为先锋，赵云合后，孔明与吾为中军，人马一万五千，留云长守荆州，糜竺、刘封守江陵。","调兵取零陵，差张飞为先锋，赵云合后，孔明与吾为中军，人马一万五千，留云长守荆州，糜竺、刘封守江陵。",1,"","","马良",232,},
        [5108] = {10001,1,1,"诸葛亮",203,0,"吾乃南阳诸葛孔明也。曹操引百万之众，被吾聊施小计，杀得片甲不回。","吾乃南阳诸葛孔明也。曹操引百万之众，被吾聊施小计，杀得片甲不回。",1,"","","邢道荣",122,},
        [5109] = {10001,2,1,"诸葛亮",203,0,"汝等岂堪与我对敌？我今来招安汝等，何不早降？","汝等岂堪与我对敌？我今来招安汝等，何不早降？",1,"","","邢道荣",122,},
        [5110] = {10001,3,2,"邢道荣",122,0,"赤壁鏖兵，乃周郎之谋也，干汝何事，敢来诳语！","赤壁鏖兵，乃周郎之谋也，干汝何事，敢来诳语！",1,"","","诸葛亮",203,},
        [5111] = {10001,4,2,"邢道荣",122,0,"问问我手中兵器！","问问我手中兵器！",1,"","","诸葛亮",203,},
        [5112] = {10002,1,1,"赵云",201,0,"认得常山赵子龙否！","认得常山赵子龙否！",1,"","","邢道荣",122,},
        [5113] = {10002,2,1,"赵云",201,0,"往哪里逃！","往哪里逃！",1,"","","邢道荣",122,},
        [5114] = {10002,3,2,"邢道荣",122,0,"子龙饶命！","子龙饶命！",1,"","","赵云",201,},
        [5115] = {10003,1,1,"诸葛亮",203,0,"汝若与我捉了刘贤，便准你投降。","汝若与我捉了刘贤，便准你投降。",1,"","","邢道荣",122,},
        [5116] = {10003,2,2,"邢道荣",122,0,"某愿往。","某愿往。",1,"","","诸葛亮",203,},
        [5117] = {10003,3,1,"诸葛亮",203,0,"你用何法捉他？","你用何法捉他？",1,"","","邢道荣",122,},
        [5118] = {10003,4,2,"邢道荣",122,0,"军师若肯放某回去，某自有巧说。今晚军师调兵劫寨，某为内应，活捉刘贤，献与军师。刘贤既擒，刘度自降矣。","军师若肯放某回去，某自有巧说。今晚军师调兵劫寨，某为内应，活捉刘贤，献与军师。刘贤既擒，刘度自降矣。",1,"","","诸葛亮",203,},
        [5119] = {10003,5,1,"刘备",202,0,"我如何信你？","我如何信你？",1,"","","邢道荣",122,},
        [5120] = {10003,6,2,"邢道荣",122,0,"我绝非谬言！","我绝非谬言！",1,"","","刘备",202,},
        [5121] = {10003,7,1,"诸葛亮",203,0,"我信你邢将军非谬言也，你回去吧。","我信你邢将军非谬言也，你回去吧。",1,"","","邢道荣",122,},
        [5122] = {10003,8,2,"邢道荣",122,0,"谢过孔明。","谢过孔明。",1,"","","诸葛亮",203,},
        [5123] = {10004,1,1,"邢道荣",122,0,"刘备放我回来，作为内应，要活捉你，让刘度自降矣。","刘备放我回来，作为内应，要活捉你，让刘度自降矣。",1,"","","刘贤",140,},
        [5124] = {10004,2,2,"刘贤",140,0,"如之奈何？","如之奈何？",1,"","","邢道荣",122,},
        [5125] = {10004,3,1,"邢道荣",122,0,"可将计就计。今夜将兵伏于寨外，寨中虚立旗幡，待孔明来劫寨，就而擒之。","可将计就计。今夜将兵伏于寨外，寨中虚立旗幡，待孔明来劫寨，就而擒之。",1,"","","刘贤",140,},
        [5126] = {10004,4,2,"刘贤",140,0,"好。","好。",1,"","","邢道荣",122,},
        [5127] = {10005,1,1,"刘贤",140,0,"一彪军到寨口，每人各带草把，欲烧军寨。","一彪军到寨口，每人各带草把，欲烧军寨。",1,"","","邢道荣",122,},
        [5128] = {10005,2,2,"邢道荣",122,0,"速速围堵擒孔明。","速速围堵擒孔明。",1,"","","刘贤",140,},
        [5129] = {10005,3,1,"刘贤",140,0,"好！","好！",1,"","","邢道荣",122,},
        [5130] = {10006,1,2,"邢道荣",122,0,"乘势追赶十余里，军皆不见。","乘势追赶十余里，军皆不见。",1,"","","刘贤",140,},
        [5131] = {10006,2,2,"邢道荣",122,0,"不好，中计也，速回本寨。","不好，中计也，速回本寨。",1,"","","刘贤",140,},
        [5132] = {10006,3,1,"刘贤",140,0,"不可入寨，寨恐怕已被占领。","不可入寨，寨恐怕已被占领。",1,"","","邢道荣",122,},
        [5133] = {10006,4,1,"刘贤",140,0,"去劫孔明寨便了。","去劫孔明寨便了。",1,"","","邢道荣",122,},
        [5134] = {10007,1,1,"赵云",201,0,"刘贤哪里跑，速速来降！","刘贤哪里跑，速速来降！",1,"","","刘贤",140,},
        [5135] = {10007,2,2,"刘贤",140,0,"我投降我投降。","我投降我投降。",1,"","","赵云",201,},
        [5136] = {10007,3,2,"刘贤",140,0,"邢道荣教某如此，实非本心也。","邢道荣教某如此，实非本心也。",1,"","","赵云",201,},
        [5137] = {10008,1,1,"赵云",201,0,"刘贤已被擒住，刘度汝投降，如其不降，打破城池，满门尽诛。","刘贤已被擒住，刘度汝投降，如其不降，打破城池，满门尽诛。",1,"","","刘度",121,},
        [5138] = {10008,2,1,"刘贤",140,0,"孔明未伤我分毫，与衣赐酒。","孔明未伤我分毫，与衣赐酒。",1,"","","刘度",121,},
        [5139] = {10008,3,1,"刘贤",140,0,"望刘度捧印出城投降。","望刘度捧印出城投降。",1,"","","刘度",121,},
        [5140] = {10008,4,2,"刘度",121,0,"好，我愿投玄德大寨纳降。","好，我愿投玄德大寨纳降。",1,"","","刘贤",140,},
        [5141] = {10008,5,1,"诸葛亮",203,0,"玄德言，今后汝刘度仍为郡守，刘贤赴荆州随军办事。","玄德言，今后汝刘度仍为郡守，刘贤赴荆州随军办事。",1,"","","刘度",121,},
        [5142] = {10008,6,2,"刘度",121,0,"谢孔明与刘玄德大恩。","谢孔明与刘玄德大恩。",1,"","","诸葛亮",203,},
        [5143] = {10101,1,1,"赵云",201,0,"吾主刘玄德，乃刘表之弟，今辅公子刘琦同领荆州，特来抚民，汝何敢迎敌？","吾主刘玄德，乃刘表之弟，今辅公子刘琦同领荆州，特来抚民，汝何敢迎敌？",1,"","","陈应",122,},
        [5144] = {10101,2,2,"陈应",122,0,"我等只服曹丞相，岂顺刘备！","我等只服曹丞相，岂顺刘备！",1,"","","赵云",201,},
        [5145] = {10101,3,1,"赵云",201,0,"岂有此理，吃我一枪！","岂有此理，吃我一枪！",1,"","","陈应",122,},
        [5146] = {10102,1,1,"赵云",201,0,"量你也打不过我，我不杀你，放你回去，快去跟赵范说，让他早来投降。","量你也打不过我，我不杀你，放你回去，快去跟赵范说，让他早来投降。",1,"","","陈应",122,},
        [5147] = {10102,2,2,"陈应",122,0,"谢将军不杀之恩。","谢将军不杀之恩。",1,"","","赵云",201,},
        [5148] = {10103,1,2,"赵范",129,0,"我本来就说要投降，你偏要战，以致如此。","我本来就说要投降，你偏要战，以致如此。",1,"","","陈应",122,},
        [5149] = {10103,2,2,"赵范",129,0,"你且回去，我这就拿着印绶去找赵云投降","你且回去，我这就拿着印绶去找赵云投降",1,"","","陈应",122,},
        [5150] = {10104,1,2,"赵范",129,0,"将军姓赵，某亦姓赵，五百年前，合是一家。将军乃真定人，某亦真定人，又是同乡。","将军姓赵，某亦姓赵，五百年前，合是一家。将军乃真定人，某亦真定人，又是同乡。",1,"","","赵云",201,},
        [5151] = {10104,2,2,"赵范",129,0,"倘得不弃，结为兄弟，实为万幸。","倘得不弃，结为兄弟，实为万幸。",1,"","","赵云",201,},
        [5152] = {10104,3,2,"赵范",129,0,"将军年长我四月，小弟愿尊将军为兄长！","将军年长我四月，小弟愿尊将军为兄长！",1,"","","赵云",201,},
        [5153] = {10104,4,1,"赵云",201,0,"贤弟不必多礼！","贤弟不必多礼！",1,"","","赵范",129,},
        [5154] = {10105,1,2,"赵范",129,0,"小弟有一事，仁兄切莫推辞。家嫂樊氏，寡居三年，弟常劝其改嫁。","小弟有一事，仁兄切莫推辞。家嫂樊氏，寡居三年，弟常劝其改嫁。",1,"","","赵云",201,},
        [5155] = {10105,2,2,"赵范",129,0,"家嫂定了三个条件：一要文武双全，名闻天下；二要相貌堂堂，威仪出众，三要与家兄同姓。","家嫂定了三个条件：一要文武双全，名闻天下；二要相貌堂堂，威仪出众，三要与家兄同姓。",1,"","","赵云",201,},
        [5156] = {10105,3,2,"赵范",129,0,"竟真这般凑巧，今尊兄正合家嫂所言。若尊兄不嫌弃家嫂，愿陪嫁资，与将军为妻，如何？","竟真这般凑巧，今尊兄正合家嫂所言。若尊兄不嫌弃家嫂，愿陪嫁资，与将军为妻，如何？",1,"","","赵云",201,},
        [5157] = {10105,4,1,"赵云",201,0,"混账！我既与你结为兄弟，你的嫂子即我的嫂子，岂可作此乱人伦之事乎！","混账！我既与你结为兄弟，你的嫂子即我的嫂子，岂可作此乱人伦之事乎！",1,"","","赵范",129,},
        [5158] = {10105,5,2,"赵范",129,0,"我好意相待，如何这般无礼！","我好意相待，如何这般无礼！",1,"","","赵云",201,},
        [5159] = {10106,1,2,"陈应",122,0,"赵云发怒出城去了，我们跟他拼了吧！","赵云发怒出城去了，我们跟他拼了吧！",1,"","","赵范",129,},
        [5160] = {10106,2,1,"赵范",129,0,"就怕打不过他。","就怕打不过他。",1,"","","陈应",122,},
        [5161] = {10106,3,2,"陈应",122,0,"我和鲍隆诈降在他军中，你带兵来，我们里应外合将他一举擒下。","我和鲍隆诈降在他军中，你带兵来，我们里应外合将他一举擒下。",1,"","","赵范",129,},
        [5162] = {10107,1,2,"陈应",122,0,"赵范欲用美人计赚将军，只等将军醉了，扶入后堂谋杀，将头去曹丞相处献功，如此不仁。","赵范欲用美人计赚将军，只等将军醉了，扶入后堂谋杀，将头去曹丞相处献功，如此不仁。",1,"","","赵云",201,},
        [5163] = {10107,2,2,"陈应",122,0,"我们二人见将军怒出，必连累于我们，因此投降。","我们二人见将军怒出，必连累于我们，因此投降。",1,"","","赵云",201,},
        [5164] = {10107,3,1,"赵云",201,0,"哈哈哈…好，来人！将此二人拿下！别以为我不知道你们是诈降，休想骗我！","哈哈哈…好，来人！将此二人拿下！别以为我不知道你们是诈降，休想骗我！",1,"","","",0,},
        [5165] = {10107,4,1,"赵云",201,0,"传令下去：要害我者，陈应、鲍隆也；不干众人之事。汝等听吾行计，皆有重赏。","传令下去：要害我者，陈应、鲍隆也；不干众人之事。汝等听吾行计，皆有重赏。",1,"","","",0,},
        [5166] = {10107,5,1,"赵云",201,0,"走，我们去桂阳城找赵范算账！","走，我们去桂阳城找赵范算账！",1,"","","",0,},
        [5167] = {10108,1,2,"刘备",202,0,"子龙干得漂亮，计取桂阳有功！","子龙干得漂亮，计取桂阳有功！",1,"","","赵云",201,},
        [5168] = {10108,2,1,"主角",1,0,"赵范要把嫂子嫁给你，本是一桩好事，子龙何故如此？","赵范要把嫂子嫁给你，本是一桩好事，子龙何故如此？",1,"","","赵云",201,},
        [5169] = {10108,3,2,"赵云",201,0,"赵范既与我结为兄弟，我若娶他嫂子，惹人唾骂，况且其妇再嫁，时失大节。","赵范既与我结为兄弟，我若娶他嫂子，惹人唾骂，况且其妇再嫁，时失大节。",1,"","","刘备",202,},
        [5170] = {10108,4,2,"赵云",201,0,"赵范初降，其心难测，主公新定江汉，枕席未安，云安敢以一妇人而废主公之大事？","赵范初降，其心难测，主公新定江汉，枕席未安，云安敢以一妇人而废主公之大事？",1,"","","刘备",202,},
        [5171] = {10108,5,1,"刘备",202,0,"今日大事已定，与汝娶之，若何？","今日大事已定，与汝娶之，若何？",1,"","","赵云",201,},
        [5172] = {10108,6,2,"赵云",201,0,"天下女子不少，但恐名誉不立，何患无妻子乎？","天下女子不少，但恐名誉不立，何患无妻子乎？",1,"","","",0,},
        [5173] = {10108,7,1,"主角",1,0,"子龙真丈夫也！","子龙真丈夫也！",1,"","","",0,},
        [5174] = {10201,1,2,"张飞",206,0,"子龙与四弟各取一郡，偏我是无用之人！给我三千兵马，与我去取武陵郡，活捉了太守金旋来献！","子龙与四妹各取一郡，偏我是无用之人！给我三千兵马，与我去取武陵郡，活捉了太守金旋来献！",1,"","","诸葛亮",203,},
        [5175] = {10201,2,1,"诸葛亮",203,0,"前者子龙取桂阳郡时，责下军令状而去。今日翼德要取武陵，必须也责下军令状，方可领兵去。","前者子龙取桂阳郡时，责下军令状而去。今日翼德要取武陵，必须也责下军令状，方可领兵去。",1,"","","张飞",206,},
        [5176] = {10201,3,2,"张星彩",216,0,"主公！爹爹向来急躁，如今立下军令状要取武陵郡，我担心会出什么乱子。","主公！爹爹向来急躁，如今立下军令状要取武陵郡，我担心会出什么乱子。",1,"","","主角",1,},
        [5177] = {10201,4,1,"主角",1,0,"星彩放心，我们暗中观察，随机应变，助三哥一臂之力。","星彩放心，我们暗中观察，随机应变，助三哥一臂之力。",1,"","","张星彩",216,},
        [5178] = {10202,1,1,"金旋",148,0,"那张飞引兵到此，要取我郡，待我整点精兵，出城迎敌！","那张飞引兵到此，要取我郡，待我整点精兵，出城迎敌！",1,"","","巩志",127,},
        [5179] = {10202,2,2,"巩志",127,0,"刘玄德乃大汉皇叔，仁义布于天下；加之张翼德骁勇非常。不可迎敌，不如纳降为上。","刘玄德乃大汉皇叔，仁义布于天下；加之张翼德骁勇非常。不可迎敌，不如纳降为上。",1,"","","金旋",148,},
        [5180] = {10202,3,1,"金旋",148,0,"岂有此理！你想与贼人内外勾结里通外合吗？来人！将巩志退出去斩了！","岂有此理！你想与贼人内外勾结里通外合吗？来人！将巩志退出去斩了！",1,"","","巩志",127,},
        [5181] = {10202,4,2,"金旋随从",128,0,"不可不可！先杀自己人，于军不利，会影响士气！","不可不可！先杀自己人，于军不利，会影响士气！",1,"","","金旋",148,},
        [5182] = {10202,5,1,"金旋",148,0,"哼！姑且饶你一命，我这就带兵出战，去会会那张翼德！","哼！姑且饶你一命，我这就带兵出战，去会会那张翼德！",1,"","","巩志",127,},
        [5183] = {10203,1,1,"张飞",206,0,"你爷爷今天要取武陵郡！金旋快出来领死！","你爷爷今天要取武陵郡！金旋快出来领死！",1,"","","",0,},
        [5184] = {10203,2,2,"金旋",148,0,"谁敢出战？……谁敢出战？……有没有人啊！谁敢出战！……算了！我自己来！","谁敢出战？……谁敢出战？……有没有人啊！谁敢出战！……算了！我自己来！",1,"","","",0,},
        [5185] = {10203,3,1,"张飞",206,0,"喝！哪里跑！","喝！哪里跑！",1,"","","",0,},
        [5186] = {10204,1,2,"金旋",148,0,"巩志！你要干什么？乱箭射我作甚！快开城门，让我回去！","巩志！你要干什么？乱箭射我作甚！快开城门，让我回去！",1,"","","巩志",127,},
        [5187] = {10204,2,1,"巩志",127,0,"汝不顺天时，自取败亡，吾与百姓自降刘矣。","汝不顺天时，自取败亡，吾与百姓自降刘矣。",1,"","","金旋",148,},
        [5188] = {10204,3,2,"金旋",148,0,"你！啊……（被一箭射中）","你！啊……（被一箭射中）",1,"","","巩志",127,},
        [5189] = {10205,1,2,"张星彩",216,0,"多亏主公计策，说服那巩志，让他这么快乖乖投降。","多亏主公计策，说服那巩志，让他这么快乖乖投降。",1,"","","",0,},
        [5190] = {10205,2,1,"主角",1,0,"星彩妹子过奖啦，是大哥仁德名扬天下，民众心悦诚服才是。","星彩妹子过奖啦，是大哥仁德名扬天下，民众心悦诚服才是。",1,"","","",0,},
        [5191] = {10206,1,1,"刘备",202,0,"翼德此次直取武陵郡，也是大功一件，甚好！","翼德此次直取武陵郡，也是大功一件，甚好！",1,"","","张飞",206,},
        [5192] = {10206,2,2,"张飞",206,0,"哪知道这次取武陵郡竟这般轻松！三千兵马都带多了，五百人足矣！","哪知道这次取武陵郡竟这般轻松！三千兵马都带多了，五百人足矣！",1,"","","刘备",202,},
        [5193] = {10301,1,1,"关羽",205,0,"听闻三弟四弟子龙各取一郡，闻长沙尚未取，教关某干这件功劳甚好。","听闻三弟四妹子龙各取一郡，闻长沙尚未取，教关某干这件功劳甚好。",1,"","","诸葛亮",203,},
        [5194] = {10301,2,2,"诸葛亮",203,0,"长沙太守韩玄平平无奇，没什么可说的，只是他手下一员大将叫黄忠，虽今年近六旬却有万夫不当之勇，不可轻敌。","长沙太守韩玄平平无奇，没什么可说的，只是他手下一员大将叫黄忠，虽今年近六旬却有万夫不当之勇，不可轻敌。",1,"","","关羽",205,},
        [5195] = {10301,3,1,"关羽",205,0,"军师何故长别人锐气，灭自己威风？量一老卒，何足道哉！","军师何故长别人锐气，灭自己威风？量一老卒，何足道哉！",1,"","","诸葛亮",203,},
        [5196] = {10301,4,1,"关羽",205,0,"关某不须用三千军，只消本部下五百名校刀手，决定斩黄忠、韩玄之首，献来麾下。","关某不须用三千军，只消本部下五百名校刀手，决定斩黄忠、韩玄之首，献来麾下。",1,"","","诸葛亮",203,},
        [5197] = {10301,5,2,"诸葛亮",203,0,"云长轻敌黄忠，只恐有失。","云长轻敌黄忠，只恐有失。",1,"","","关羽",205,},
        [5198] = {10301,6,1,"主角",1,0,"我愿跟随二哥一同前往，有个照应。","我愿跟随二哥一同前往，有个照应。",1,"","","诸葛亮",203,},
        [5199] = {10301,7,2,"诸葛亮",203,0,"如此甚好。","如此甚好。",1,"","","主角",1,},
        [5200] = {10302,1,1,"韩玄",122,0,"那关羽要来取我们长沙，如何是好？","那关羽要来取我们长沙，如何是好？",1,"","","",0,},
        [5201] = {10302,2,2,"黄忠",208,0,"主公不须忧虑，凭某这口刀，这张弓，一千个来，一千个死！","主公不须忧虑，凭某这口刀，这张弓，一千个来，一千个死！",1,"","","韩玄",122,},
        [5202] = {10302,3,2,"杨龄",129,0,"不须老将军出战，只就某手中定活捉关某。","不须老将军出战，只就某手中定活捉关某。",1,"","","韩玄",122,},
        [5203] = {10302,4,1,"韩玄",122,0,"好，给你一千兵马，出城迎战！","好，给你一千兵马，出城迎战！",1,"","","杨龄",129,},
        [5204] = {10303,1,1,"关羽",205,0,"先前那杨龄实在太弱，被我一刀斩于马下。今来将莫非黄忠否？","先前那杨龄实在太弱，被我一刀斩于马下。今来将莫非黄忠否？",1,"","","黄忠",208,},
        [5205] = {10303,2,2,"黄忠",208,0,"既知我名，焉敢犯我境！","既知我名，焉敢犯我境！",1,"","","关羽",205,},
        [5206] = {10303,3,1,"关羽",205,0,"特来取汝首级！","特来取汝首级！",1,"","","黄忠",208,},
        [5207] = {10304,1,2,"韩玄",122,0,"都打了一百多回合了还没分胜负，黄将军快回来，千万别有什么闪失。","都打了一百多回合了还没分胜负，黄将军快回来，千万别有什么闪失。",1,"","","",0,},
        [5208] = {10304,2,1,"关羽",205,0,"老将黄忠，名不虚传，斗一百回合，竟然全无破绽。下次要用计才能赢他了。","老将黄忠，名不虚传，斗一百回合，竟然全无破绽。下次要用计才能赢他了。",1,"","","",0,},
        [5209] = {10305,1,1,"关羽",205,0,"这次我可不会让着你了，定要分个高下。","这次我可不会让着你了，定要分个高下。",1,"","","黄忠",208,},
        [5210] = {10305,2,2,"黄忠",208,0,"哼，好大的口气，谁输谁赢还不一定呢。","哼，好大的口气，谁输谁赢还不一定呢。",1,"","","关羽",205,},
        [5211] = {10305,3,1,"关羽",205,0,"看刀！","看刀！",1,"","","黄忠",208,},
        [5212] = {10305,4,2,"黄忠",208,0,"喝！……啊！（战马失了前蹄将黄忠掀翻在地）","喝！……啊！（战马失了前蹄将黄忠掀翻在地）",1,"","","关羽",205,},
        [5213] = {10305,5,1,"关羽",205,0,"（回马收刀）我且饶你性命，快换马来厮杀！","（回马收刀）我且饶你性命，快换马来厮杀！",1,"","","黄忠",208,},
        [5214] = {10306,1,1,"韩玄",122,0,"怎么回事黄将军？竟然被自己的马掀在地下？","怎么回事黄将军？竟然被自己的马掀在地下？",1,"","","黄忠",208,},
        [5215] = {10306,2,2,"黄忠",208,0,"此马久不上阵，故有此失。来日再战，我用弓箭射之。","此马久不上阵，故有此失。来日再战，我用弓箭射之。",1,"","","韩玄",122,},
        [5216] = {10307,1,2,"黄忠",208,0,"难得云长如此义气，他不忍杀害我，我又安忍射他？若不射，又恐违了将令。","难得云长如此义气，他不忍杀害我，我又安忍射他？若不射，又恐违了将令。",1,"","","",0,},
        [5217] = {10307,2,2,"黄忠",208,0,"我虚拉两弦，将他震慑回去便是。","我虚拉两弦，将他震慑回去便是。",1,"","","",0,},
        [5218] = {10307,3,1,"关羽",205,0,"听闻黄忠百发百中，为何只闻弓弦声而不见箭？难道传言有假？","听闻黄忠百发百中，为何只闻弓弦声而不见箭？难道传言有假？",1,"","","",0,},
        [5219] = {10307,4,2,"黄忠",208,0,"吃我一箭！（搭箭开弓，弦响箭到，正射在云长盔缨根上）","吃我一箭！（搭箭开弓，弦响箭到，正射在云长盔缨根上）",1,"","","",0,},
        [5220] = {10307,5,1,"关羽",205,0,"原来那黄忠真的有百步穿杨之能，今日只射盔缨，正是报昨日不杀之恩也。","原来那黄忠真的有百步穿杨之能，今日只射盔缨，正是报昨日不杀之恩也。",1,"","","",0,},
        [5221] = {10401,1,2,"韩玄",122,0,"我看了三日，汝前日出战没有用尽全力，必有私心；昨日马失，他不杀汝，必有关通；","我看了三日，汝前日出战没有用尽全力，必有私心；昨日马失，他不杀汝，必有关通；",1,"","","黄忠",208,},
        [5222] = {10401,2,2,"韩玄",122,0,"今日虚拽两弦，第三箭却只射他盔缨，如何不是外通内连？若不斩汝，必为后患！","今日虚拽两弦，第三箭却只射他盔缨，如何不是外通内连？若不斩汝，必为后患！",1,"","","黄忠",208,},
        [5223] = {10401,3,1,"黄忠",208,0,"我无罪！","我无罪！",1,"","","韩玄",122,},
        [5224] = {10401,4,2,"韩玄",122,0,"我不听！来人，将黄忠推出去斩了！如果有求情的，一同处斩！","我不听！来人，将黄忠推出去斩了！如果有求情的，一同处斩！",1,"","","",0,},
        [5225] = {10401,5,1,"魏延",210,0,"黄忠将军是长沙之保障，如今如果杀了黄将军就是杀了长沙百姓！","黄忠将军是长沙之保障，如今如果杀了黄将军就是杀了长沙百姓！",1,"","","",0,},
        [5226] = {10401,6,1,"魏延",210,0,"韩玄残暴不仁，轻贤慢士，大家一起杀了他！愿随我者便来！","韩玄残暴不仁，轻贤慢士，大家一起杀了他！愿随我者便来！",1,"","","",0,},
        [5227] = {10402,1,1,"关羽",205,0,"大哥，我已得长沙郡，降将黄忠、魏延。","大哥，我已得长沙郡，降将黄忠、魏延。",1,"","","刘备",202,},
        [5228] = {10402,2,2,"刘备",202,0,"甚好！二弟有功了！我要去拜会一下黄忠将军。","甚好！二弟有功了！我要去拜会一下黄忠将军。",1,"","","关羽",205,},
        [5229] = {10402,3,1,"黄忠",208,0,"我愿归顺玄德公麾下，只是原主公韩玄还请厚葬。","我愿归顺玄德公麾下，只是原主公韩玄还请厚葬。",1,"","","刘备",202,},
        [5230] = {10402,4,2,"刘备",202,0,"将军真是忠义！","将军真是忠义！",1,"","","黄忠",208,},
        [5231] = {10403,1,1,"关羽",205,0,"给大哥引荐，这便是魏延。","给大哥引荐，这便是魏延。",1,"","","刘备",202,},
        [5232] = {10403,2,2,"诸葛亮",203,0,"来人，将魏延推下去斩了！","来人，将魏延推下去斩了！",1,"","","",0,},
        [5233] = {10403,3,1,"刘备",202,0,"魏延乃有功无罪之人，军师何故欲杀之？","魏延乃有功无罪之人，军师何故欲杀之？",1,"","","诸葛亮",203,},
        [5234] = {10403,4,2,"诸葛亮",203,0,"食其禄而杀其主，是不忠也；居其土而献其地，是不义也。","食其禄而杀其主，是不忠也；居其土而献其地，是不义也。",1,"","","刘备",202,},
        [5235] = {10403,5,2,"诸葛亮",203,0,"吾观魏延脑后有反骨，久后必反，故先斩之，以绝祸根。","吾观魏延脑后有反骨，久后必反，故先斩之，以绝祸根。",1,"","","刘备",202,},
        [5236] = {10403,6,1,"刘备",202,0,"若斩此人，恐降者人人自危。望军师恕之。","若斩此人，恐降者人人自危。望军师恕之。",1,"","","诸葛亮",203,},
        [5237] = {10403,7,2,"诸葛亮",203,0,"也罢，吾今饶汝性命。汝可尽忠报主，勿生异心，否则我定取汝首级。","也罢，吾今饶汝性命。汝可尽忠报主，勿生异心，否则我定取汝首级。",1,"","","魏延",210,},
        [5238] = {10501,1,1,"周瑜",303,0,"吾欲起兵与刘备、诸葛亮共决雌雄，幸得子敬和程德谋助我，攻打南郡，定要夺还东吴。","吾欲起兵与刘备、诸葛亮共决雌雄，幸得子敬和程德谋助我，攻打南郡，定要夺还东吴。",1,"","","鲁肃",312,},
        [5239] = {10501,2,2,"鲁肃",312,0,"不可。方今与曹操相持，尚未分成败。刘玄德旧曾与曹操相厚，若逼得紧急，献了城池，一同攻打东吴，如之奈何？","不可。方今与曹操相持，尚未分成败。刘玄德旧曾与曹操相厚，若逼得紧急，献了城池，一同攻打东吴，如之奈何？",1,"","","周瑜",303,},
        [5240] = {10501,3,1,"周瑜",303,0,"吾等用计策，损兵马，费钱粮，他去图现成，岂不可恨！","吾等用计策，损兵马，费钱粮，他去图现成，岂不可恨！",1,"","","鲁肃",312,},
        [5241] = {10501,4,2,"鲁肃",312,0,"公瑾且耐。容某亲见玄德，将理来说他。若说不通，那时动兵未迟。","公瑾且耐。容某亲见玄德，将理来说他。若说不通，那时动兵未迟。",1,"","","周瑜",303,},
        [5242] = {10501,5,2,"周瑜随从",140,0,"子敬所言极是。","子敬所言极是。",1,"","","周瑜",303,},
        [5243] = {10502,1,1,"鲁肃",312,0,"我要见刘玄德有话说。","我要见刘玄德有话说。",1,"","","赵云",201,},
        [5244] = {10502,2,2,"赵云",201,0,"吾主与军师在荆州城中。","吾主与军师在荆州城中。",1,"","","鲁肃",312,},
        [5245] = {10502,3,1,"主角",1,0,"鲁肃来找大哥肯定是想要回南郡，我要跟着去看看，帮大哥说话。","鲁肃来找大哥肯定是想要回南郡，我要跟着去看看，帮大哥说话。",1,"","","赵云",201,},
        [5246] = {10503,1,1,"鲁肃",312,0,"吾主吴侯，与都督公瑾，教某再三申意皇叔，之前曹操想要攻打皇叔，是东吴杀退曹兵，救了皇叔。","吾主吴侯，与都督公瑾，教某再三申意皇叔，之前曹操想要攻打皇叔，是东吴杀退曹兵，救了皇叔。",1,"","","诸葛亮",203,},
        [5247] = {10503,2,1,"鲁肃",312,0,"所有荆州九郡，合当归于东吴。今皇叔用诡计，夺占荆襄，使江东空费钱粮军马，而皇叔安受其利，恐怕不合情理。","所有荆州九郡，合当归于东吴。今皇叔用诡计，夺占荆襄，使江东空费钱粮军马，而皇叔安受其利，恐怕不合情理。",1,"","","诸葛亮",203,},
        [5248] = {10503,3,2,"诸葛亮",203,0,"子敬何出此言？荆襄九郡，非东吴之地，乃刘景升之基业。吾主固景升之弟也。","子敬何出此言？荆襄九郡，非东吴之地，乃刘景升之基业。吾主固景升之弟也。",1,"","","鲁肃",312,},
        [5249] = {10503,4,2,"诸葛亮",203,0,"景升虽亡，其子尚在；以叔辅侄，而取荆州，有何不可？","景升虽亡，其子尚在；以叔辅侄，而取荆州，有何不可？",1,"","","鲁肃",312,},
        [5250] = {10504,1,2,"鲁肃",312,0,"如果真的是公子刘琦占据，尚有可解；今公子在江夏，并不在这里呀！","如果真的是公子刘琦占据，尚有可解；今公子在江夏，并不在这里呀！",1,"","","主角",1,},
        [5251] = {10504,2,1,"主角",1,0,"子敬要见公子刘琦吗？来人，请公子出来。","子敬要见公子刘琦吗？来人，请公子出来。",1,"","","鲁肃",312,},
        [5252] = {10504,3,2,"刘琦",128,0,"病躯不能施礼，子敬勿罪。","病躯不能施礼，子敬勿罪。",1,"","","鲁肃",312,},
        [5253] = {10504,4,2,"鲁肃",312,0,"公子若不在，便如何？","公子若不在，便如何？",1,"","","主角",1,},
        [5254] = {10504,5,1,"主角",1,0,"公子在一日，守一日；若不在，别有商议。","公子在一日，守一日；若不在，别有商议。",1,"","","鲁肃",312,},
        [5255] = {10504,6,2,"鲁肃",312,0,"若公子不在，须将城池还我东吴。","若公子不在，须将城池还我东吴。",1,"","","主角",1,},
        [5256] = {10504,7,1,"主角",1,0,"子敬之言是也。","子敬之言是也。",1,"","","鲁肃",312,},
        [5257] = {10505,1,1,"鲁肃",312,0,"禀告都督，诸葛亮说刘琦在一日，他们守荆州一日，如果刘琦不在了，就将城池还我东吴。","禀告都督，诸葛亮说刘琦在一日，他们守荆州一日，如果刘琦不在了，就将城池还我东吴。",1,"","","周瑜",303,},
        [5258] = {10505,2,2,"周瑜",303,0,"刘琦正青春年少，如何便得他死？这荆州何日得还？","刘琦正青春年少，如何便得他死？这荆州何日得还？",1,"","","鲁肃",312,},
        [5259] = {10505,3,1,"鲁肃",312,0,"都督放心。只在鲁肃身上，务要讨荆襄还东吴。","都督放心。只在鲁肃身上，务要讨荆襄还东吴。",1,"","","周瑜",303,},
        [5260] = {10505,4,2,"周瑜",303,0,"子敬有何高见？","子敬有何高见？",1,"","","鲁肃",312,},
        [5261] = {10505,5,1,"鲁肃",312,0,"吾观刘琦过于酒色，病入膏肓，现今面色羸弱，气喘呕血，不过半年，其人必死。那时往取荆州，刘备须无得推故。","吾观刘琦过于酒色，病入膏肓，现今面色羸弱，气喘呕血，不过半年，其人必死。那时往取荆州，刘备须无得推故。",1,"","","周瑜",303,},
        [5262] = {10505,6,2,"孙权信使",128,0,"主公围合淝，累战不捷。特令都督收回大军，且拨兵赴合淝相助。","主公围合淝，累战不捷。特令都督收回大军，且拨兵赴合淝相助。",1,"","","周瑜",303,},
        [5263] = {10601,1,1,"主角",1,0,"听闻孙权自赤壁一战后，久在合肥，与曹兵交锋大小十余战未分胜负，我们去他那边看看吧。","听闻孙权自赤壁一战后，久在合肥，与曹兵交锋大小十余战未分胜负，我们去他那边看看吧。",1,"","","张星彩",216,},
        [5264] = {10601,2,2,"张星彩",216,0,"星彩跟着主公走，主公说去哪里就去哪里！","星彩跟着主公走，主公说去哪里就去哪里！",1,"","","主角",1,},
        [5265] = {10602,1,1,"孙权",306,0,"我们与曹军大大小小打了不知道多少仗，也未分胜负。听闻曹军在等援军来，不知是真是假。","我们与曹军大大小小打了不知道多少仗，也未分胜负。听闻曹军在等援军来，不知是真是假。",1,"","","",0,},
        [5266] = {10602,2,2,"孙权随从",120,0,"不如抓几个他们的小吏来询问一番，探探虚实。","不如抓几个他们的小吏来询问一番，探探虚实。",1,"","","孙权",306,},
        [5267] = {10603,1,1,"孙权随从",120,0,"禀报主公，我们抓到了两个曹军的信使，这是他们携带的密报。","禀报主公，我们抓到了两个曹军的信使，这是他们携带的密报。",1,"","","孙权",306,},
        [5268] = {10603,2,2,"孙权",306,0,"什么？敌方有四万援军即将抵达？这…容我三思…","什么？敌方有四万援军即将抵达？这…容我三思…",1,"","","",0,},
        [5269] = {10603,3,2,"孙权",306,0,"大大小小打了这么多仗，人困马乏，士气损耗不少，如今敌方大军将至，若继续对峙下去于我方大不利……","大大小小打了这么多仗，人困马乏，士气损耗不少，如今敌方大军将至，若继续对峙下去于我方大不利……",1,"","","",0,},
        [5270] = {10603,4,2,"孙权",306,0,"我们还是撤军吧。回去养精蓄锐，来日再战。","我们还是撤军吧。回去养精蓄锐，来日再战。",1,"","","",0,},
        [5271] = {10603,5,1,"主角",1,0,"仲谋先不忙，我看这四万大军的消息来得蹊跷，说不定有诈。","仲谋先不忙，我看这四万大军的消息来得蹊跷，说不定有诈。",1,"","","孙权",306,},
        [5272] = {10603,6,1,"主角",1,0,"不如我前去探探虚实，听闻曹军有一员大将名叫张辽，刚好想去会一会他。","不如我前去探探虚实，听闻曹军有一员大将名叫张辽，刚好想去会一会他。",1,"","","孙权",306,},
        [5273] = {10603,7,2,"孙权",306,0,"那将军请多保重，小心为上。","那将军请多保重，小心为上。",1,"","","主角",1,},
        [5274] = {10604,1,2,"张星彩",216,0,"主公，听闻那曹军大将张辽勇猛无比，主公要小心呀。","主公，听闻那曹军大将张辽勇猛无比，主公要小心呀。",1,"","","主角",1,},
        [5275] = {10604,2,1,"主角",1,0,"面如紫玉，目若朗星，张将军果然英武。","面如紫玉，目若朗星，张将军果然英武。",1,"","","张辽",109,},
        [5276] = {10604,3,1,"主角",1,0,"今日愿与将军切磋一下，点到为止。","今日愿与将军切磋一下，点到为止。",1,"","","张辽",109,},
        [5277] = {10604,4,2,"张辽",109,0,"承让！","承让！",1,"","","主角",1,},
        [5278] = {10701,1,1,"孙权",306,0,"今日程普大军赶到，来得正是时候，我要亲自出营劳军。","今日程普大军赶到，来得正是时候，我要亲自出营劳军。",1,"","","鲁肃",312,},
        [5279] = {10701,2,2,"鲁肃",312,0,"主公怎么亲自下马相迎，真是折煞我也。","主公怎么亲自下马相迎，真是折煞我也。",1,"","","孙权",306,},
        [5280] = {10701,3,1,"孙权",306,0,"我下马来迎接你，能彰显你的地位么？","我下马来迎接你，能彰显你的地位么？",1,"","","鲁肃",312,},
        [5281] = {10701,4,2,"鲁肃",312,0,"希望主公威德行于天下，成就帝王之业，使我青史留名，才是彰显我的地位了。","希望主公威德行于天下，成就帝王之业，使我青史留名，才是彰显我的地位了。",1,"","","孙权",306,},
        [5282] = {10701,5,1,"孙权",306,0,"哈哈哈，好！","哈哈哈，好！",1,"","","鲁肃",312,},
        [5283] = {10702,1,2,"孙权随从",120,0,"禀告主公，张辽派人送来战书。","禀告主公，张辽派人送来战书。",1,"","","孙权",306,},
        [5284] = {10702,2,1,"孙权",306,0,"张辽欺人太甚，听闻程普大军来了便要一战，是瞧不起之前带兵的我吗？","张辽欺人太甚，听闻程普大军来了便要一战，是瞧不起之前带兵的我吗？",1,"","","",0,},
        [5285] = {10702,3,1,"孙权",306,0,"来日吾不用新军赴敌，看我大战一场！","来日吾不用新军赴敌，看我大战一场！",1,"","","",0,},
        [5286] = {10703,1,1,"李典",124,0,"对面金盔者，孙权也。若捉得孙权，足可与八十三万大军报仇。","对面金盔者，孙权也。若捉得孙权，足可与八十三万大军报仇。",1,"","","乐进",113,},
        [5287] = {10703,2,2,"乐进",113,0,"好，我先去解决了宋谦和太史慈，我们一起去捉拿孙权。","好，我先去解决了宋谦和太史慈，我们一起去捉拿孙权。",1,"","","李典",124,},
        [5288] = {10704,1,1,"宋谦",141,0,"主公快走，这里有我！","主公快走，这里有我！",1,"","","",0,},
        [5289] = {10704,2,2,"太史慈",305,0,"糟了，宋谦中箭了，保护主公！","糟了，宋谦中箭了，保护主公！",1,"","","",0,},
        [5290] = {10704,3,1,"程普",320,0,"主公莫慌，程普在此。","主公莫慌，程普在此。",1,"","","",0,},
        [5291] = {10704,4,2,"孙权",306,0,"回营！","回营！",1,"","","",0,},
        [5292] = {10705,1,1,"鲁肃",312,0,"主公恃盛壮之气，轻视大敌，三军之众，莫不寒心。","主公恃盛壮之气，轻视大敌，三军之众，莫不寒心。",1,"","","孙权",306,},
        [5293] = {10705,2,1,"鲁肃",312,0,"且今日宋谦死于锋镝之下，皆主公轻敌之故。今后切宜保重。","且今日宋谦死于锋镝之下，皆主公轻敌之故。今后切宜保重。",1,"","","孙权",306,},
        [5294] = {10705,3,2,"孙权",306,0,"是孤之过也。从今当改之。","是孤之过也。从今当改之。",1,"","","鲁肃",312,},
        [5295] = {10705,4,1,"太史慈",305,0,"某手下有一人，叫戈定。与张辽手下养马后槽是弟兄。","某手下有一人，叫戈定。与张辽手下养马后槽是弟兄。",1,"","","孙权",306,},
        [5296] = {10705,5,1,"太史慈",305,0,"那后槽被责怀怨，今晚使人报来，举火为号，刺杀张辽，以报宋谦之仇。某请引兵为外应。","那后槽被责怀怨，今晚使人报来，举火为号，刺杀张辽，以报宋谦之仇。某请引兵为外应。",1,"","","孙权",306,},
        [5297] = {10705,6,2,"诸葛瑾",319,0,"张辽多谋，恐有准备，多加小心。","张辽多谋，恐有准备，多加小心。",1,"","","太史慈",305,},
        [5298] = {10706,1,1,"戈定",134,0,"我已使人报太史慈将军去了，今夜必来接应。我们怎么搞？","我已使人报太史慈将军去了，今夜必来接应。我们怎么搞？",1,"","","养马后槽",124,},
        [5299] = {10706,2,2,"养马后槽",124,0,"此间离中军较远，夜间急不能进，只就草堆上放起一把火，你去前面叫反，城中兵乱，就里刺杀张辽，余军自走也。","此间离中军较远，夜间急不能进，只就草堆上放起一把火，你去前面叫反，城中兵乱，就里刺杀张辽，余军自走也。",1,"","","戈定",134,},
        [5300] = {10706,3,1,"张辽",109,0,"传令下去，今夜加紧城防，不得解甲宿睡。","传令下去，今夜加紧城防，不得解甲宿睡。",1,"","","",0,},
        [5301] = {10706,4,2,"张辽随从",142,0,"今日全胜，吴兵远遁，将军何不卸甲安息？","今日全胜，吴兵远遁，将军何不卸甲安息？",1,"","","张辽",109,},
        [5302] = {10706,5,1,"张辽",109,0,"非也。倘吴兵度我无备，乘虚攻击，何以应之？今夜防备，当比每夜更加谨慎。","非也。倘吴兵度我无备，乘虚攻击，何以应之？今夜防备，当比每夜更加谨慎。",1,"","","张辽随从",142,},
        [5303] = {10706,6,2,"张辽随从",142,0,"报告！后寨起火了，造反声一片！将军去看看吧！","报告！后寨起火了，造反声一片！将军去看看吧！",1,"","","张辽",109,},
        [5304] = {10706,7,1,"张辽",109,0,"岂有一城皆反者？此是造反之人，故惊军士耳。如乱者先斩！","岂有一城皆反者？此是造反之人，故惊军士耳。如乱者先斩！",1,"","","张辽随从",142,},
        [5305] = {10706,8,2,"张辽随从",142,0,"报告！城外一片叫喊声，敌方来了很多人！","报告！城外一片叫喊声，敌方来了很多人！",1,"","","张辽",109,},
        [5306] = {10706,9,1,"张辽",109,0,"此是吴兵外应，可就计破之。","此是吴兵外应，可就计破之。",1,"","","张辽随从",142,},
        [5307] = {10707,1,1,"太史慈",305,0,"城内起火，反声大噪，应是戈定他们成功了。我们前去一举拿下张辽，为宋谦报仇！","城内起火，反声大噪，应是戈定他们成功了。我们前去一举拿下张辽，为宋谦报仇！",1,"","","",0,},
        [5308] = {10707,2,2,"张辽",109,0,"哼，他们上当了，放箭！","哼，他们上当了，放箭！",1,"","","",0,},
        [5309] = {10707,3,1,"陆逊",311,0,"太史慈将军！有埋伏，快撤！","太史慈将军！有埋伏，快撤！",1,"","","太史慈",305,},
        [5310] = {10707,4,1,"太史慈",305,0,"大丈夫生于乱世，当带三尺剑立不世之功；今所志未遂，奈何死乎！（言讫而亡，年四十一岁。）","大丈夫生于乱世，当带三尺剑立不世之功；今所志未遂，奈何死乎！（言讫而亡，年四十一岁。）",1,"","","",0,},
        [5311] = {10801,1,1,"诸葛亮",203,0,"亮夜观星象，见西北有星坠地，应该是有皇族成员陨落了。","亮夜观星象，见西北有星坠地，应该是有皇族成员陨落了。",1,"","","刘备",202,},
        [5312] = {10801,2,2,"刘备随从",120,0,"报告主公，公子刘琦病亡。","报告主公，公子刘琦病亡。",1,"","","刘备",202,},
        [5313] = {10801,3,1,"诸葛亮",203,0,"生死分定，主公勿忧，恐伤贵体。","生死分定，主公勿忧，恐伤贵体。",1,"","","刘备",202,},
        [5314] = {10801,4,2,"刘备",202,0,"今日刘琦已死，东吴必来讨荆州，如何对答？","今日刘琦已死，东吴必来讨荆州，如何对答？",1,"","","诸葛亮",203,},
        [5315] = {10801,5,1,"诸葛亮",203,0,"若有人来，亮自有言对答。","若有人来，亮自有言对答。",1,"","","刘备",202,},
        [5316] = {10802,1,2,"鲁肃",312,0,"主公闻令侄弃世，特具薄礼，遣某前来致祭。周都督再三致意刘皇叔、诸葛先生。","主公闻令侄弃世，特具薄礼，遣某前来致祭。周都督再三致意刘皇叔、诸葛先生。",1,"","","刘备",202,},
        [5317] = {10802,2,2,"鲁肃",312,0,"前者皇叔有言：公子不在，即还荆州。今公子已去世，必然见还。不识几时可以交割？","前者皇叔有言：公子不在，即还荆州。今公子已去世，必然见还。不识几时可以交割？",1,"","","刘备",202,},
        [5318] = {10802,3,1,"刘备",202,0,"这个不急，先喝酒，我们慢慢说。","这个不急，先喝酒，我们慢慢说。",1,"","","鲁肃",312,},
        [5319] = {10802,4,2,"鲁肃",312,0,"先把正经事处理完，我们再喝酒不迟。","先把正经事处理完，我们再喝酒不迟。",1,"","","刘备",202,},
        [5320] = {10802,5,1,"诸葛亮",203,0,"子敬好不通理，直须待人开口！我主人乃今皇上之叔，刘景升之弟。刘氏天下，我主姓刘倒无分，汝主姓孙反要强争？","子敬好不通理，直须待人开口！我主人乃今皇上之叔，刘景升之弟。刘氏天下，我主姓刘倒无分，汝主姓孙反要强争？",1,"","","鲁肃",312,},
        [5321] = {10802,6,1,"诸葛亮",203,0,"且赤壁之战，我主多负勤劳，众将并皆用命，岂独是汝东吴之为？若非我借东南风，周郎安能展半筹之功？","且赤壁之战，我主多负勤劳，众将并皆用命，岂独是汝东吴之为？若非我借东南风，周郎安能展半筹之功？",1,"","","鲁肃",312,},
        [5322] = {10802,7,1,"诸葛亮",203,0,"刚才我主人没有立即回答你，是因为子敬乃高明之士，不待细说。你自己心里就没点数吗？","刚才我主人没有立即回答你，是因为子敬乃高明之士，不待细说。你自己心里就没点数吗？",1,"","","鲁肃",312,},
        [5323] = {10803,1,2,"鲁肃",312,0,"孔明之言，怕不有理；争奈鲁肃身上甚是不便。","孔明之言，怕不有理；争奈鲁肃身上甚是不便。",1,"","","诸葛亮",203,},
        [5324] = {10803,2,1,"诸葛亮",203,0,"有何不便处？","有何不便处？",1,"","","鲁肃",312,},
        [5325] = {10803,3,2,"鲁肃",312,0,"之前刘皇叔当阳受难时，是我引你们见我主公；后来周公瑾要打荆州，又是我挡住；你们说公子去世还荆州，又是我担承；","之前刘皇叔当阳受难时，是我引你们见我主公；后来周公瑾要打荆州，又是我挡住；你们说公子去世还荆州，又是我担承；",1,"","","诸葛亮",203,},
        [5326] = {10803,4,2,"鲁肃",312,0,"如今你们不兑现诺言，让我回去如何复命？主公和周公瑾一定会治我的罪。","如今你们不兑现诺言，让我回去如何复命？主公和周公瑾一定会治我的罪。",1,"","","诸葛亮",203,},
        [5327] = {10803,5,1,"诸葛亮",203,0,"这样，让我主人立一纸文书，暂借荆州为本，等我主别图得城池时，便交付还东吴，如何？","这样，让我主人立一纸文书，暂借荆州为本，等我主别图得城池时，便交付还东吴，如何？",1,"","","鲁肃",312,},
        [5328] = {10803,6,2,"鲁肃",312,0,"孔明待夺得何处，还我荆州？","孔明待夺得何处，还我荆州？",1,"","","诸葛亮",203,},
        [5329] = {10803,7,1,"诸葛亮",203,0,"等我主图得西川，那时便还。","等我主图得西川，那时便还。",1,"","","鲁肃",312,},
        [5330] = {10804,1,1,"诸葛亮",203,0,"我已写好文书，押了字。烦子敬先生也押个字，回见吴侯也好看。","我已写好文书，押了字。烦子敬先生也押个字，回见吴侯也好看。",1,"","","鲁肃",312,},
        [5331] = {10804,2,2,"鲁肃",312,0,"某知皇叔乃仁义之人，必不相负。","某知皇叔乃仁义之人，必不相负。",1,"","","诸葛亮",203,},
        [5332] = {10804,3,1,"诸葛亮",203,0,"子敬回见吴侯，善言伸意，休生妄想。若不准我文书，我翻了面皮，连八十一州都夺了。","子敬回见吴侯，善言伸意，休生妄想。若不准我文书，我翻了面皮，连八十一州都夺了。",1,"","","鲁肃",312,},
        [5333] = {10804,4,1,"诸葛亮",203,0,"今只要两家和气，休教曹贼笑话。","今只要两家和气，休教曹贼笑话。",1,"","","鲁肃",312,},
        [5334] = {10805,1,1,"周瑜",303,0,"子敬讨荆州如何？","子敬讨荆州如何？",1,"","","鲁肃",312,},
        [5335] = {10805,2,2,"鲁肃",312,0,"有文书在此。","有文书在此。",1,"","","周瑜",303,},
        [5336] = {10805,3,1,"周瑜",303,0,"子敬中诸葛之谋也！名为借地，实是混赖。他说取了西川便还，知他几时取西川？","子敬中诸葛之谋也！名为借地，实是混赖。他说取了西川便还，知他几时取西川？",1,"","","鲁肃",312,},
        [5337] = {10805,4,1,"周瑜",303,0,"这等文书，如何中用，你却与他做保！他若不还时，必须连累足下，主公见罪奈何？","这等文书，如何中用，你却与他做保！他若不还时，必须连累足下，主公见罪奈何？",1,"","","鲁肃",312,},
        [5338] = {10805,5,2,"鲁肃",312,0,"这……玄德应该不会骗我的吧。","这……玄德应该不会骗我的吧。",1,"","","周瑜",303,},
        [5339] = {10805,6,1,"周瑜",303,0,"子敬乃诚实人也。刘备枭雄之辈，诸葛亮奸猾之徒，恐不似先生心地。","子敬乃诚实人也。刘备枭雄之辈，诸葛亮奸猾之徒，恐不似先生心地。",1,"","","鲁肃",312,},
        [5340] = {10901,1,2,"细作",131,0,"荆州城中扬起布幡做好事，城外别建新坟，军士各挂孝。","荆州城中扬起布幡做好事，城外别建新坟，军士各挂孝。",1,"","","周瑜",303,},
        [5341] = {10901,2,1,"周瑜",303,0,"没了甚人？","没了甚人？",1,"","","细作",131,},
        [5342] = {10901,3,2,"细作",131,0,"刘玄德没了甘夫人，即日安排殡葬。","刘玄德没了甘夫人，即日安排殡葬。",1,"","","周瑜",303,},
        [5343] = {10901,4,1,"周瑜",303,0,"吾计成矣：使刘备束手就缚，荆州反掌可得！","吾计成矣：使刘备束手就缚，荆州反掌可得！",1,"","","细作",131,},
        [5344] = {10901,5,1,"周瑜",303,0,"我今上书主公，教人去荆州为媒，说刘备来入赘。赚到南徐，妻子不能勾得，幽囚在狱中，却使人去讨荆州换刘备。","我今上书主公，教人去荆州为媒，说刘备来入赘。赚到南徐，妻子不能勾得，幽囚在狱中，却使人去讨荆州换刘备。",1,"","","细作",131,},
        [5345] = {10901,6,1,"周瑜",303,0,"等他交割了荆州城池，我别有主意。于子敬身上，须无事也。","等他交割了荆州城池，我别有主意。于子敬身上，须无事也。",1,"","","细作",131,},
        [5346] = {10902,1,2,"吕范",121,0,"范近闻皇叔失偶，有一门好亲，故不避嫌，特来作媒。未知尊意若何？","范近闻皇叔失偶，有一门好亲，故不避嫌，特来作媒。未知尊意若何？",1,"","","刘备",202,},
        [5347] = {10902,2,1,"刘备",202,0,"中年丧妻，大不幸也。骨肉未寒，安忍便议亲？","中年丧妻，大不幸也。骨肉未寒，安忍便议亲？",1,"","","吕范",121,},
        [5348] = {10902,3,2,"吕范",121,0,"人若无妻，如屋无梁，岂可中道而废人伦？吾主吴侯有一妹，美而贤，堪奉箕帚。","人若无妻，如屋无梁，岂可中道而废人伦？吾主吴侯有一妹，美而贤，堪奉箕帚。",1,"","","刘备",202,},
        [5349] = {10902,4,2,"吕范",121,0,"若两家共结秦、晋之好，则曹贼不敢正视东南也。但我国太吴夫人甚爱幼女，不肯远嫁，必求皇叔到东吴就婚。","若两家共结秦、晋之好，则曹贼不敢正视东南也。但我国太吴夫人甚爱幼女，不肯远嫁，必求皇叔到东吴就婚。",1,"","","刘备",202,},
        [5350] = {10902,5,1,"刘备",202,0,"吾年已半百，鬓发斑白；吴侯之妹，正当妙龄：恐非配偶。","吾年已半百，鬓发斑白；吴侯之妹，正当妙龄：恐非配偶。",1,"","","吕范",121,},
        [5351] = {10902,6,2,"吕范",121,0,"吴侯之妹，身虽女子，志胜男儿。今皇叔名闻四海，正所谓淑女配君子，岂以年齿上下相嫌乎！","吴侯之妹，身虽女子，志胜男儿。今皇叔名闻四海，正所谓淑女配君子，岂以年齿上下相嫌乎！",1,"","","刘备",202,},
        [5352] = {10902,7,1,"刘备",202,0,"公且少留，来日回报。","公且少留，来日回报。",1,"","","吕范",121,},
        [5353] = {10903,1,1,"诸葛亮",203,0,"他们的来意我已经知道了。刚才算了一卦，大吉大利。主公可以应允这门亲事，择日便去就亲。","他们的来意我已经知道了。刚才算了一卦，大吉大利。主公可以应允这门亲事，择日便去就亲。",1,"","","刘备",202,},
        [5354] = {10903,2,2,"刘备",202,0,"周瑜定计欲害刘备，岂可以身轻入危险之地？","周瑜定计欲害刘备，岂可以身轻入危险之地？",1,"","","诸葛亮",203,},
        [5355] = {10903,3,1,"诸葛亮",203,0,"周瑜虽能用计，岂能出诸葛亮之料乎！","周瑜虽能用计，岂能出诸葛亮之料乎！",1,"","","刘备",202,},
        [5356] = {10904,1,2,"孙乾",140,0,"吴侯专候主公去结亲。","吴侯专候主公去结亲。",1,"","","刘备",202,},
        [5357] = {10904,2,1,"刘备",202,0,"只恐有诈，不敢轻往。","只恐有诈，不敢轻往。",1,"","","孙乾",140,},
        [5358] = {10904,3,2,"诸葛亮",203,0,"吾已定下三条计策，非子龙不可行也。","吾已定下三条计策，非子龙不可行也。",1,"","","刘备",202,},
        [5359] = {10904,4,2,"诸葛亮",203,0,"汝保主公入吴，当领此三个锦囊。囊中有三条妙计，依次而行。","汝保主公入吴，当领此三个锦囊。囊中有三条妙计，依次而行。",1,"","","刘备",202,},
        [5360] = {10905,1,1,"赵云",201,0,"军师分付三条妙计，依次而行。今已到南徐，当先开第一个锦囊来看。","军师分付三条妙计，依次而行。今已到南徐，当先开第一个锦囊来看。",1,"","","",0,},
        [5361] = {10905,2,1,"赵云",201,0,"哦……如此如此，我这就去办。","哦……如此如此，我这就去办。",1,"","","",0,},
        [5362] = {10906,1,1,"乔国老",133,0,"令爱已许刘玄德为夫人，今玄德已到，何故相瞒？","令爱已许刘玄德为夫人，今玄德已到，何故相瞒？",1,"","","吴国太",322,},
        [5363] = {10906,2,2,"吴国太",322,0,"老身不知此事！","老身不知此事！",1,"","","乔国老",133,},
        [5364] = {10906,3,1,"乔国老",133,0,"果有此事。女婿已在馆驿安歇，五百随行军士都在城中买猪羊果品，准备成亲。","果有此事。女婿已在馆驿安歇，五百随行军士都在城中买猪羊果品，准备成亲。",1,"","","吴国太",322,},
        [5365] = {10906,4,1,"乔国老",133,0,"做媒的女家是吕范，男家是孙乾，俱在馆驿中相待。","做媒的女家是吕范，男家是孙乾，俱在馆驿中相待。",1,"","","吴国太",322,},
        [5366] = {10906,5,2,"吴国太",322,0,"这，他们竟不告诉我！","这，他们竟不告诉我！",1,"","","乔国老",133,},
        [5367] = {10907,1,1,"孙权",306,0,"母亲何故烦恼？","母亲何故烦恼？",1,"","","吴国太",322,},
        [5368] = {10907,2,2,"吴国太",322,0,"你直如此将我看承得如无物！我姐姐临危之时，分付你甚么话来！","你直如此将我看承得如无物！我姐姐临危之时，分付你甚么话来！",1,"","","孙权",306,},
        [5369] = {10907,3,1,"孙权",306,0,"母亲有话明说，何苦如此？","母亲有话明说，何苦如此？",1,"","","吴国太",322,},
        [5370] = {10907,4,2,"吴国太",322,0,"男大须婚，女大须嫁，古今常理。我为你母亲，事当禀命于我。你招刘玄德为婿，如何瞒我？女儿须是我的！","男大须婚，女大须嫁，古今常理。我为你母亲，事当禀命于我。你招刘玄德为婿，如何瞒我？女儿须是我的！",1,"","","孙权",306,},
        [5371] = {10907,5,1,"孙权",306,0,"非也。此是周瑜之计，将此为名，赚刘备来拘囚在此，要他把荆州来换；若其不从，先斩刘备。此是计策，非实意也。","非也。此是周瑜之计，将此为名，赚刘备来拘囚在此，要他把荆州来换；若其不从，先斩刘备。此是计策，非实意也。",1,"","","吴国太",322,},
        [5372] = {10907,6,2,"吴国太",322,0,"周瑜做六郡八十一州大都督，直恁无条计策去取荆州，却将我女儿为名，使美人计！","周瑜做六郡八十一州大都督，直恁无条计策去取荆州，却将我女儿为名，使美人计！",1,"","","孙权",306,},
        [5373] = {10907,7,2,"吴国太",322,0,"杀了刘备，我女便是望门寡，明日再怎的说亲？须误了我女儿一世！你们好做作！","杀了刘备，我女便是望门寡，明日再怎的说亲？须误了我女儿一世！你们好做作！",1,"","","孙权",306,},
        [5374] = {10907,8,1,"乔国老",133,0,"若用此计，便得荆州，也被天下人耻笑。此事如何行得！","若用此计，便得荆州，也被天下人耻笑。此事如何行得！",1,"","","吴国太",322,},
        [5375] = {10908,1,2,"乔国老",133,0,"事已如此，刘皇叔乃汉室宗亲，不如真个招他为婿，免得出丑。","事已如此，刘皇叔乃汉室宗亲，不如真个招他为婿，免得出丑。",1,"","","孙权",306,},
        [5376] = {10908,2,1,"孙权",306,0,"年纪恐不相当。","年纪恐不相当。",1,"","","乔国老",133,},
        [5377] = {10908,3,2,"乔国老",133,0,"刘皇叔乃当世豪杰，若招得这个女婿，也不辱了令妹。","刘皇叔乃当世豪杰，若招得这个女婿，也不辱了令妹。",1,"","","孙权",306,},
        [5378] = {10908,4,2,"吴国太",322,0,"我不曾认得刘皇叔。明日约在甘露寺相见：如不中我意，任从你们行事；若中我的意，我自把女儿嫁他！","我不曾认得刘皇叔。明日约在甘露寺相见：如不中我意，任从你们行事；若中我的意，我自把女儿嫁他！",1,"","","孙权",306,},
        [5379] = {10909,1,2,"孙乾",140,0,"来日吴侯、国太亲自要见，好生在意！","来日吴侯、国太亲自要见，好生在意！",1,"","","赵云",201,},
        [5380] = {10909,2,1,"赵云",201,0,"来日此会，多凶少吉，云自引五百军保护。","来日此会，多凶少吉，云自引五百军保护。",1,"","","孙乾",140,},
        [5381] = {10910,1,1,"刘备",202,0,"刘备拜见吴国太。","刘备拜见吴国太。",1,"","","吴国太",322,},
        [5382] = {10910,2,2,"吴国太",322,0,"真吾婿也！","真吾婿也！",1,"","","刘备",202,},
        [5383] = {10910,3,2,"乔国老",133,0,"玄德有龙凤之姿，天日之表；更兼仁德布于天下：国太得此佳婿，真可庆也！","玄德有龙凤之姿，天日之表；更兼仁德布于天下：国太得此佳婿，真可庆也！",1,"","","刘备",202,},
        [5384] = {10910,4,1,"赵云",201,0,"（悄悄告诉刘备）刚才我在廊下巡视，见房内有刀斧手埋伏，必无好意。可告知国太。","（悄悄告诉刘备）刚才我在廊下巡视，见房内有刀斧手埋伏，必无好意。可告知国太。",1,"","","刘备",202,},
        [5385] = {10910,5,1,"刘备",202,0,"若杀刘备，就此请诛。廊下暗伏刀斧手，是何用意？","若杀刘备，就此请诛。廊下暗伏刀斧手，是何用意？",1,"","","吴国太",322,},
        [5386] = {10910,6,2,"吴国太",322,0,"（大怒）混账！今日玄德既为我婿，即我之儿女也。何故伏刀斧手于廊下！","（大怒）混账！今日玄德既为我婿，即我之儿女也。何故伏刀斧手于廊下！",1,"","","刘备",202,},
        [5387] = {10910,7,1,"孙权",306,0,"啊？这我不太清楚……吕范，是你的主意吗？","啊？这我不太清楚……吕范，是你的主意吗？",1,"","","吕范",121,},
        [5388] = {10910,8,2,"吕范",121,0,"啊！这我也不知道……贾华，这怎么回事？","啊！这我也不知道……贾华，这怎么回事？",1,"","","贾华",143,},
        [5389] = {10910,9,1,"贾华",143,0,"……","……",1,"","","",0,},
        [5390] = {11001,1,1,"刘备",202,0,"若刘备能勾回荆州，成王霸之业，一剑挥石为两段。如死于此地，剑剁石不开。","若刘备能勾回荆州，成王霸之业，一剑挥石为两段。如死于此地，剑剁石不开。",1,"","","",0,},
        [5391] = {11001,2,2,"孙权",306,0,"玄德公如何恨此石？竟将之砍为两半？","玄德公如何恨此石？竟将之砍为两半？",1,"","","刘备",202,},
        [5392] = {11001,3,1,"刘备",202,0,"备年近五旬，不能为国家剿除贼党，心常自恨。今蒙国太招为女婿，此平生之际遇也。","备年近五旬，不能为国家剿除贼党，心常自恨。今蒙国太招为女婿，此平生之际遇也。",1,"","","孙权",306,},
        [5393] = {11001,4,1,"刘备",202,0,"恰才问天买卦，如破曹兴汉，砍断此石。今果然如此。","恰才问天买卦，如破曹兴汉，砍断此石。今果然如此。",1,"","","孙权",306,},
        [5394] = {11001,5,2,"孙权",306,0,"吾亦问天买卦。若破得曹贼，亦断此石。","吾亦问天买卦。若破得曹贼，亦断此石。",1,"","","",0,},
        [5395] = {11001,6,2,"孙权",306,0,"（心里暗暗祝告）若再取得荆州，兴旺东吴，砍石为两半！","（心里暗暗祝告）若再取得荆州，兴旺东吴，砍石为两半！",1,"","","",0,},
        [5396] = {11002,1,1,"刘备",202,0,"南人驾船，北人乘马，信有之也。","南人驾船，北人乘马，信有之也。",1,"","","孙权",306,},
        [5397] = {11002,2,2,"孙权",306,0,"刘备这话是嘲笑我不擅长骑马么？","刘备这话是嘲笑我不擅长骑马么？",1,"","","刘备",202,},
        [5398] = {11002,3,2,"孙权",306,0,"来人！牵马！让刘皇叔看看，南人不能乘马乎？","来人！牵马！让刘皇叔看看，南人不能乘马乎？",1,"","","",0,},
        [5399] = {11002,4,1,"刘备",202,0,"吴侯是要与我赛马吗？请！","吴侯是要与我赛马吗？请！",1,"","","孙权",306,},
        [5400] = {11003,1,1,"主角",1,0,"看大哥和孙权相处得还挺开心的，之前军师给的锦囊不知何时派上用场。","看大哥和孙权相处得还挺开心的，之前军师给的锦囊不知何时派上用场。",1,"","","赵云",201,},
        [5401] = {11003,2,2,"赵云",201,0,"军师料事如神，此次麻烦能够化解还是多亏这锦囊中的妙计。","军师料事如神，此次麻烦能够化解还是多亏这锦囊中的妙计。",1,"","","主角",1,},
        [5402] = {11003,3,2,"赵云",201,0,"军师吩咐每个锦囊都有打开的时机，云谨记在心。我们不可松懈，万事定要小心。","军师吩咐每个锦囊都有打开的时机，云谨记在心。我们不可松懈，万事定要小心。",1,"","","主角",1,},
        [5403] = {11003,4,1,"主角",1,0,"子龙说的是。","子龙说的是。",1,"","","赵云",201,},
        [5404] = {11101,1,1,"刘备",202,0,"江左之人，多有要害刘备者，恐不能久居。","江左之人，多有要害刘备者，恐不能久居。",1,"","","乔国老",133,},
        [5405] = {11101,2,2,"乔国老",133,0,"玄德宽心。吾为公告国太，令作护持。","玄德宽心。吾为公告国太，令作护持。",1,"","","刘备",202,},
        [5406] = {11101,3,2,"吴国太",322,0,"我的女婿，谁敢害他！让他明天就搬到书院暂住，择日完婚。","我的女婿，谁敢害他！让他明天就搬到书院暂住，择日完婚。",1,"","","刘备",202,},
        [5407] = {11101,4,1,"刘备",202,0,"只恐赵云在外不便，军士无人约束。","只恐赵云在外不便，军士无人约束。",1,"","","吴国太",322,},
        [5408] = {11101,5,2,"吴国太",322,0,"让他们全都搬到府中安歇，休留在馆驿中，免得生事。","让他们全都搬到府中安歇，休留在馆驿中，免得生事。",1,"","","刘备",202,},
        [5409] = {11102,1,2,"小乔",304,0,"听说刘皇叔要娶孙尚香为妻？","听说刘皇叔要娶孙尚香为妻？",1,"","","刘备",202,},
        [5410] = {11102,2,1,"刘备",202,0,"刘备不才，有幸得吴国太赏识，愿嫁女儿与我，实是幸事。","刘备不才，有幸得吴国太赏识，愿嫁女儿与我，实是幸事。",1,"","","小乔",304,},
        [5411] = {11102,3,2,"小乔",304,0,"尚香身虽女子，志胜男儿，想娶她可不是什么易事。我先来会一会你们！","尚香身虽女子，志胜男儿，想娶她可不是什么易事。我先来会一会你们！",1,"","","刘备",202,},
        [5412] = {11102,4,1,"主角",1,0,"刚好！好久没有活动一下了，小乔妹子要小心哦！","刚好！好久没有活动一下了，小乔妹子要小心哦！",1,"","","小乔",304,},
        [5413] = {11103,1,2,"大乔",302,0,"小乔这么容易就让你们过关了？我这里你们可要小心了！","小乔这么容易就让你们过关了？我这里你们可要小心了！",1,"","","主角",1,},
        [5414] = {11103,2,1,"主角",1,0,"大乔妹妹你最好了，连吴国太都认准了我大哥做她的女婿，你就直接成人之美多好！","大乔妹妹你最好了，连吴国太都认准了我大哥做她的女婿，你就直接成人之美多好！",1,"","","大乔",302,},
        [5415] = {11103,3,2,"大乔",302,0,"尚香妹妹可是我的小姑子，若非真英雄，如何做她的夫婿？","尚香妹妹可是我的小姑子，若非真英雄，如何做她的夫婿？",1,"","","主角",1,},
        [5416] = {11103,4,1,"主角",1,0,"那就请大乔妹妹多指教啦！","那就请大乔妹妹多指教啦！",1,"","","大乔",302,},
        [5417] = {11104,1,2,"主角",1,0,"恭贺大哥新婚之喜！","恭贺大哥新婚之喜！",1,"","","刘备",202,},
        [5418] = {11104,2,1,"刘备",202,0,"这……新房之中为何枪刀簇满？侍婢皆佩剑悬刀，立于两傍，难道是孙权设的埋伏？","这……新房之中为何枪刀簇满？侍婢皆佩剑悬刀，立于两傍，难道是孙权设的埋伏？",1,"","","孙尚香",310,},
        [5419] = {11104,3,2,"孙尚香侍女",125,0,"贵人休得惊惧，夫人自幼好观武事，居常令侍婢击剑为乐，故尔如此。","贵人休得惊惧，夫人自幼好观武事，居常令侍婢击剑为乐，故尔如此。",1,"","","刘备",202,},
        [5420] = {11104,4,1,"刘备",202,0,"非夫人所观之事，吾甚心寒，可命暂去。","非夫人所观之事，吾甚心寒，可命暂去。",1,"","","孙尚香",310,},
        [5421] = {11104,5,2,"孙尚香",310,0,"厮杀半生，尚惧兵器乎！","厮杀半生，尚惧兵器乎！",1,"","","刘备",202,},
        [5422] = {11104,6,2,"孙尚香",310,0,"房中摆列兵器，娇客不安，今且去之。","房中摆列兵器，娇客不安，今且去之。",1,"","","刘备",202,},
        [5423] = {11105,1,1,"孙权",306,0,"我母亲力主，已将吾妹嫁刘备。不想弄假成真。此事还复如何？","我母亲力主，已将吾妹嫁刘备。不想弄假成真。此事还复如何？",1,"","","周瑜",303,},
        [5424] = {11105,2,2,"周瑜",303,0,"既已弄假成真，又当就此用计。愚意莫如软困之于吴中，盛为筑宫室，以丧其心志；多送美色玩好，以娱其耳目。","既已弄假成真，又当就此用计。愚意莫如软困之于吴中，盛为筑宫室，以丧其心志；多送美色玩好，以娱其耳目。",1,"","","孙权",306,},
        [5425] = {11105,3,2,"周瑜",303,0,"使分开关、张之情，隔远诸葛之契，各置一方，然后以兵击之，大事可定矣。","使分开关、张之情，隔远诸葛之契，各置一方，然后以兵击之，大事可定矣。",1,"","","孙权",306,},
        [5426] = {11105,4,2,"张昭",315,0,"公瑾之谋，正合愚意。刘备起身微末，未尝受享富贵。今以锦衣玉食令彼享用，自然疏远关张，则荆州可图也。","公瑾之谋，正合愚意。刘备起身微末，未尝受享富贵。今以锦衣玉食令彼享用，自然疏远关张，则荆州可图也。",1,"","","孙权",306,},
        [5427] = {11105,5,1,"孙权",306,0,"传令下去，整修东府，请玄德与妹居住。增女乐，并金玉锦绮玩好之物。","传令下去，整修东府，请玄德与妹居住。增女乐，并金玉锦绮玩好之物。",1,"","","周瑜",303,},
        [5428] = {11201,1,1,"赵云",201,0,"孔明分付三个锦囊与我，教我一到南徐，开第一个；住到年终，开第二个；临到危急无路之时，开第三个。","孔明分付三个锦囊与我，教我一到南徐，开第一个；住到年终，开第二个；临到危急无路之时，开第三个。",1,"","","",0,},
        [5429] = {11201,2,1,"赵云",201,0,"此时岁已将终，主公贪恋女色，并不见面，何不拆开第二个锦囊，看计而行？","此时岁已将终，主公贪恋女色，并不见面，何不拆开第二个锦囊，看计而行？",1,"","","",0,},
        [5430] = {11202,1,1,"赵云",201,0,"今早孔明使人来报，说曹操要报赤壁鏖兵之恨，起精兵五十万，杀奔荆州，甚是危急，请主公便回。","今早孔明使人来报，说曹操要报赤壁鏖兵之恨，起精兵五十万，杀奔荆州，甚是危急，请主公便回。",1,"","","刘备",202,},
        [5431] = {11202,2,2,"刘备",202,0,"必须与夫人商议。","必须与夫人商议。",1,"","","赵云",201,},
        [5432] = {11202,3,1,"赵云",201,0,"若和夫人商议，必不肯教主公回。不如休说，今晚便好起程。迟则误事！","若和夫人商议，必不肯教主公回。不如休说，今晚便好起程。迟则误事！",1,"","","刘备",202,},
        [5433] = {11202,4,2,"刘备",202,0,"你且暂退，我自有道理。","你且暂退，我自有道理。",1,"","","赵云",201,},
        [5434] = {11202,5,1,"赵云",201,0,"主公务必早做决定，切莫耽搁。","主公务必早做决定，切莫耽搁。",1,"","","刘备",202,},
        [5435] = {11203,1,2,"孙尚香",310,0,"丈夫何故烦恼？","丈夫何故烦恼？",1,"","","刘备",202,},
        [5436] = {11203,2,1,"刘备",202,0,"念备一身飘荡异乡，生不能侍奉二亲，又不能祭祀宗祖，乃大逆不孝也。眼看年关将至，想到此事有些伤感。","念备一身飘荡异乡，生不能侍奉二亲，又不能祭祀宗祖，乃大逆不孝也。眼看年关将至，想到此事有些伤感。",1,"","","孙尚香",310,},
        [5437] = {11203,3,2,"孙尚香",310,0,"你休瞒我，我已听知了也！方才赵子龙报说荆州危急，你欲还乡，故推此意。","你休瞒我，我已听知了也！方才赵子龙报说荆州危急，你欲还乡，故推此意。",1,"","","刘备",202,},
        [5438] = {11203,4,1,"刘备",202,0,"夫人既知，备安敢相瞒。备欲不去，使荆州有失，被天下人耻笑；欲去，又舍不得夫人：因此烦恼。","夫人既知，备安敢相瞒。备欲不去，使荆州有失，被天下人耻笑；欲去，又舍不得夫人：因此烦恼。",1,"","","孙尚香",310,},
        [5439] = {11203,5,2,"孙尚香",310,0,"妾已事君，任君所之，妾当相随。","妾已事君，任君所之，妾当相随。",1,"","","刘备",202,},
        [5440] = {11203,6,1,"刘备",202,0,"夫人之心，虽则如此，争奈国太与吴侯安肯容夫人去？夫人若可怜刘备，暂时辞别。","夫人之心，虽则如此，争奈国太与吴侯安肯容夫人去？夫人若可怜刘备，暂时辞别。",1,"","","孙尚香",310,},
        [5441] = {11203,7,2,"孙尚香",310,0,"妾与君正旦拜贺时，推称江边祭祖，不告而去，若何？","妾与君正旦拜贺时，推称江边祭祖，不告而去，若何？",1,"","","刘备",202,},
        [5442] = {11203,8,1,"刘备",202,0,"若如此，生死难忘！切勿漏泄。","若如此，生死难忘！切勿漏泄。",1,"","","孙尚香",310,},
        [5443] = {11204,1,1,"孙权",306,0,"什么？刘备带着我妹妹走了？","什么？刘备带着我妹妹走了？",1,"","","张昭",315,},
        [5444] = {11204,2,2,"张昭",315,0,"今日走了此人，早晚必生祸乱。可急追之。","今日走了此人，早晚必生祸乱。可急追之。",1,"","","孙权",306,},
        [5445] = {11204,3,2,"程普",320,0,"主公空有冲天之怒，我料定陈武、潘璋必擒不得他们。","主公空有冲天之怒，我料定陈武、潘璋必擒不得他们。",1,"","","孙权",306,},
        [5446] = {11204,4,2,"程普",320,0,"郡主自幼好观武事，严毅刚正，诸将皆惧。既然肯顺刘备，必同心而去。所追之将，若见郡主，岂肯下手？","郡主自幼好观武事，严毅刚正，诸将皆惧。既然肯顺刘备，必同心而去。所追之将，若见郡主，岂肯下手？",1,"","","孙权",306,},
        [5447] = {11204,5,1,"孙权",306,0,"此乃吾随身佩剑，汝二人将这口剑去取吾妹和刘备头来！违令者立斩！","此乃吾随身佩剑，汝二人将这口剑去取吾妹和刘备头来！违令者立斩！",1,"","","程普",320,},
        [5448] = {11205,1,1,"徐盛",328,0,"刘备早早下马受缚！吾奉周都督将令，守候多时！","刘备早早下马受缚！吾奉周都督将令，守候多时！",1,"","","",0,},
        [5449] = {11205,2,2,"刘备",202,0,"前有拦截之兵，后有追赶之兵：前后无路，如之奈何？","前有拦截之兵，后有追赶之兵：前后无路，如之奈何？",1,"","","赵云",201,},
        [5450] = {11205,3,1,"赵云",201,0,"主公休慌。军师有三条妙计，多在锦囊之中。已拆了两个，并皆应验。","主公休慌。军师有三条妙计，多在锦囊之中。已拆了两个，并皆应验。",1,"","","刘备",202,},
        [5451] = {11205,4,1,"赵云",201,0,"今尚有第三个在此，分付遇危难之时，方可拆看。今日危急，当拆观之。","今尚有第三个在此，分付遇危难之时，方可拆看。今日危急，当拆观之。",1,"","","刘备",202,},
        [5452] = {11206,1,1,"刘备",202,0,"昔日吴侯与周瑜同谋，以夫人为香饵而钓备，乃欲幽困刘备而夺荆州耳。备不惧万死而来，盖知夫人必能怜备。","昔日吴侯与周瑜同谋，以夫人为香饵而钓备，乃欲幽困刘备而夺荆州耳。备不惧万死而来，盖知夫人必能怜备。",1,"","","孙尚香",310,},
        [5453] = {11206,2,1,"刘备",202,0,"昨闻吴侯将欲加害，故托荆州有难，以图归计。幸得夫人不弃，同至于此。","昨闻吴侯将欲加害，故托荆州有难，以图归计。幸得夫人不弃，同至于此。",1,"","","孙尚香",310,},
        [5454] = {11206,3,1,"刘备",202,0,"今吴侯又令人在后追赶，周瑜又使人于前截住，非夫人莫解此祸。如夫人不允，备请死于车前，以报夫人之德。","今吴侯又令人在后追赶，周瑜又使人于前截住，非夫人莫解此祸。如夫人不允，备请死于车前，以报夫人之德。",1,"","","孙尚香",310,},
        [5455] = {11206,4,2,"孙尚香",310,0,"吾兄既然不认我这个妹妹，我有何面目重相见乎！今日之危，我当自解。","吾兄既然不认我这个妹妹，我有何面目重相见乎！今日之危，我当自解。",1,"","","刘备",202,},
        [5456] = {11207,1,1,"孙尚香",310,0,"你二人欲造反耶？","你二人欲造反耶？",1,"","","徐盛",328,},
        [5457] = {11207,2,2,"徐盛",328,0,"安敢造反。为奉周都督将令，屯兵在此专候刘备。","安敢造反。为奉周都督将令，屯兵在此专候刘备。",1,"","","孙尚香",310,},
        [5458] = {11207,3,1,"孙尚香",310,0,"周瑜逆贼！我东吴不曾亏负你！玄德乃大汉皇叔，是我丈夫。我已对母亲、哥哥说知回荆州去。","周瑜逆贼！我东吴不曾亏负你！玄德乃大汉皇叔，是我丈夫。我已对母亲、哥哥说知回荆州去。",1,"","","徐盛",328,},
        [5459] = {11207,4,1,"孙尚香",310,0,"今你两个于山脚去处，引着军马拦截道路，意欲劫掠我夫妻财物耶？","今你两个于山脚去处，引着军马拦截道路，意欲劫掠我夫妻财物耶？",1,"","","徐盛",328,},
        [5460] = {11207,5,2,"徐盛",328,0,"不敢。请夫人息怒。这不干我等之事，乃是周都督的将令。","不敢。请夫人息怒。这不干我等之事，乃是周都督的将令。",1,"","","孙尚香",310,},
        [5461] = {11207,6,1,"孙尚香",310,0,"你只怕周瑜，独不怕我？周瑜杀得你，我岂杀不得周瑜？","你只怕周瑜，独不怕我？周瑜杀得你，我岂杀不得周瑜？",1,"","","徐盛",328,},
        [5462] = {11208,1,1,"孙尚香",310,0,"都是你这伙匹夫，离间我兄妹不睦！我已嫁他人，今日归去，须不是与人私奔。","都是你这伙匹夫，离间我兄妹不睦！我已嫁他人，今日归去，须不是与人私奔。",1,"","","陈武",148,},
        [5463] = {11208,2,1,"孙尚香",310,0,"我奉母亲慈旨，令我夫妇回荆州。便是我哥哥来，也须依礼而行。你二人倚仗兵威，欲待杀害我耶？","我奉母亲慈旨，令我夫妇回荆州。便是我哥哥来，也须依礼而行。你二人倚仗兵威，欲待杀害我耶？",1,"","","陈武",148,},
        [5464] = {11208,3,2,"陈武",148,0,"他一万年也只是兄妹。更兼国太作主；吴侯乃大孝之人，怎敢违逆母言？明日翻过脸来，只是我等不是。不如做个人情。","他一万年也只是兄妹。更兼国太作主；吴侯乃大孝之人，怎敢违逆母言？明日翻过脸来，只是我等不是。不如做个人情。",1,"","","孙尚香",310,},
        [5465] = {11209,1,1,"周泰",316,0,"你等曾见刘备否？何不拿下？","你等曾见刘备否？何不拿下？",1,"","","徐盛",328,},
        [5466] = {11209,2,2,"徐盛",328,0,"有孙夫人在，且孙夫人又奉了国太之命，我等不敢造次。","有孙夫人在，且孙夫人又奉了国太之命，我等不敢造次。",1,"","","周泰",316,},
        [5467] = {11209,3,1,"周泰",316,0,"便是吴侯怕道如此，封一口剑在此，教先杀他妹，后斩刘备。违者立斩！","便是吴侯怕道如此，封一口剑在此，教先杀他妹，后斩刘备。违者立斩！",1,"","","徐盛",328,},
        [5468] = {11209,4,2,"徐盛",328,0,"这！那我们快去追！他终是些步军，急行不上。我四人在岸上追赶，无问水旱之路，赶上杀了，休听他言语。","这！那我们快去追！他终是些步军，急行不上。我四人在岸上追赶，无问水旱之路，赶上杀了，休听他言语。",1,"","","周泰",316,},
        [5469] = {11209,5,1,"刘备",202,0,"糟了，他们又有人追过来了。如何是好？","糟了，他们又有人追过来了。如何是好？",1,"","","主角",1,},
        [5470] = {11209,6,2,"主角",1,0,"大哥先走，这里交给我吧！","大哥先走，这里交给我吧！",1,"","","刘备",202,},
        [5471] = {11209,7,1,"刘备",202,0,"四弟多加小心！","四妹多加小心！",1,"","","主角",1,},
        [5472] = {11301,1,2,"刘备",202,0,"连日奔走，人困马乏，追兵又到，死无地矣！","连日奔走，人困马乏，追兵又到，死无地矣！",1,"","","",0,},
        [5473] = {11301,2,1,"主角",1,0,"大哥别急，来人只须交给我去应付，大哥放心回荆州。","大哥别急，来人只须交给我去应付，大哥放心回荆州。",1,"","","刘备",202,},
        [5474] = {11301,3,2,"赵云",201,0,"天幸有船在此！何不速下，棹过对岸，再作区处！","天幸有船在此！何不速下，棹过对岸，再作区处！",1,"","","",0,},
        [5475] = {11302,1,1,"诸葛亮",203,0,"主公且喜！诸葛亮在此等候多时。主公已到，我们开船！","主公且喜！诸葛亮在此等候多时。主公已到，我们开船！",1,"","","",0,},
        [5476] = {11302,2,2,"周泰",316,0,"刘备站住！","刘备站住！",1,"","","",0,},
        [5477] = {11302,3,1,"主角",1,0,"别追啦！船已经开这么远啦！你们追不上的！","别追啦！船已经开这么远啦！你们追不上的！",1,"","","周泰",316,},
        [5478] = {11302,4,1,"诸葛亮",203,0,"吾已算定多时矣。汝等回去传示周郎，教休再使美人局手段。","吾已算定多时矣。汝等回去传示周郎，教休再使美人局手段。",1,"","","周泰",316,},
        [5479] = {11303,1,2,"周瑜",303,0,"哼！以为我们没有船吗？我东吴战船在此，你们哪里跑？","哼！以为我们没有船吗？我东吴战船在此，你们哪里跑？",1,"","","",0,},
        [5480] = {11303,2,1,"诸葛亮",203,0,"我们掉船投北岸，弃船上岸。","我们掉船投北岸，弃船上岸。",1,"","","",0,},
        [5481] = {11303,3,2,"周瑜",303,0,"我们也上岸，刘备一行就在前方，快追！","我们也上岸，刘备一行就在前方，快追！",1,"","","",0,},
        [5482] = {11303,4,1,"关羽",205,0,"关云长在此！周郎想要切磋一番吗？","关云长在此！周郎想要切磋一番吗？",1,"","","周瑜",303,},
        [5483] = {11303,5,2,"周瑜",303,0,"哼，算你们狠！撤！","哼，算你们狠！撤！",1,"","","",0,},
        [5484] = {11304,1,1,"主角",1,0,"周瑜机关算尽，却还是孔明技高一筹。真是周郎妙计安天下，陪了夫人又折兵！","周瑜机关算尽，却还是孔明技高一筹。真是周郎妙计安天下，陪了夫人又折兵！",1,"","","",0,},
        [5485] = {11304,2,2,"周瑜",303,0,"你们不要太过分！看我上岸我们决一死战！","你们不要太过分！看我上岸我们决一死战！",1,"","","",0,},
        [5486] = {11304,3,1,"黄盖",314,0,"都督息怒，小心你的伤……","都督息怒，小心你的伤……",1,"","","周瑜",303,},
        [5487] = {11304,4,2,"周瑜",303,0,"吾计不成，有何面目去见吴侯！噗……（吐血昏迷）","吾计不成，有何面目去见吴侯！噗……（吐血昏迷）",1,"","","黄盖",314,},
        [5488] = {11401,1,2,"张星彩",216,0,"刘皇叔从东吴平安归来，还带回了孙夫人，多亏了主公呢。","刘皇叔从东吴平安归来，还带回了孙夫人，多亏了主公呢。",1,"","","主角",1,},
        [5489] = {11401,2,1,"主角",1,0,"小意思啦。主要是大哥的人格魅力征服了孙夫人。","小意思啦。主要是大哥的人格魅力征服了孙夫人。",1,"","","张星彩",216,},
        [5490] = {11401,3,2,"张星彩",216,0,"东吴这下应该安稳点了吧，之前与曹军的争斗，也是伤了元气呢。","东吴这下应该安稳点了吧，之前与曹军的争斗，也是伤了元气呢。",1,"","","主角",1,},
        [5491] = {11401,4,1,"主角",1,0,"说起来，不知道曹操那边怎么样，我们过去看看吧。","说起来，不知道曹操那边怎么样，我们过去看看吧。",1,"","","张星彩",216,},
        [5492] = {11401,5,2,"张星彩",216,0,"哼，虽然不是很愿意看到曹操，但主公去哪里我就跟去哪里。","哼，虽然不是很愿意看到曹操，但主公去哪里我就跟去哪里。",1,"","","主角",1,},
        [5493] = {11402,1,2,"曹操",103,0,"赤壁一战真是让我军元气大伤……甚是不甘心。","赤壁一战真是让我军元气大伤……甚是不甘心。",1,"","","主角",1,},
        [5494] = {11402,2,1,"主角",1,0,"孟德兄可曾想过为何赤壁之战会被孙刘两家以少胜多？","孟德兄可曾想过为何赤壁之战会被孙刘两家以少胜多？",1,"","","曹操",103,},
        [5495] = {11402,3,2,"曹操",103,0,"小英雄有何高见？","小英雄有何高见？",1,"","","主角",1,},
        [5496] = {11402,4,1,"主角",1,0,"舍鞍马、杖舟楫乃孙吴之所长，而曹兄军队多北方人，不习水战，铁索连环致使舟船调动不便，此其一也。","舍鞍马、杖舟楫乃孙吴之所长，而曹兄军队多北方人，不习水战，铁索连环致使舟船调动不便，此其一也。",1,"","","曹操",103,},
        [5497] = {11402,5,1,"主角",1,0,"士卒们自北而南远道行军至长江，已然筋疲力尽，加上不习南方水土，大大降低了战斗力，此其二也。","士卒们自北而南远道行军至长江，已然筋疲力尽，加上不习南方水土，大大降低了战斗力，此其二也。",1,"","","曹操",103,},
        [5498] = {11402,6,2,"曹操",103,0,"这些我已知晓，我也命人加紧操练，部署军力重整旗鼓，以备来日再战。","这些我已知晓，我也命人加紧操练，部署军力重整旗鼓，以备来日再战。",1,"","","主角",1,},
        [5499] = {11402,7,1,"主角",1,0,"非也，还有一点原因更为重要，曹兄须知晓。","非也，还有一点原因更为重要，曹兄须知晓。",1,"","","曹操",103,},
        [5500] = {11402,8,2,"曹操",103,0,"愿闻其详。","愿闻其详。",1,"","","主角",1,},
        [5501] = {11403,1,1,"主角",1,0,"曹兄兵力雄厚，统一了北方，南进又节节胜利，所向披靡。","曹兄兵力雄厚，统一了北方，南进又节节胜利，所向披靡。",1,"","","曹操",103,},
        [5502] = {11403,2,1,"主角",1,0,"所以曹兄，或是你手下的士卒们，是不是有点小骄傲了？觉得没有自己打不赢的仗？","所以曹兄，或是你手下的士卒们，是不是有点小骄傲了？觉得没有自己打不赢的仗？",1,"","","曹操",103,},
        [5503] = {11403,3,2,"曹操",103,0,"这……","这……",1,"","","主角",1,},
        [5504] = {11403,4,1,"主角",1,0,"而孙刘则是存亡攸关，故处处谨慎，将领团结，士卒用命。","而孙刘则是存亡攸关，故处处谨慎，将领团结，士卒用命。",1,"","","曹操",103,},
        [5505] = {11403,5,2,"曹操",103,0,"小英雄所言极是。","小英雄所言极是。",1,"","","主角",1,},
        [5506] = {11404,1,2,"曹操",103,0,"那么对于现在的局势，小英雄有何高见？","那么对于现在的局势，小英雄有何高见？",1,"","","主角",1,},
        [5507] = {11404,2,1,"主角",1,0,"现在天下局势已定，曹兄不可冒进。宜先安稳内部，发展壮大，再去考虑扩张。","现在天下局势已定，曹兄不可冒进。宜先安稳内部，发展壮大，再去考虑扩张。",1,"","","曹操",103,},
        [5508] = {11404,3,2,"曹操",103,0,"说的是，那该如何发展壮大？","说的是，那该如何发展壮大？",1,"","","主角",1,},
        [5509] = {11404,4,1,"主角",1,0,"天地之间以人为贵，有人才有一切。曹兄应广纳贤士，唯才是举，突破世家门第的限制，才能真正觅得共谋大业之人。","天地之间以人为贵，有人才有一切。曹兄应广纳贤士，唯才是举，突破世家门第的限制，才能真正觅得共谋大业之人。",1,"","","曹操",103,},
        [5510] = {11404,5,2,"曹操",103,0,"我要颁布求贤令，广招天下人才，为我所用。","我要颁布求贤令，广招天下人才，为我所用。",1,"","","主角",1,},
        [5511] = {11501,1,2,"曹操",103,0,"铜雀台建成了，我要宴请群臣，小英雄也一起来呀。","铜雀台建成了，我要宴请群臣，小英雄也一起来呀。",1,"","","主角",1,},
        [5512] = {11501,2,1,"主角",1,0,"没问题，最喜欢这种热闹了。","没问题，最喜欢这种热闹了。",1,"","","曹操",103,},
        [5513] = {11502,1,2,"曹操",103,0,"铜雀台正临漳河，左边一座名玉龙台，右边一座名金凤台，各高十丈，上横二桥相通，千门万户，金碧交辉。","铜雀台正临漳河，左边一座名玉龙台，右边一座名金凤台，各高十丈，上横二桥相通，千门万户，金碧交辉。",1,"","","主角",1,},
        [5514] = {11502,2,1,"主角",1,0,"真是妙哉。","真是妙哉。",1,"","","曹操",103,},
        [5515] = {11502,3,1,"主角",1,0,"佳肴虽妙，舞姿也美，总觉得少了点什么，想活动一下拳脚，心痒难耐。","佳肴虽妙，舞姿也美，总觉得少了点什么，想活动一下拳脚，心痒难耐。",1,"","","曹操",103,},
        [5516] = {11502,4,2,"曹操",103,0,"在座都是我重要的文臣武将，小英雄想要与谁切磋一番？","在座都是我重要的文臣武将，小英雄想要与谁切磋一番？",1,"","","主角",1,},
        [5517] = {11502,5,1,"文聘",122,0,"文聘愿往！早就听闻扬威将军为名，请赐教！","文聘愿往！早就听闻扬威将军为名，请赐教！",1,"","","曹操",103,},
        [5518] = {11503,1,2,"张郃",110,0,"扬威将军好身手！张郃也来领教一番！","扬威将军好身手！张郃也来领教一番！",1,"","","主角",1,},
        [5519] = {11503,2,1,"主角",1,0,"请！","请！",1,"","","张郃",110,},
        [5520] = {11504,1,2,"夏侯渊",117,0,"他们两个被扬威将军威名蛰伏，没有拼上全力。我也想来与将军一较高下！","他们两个被扬威将军威名蛰伏，没有拼上全力。我也想来与将军一较高下！",1,"","","主角",1,},
        [5521] = {11504,2,1,"主角",1,0,"客气了，尽管放马过来！","客气了，尽管放马过来！",1,"","","夏侯渊",117,},
        [5522] = {11504,3,2,"夏侯渊",117,0,"将军看准了，夏侯渊来也！","将军看准了，夏侯渊来也！",1,"","","主角",1,},
        [5523] = {11505,1,1,"荀彧",102,0,"扬威将军当真武艺了得，竟能与多名大将切磋连胜。","扬威将军当真武艺了得，竟能与多名大将切磋连胜。",1,"","","主角",1,},
        [5524] = {11505,2,1,"曹操",103,0,"哈哈哈，好！真不愧是小英雄。来，我敬小英雄一杯！","哈哈哈，好！真不愧是小英雄。来，我敬小英雄一杯！",1,"","","主角",1,},
        [5525] = {11505,3,2,"主角",1,0,"干！","干！",1,"","","曹操",103,},
        [5526] = {11601,1,1,"曹操",103,0,"这里有锦袍一领，挂于垂杨枝上，下设一箭垛，以百步为界。有能射中箭垛红心者，即以锦袍赐之。射不中则罚酒。","这里有锦袍一领，挂于垂杨枝上，下设一箭垛，以百步为界。有能射中箭垛红心者，即以锦袍赐之。射不中则罚酒。",1,"","","",0,},
        [5527] = {11601,2,2,"曹休",134,0,"我来！（一箭射中靶心）","我来！（一箭射中靶心）",1,"","","",0,},
        [5528] = {11601,3,1,"曹操",103,0,"好！好箭法！","好！好箭法！",1,"","","",0,},
        [5529] = {11601,4,2,"曹休",134,0,"嘿嘿，锦袍是我的了！","嘿嘿，锦袍是我的了！",1,"","","",0,},
        [5530] = {11601,5,2,"文聘",122,0,"哪里话！丞相锦袍，合让俺外姓先取，你们自家人不要抢。看我的！（一箭射中靶心）","哪里话！丞相锦袍，合让俺外姓先取，你们自家人不要抢。看我的！（一箭射中靶心）",1,"","","",0,},
        [5531] = {11602,1,1,"文聘",122,0,"快把袍子给我吧！","快把袍子给我吧！",1,"","","",0,},
        [5532] = {11602,2,2,"曹洪",142,0,"别忙，文烈先射，汝何得争夺？看我与你两个解箭！（一箭射中靶心）","别忙，文烈先射，汝何得争夺？看我与你两个解箭！（一箭射中靶心）",1,"","","文聘",122,},
        [5533] = {11602,3,2,"曹洪",142,0,"我就当之无愧啦。","我就当之无愧啦。",1,"","","",0,},
        [5534] = {11602,4,1,"张郃",110,0,"慢着！你三人射法，何足为奇！看我射来！（翻身背射，一箭射中靶心）","慢着！你三人射法，何足为奇！看我射来！（翻身背射，一箭射中靶心）",1,"","","曹洪",142,},
        [5535] = {11602,5,1,"张郃",110,0,"锦袍须该是我的！","锦袍须该是我的！",1,"","","",0,},
        [5536] = {11603,1,2,"夏侯渊",117,0,"汝翻身背射，何足称异！看我夺射红心！（一箭射在四支箭中心）","汝翻身背射，何足称异！看我夺射红心！（一箭射在四支箭中心）",1,"","","张郃",110,},
        [5537] = {11603,2,2,"夏侯渊",117,0,"此箭可夺得锦袍么？","此箭可夺得锦袍么？",1,"","","",0,},
        [5538] = {11603,3,1,"徐晃",114,0,"且留下锦袍与我徐晃！","且留下锦袍与我徐晃！",1,"","","夏侯渊",117,},
        [5539] = {11603,4,2,"夏侯渊",117,0,"汝更有何射法，可夺我袍？","汝更有何射法，可夺我袍？",1,"","","",0,},
        [5540] = {11603,5,1,"徐晃",114,0,"嘿嘿嘿，我不射箭，单取锦袍。（一箭将锦袍射下）","嘿嘿嘿，我不射箭，单取锦袍。（一箭将锦袍射下）",1,"","","",0,},
        [5541] = {11604,1,2,"曹操",103,0,"（大笑）好了好了，看你们为了一件袍子争得面红耳赤，白白惹人笑话。我赐你们一人一匹蜀锦，可以了吧？","（大笑）好了好了，看你们为了一件袍子争得面红耳赤，白白惹人笑话。我赐你们一人一匹蜀锦，可以了吧？",1,"","","",0,},
        [5542] = {11604,2,1,"主角",1,0,"武将既以骑射为乐，足显威勇矣。在场还有许多饱学之士，登此高台，可不进佳章以纪一时之胜事乎？","武将既以骑射为乐，足显威勇矣。在场还有许多饱学之士，登此高台，可不进佳章以纪一时之胜事乎？",1,"","","曹操",103,},
        [5543] = {11604,3,2,"曹植",116,0,"从明后而嬉游兮，登层台以娱情。见太府之广开兮，观圣德之所营。","从明后而嬉游兮，登层台以娱情。见太府之广开兮，观圣德之所营。",1,"","","",0,},
        [5544] = {11604,4,2,"曹植",116,0,"……翼佐我皇家兮，宁彼四方。同天地之规量兮，齐日月之晖光。","……翼佐我皇家兮，宁彼四方。同天地之规量兮，齐日月之晖光。",1,"","","",0,},
        [5545] = {11604,5,2,"曹操",103,0,"好！子建不愧是我的好儿子，真是才华横溢，必能成大器！","好！子建不愧是我的好儿子，真是才华横溢，必能成大器！",1,"","","",0,},
        [5546] = {11701,1,1,"华歆",128,0,"东吴派我来禀告曹丞相，表刘备为荆州牧，孙权以妹嫁刘备，汉上九郡大半已属备矣。","东吴派我来禀告曹丞相，表刘备为荆州牧，孙权以妹嫁刘备，汉上九郡大半已属备矣。",1,"","","曹操",103,},
        [5547] = {11701,2,2,"曹操",103,0,"什么？竟让刘备得了荆州，还和孙权联姻？这是要联合起来对抗我吗？","什么？竟让刘备得了荆州，还和孙权联姻？这是要联合起来对抗我吗？",1,"","","华歆",128,},
        [5548] = {11701,3,1,"程昱",121,0,"丞相在万军之中，矢石交攻之际，未尝动心；今闻刘备得了荆州，何故如此失惊？","丞相在万军之中，矢石交攻之际，未尝动心；今闻刘备得了荆州，何故如此失惊？",1,"","","曹操",103,},
        [5549] = {11701,4,2,"曹操",103,0,"刘备，人中之龙也，生平未尝得水。今得荆州，是困龙入大海矣。孤安得不动心哉！","刘备，人中之龙也，生平未尝得水。今得荆州，是困龙入大海矣。孤安得不动心哉！",1,"","","程昱",121,},
        [5550] = {11702,1,2,"程昱",121,0,"丞相知华歆来意否？","丞相知华歆来意否？",1,"","","曹操",103,},
        [5551] = {11702,2,1,"曹操",103,0,"这……难道其中有诈？","这……难道其中有诈？",1,"","","程昱",121,},
        [5552] = {11702,3,2,"程昱",121,0,"孙权本忌刘备，欲以兵攻之；但恐丞相乘虚而击，故令华歆为使，表荐刘备，乃安备之心，以塞丞相之望耳。","孙权本忌刘备，欲以兵攻之；但恐丞相乘虚而击，故令华歆为使，表荐刘备，乃安备之心，以塞丞相之望耳。",1,"","","曹操",103,},
        [5553] = {11702,4,1,"曹操",103,0,"说的是。","说的是。",1,"","","程昱",121,},
        [5554] = {11702,5,2,"程昱",121,0,"某有一计，使孙、刘自相吞并，丞相乘间图之，一鼓而二敌俱破。","某有一计，使孙、刘自相吞并，丞相乘间图之，一鼓而二敌俱破。",1,"","","曹操",103,},
        [5555] = {11703,1,1,"曹操",103,0,"仲德有何妙计？","仲德有何妙计？",1,"","","程昱",121,},
        [5556] = {11703,2,2,"程昱",121,0,"东吴所倚者，周瑜也。丞相今表奏周瑜为南郡太守，程普为江夏太守，留华歆在朝重用之；","东吴所倚者，周瑜也。丞相今表奏周瑜为南郡太守，程普为江夏太守，留华歆在朝重用之；",1,"","","曹操",103,},
        [5557] = {11703,3,2,"程昱",121,0,"瑜必自与刘备为仇敌矣。我乘其相并而图之，不亦善乎？","瑜必自与刘备为仇敌矣。我乘其相并而图之，不亦善乎？",1,"","","曹操",103,},
        [5558] = {11703,4,1,"曹操",103,0,"仲德之言，正合孤意。","仲德之言，正合孤意。",1,"","","程昱",121,},
        [5559] = {11704,1,2,"张星彩",216,0,"主公主公！我听说程昱给曹操出的建议，是他的计策，要挑拨刘皇叔与孙权的关系。","主公主公！我听说程昱给曹操出的建议，是他的计策，要挑拨刘皇叔与孙权的关系。",1,"","","主角",1,},
        [5560] = {11704,2,1,"主角",1,0,"是的，我也听说了，我们要赶快回荆州去告诉大哥这件事。","是的，我也听说了，我们要赶快回荆州去告诉大哥这件事。",1,"","","张星彩",216,},
        [5561] = {11704,3,2,"张星彩",216,0,"太好啦，终于可以回家了。","太好啦，终于可以回家了。",1,"","","主角",1,},
        [5562] = {11801,1,1,"孙权",306,0,"汝昔保借荆州与刘备，今备迁延不还，等待何时？","汝昔保借荆州与刘备，今备迁延不还，等待何时？",1,"","","鲁肃",312,},
        [5563] = {11801,2,2,"鲁肃",312,0,"文书上明白写着，得了西川便还。","文书上明白写着，得了西川便还。",1,"","","孙权",306,},
        [5564] = {11801,3,1,"孙权",306,0,"只说取西川，到今又不动兵，不等老了人！","只说取西川，到今又不动兵，不等老了人！",1,"","","鲁肃",312,},
        [5565] = {11801,4,2,"鲁肃",312,0,"某愿往言之。","某愿往言之。",1,"","","孙权",306,},
        [5566] = {11802,1,2,"鲁肃",312,0,"今奉吴侯钧命，专为荆州一事而来。皇叔已借住多时，未蒙见还。今既两家结亲，当看亲情面上，早早交付。","今奉吴侯钧命，专为荆州一事而来。皇叔已借住多时，未蒙见还。今既两家结亲，当看亲情面上，早早交付。",1,"","","刘备",202,},
        [5567] = {11802,2,1,"刘备",202,0,"这……呜呜呜哇……","这……呜呜呜哇……",1,"","","",0,},
        [5568] = {11802,3,2,"鲁肃",312,0,"皇叔何故大哭？","皇叔何故大哭？",1,"","","刘备",202,},
        [5569] = {11802,4,1,"诸葛亮",203,0,"当初我主人借荆州时，许下取得西川便还。仔细想来，益州刘璋是我主人之弟，若要兴兵去取他城池时，恐被外人唾骂；","当初我主人借荆州时，许下取得西川便还。仔细想来，益州刘璋是我主人之弟，若要兴兵去取他城池时，恐被外人唾骂；",1,"","","鲁肃",312,},
        [5570] = {11802,5,1,"诸葛亮",203,0,"若要不取，还了荆州，何处安身？若不还时，于尊舅面上又不好看。事实两难，因此泪出痛肠。呜呜呜……","若要不取，还了荆州，何处安身？若不还时，于尊舅面上又不好看。事实两难，因此泪出痛肠。呜呜呜……",1,"","","鲁肃",312,},
        [5571] = {11802,6,2,"鲁肃",312,0,"这……你们都别哭啊，皇叔且休烦恼，与孔明从长计议。","这……你们都别哭啊，皇叔且休烦恼，与孔明从长计议。",1,"","","诸葛亮",203,},
        [5572] = {11802,7,1,"诸葛亮",203,0,"有烦子敬，回见吴侯，勿惜一言之劳，将此烦恼情节，恳告吴侯，再容几时。","有烦子敬，回见吴侯，勿惜一言之劳，将此烦恼情节，恳告吴侯，再容几时。",1,"","","鲁肃",312,},
        [5573] = {11803,1,1,"周瑜",303,0,"子敬又中诸葛亮之计也！当初刘备依刘表时，常有吞并之意，何况西川刘璋乎？","子敬又中诸葛亮之计也！当初刘备依刘表时，常有吞并之意，何况西川刘璋乎？",1,"","","鲁肃",312,},
        [5574] = {11803,2,1,"周瑜",303,0,"吾有一计，子敬不必去见吴侯，直接回去荆州对刘备说：孙、刘两家，既结为亲，便是一家；","吾有一计，子敬不必去见吴侯，直接回去荆州对刘备说：孙、刘两家，既结为亲，便是一家；",1,"","","鲁肃",312,},
        [5575] = {11803,3,1,"周瑜",303,0,"若刘氏不忍去取西川，我东吴起兵去敢，取得西川时，以作嫁资，却把荆州交还东吴。","若刘氏不忍去取西川，我东吴起兵去敢，取得西川时，以作嫁资，却把荆州交还东吴。",1,"","","鲁肃",312,},
        [5576] = {11803,4,2,"鲁肃",312,0,"西川迢递，取之非易。都督此计，莫非不可？","西川迢递，取之非易。都督此计，莫非不可？",1,"","","周瑜",303,},
        [5577] = {11803,5,1,"周瑜",303,0,"我只以此为名，实欲去取荆州，且教他不做准备。东吴军马收川路过荆州，就问他索要钱粮，趁刘备出城劳军乘势杀之。","我只以此为名，实欲去取荆州，且教他不做准备。东吴军马收川路过荆州，就问他索要钱粮，趁刘备出城劳军乘势杀之。",1,"","","鲁肃",312,},
        [5578] = {11804,1,2,"鲁肃",312,0,"我去荆州跟他们说过了，刘备和诸葛亮大喜，准备出城劳军。","我去荆州跟他们说过了，刘备和诸葛亮大喜，准备出城劳军。",1,"","","周瑜",303,},
        [5579] = {11804,2,1,"周瑜",303,0,"原来孔明也有中了吾计的一天！荆州有人接应吗？","原来孔明也有中了吾计的一天！荆州有人接应吗？",1,"","","鲁肃",312,},
        [5580] = {11804,3,2,"糜竺",243,0,"刘皇叔使糜竺来见都督。主公皆准备安排下了。在荆州城门外相等，与都督把盏。","刘皇叔使糜竺来见都督。主公皆准备安排下了。在荆州城门外相等，与都督把盏。",1,"","","周瑜",303,},
        [5581] = {11804,4,1,"周瑜",303,0,"今为汝家之事，出兵远征；劳军之礼，休得轻易。","今为汝家之事，出兵远征；劳军之礼，休得轻易。",1,"","","糜竺",243,},
        [5582] = {11805,1,2,"周瑜",303,0,"如今我军已到荆州城下，为何不见接应？","如今我军已到荆州城下，为何不见接应？",1,"","","赵云",201,},
        [5583] = {11805,2,1,"赵云",201,0,"终将听令，戒备！","终将听令，戒备！",1,"","","周瑜",303,},
        [5584] = {11805,3,2,"周瑜",303,0,"吾替汝主取西川，汝岂犹未知耶？你们这是干什么？","吾替汝主取西川，汝岂犹未知耶？你们这是干什么？",1,"","","赵云",201,},
        [5585] = {11805,4,1,"赵云",201,0,"孔明军师已知都督假途灭虢之计，故留赵云在此。","孔明军师已知都督假途灭虢之计，故留赵云在此。",1,"","","周瑜",303,},
        [5586] = {11805,5,1,"赵云",201,0,"主公说他与刘璋都是汉室宗亲，安忍背义而取西川？若汝东吴端的取蜀，他当披发入山，不失信于天下也。","主公说他与刘璋都是汉室宗亲，安忍背义而取西川？若汝东吴端的取蜀，他当披发入山，不失信于天下也。",1,"","","周瑜",303,},
        [5587] = {11805,6,2,"周瑜",303,0,"哼！撤！","哼！撤！",1,"","","",0,},
        [5588] = {11806,1,1,"周瑜",303,0,"刘备，诸葛亮，你们欺人太甚！你道我取不得西川，吾誓取之！","刘备，诸葛亮，你们欺人太甚！你道我取不得西川，吾誓取之！",1,"","","",0,},
        [5589] = {11806,2,2,"周瑜随从",140,0,"报告都督，诸葛亮派人来送信。","报告都督，诸葛亮派人来送信。",1,"","","周瑜",303,},
        [5590] = {11806,3,1,"周瑜",303,0,"哼！我倒要看看他还要说些什么。”……闻足下欲取西川，亮窃以为不可。益州民强地险……“","哼！我倒要看看他还要说些什么。”……闻足下欲取西川，亮窃以为不可。益州民强地险……“",1,"","","",0,},
        [5591] = {11806,4,1,"周瑜",303,0,"“曹操失利于赤壁，志岂须臾忘报仇哉？今足下兴兵远征，倘操乘虚而至，江南齑粉矣！亮不忍坐视，特此告知。”岂有此理！","“曹操失利于赤壁，志岂须臾忘报仇哉？今足下兴兵远征，倘操乘虚而至，江南齑粉矣！亮不忍坐视，特此告知。”岂有此理！",1,"","","",0,},
        [5592] = {11806,5,1,"周瑜",303,0,"噗……（吐出一口鲜血）吾非不欲尽忠报国，奈天命已绝矣。","噗……（吐出一口鲜血）吾非不欲尽忠报国，奈天命已绝矣。",1,"","","",0,},
        [5593] = {11806,6,1,"周瑜",303,0,"既生瑜，何生亮啊！（连叫数声而亡）","既生瑜，何生亮啊！（连叫数声而亡）",1,"","","",0,},
        [5594] = {11901,1,1,"孙权",306,0,"什么！公瑾他……公瑾有王佐之才，今忽短命而死，孤何赖哉？既遗书特荐子敬，孤敢不从之。（泣不成声）","什么！公瑾他……公瑾有王佐之才，今忽短命而死，孤何赖哉？既遗书特荐子敬，孤敢不从之。（泣不成声）",1,"","","",0,},
        [5595] = {11901,2,2,"鲁肃",312,0,"主公节哀。都督生前留书让我来接替他，我定当竭尽全力辅佐主公。","主公节哀。都督生前留书让我来接替他，我定当竭尽全力辅佐主公。",1,"","","孙权",306,},
        [5596] = {11901,3,1,"孙权",306,0,"命鲁肃为都督，总统兵马；发公瑾灵柩回柴桑厚葬。","命鲁肃为都督，总统兵马；发公瑾灵柩回柴桑厚葬。",1,"","","鲁肃",312,},
        [5597] = {11902,1,1,"诸葛亮",203,0,"吾夜观天象，见星坠地，周瑜死矣。","吾夜观天象，见星坠地，周瑜死矣。",1,"","","刘备",202,},
        [5598] = {11902,2,2,"刘备",202,0,"周瑜既死，还当如何？","周瑜既死，还当如何？",1,"","","诸葛亮",203,},
        [5599] = {11902,3,1,"诸葛亮",203,0,"代瑜领兵者，必鲁肃也。亮观天象，将星聚于东方。亮当以吊丧为由。往江东走一遭，就寻贤士佐助主公。","代瑜领兵者，必鲁肃也。亮观天象，将星聚于东方。亮当以吊丧为由。往江东走一遭，就寻贤士佐助主公。",1,"","","刘备",202,},
        [5600] = {11902,4,2,"刘备",202,0,"只恐吴中将士加害于先生。","只恐吴中将士加害于先生。",1,"","","诸葛亮",203,},
        [5601] = {11902,5,1,"诸葛亮",203,0,"瑜在之日，亮犹不惧；今瑜已死，又何患乎？","瑜在之日，亮犹不惧；今瑜已死，又何患乎？",1,"","","刘备",202,},
        [5602] = {11903,1,2,"周瑜部将",129,0,"什么？那诸葛亮还敢来吊唁？我们要杀了他，为都督报仇！","什么？那诸葛亮还敢来吊唁？我们要杀了他，为都督报仇！",1,"","","",0,},
        [5603] = {11903,2,2,"鲁肃",312,0,"慢，诸葛亮来我东吴，所到之处皆有赵云相随，赵云勇猛，汝等休得轻举妄动。","慢，诸葛亮来我东吴，所到之处皆有赵云相随，赵云勇猛，汝等休得轻举妄动。",1,"","","",0,},
        [5604] = {11903,3,1,"诸葛亮",203,0,"（泣不成声）呜呼公瑾，不幸夭亡！修短故天，人岂不伤？","（泣不成声）呜呼公瑾，不幸夭亡！修短故天，人岂不伤？",1,"","","",0,},
        [5605] = {11903,4,1,"诸葛亮",203,0,"忠义之心，英灵之气；命终三纪，名垂百世，哀君情切，愁肠千结；惟我肝胆，悲无断绝。","忠义之心，英灵之气；命终三纪，名垂百世，哀君情切，愁肠千结；惟我肝胆，悲无断绝。",1,"","","",0,},
        [5606] = {11903,5,1,"诸葛亮",203,0,"朴守其贞，冥冥灭灭，魂如有灵，以鉴我心：从此天下，更无知音！呜呼痛哉！","朴守其贞，冥冥灭灭，魂如有灵，以鉴我心：从此天下，更无知音！呜呼痛哉！",1,"","","",0,},
        [5607] = {11903,6,2,"鲁肃",312,0,"孔明自是多情，乃公瑾量窄，自取死耳。","孔明自是多情，乃公瑾量窄，自取死耳。",1,"","","",0,},
        [5608] = {11904,1,2,"庞统",211,0,"汝气死周郎，却又来吊孝，明欺东吴无人耶！","汝气死周郎，却又来吊孝，明欺东吴无人耶！",1,"","","诸葛亮",203,},
        [5609] = {11904,2,1,"诸葛亮",203,0,"哈哈哈哈……别这么说嘛。","哈哈哈哈……别这么说嘛。",1,"","","庞统",211,},
        [5610] = {11904,3,2,"庞统",211,0,"好久不见，吾可要与你好好叙叙旧。","好久不见，吾可要与你好好叙叙旧。",1,"","","诸葛亮",203,},
        [5611] = {11904,4,1,"诸葛亮",203,0,"吾料孙仲谋必不能重用足下。稍有不如意，可来荆州共扶玄德。此人宽仁厚德，必不负公平生之所学。","吾料孙仲谋必不能重用足下。稍有不如意，可来荆州共扶玄德。此人宽仁厚德，必不负公平生之所学。",1,"","","庞统",211,},
        [5612] = {12001,1,2,"鲁肃",312,0,"肃碌碌庸才，误蒙公瑾重荐，其实不称所职，愿举一人以助主公。此人上通天文，下晓地理；","肃碌碌庸才，误蒙公瑾重荐，其实不称所职，愿举一人以助主公。此人上通天文，下晓地理；",1,"","","孙权",306,},
        [5613] = {12001,2,2,"鲁肃",312,0,"谋略不减于管、乐，枢机可并于孙、吴。往日周公瑾多用其言，孔明亦深服其智，现在江南，何不重用！","谋略不减于管、乐，枢机可并于孙、吴。往日周公瑾多用其言，孔明亦深服其智，现在江南，何不重用！",1,"","","孙权",306,},
        [5614] = {12001,3,1,"孙权",306,0,"当真？此人姓甚名谁？","当真？此人姓甚名谁？",1,"","","鲁肃",312,},
        [5615] = {12001,4,2,"鲁肃",312,0,"此人乃襄阳人，姓庞，名统，字士元：道号凤雏先生。","此人乃襄阳人，姓庞，名统，字士元：道号凤雏先生。",1,"","","孙权",306,},
        [5616] = {12001,5,1,"孙权",306,0,"孤亦闻其名久矣。今既在此，可即请来相见。","孤亦闻其名久矣。今既在此，可即请来相见。",1,"","","鲁肃",312,},
        [5617] = {12002,1,2,"鲁肃",312,0,"主公，这便是凤雏先生庞统了。","主公，这便是凤雏先生庞统了。",1,"","","孙权",306,},
        [5618] = {12002,2,1,"孙权",306,0,"（这人怎么相貌如此古怪）公平生所学，以何为主？","（这人怎么相貌如此古怪）公平生所学，以何为主？",1,"","","庞统",211,},
        [5619] = {12002,3,2,"庞统",211,0,"不必拘执，随机应变。","不必拘执，随机应变。",1,"","","孙权",306,},
        [5620] = {12002,4,1,"孙权",306,0,"公之才学，比公瑾如何？","公之才学，比公瑾如何？",1,"","","庞统",211,},
        [5621] = {12002,5,2,"庞统",211,0,"某之所学，与公瑾大不相同。","某之所学，与公瑾大不相同。",1,"","","孙权",306,},
        [5622] = {12002,6,1,"孙权",306,0,"公且退。待有用公之时，却来相请。","公且退。待有用公之时，却来相请。",1,"","","庞统",211,},
        [5623] = {12002,7,2,"鲁肃",312,0,"主公何不用庞士元？","主公何不用庞士元？",1,"","","孙权",306,},
        [5624] = {12002,8,1,"孙权",306,0,"狂士也，用之何益！","狂士也，用之何益！",1,"","","鲁肃",312,},
        [5625] = {12003,1,1,"鲁肃",312,0,"非肃不荐足下，奈吴侯不肯用公。公且耐心。","非肃不荐足下，奈吴侯不肯用公。公且耐心。",1,"","","庞统",211,},
        [5626] = {12003,2,2,"庞统",211,0,"唉……","唉……",1,"","","鲁肃",312,},
        [5627] = {12003,3,1,"鲁肃",312,0,"公莫非无意于吴中乎？公抱匡济之才，何往不利？可实对肃言，将欲何往？","公莫非无意于吴中乎？公抱匡济之才，何往不利？可实对肃言，将欲何往？",1,"","","庞统",211,},
        [5628] = {12003,4,2,"庞统",211,0,"吾欲投荆州刘皇叔。","吾欲投荆州刘皇叔。",1,"","","鲁肃",312,},
        [5629] = {12003,5,1,"鲁肃",312,0,"某当作书奉荐，公辅玄德，必令孙、刘两家，无相攻击，同力破曹。","某当作书奉荐，公辅玄德，必令孙、刘两家，无相攻击，同力破曹。",1,"","","庞统",211,},
        [5630] = {12003,6,2,"庞统",211,0,"此某平生之素志也。","此某平生之素志也。",1,"","","鲁肃",312,},
        [5631] = {12004,1,1,"刘备",202,0,"足下远来不易？","足下远来不易？",1,"","","庞统",211,},
        [5632] = {12004,2,2,"庞统",211,0,"闻皇叔招贤纳士，特来相投。","闻皇叔招贤纳士，特来相投。",1,"","","刘备",202,},
        [5633] = {12004,3,1,"刘备",202,0,"荆楚稍定，苦无闲职。一百三十里外有一耒阳县少一县令，屈公任之。","荆楚稍定，苦无闲职。一百三十里外有一耒阳县少一县令，屈公任之。",1,"","","庞统",211,},
        [5634] = {12004,4,2,"庞统",211,0,"玄德待我何薄！今日孔明不在，他日再细说也罢。","玄德待我何薄！今日孔明不在，他日再细说也罢。",1,"","","刘备",202,},
        [5635] = {12005,1,1,"张飞",206,0,"听闻那庞统任耒阳县令，县事尽废，岂有此理！","听闻那庞统任耒阳县令，县事尽废，岂有此理！",1,"","","",0,},
        [5636] = {12005,2,2,"庞统",211,0,"量百里小县，些小公事，何难决断！将军少坐，待我发落。","量百里小县，些小公事，何难决断！将军少坐，待我发落。",1,"","","张飞",206,},
        [5637] = {12006,1,2,"庞统",211,0,"（将百余日事尽断毕了）所废之事何在！曹操、孙权，吾视之若掌上观文，量此小县，何足介意！","（将百余日事尽断毕了）所废之事何在！曹操、孙权，吾视之若掌上观文，量此小县，何足介意！",1,"","","张飞",206,},
        [5638] = {12006,2,1,"张飞",206,0,"先生大才，小子失敬。吾当于兄长处极力举荐。","先生大才，小子失敬。吾当于兄长处极力举荐。",1,"","","庞统",211,},
        [5639] = {12006,3,2,"庞统",211,0,"我这里有鲁肃的推荐书信一封。","我这里有鲁肃的推荐书信一封。",1,"","","张飞",206,},
        [5640] = {12006,4,1,"张飞",206,0,"先生初见吾兄，何不将出？","先生初见吾兄，何不将出？",1,"","","庞统",211,},
        [5641] = {12006,5,2,"庞统",211,0,"若便将出，似乎专藉荐书来干谒矣。","若便将出，似乎专藉荐书来干谒矣。",1,"","","张飞",206,},
        [5642] = {12006,6,1,"刘备",202,0,"屈待大贤，吾之过也！若非吾弟所言，险失大贤。","屈待大贤，吾之过也！若非吾弟所言，险失大贤。",1,"","","庞统",211,},
        [5643] = {12006,7,1,"刘备",202,0,"昔司马德操言：‘伏龙、凤雏，两人得一，可安天下。’今吾二人皆得，汉室可兴矣。","昔司马德操言：‘伏龙、凤雏，两人得一，可安天下。’今吾二人皆得，汉室可兴矣。",1,"","","庞统",211,},
        [5644] = {12101,1,1,"马腾",438,0,"自衣带诏以来，我和刘玄德一直相约共讨曹贼，不幸董承已死，玄德屡败。","自衣带诏以来，我和刘玄德一直相约共讨曹贼，不幸董承已死，玄德屡败。",1,"","","",0,},
        [5645] = {12101,2,1,"马腾",438,0,"这曹操忽然降诏加我为征南将军，让我去讨伐孙权，当时如何？","这曹操忽然降诏加我为征南将军，让我去讨伐孙权，当时如何？",1,"","","",0,},
        [5646] = {12101,3,2,"马岱",220,0,"曹操心怀叵测，叔父若往，恐遭其害。","曹操心怀叵测，叔父若往，恐遭其害。",1,"","","马腾",438,},
        [5647] = {12101,4,2,"马超",207,0,"儿愿尽起西凉之兵，随父亲杀入许昌，为天下除害！","儿愿尽起西凉之兵，随父亲杀入许昌，为天下除害！",1,"","","马腾",438,},
        [5648] = {12101,5,1,"马腾",438,0,"你留在西凉，叫马休和马岱和我一起去。曹操忌惮你在西凉，谅他也不敢加害于我。","你留在西凉，叫马休和马岱和我一起去。曹操忌惮你在西凉，谅他也不敢加害于我。",1,"","","马超",207,},
        [5649] = {12101,6,2,"马超",207,0,"父亲要去，切不可轻入京师。当随机应变，观其动静。","父亲要去，切不可轻入京师。当随机应变，观其动静。",1,"","","马腾",438,},
        [5650] = {12102,1,1,"黄奎",121,0,"曹操派我来引将军你入城面君，不怀好意。来日等曹操出城点军，就在点军处杀之，大事成矣。","曹操派我来引将军你入城面君，不怀好意。来日等曹操出城点军，就在点军处杀之，大事成矣。",1,"","","马腾",438,},
        [5651] = {12102,2,2,"马腾",438,0,"谢将军如实相告。","谢将军如实相告。",1,"","","黄奎",121,},
        [5652] = {12103,1,1,"李春香",227,0,"世人都说刘皇叔仁德，曹操是奸雄，是这样吗？","世人都说刘皇叔仁德，曹操是奸雄，是这样吗？",1,"","","黄奎",121,},
        [5653] = {12103,2,2,"黄奎",121,0,"你是一个女子，都知道邪正，何况我乎？我恨不得杀了曹贼！","你是一个女子，都知道邪正，何况我乎？我恨不得杀了曹贼！",1,"","","李春香",227,},
        [5654] = {12103,3,1,"李春香",227,0,"你想杀他？你怎么下手？","你想杀他？你怎么下手？",1,"","","黄奎",121,},
        [5655] = {12103,4,2,"黄奎",121,0,"我已约定马将军，明日在城外点兵时杀之。","我已约定马将军，明日在城外点兵时杀之。",1,"","","李春香",227,},
        [5656] = {12103,5,1,"李春香",227,0,"啊…原来是这样……（赶快告诉丞相）","啊…原来是这样……（赶快告诉丞相）",1,"","","黄奎",121,},
        [5657] = {12104,1,1,"曹操",103,0,"我已得知密报，有人要在今天我出城点军时来杀我，原来是马将军这么大胆。","我已得知密报，有人要在今天我出城点军时来杀我，原来是马将军这么大胆。",1,"","","",0,},
        [5658] = {12104,2,1,"曹操",103,0,"来人，乱箭射死他们！","来人，乱箭射死他们！",1,"","","",0,},
        [5659] = {12104,3,2,"马腾",438,0,"我不能为国杀贼，是乃天意也！（含恨而亡）","我不能为国杀贼，是乃天意也！（含恨而亡）",1,"","","",0,},
        [5660] = {12201,1,1,"曹操",103,0,"听说刘备最近调练军马，收拾器械，若他收川成功，则羽翼成矣，我将何以图之？","听说刘备最近调练军马，收拾器械，若他收川成功，则羽翼成矣，我将何以图之？",1,"","","",0,},
        [5661] = {12201,2,2,"陈群",145,0,"如今刘备孙权结为唇齿，丞相带兵去取江东，刘备意在西川必定无心救援，江东必为丞相所得。","如今刘备孙权结为唇齿，丞相带兵去取江东，刘备意在西川必定无心救援，江东必为丞相所得。",1,"","","曹操",103,},
        [5662] = {12201,3,1,"曹操",103,0,"长文之言，正合吾意。这就起兵三十万，准备去取江东！","长文之言，正合吾意。这就起兵三十万，准备去取江东！",1,"","","陈群",145,},
        [5663] = {12202,1,1,"孙权",306,0,"有细作来报，曹操欲起三十万大军来攻打我们。","有细作来报，曹操欲起三十万大军来攻打我们。",1,"","","",0,},
        [5664] = {12202,2,2,"张昭",315,0,"可叫鲁肃写信给玄德，鲁子敬有恩于玄德，其言必从。况且玄德还是东吴的女婿，义不容辞。","可叫鲁肃写信给玄德，鲁子敬有恩于玄德，其言必从。况且玄德还是东吴的女婿，义不容辞。",1,"","","孙权",306,},
        [5665] = {12203,1,1,"诸葛亮",203,0,"也不用动江南之兵，也不用动荆州之兵，我自有办法使曹操不敢正觑东南。","也不用动江南之兵，也不用动荆州之兵，我自有办法使曹操不敢正觑东南。",1,"","","",0,},
        [5666] = {12203,2,1,"诸葛亮",203,0,"叫鲁肃高枕无忧，若但有北兵侵犯，皇叔自有退兵之策。","叫鲁肃高枕无忧，若但有北兵侵犯，皇叔自有退兵之策。",1,"","","",0,},
        [5667] = {12203,3,2,"刘备",202,0,"先生有何妙计，可以退曹操？","先生有何妙计，可以退曹操？",1,"","","诸葛亮",203,},
        [5668] = {12203,4,1,"诸葛亮",203,0,"操平生所虑者，乃西凉之兵也。今操杀马腾，其子马超现统西凉之众，必切齿操贼。","操平生所虑者，乃西凉之兵也。今操杀马腾，其子马超现统西凉之众，必切齿操贼。",1,"","","刘备",202,},
        [5669] = {12203,5,1,"诸葛亮",203,0,"主公可写信给马超，让他兴兵入关，那曹操哪里还顾得上取江东了？","主公可写信给马超，让他兴兵入关，那曹操哪里还顾得上取江东了？",1,"","","刘备",202,},
        [5670] = {12301,1,1,"马超",207,0,"昨夜梦见身卧雪地，群虎来咬，惊吓而醒，现在甚是疑惑。","昨夜梦见身卧雪地，群虎来咬，惊吓而醒，现在甚是疑惑。",1,"","","庞德",122,},
        [5671] = {12301,2,2,"庞德",122,0,"雪地遇虎，梦兆殊恶。莫非老将军在许昌有事否？","雪地遇虎，梦兆殊恶。莫非老将军在许昌有事否？",1,"","","马超",207,},
        [5672] = {12301,3,2,"马岱",220,0,"（踉跄而入）呜呜呜……叔父与弟都被曹操杀了！","（踉跄而入）呜呜呜……叔父与弟都被曹操杀了！",1,"","","马超",207,},
        [5673] = {12301,4,1,"马超",207,0,"什么！岂有此理！我与曹操之仇不共戴天！","什么！岂有此理！我与曹操之仇不共戴天！",1,"","","庞德",122,},
        [5674] = {12301,5,2,"庞德",122,0,"刘皇叔派人送信来，欲联合将军共讨曹贼。","刘皇叔派人送信来，欲联合将军共讨曹贼。",1,"","","马超",207,},
        [5675] = {12301,6,1,"马超",207,0,"杀父之仇必报！我这就带大军，与刘皇叔会合。","杀父之仇必报！我这就带大军，与刘皇叔会合。",1,"","","庞德",122,},
        [5676] = {12302,1,1,"韩遂",141,0,"曹操给我写信，说如果将你擒至许都，即封我做西凉侯。","曹操给我写信，说如果将你擒至许都，即封我做西凉侯。",1,"","","马超",207,},
        [5677] = {12302,2,2,"马超",207,0,"那请叔父就地将我和马岱兄弟二人绑了押至许昌，免叔父戈戟之劳。","那请叔父就地将我和马岱兄弟二人绑了押至许昌，免叔父戈戟之劳。",1,"","","韩遂",141,},
        [5678] = {12302,3,1,"韩遂",141,0,"我跟你父亲结为兄弟，怎么可能害你？你若兴兵，我定当辅助。","我跟你父亲结为兄弟，怎么可能害你？你若兴兵，我定当辅助。",1,"","","马超",207,},
        [5679] = {12303,1,1,"马超",207,0,"这长安城乃西汉建都之处，城郭坚固。我大军已围了十日，久攻不下，如何是好？","这长安城乃西汉建都之处，城郭坚固。我大军已围了十日，久攻不下，如何是好？",1,"","","",0,},
        [5680] = {12303,2,2,"主角",1,0,"长安城中土硬水碱，如今围困十日，应是军民饥荒。不如暂且收军，只须如此如此，长安唾手可得。","长安城中土硬水碱，如今围困十日，应是军民饥荒。不如暂且收军，只须如此如此，长安唾手可得。",1,"","","马超",207,},
        [5681] = {12303,3,1,"马超",207,0,"此计甚妙。","此计甚妙。",1,"","","主角",1,},
        [5682] = {12304,1,2,"庞德",122,0,"五日已过，军民出城打柴取水时，我军派人趁虚而入，里外夹击，打他们一个措手不及！","五日已过，军民出城打柴取水时，我军派人趁虚而入，里外夹击，打他们一个措手不及！",1,"","","马超",207,},
        [5683] = {12304,2,1,"马超",207,0,"此次攻占城池，大家都功不可没，来人传令下去，犒赏三军！","此次攻占城池，大家都功不可没，来人传令下去，犒赏三军！",1,"","","庞德",122,},
        [5684] = {12401,1,1,"曹操",103,0,"混账！竟然丢了长安！徐晃、曹洪去守潼关，我带大军随后到，若失了关隘便斩了你们！","混账！竟然丢了长安！徐晃、曹洪去守潼关，我带大军随后到，若失了关隘便斩了你们！",1,"","","",0,},
        [5685] = {12401,2,2,"曹仁",106,0,"曹洪性情急躁，我担心他会误事。","曹洪性情急躁，我担心他会误事。",1,"","","曹操",103,},
        [5686] = {12401,3,1,"曹操",103,0,"你与我押运粮草，便随后接应。","你与我押运粮草，便随后接应。",1,"","","曹仁",106,},
        [5687] = {12402,1,1,"马超",207,0,"曹操！你们全家都是胆小鬼！有本事下来跟我打啊！在城上面当缩头乌龟！","曹操！你们全家都是胆小鬼！有本事下来跟我打啊！在城上面当缩头乌龟！",1,"","","",0,},
        [5688] = {12402,2,2,"曹洪",142,0,"岂有此理！我要下去教训这个不知天高地厚的家伙！","岂有此理！我要下去教训这个不知天高地厚的家伙！",1,"","","徐晃",114,},
        [5689] = {12402,3,1,"徐晃",114,0,"这是马超故意要激怒将军，切不可上了他的当。待丞相大军来，必有打算。","这是马超故意要激怒将军，切不可上了他的当。待丞相大军来，必有打算。",1,"","","曹洪",142,},
        [5690] = {12403,1,1,"曹洪",142,0,"机会来了！趁他们现在人困马乏，我要下去跟马超决一死战！被他骂了这么多天，可气死我了。","机会来了！趁他们现在人困马乏，我要下去跟马超决一死战！被他骂了这么多天，可气死我了。",1,"","","",0,},
        [5691] = {12403,2,2,"徐晃",114,0,"曹洪将军别去，快回来！","曹洪将军别去，快回来！",1,"","","",0,},
        [5692] = {12403,3,1,"马超",207,0,"晚了！吃我一枪！","晚了！吃我一枪！",1,"","","",0,},
        [5693] = {12403,4,2,"徐晃",114,0,"糟了，中了贼的奸计！曹洪将军快跑，我掩护你！","糟了，中了贼的奸计！曹洪将军快跑，我掩护你！",1,"","","",0,},
        [5694] = {12501,1,1,"曹操",103,0,"说了十天为限，你怎么九天失了潼关？","说了十天为限，你怎么九天失了潼关？",1,"","","曹洪",142,},
        [5695] = {12501,2,2,"曹洪",142,0,"西凉军那个马超，百般辱骂咱们全家，我气不过，趁他们士气懈怠想去教训一番，不了中了奸计。","西凉军那个马超，百般辱骂咱们全家，我气不过，趁他们士气懈怠想去教训一番，不了中了奸计。",1,"","","曹操",103,},
        [5696] = {12501,3,1,"曹操",103,0,"曹洪年幼暴躁，徐晃你怎么也不懂事？","曹洪年幼暴躁，徐晃你怎么也不懂事？",1,"","","徐晃",114,},
        [5697] = {12501,4,2,"徐晃",114,0,"我劝了多次，曹洪将军不听。当日我在关上点粮车，发现的时候小将军已经下关中计了。","我劝了多次，曹洪将军不听。当日我在关上点粮车，发现的时候小将军已经下关中计了。",1,"","","曹操",103,},
        [5698] = {12501,5,1,"曹操",103,0,"曹洪！本该杀了你，看在众官求情的份上，先饶你一命。明日我去把潼关夺回来。","曹洪！本该杀了你，看在众官求情的份上，先饶你一命。明日我去把潼关夺回来。",1,"","","曹洪",142,},
        [5699] = {12502,1,1,"马超",207,0,"曹贼！欺君罔上，罪不容诛！害我父弟，不共戴天之仇！","曹贼！欺君罔上，罪不容诛！害我父弟，不共戴天之仇！",1,"","","",0,},
        [5700] = {12502,2,2,"曹操",103,0,"糟了，这马超好生勇猛，我不是他的对手。还是先逃吧。","糟了，这马超好生勇猛，我不是他的对手。还是先逃吧。",1,"","","",0,},
        [5701] = {12502,3,1,"西凉军",134,0,"快追曹贼！别让他跑啦！穿红袍的那个是曹操！","快追曹贼！别让他跑啦！穿红袍的那个是曹操！",1,"","","",0,},
        [5702] = {12502,4,2,"曹操",103,0,"袍子不要了，脱下丢掉。","袍子不要了，脱下丢掉。",1,"","","",0,},
        [5703] = {12502,5,1,"西凉军",134,0,"长胡子的那个是曹操！","长胡子的那个是曹操！",1,"","","",0,},
        [5704] = {12502,6,2,"曹操",103,0,"胡子割断，快跑快跑！","胡子割断，快跑快跑！",1,"","","",0,},
        [5705] = {12601,1,1,"许褚",108,0,"听说那马超好生厉害，某誓与之死战！","听说那马超好生厉害，某誓与之死战！",1,"","","",0,},
        [5706] = {12601,2,2,"马超",207,0,"许褚竟然下战书给我，这么瞧不起人！","许褚竟然下战书给我，这么瞧不起人！",1,"","","",0,},
        [5707] = {12601,3,1,"许褚",108,0,"话不多说，来战吧！","话不多说，来战吧！",1,"","","马超",207,},
        [5708] = {12601,4,2,"马超",207,0,"吾见恶战者莫如许褚，真虎痴也！","吾见恶战者莫如许褚，真虎痴也！",1,"","","许褚",108,},
        [5709] = {12701,1,1,"曹操",103,0,"韩遂派人来送信，商量割地请和的事情。文和觉得如何？","韩遂派人来送信，商量割地请和的事情。文和觉得如何？",1,"","","贾诩",407,},
        [5710] = {12701,2,2,"贾诩",407,0,"兵不厌诈，可以假意答应他们。然后用反间计，让韩遂和马超相疑，则可破也。","兵不厌诈，可以假意答应他们。然后用反间计，让韩遂和马超相疑，则可破也。",1,"","","曹操",103,},
        [5711] = {12701,3,1,"曹操",103,0,"天下高见，多有相合。文和之谋，正吾心中之事也。","天下高见，多有相合。文和之谋，正吾心中之事也。",1,"","","贾诩",407,},
        [5712] = {12701,4,1,"曹操",103,0,"传我书信给马超，说待我徐徐退兵，还他河西之地。","传我书信给马超，说待我徐徐退兵，还他河西之地。",1,"","","",0,},
        [5713] = {12702,1,1,"马超",207,0,"听闻曹操送来书信一封，叔父可否借我一看？","听闻曹操送来书信一封，叔父可否借我一看？",1,"","","韩遂",141,},
        [5714] = {12702,2,2,"韩遂",141,0,"这便是。不知何故书上被改抹了许多，莫非误将草稿送来了。","这便是。不知何故书上被改抹了许多，莫非误将草稿送来了。",1,"","","马超",207,},
        [5715] = {12702,3,1,"马超",207,0,"岂有以草稿送与人的？莫不是叔父怕我知了详细，先改抹了。","岂有以草稿送与人的？莫不是叔父怕我知了详细，先改抹了。",1,"","","韩遂",141,},
        [5716] = {12702,4,2,"韩遂",141,0,"贤侄休疑，我无歹心。","贤侄休疑，我无歹心。",1,"","","马超",207,},
        [5717] = {12703,1,1,"杨秋",131,0,"马超仗着自己勇猛，常有欺凌主公之心，以某愚见，不如按投曹公，他日不失封侯之位。","马超仗着自己勇猛，常有欺凌主公之心，以某愚见，不如按投曹公，他日不失封侯之位。",1,"","","韩遂",141,},
        [5718] = {12703,2,2,"韩遂",141,0,"吾与马腾结为兄弟，怎么忍心背叛他呢？","吾与马腾结为兄弟，怎么忍心背叛他呢？",1,"","","杨秋",131,},
        [5719] = {12703,3,1,"杨秋",131,0,"事已至此，不得不这样做了。我派人去给曹操送消息，约定今夜放火，里应外合，共谋马超。","事已至此，不得不这样做了。我派人去给曹操送消息，约定今夜放火，里应外合，共谋马超。",1,"","","韩遂",141,},
        [5720] = {12704,1,1,"马超",207,0,"群贼安敢谋害我！看剑！（一剑斩断韩遂左手）","群贼安敢谋害我！看剑！（一剑斩断韩遂左手）",1,"","","",0,},
        [5721] = {12704,2,2,"于禁",119,0,"曹丞相派我来支援韩将军！","曹丞相派我来支援韩将军！",1,"","","",0,},
        [5722] = {12704,3,1,"主角",1,0,"好汉不吃眼前亏，马将军快随我离开，来日方长！","好汉不吃眼前亏，马将军快随我离开，来日方长！",1,"","","马超",207,},
        [5723] = {12801,1,1,"张鲁",442,0,"西凉马腾被曹操杀了，马超新败，曹操必将入侵我汉中。我欲自称汉宁王，督兵拒曹操，如何？","西凉马腾被曹操杀了，马超新败，曹操必将入侵我汉中。我欲自称汉宁王，督兵拒曹操，如何？",1,"","","阎圃",138,},
        [5724] = {12801,2,2,"阎圃",138,0,"汉川之民财粮富足，四面险固，如今马超新败，西凉之民涌入我汉中者不下数万。","汉川之民财粮富足，四面险固，如今马超新败，西凉之民涌入我汉中者不下数万。",1,"","","张鲁",442,},
        [5725] = {12801,3,2,"阎圃",138,0,"益州刘璋昏庸，我们不如先取西川四十一州为本，再称王不迟。","益州刘璋昏庸，我们不如先取西川四十一州为本，再称王不迟。",1,"","","张鲁",442,},
        [5726] = {12802,1,1,"刘璋",246,0,"听说张鲁要来取我西川，这可如何是好？","听说张鲁要来取我西川，这可如何是好？",1,"","","张松",245,},
        [5727] = {12802,2,2,"张松",245,0,"主公放心，某虽不才，凭三寸不烂之舌，使张鲁不敢正眼来觑西川。","主公放心，某虽不才，凭三寸不烂之舌，使张鲁不敢正眼来觑西川。",1,"","","刘璋",246,},
        [5728] = {12802,3,1,"刘璋",246,0,"愿闻其详。","愿闻其详。",1,"","","张松",245,},
        [5729] = {12802,4,2,"张松",245,0,"听闻曹操灭了吕布，又破马超，天下无敌矣。主公可备进献之物，劝曹操兴兵汉中以图张鲁。","听闻曹操灭了吕布，又破马超，天下无敌矣。主公可备进献之物，劝曹操兴兵汉中以图张鲁。",1,"","","刘璋",246,},
        [5730] = {12802,5,2,"张松",245,0,"张鲁自顾不暇，哪里还敢觊觎我们西川？","张鲁自顾不暇，哪里还敢觊觎我们西川？",1,"","","刘璋",246,},
        [5731] = {12802,6,1,"刘璋",246,0,"甚好，就按你说的办。","甚好，就按你说的办。",1,"","","张松",245,},
        [5732] = {12901,1,1,"曹操",103,0,"汝主刘璋为什么连年不进贡？","汝主刘璋为什么连年不进贡？",1,"","","张松",245,},
        [5733] = {12901,2,2,"张松",245,0,"路途艰难，贼寇窃发，不能通进。","路途艰难，贼寇窃发，不能通进。",1,"","","曹操",103,},
        [5734] = {12901,3,1,"曹操",103,0,"我平定中原，哪来的盗贼？","我平定中原，哪来的盗贼？",1,"","","张松",245,},
        [5735] = {12901,4,2,"张松",245,0,"南有孙权，北有张鲁，西有刘备，哪里能说得上太平了？","南有孙权，北有张鲁，西有刘备，哪里能说得上太平了？",1,"","","曹操",103,},
        [5736] = {12901,5,1,"曹操",103,0,"哼，长相猥琐，言语冲撞，竟然如此无礼！","哼，长相猥琐，言语冲撞，竟然如此无礼！",1,"","","张松",245,},
        [5737] = {12902,1,1,"张松",245,0,"曹丞相不过如此，文不明孔、孟之道，武不达孙、吴之机，专务强霸而居大位。","曹丞相不过如此，文不明孔、孟之道，武不达孙、吴之机，专务强霸而居大位。",1,"","","",0,},
        [5738] = {12902,2,2,"杨修",131,0,"你在偏远地区，怎么知道丞相大才？我给你看看厉害的，《孟德新书》听过吗？","你在偏远地区，怎么知道丞相大才？我给你看看厉害的，《孟德新书》听过吗？",1,"","","张松",245,},
        [5739] = {12902,3,1,"张松",245,0,"（从头至尾看了一遍）……我还以为是什么书呢，这书我们蜀中小孩子都会背，有什么新的！","（从头至尾看了一遍）……我还以为是什么书呢，这书我们蜀中小孩子都会背，有什么新的！",1,"","","杨修",131,},
        [5740] = {12902,4,2,"杨修",131,0,"这是丞相秘藏之书，未传于世，怎么可能蜀中小儿都会背呢？","这是丞相秘藏之书，未传于世，怎么可能蜀中小儿都会背呢？",1,"","","张松",245,},
        [5741] = {12902,5,1,"张松",245,0,"你要是不信，我背给你听。（从头到尾背诵一遍，并无差错）","你要是不信，我背给你听。（从头到尾背诵一遍，并无差错）",1,"","","杨修",131,},
        [5742] = {12902,6,2,"杨修",131,0,"公过目不忘，真乃天下奇才也！","公过目不忘，真乃天下奇才也！",1,"","","张松",245,},
        [5743] = {12903,1,1,"曹操",103,0,"吾视天下鼠辈为草芥耳，顺我者生，逆我者死，你可知道？","吾视天下鼠辈为草芥耳，顺我者生，逆我者死，你可知道？",1,"","","张松",245,},
        [5744] = {12903,2,2,"张松",245,0,"丞相驱兵到处，战必胜，攻必取。昔日濮阳攻吕布，宛城战张绣，赤壁遇周郎，华容逢关羽；","丞相驱兵到处，战必胜，攻必取。昔日濮阳攻吕布，宛城战张绣，赤壁遇周郎，华容逢关羽；",1,"","","曹操",103,},
        [5745] = {12903,3,2,"张松",245,0,"割须弃袍于潼关，夺船避箭于渭水，此皆无敌于天下也！","割须弃袍于潼关，夺船避箭于渭水，此皆无敌于天下也！",1,"","","曹操",103,},
        [5746] = {12903,4,1,"曹操",103,0,"竖儒怎敢揭吾短处！乱棒把他轰出去！","竖儒怎敢揭吾短处！乱棒把他轰出去！",1,"","","张松",245,},
        [5747] = {13001,1,1,"张松",245,0,"本来想把西川地图献给曹操，谁料他竟如此目中无人！不如绕道去荆州看看刘备那边什么情况。","本来想把西川地图献给曹操，谁料他竟如此目中无人！不如绕道去荆州看看刘备那边什么情况。",1,"","","",0,},
        [5748] = {13001,2,2,"主角",1,0,"来者可是张松张别驾？我与兄长在此等候多时。","来者可是张松张别驾？我与兄长在此等候多时。",1,"","","张松",245,},
        [5749] = {13001,3,2,"刘备",202,0,"久闻大夫高名，如雷贯耳。特在此相接，倘若不嫌弃，到我这暂歇片时，以叙渴仰之思。","久闻大夫高名，如雷贯耳。特在此相接，倘若不嫌弃，到我这暂歇片时，以叙渴仰之思。",1,"","","张松",245,},
        [5750] = {13001,4,1,"张松",245,0,"人言刘玄德宽仁爱客，今果如此。","人言刘玄德宽仁爱客，今果如此。",1,"","","",0,},
        [5751] = {13002,1,1,"张松",245,0,"这几日多谢玄德款待，我该回去了，就此别过。","这几日多谢玄德款待，我该回去了，就此别过。",1,"","","刘备",202,},
        [5752] = {13002,2,2,"刘备",202,0,"承蒙大夫不弃，留叙三日，今日相别，不知何时再得听教。","承蒙大夫不弃，留叙三日，今日相别，不知何时再得听教。",1,"","","张松",245,},
        [5753] = {13002,3,1,"张松",245,0,"某非卖主求荣，今遇明公，愿请明公取西川为基，然后北图汉中，收取中原，匡扶汉室。","某非卖主求荣，今遇明公，愿请明公取西川为基，然后北图汉中，收取中原，匡扶汉室。",1,"","","刘备",202,},
        [5754] = {13002,4,1,"张松",245,0,"若明公有取西川之意，松愿效犬马之劳，以为内应，如何？","若明公有取西川之意，松愿效犬马之劳，以为内应，如何？",1,"","","刘备",202,},
        [5755] = {13002,5,2,"刘备",202,0,"刘季玉与我同宗，若攻之，恐天下人唾骂。","刘季玉与我同宗，若攻之，恐天下人唾骂。",1,"","","张松",245,},
        [5756] = {13003,1,1,"主角",1,0,"大丈夫处世，当建功立业，著鞭在先。今天不取，也会被他人所取，到时候后悔就晚了。","大丈夫处世，当建功立业，著鞭在先。今天不取，也会被他人所取，到时候后悔就晚了。",1,"","","",0,},
        [5757] = {13003,2,2,"张松",245,0,"将军说的是，深感明公盛德，特将此图赠予将军。但看此图，便知蜀中道路矣。","将军说的是，深感明公盛德，特将此图赠予将军。但看此图，便知蜀中道路矣。",1,"","","主角",1,},
        [5758] = {13003,3,2,"张松",245,0,"明公可速图之，我有心腹契友法正和孟达，此二人必能相助。","明公可速图之，我有心腹契友法正和孟达，此二人必能相助。",1,"","","主角",1,},
        [5759] = {13003,4,1,"主角",1,0,"青山不老，绿水长存，他日事成，必当厚报。","青山不老，绿水长存，他日事成，必当厚报。",1,"","","张松",245,},
        [5760] = {13004,1,1,"张松",245,0,"我这次见到曹操，轻贤傲士，只可同忧，不可同乐。吾已将益州许给了刘皇叔。","我这次见到曹操，轻贤傲士，只可同忧，不可同乐。吾已将益州许给了刘皇叔。",1,"","","法正",219,},
        [5761] = {13004,2,2,"法正",219,0,"我早就觉得刘璋无能，有心想见刘皇叔很久了。既然我们想法一致，还在犹豫什么呢？","我早就觉得刘璋无能，有心想见刘皇叔很久了。既然我们想法一致，还在犹豫什么呢？",1,"","","张松",245,},
        [5762] = {13004,3,1,"张松",245,0,"明日我去见刘璋，让他派二位哥哥为使，前往荆州。与刘皇叔共商大计。","明日我去见刘璋，让他派二位哥哥为使，前往荆州。与刘皇叔共商大计。",1,"","","法正",219,},
        [5763] = {13101,1,1,"法正",219,0,"益州天府之国，非治乱之主，不可居也。今刘季玉不能用贤，此大业不久必属他人。","益州天府之国，非治乱之主，不可居也。今刘季玉不能用贤，此大业不久必属他人。",1,"","","刘备",202,},
        [5764] = {13101,2,1,"法正",219,0,"今日自付与将军，不可错失。将军欲取，某当效死。","今日自付与将军，不可错失。将军欲取，某当效死。",1,"","","刘备",202,},
        [5765] = {13101,3,2,"庞统",211,0,"我们就在宴会上除掉刘璋，埋伏刀斧手一百人，主公掷杯为号。","我们就在宴会上除掉刘璋，埋伏刀斧手一百人，主公掷杯为号。",1,"","","刘备",202,},
        [5766] = {13101,4,1,"刘备",202,0,"刘季玉与我同宗，不忍取之。你们不必再说了。","刘季玉与我同宗，不忍取之。你们不必再说了。",1,"","","",0,},
        [5767] = {13102,1,2,"刘璋部下",142,0,"主公叫刘玄德来益州，恐引狼入室，不如早些叫他回去，免生后患。","主公叫刘玄德来益州，恐引狼入室，不如早些叫他回去，免生后患。",1,"","","刘璋",246,},
        [5768] = {13102,2,1,"刘璋",246,0,"吾兄刘玄德，非比他人。","吾兄刘玄德，非比他人。",1,"","","刘璋部下",142,},
        [5769] = {13102,3,2,"刘璋部下",142,0,"虽玄德无此心，他手下人皆欲吞并西川，以图富贵。","虽玄德无此心，他手下人皆欲吞并西川，以图富贵。",1,"","","刘璋",246,},
        [5770] = {13102,4,1,"刘璋",246,0,"你们不要再说了，不要离间我们兄弟之情。","你们不要再说了，不要离间我们兄弟之情。",1,"","","刘璋部下",142,},
        [5771] = {13103,1,1,"刘璋",246,0,"我接到报告说，张鲁忽然整顿兵马，将犯葭萌关。玄德可有何良策？","我接到报告说，张鲁忽然整顿兵马，将犯葭萌关。玄德可有何良策？",1,"","","刘备",202,},
        [5772] = {13103,2,2,"刘备",202,0,"我愿带兵去守葭萌关，抵御张鲁。","我愿带兵去守葭萌关，抵御张鲁。",1,"","","刘璋",246,},
        [5773] = {13103,3,1,"刘璋",246,0,"如此便有劳啦。","如此便有劳啦。",1,"","","刘备",202,},
        [5774] = {13201,1,2,"顾雍",321,0,"听闻刘备分兵远涉山险而去，未易往返。何不差一军先截川口，断其归路，随后起兵荆州？","听闻刘备分兵远涉山险而去，未易往返。何不差一军先截川口，断其归路，随后起兵荆州？",1,"","","孙权",306,},
        [5775] = {13201,2,1,"孙权",306,0,"此计大妙！","此计大妙！",1,"","","顾雍",321,},
        [5776] = {13201,3,2,"吴国太",322,0,"进此计者可斩之！我就这个一个女儿，嫁给了刘备，你们出兵是想害我女儿的命吗？","进此计者可斩之！我就这个一个女儿，嫁给了刘备，你们出兵是想害我女儿的命吗？",1,"","","孙权",306,},
        [5777] = {13201,4,2,"吴国太",322,0,"你掌握着父兄的基业，坐领八十一州还不够，现在还要顾小利而不念骨肉！","你掌握着父兄的基业，坐领八十一州还不够，现在还要顾小利而不念骨肉！",1,"","","孙权",306,},
        [5778] = {13201,5,1,"孙权",306,0,"母亲说的是，不敢有违。","母亲说的是，不敢有违。",1,"","","吴国太",322,},
        [5779] = {13201,6,2,"张昭",315,0,"主公，我有一计，不如这样这样……","主公，我有一计，不如这样这样……",1,"","","孙权",306,},
        [5780] = {13202,1,1,"周善",122,0,"夫人！国太好生病重，旦夕只是思念夫人。叫夫人带阿斗去见一面。倘若去迟了，恐不能相见。","夫人！国太好生病重，旦夕只是思念夫人。叫夫人带阿斗去见一面。倘若去迟了，恐不能相见。",1,"","","孙尚香",310,},
        [5781] = {13202,2,2,"孙尚香",310,0,"皇叔引兵远出，我现在要回去，需要先知会军师。","皇叔引兵远出，我现在要回去，需要先知会军师。",1,"","","周善",122,},
        [5782] = {13202,3,1,"周善",122,0,"如果军师说要等皇叔回话呢？现在时间不多了，我们已备好了船就等夫人上车出城了。","如果军师说要等皇叔回话呢？现在时间不多了，我们已备好了船就等夫人上车出城了。",1,"","","孙尚香",310,},
        [5783] = {13202,4,2,"孙尚香",310,0,"好吧，我们走。","好吧，我们走。",1,"","","周善",122,},
        [5784] = {13203,1,1,"赵云",201,0,"休要开船！容与夫人践行！任从夫人去，只有一句话拜禀。主母要去哪？为什么不知会军师？","休要开船！容与夫人践行！任从夫人去，只有一句话拜禀。主母要去哪？为什么不知会军师？",1,"","","",0,},
        [5785] = {13203,2,2,"孙尚香",310,0,"我母亲病在危笃，无暇报知。","我母亲病在危笃，无暇报知。",1,"","","赵云",201,},
        [5786] = {13203,3,1,"赵云",201,0,"主母探病可以，留下小主人。（抢过阿斗）","主母探病可以，留下小主人。（抢过阿斗）",1,"","","孙尚香",310,},
        [5787] = {13203,4,1,"张飞",206,0,"嫂嫂留下侄儿去！（斩了周善）","嫂嫂留下侄儿去！（斩了周善）",1,"","","孙尚香",310,},
        [5788] = {13203,5,2,"孙尚香",310,0,"你们竟如此无礼！","你们竟如此无礼！",1,"","","张飞",206,},
        [5789] = {13203,6,1,"张飞",206,0,"俺哥哥大汉皇叔，也不辱没嫂嫂，今日相别，若思哥哥恩义，早早回来。","俺哥哥大汉皇叔，也不辱没嫂嫂，今日相别，若思哥哥恩义，早早回来。",1,"","","孙尚香",310,},
        [5790] = {13301,1,1,"董昭",133,0,"自古以来，人臣未有如丞相之功者，丞相立下多少丰功伟绩，怎可与诸臣宰同列乎？","自古以来，人臣未有如丞相之功者，丞相立下多少丰功伟绩，怎可与诸臣宰同列乎？",1,"","","",0,},
        [5791] = {13301,2,1,"董昭",133,0,"以微臣之见，应受魏公之位，加九锡以彰功德。","以微臣之见，应受魏公之位，加九锡以彰功德。",1,"","","",0,},
        [5792] = {13301,3,2,"荀彧",102,0,"不可，丞相本兴义兵，匡扶汉室，秉忠贞之志，守谦退之节。君子爱人以德，不宜如此。","不可，丞相本兴义兵，匡扶汉室，秉忠贞之志，守谦退之节。君子爱人以德，不宜如此。",1,"","","董昭",133,},
        [5793] = {13301,4,1,"董昭",133,0,"岂可以一人而阻众望？","岂可以一人而阻众望？",1,"","","荀彧",102,},
        [5794] = {13301,5,2,"荀彧",102,0,"吾不想今日见此事！","吾不想今日见此事！",1,"","","董昭",133,},
        [5795] = {13302,1,1,"曹操",103,0,"我要兴兵下江南，让荀令君与我同行。","我要兴兵下江南，让荀令君与我同行。",1,"","","",0,},
        [5796] = {13302,2,2,"曹操侍卫",128,0,"荀令君有恙在身，说不能远行，只能在寿春修养。","荀令君有恙在身，说不能远行，只能在寿春修养。",1,"","","曹操",103,},
        [5797] = {13302,3,1,"曹操",103,0,"好吧，派人送这个食盒给他。","好吧，派人送这个食盒给他。",1,"","","曹操侍卫",128,},
        [5798] = {13303,1,2,"曹操侍卫",128,0,"荀令君，丞相送这个食盒给你。","荀令君，丞相送这个食盒给你。",1,"","","荀彧",102,},
        [5799] = {13303,2,1,"荀彧",102,0,"这……竟是空的！我明白了，丞相这是说我已无汉禄可食！（服毒自尽）","这……竟是空的！我明白了，丞相这是说我已无汉禄可食！（服毒自尽）",1,"","","",0,},
        [5800] = {13401,1,2,"曹操侍卫",128,0,"遥望沿江一带，旗幡无数，不知兵聚何处。","遥望沿江一带，旗幡无数，不知兵聚何处。",1,"","","",0,},
        [5801] = {13401,2,1,"曹操",103,0,"随我上山一看。遥望对方战船，各分队伍，依次排列。旗分五色，兵器鲜明。","随我上山一看。遥望对方战船，各分队伍，依次排列。旗分五色，兵器鲜明。",1,"","","",0,},
        [5802] = {13401,3,1,"曹操",103,0,"甚是英武！生子当如孙仲谋！若刘景升儿子，猪狗罢了！","甚是英武！生子当如孙仲谋！若刘景升儿子，猪狗罢了！",1,"","","",0,},
        [5803] = {13402,1,1,"孙权",306,0,"丞相坐镇中原，富贵已极，何故贪心不足，又来侵我江南？","丞相坐镇中原，富贵已极，何故贪心不足，又来侵我江南？",1,"","","曹操",103,},
        [5804] = {13402,2,2,"曹操",103,0,"你是臣下，不尊王室。我奉天子诏命，特来讨伐你。","你是臣下，不尊王室。我奉天子诏命，特来讨伐你。",1,"","","孙权",306,},
        [5805] = {13402,3,1,"孙权",306,0,"这不是笑话吗？天下谁不知是你挟天子以令诸侯？我才是要讨伐你以正国家。","这不是笑话吗？天下谁不知是你挟天子以令诸侯？我才是要讨伐你以正国家。",1,"","","曹操",103,},
        [5806] = {13403,1,1,"曹操",103,0,"我昨晚梦到天空中两轮红日。孙权并非等闲人物，红日之应，久后必为帝王。","我昨晚梦到天空中两轮红日。孙权并非等闲人物，红日之应，久后必为帝王。",1,"","","",0,},
        [5807] = {13403,2,1,"曹操",103,0,"战了数场，互相胜负，现在又春雨连绵，军士多在泥水之中，困苦异常，不如收兵？","战了数场，互相胜负，现在又春雨连绵，军士多在泥水之中，困苦异常，不如收兵？",1,"","","",0,},
        [5808] = {13403,3,2,"曹操随从",128,0,"报告，东吴有使送书到。","报告，东吴有使送书到。",1,"","","曹操",103,},
        [5809] = {13403,4,1,"曹操",103,0,"孙权信里说：孤与丞相，彼此皆汉朝臣宰……即日春水方生，公当速去。如其不然，复有赤壁之祸矣。","孙权信里说：孤与丞相，彼此皆汉朝臣宰……即日春水方生，公当速去。如其不然，复有赤壁之祸矣。",1,"","","",0,},
        [5810] = {13403,5,1,"曹操",103,0,"公宜自思焉。足下不死，孤不得安。哈哈哈！孙仲谋不欺我也！退兵！","公宜自思焉。足下不死，孤不得安。哈哈哈！孙仲谋不欺我也！退兵！",1,"","","",0,},
        [5811] = {13501,1,1,"刘备",202,0,"曹操战孙权，无论谁胜，都必取荆州，我该怎么办？","曹操战孙权，无论谁胜，都必取荆州，我该怎么办？",1,"","","",0,},
        [5812] = {13501,2,2,"主角",1,0,"大哥放心，有孔明军师在荆州，料想东吴不敢来犯。大哥可以去问刘璋要兵马钱粮，以抵御曹操。","大哥放心，有孔明军师在荆州，料想东吴不敢来犯。大哥可以去问刘璋要兵马钱粮，以抵御曹操。",1,"","","刘备",202,},
        [5813] = {13501,3,1,"刘备",202,0,"好，我这就修书一封，劳烦四弟送给刘璋。","好，我这就修书一封，劳烦四妹送给刘璋。",1,"","","主角",1,},
        [5814] = {13502,1,1,"杨怀",131,0,"刘备自从入川，广布恩德，以收民心，气意甚是不善。如今求军马钱粮，切不可给他。","刘备自从入川，广布恩德，以收民心，气意甚是不善。如今求军马钱粮，切不可给他。",1,"","","刘璋",246,},
        [5815] = {13502,2,2,"刘璋",246,0,"我和玄德有兄弟之情，怎么能不给他呢？","我和玄德有兄弟之情，怎么能不给他呢？",1,"","","杨怀",131,},
        [5816] = {13502,3,1,"杨怀",131,0,"刘备枭雄，让他久留在蜀地是纵虎入室。如今还要给他兵马钱粮，这不是让他如虎添翼吗？","刘备枭雄，让他久留在蜀地是纵虎入室。如今还要给他兵马钱粮，这不是让他如虎添翼吗？",1,"","","刘璋",246,},
        [5817] = {13502,4,2,"黄权",121,0,"主公可以给刘备一些老弱军，这样不伤兄弟之情，又不会为虎添翼。","主公可以给刘备一些老弱军，这样不伤兄弟之情，又不会为虎添翼。",1,"","","杨怀",131,},
        [5818] = {13503,1,1,"刘备",202,0,"岂有此理！我为刘璋御敌，费心劳力！他如今吝啬钱财只给我老弱残兵，让人如何给他效命？","岂有此理！我为刘璋御敌，费心劳力！他如今吝啬钱财只给我老弱残兵，让人如何给他效命？",1,"","","",0,},
        [5819] = {13503,2,2,"庞统",211,0,"主公向来以仁义为重，今日发怒，前请尽弃矣。","主公向来以仁义为重，今日发怒，前请尽弃矣。",1,"","","刘备",202,},
        [5820] = {13503,3,1,"刘备",202,0,"如此，那现在怎么办？","如此，那现在怎么办？",1,"","","庞统",211,},
        [5821] = {13503,4,2,"庞统",211,0,"我有三计，现在连夜抄小路入侵成都，此为上计；假意回荆州，杨怀、高沛听闻必来相送，","我有三计，现在连夜抄小路入侵成都，此为上计；假意回荆州，杨怀、高沛听闻必来相送，",1,"","","刘备",202,},
        [5822] = {13503,5,2,"庞统",211,0,"趁机杀之，夺了关隘，先取涪城再向成都，此为中计；退还白帝，连夜回荆州，徐图进取，此为下计。","趁机杀之，夺了关隘，先取涪城再向成都，此为中计；退还白帝，连夜回荆州，徐图进取，此为下计。",1,"","","刘备",202,},
        [5823] = {13503,6,1,"主角",1,0,"此三计，上计太促，下计太缓；中计不迟不疾，可以行之。","此三计，上计太促，下计太缓；中计不迟不疾，可以行之。",1,"","","刘备",202,},
        [5824] = {13601,1,1,"张松",245,0,"为什么刘皇叔不听我的，要回荆州呢！郁闷！","为什么刘皇叔不听我的，要回荆州呢！郁闷！",1,"","","",0,},
        [5825] = {13601,2,2,"张肃",122,0,"捡到张松给刘备的书信：……松当为内应，万勿自误！这！张松要造反！我要通知主公！","捡到张松给刘备的书信：……松当为内应，万勿自误！这！张松要造反！我要通知主公！",1,"","","",0,},
        [5826] = {13601,3,1,"刘璋",246,0,"什么！我待张松不薄，他竟串通外人想要谋反！来人，杀了张松全家！","什么！我待张松不薄，他竟串通外人想要谋反！来人，杀了张松全家！",1,"","","",0,},
        [5827] = {13601,4,2,"杨怀",131,0,"我与高沛二人计划藏利刃在身，借送行的名义杀了刘备，以绝后患。","我与高沛二人计划藏利刃在身，借送行的名义杀了刘备，以绝后患。",1,"","","",0,},
        [5828] = {13602,1,1,"庞统",211,0,"杨怀、高沛如果来了，要提防着点，如果不来，我们就直接起兵直取其关，不得迟缓。","杨怀、高沛如果来了，要提防着点，如果不来，我们就直接起兵直取其关，不得迟缓。",1,"","","刘备",202,},
        [5829] = {13602,2,2,"刘备",202,0,"风把帅字旗吹倒了，何解？","风把帅字旗吹倒了，何解？",1,"","","庞统",211,},
        [5830] = {13602,3,1,"庞统",211,0,"此警报也，杨怀、高沛二人必有行刺之意，宜好好防着点。","此警报也，杨怀、高沛二人必有行刺之意，宜好好防着点。",1,"","","刘备",202,},
        [5831] = {13602,4,2,"杨怀",131,0,"听说刘皇叔要回荆州了，我兄弟二人特来送行。","听说刘皇叔要回荆州了，我兄弟二人特来送行。",1,"","","刘备",202,},
        [5832] = {13602,5,1,"主角",1,0,"既然是送行，为何要带着利刃？我大哥为刘璋兢兢业业，你们跑来挑拨离间还要行刺，罪不容诛！","既然是送行，为何要带着利刃？我大哥为刘璋兢兢业业，你们跑来挑拨离间还要行刺，罪不容诛！",1,"","","杨怀",131,},
        [5833] = {13603,1,1,"主角",1,0,"其余将士们听好了！此二人离间皇叔兄弟，又藏利刃行刺，因此被诛。尔等无罪，不必惊疑。","其余将士们听好了！此二人离间皇叔兄弟，又藏利刃行刺，因此被诛。尔等无罪，不必惊疑。",1,"","","",0,},
        [5834] = {13603,2,2,"庞统",211,0,"如今还要靠大家引路，带我大军入关。各有重赏！","如今还要靠大家引路，带我大军入关。各有重赏！",1,"","","",0,},
        [5835] = {13701,1,1,"刘备",202,0,"我们如今得了涪水关，接下来要进取雒城。泠苞、邓贤领二万军扎下两个大寨，谁去建头功？","我们如今得了涪水关，接下来要进取雒城。泠苞、邓贤领二万军扎下两个大寨，谁去建头功？",1,"","","",0,},
        [5836] = {13701,2,2,"黄忠",208,0,"老夫愿往。","老夫愿往。",1,"","","刘备",202,},
        [5837] = {13701,3,1,"魏延",210,0,"老将军年纪高大，如何去得？小将不才愿往。","老将军年纪高大，如何去得？小将不才愿往。",1,"","","刘备",202,},
        [5838] = {13701,4,2,"庞统",211,0,"二位不用争，可以各带一路兵马，各领一寨，谁先抢到一寨便是头功。","二位不用争，可以各带一路兵马，各领一寨，谁先抢到一寨便是头功。",1,"","","魏延",210,},
        [5839] = {13702,1,1,"魏延",210,0,"黄忠将军还没出发，我不如提前出发，可以抢到头功。","黄忠将军还没出发，我不如提前出发，可以抢到头功。",1,"","","",0,},
        [5840] = {13702,2,1,"魏延",210,0,"既然已经出动了，为何不把两个寨子都取了，可以立两个功。","既然已经出动了，为何不把两个寨子都取了，可以立两个功。",1,"","","",0,},
        [5841] = {13702,3,2,"邓贤",135,0,"魏延快下马受降！","魏延快下马受降！",1,"","","魏延",210,},
        [5842] = {13702,4,1,"魏延",210,0,"不好，快撤！","不好，快撤！",1,"","","",0,},
        [5843] = {13703,1,1,"黄忠",208,0,"老将黄忠在此！邓贤受死！","老将黄忠在此！邓贤受死！",1,"","","",0,},
        [5844] = {13703,2,2,"主角",1,0,"寨子吾已夺下，你还往哪里逃？","寨子吾已夺下，你还往哪里逃？",1,"","","",0,},
        [5845] = {13801,1,1,"刘备",202,0,"军师从荆州送信来，说夜观星象，有不吉之事要发生，主将帅身上凶多吉少，切宜谨慎。","军师从荆州送信来，说夜观星象，有不吉之事要发生，主将帅身上凶多吉少，切宜谨慎。",1,"","","",0,},
        [5846] = {13801,2,1,"刘备",202,0,"既然这样，不如我们先回去吧。","既然这样，不如我们先回去吧。",1,"","","",0,},
        [5847] = {13801,3,2,"庞统",211,0,"观星之术，我也略懂。大凶之相已应验在泠苞身上了，主公不必忧心，可急进兵。","观星之术，我也略懂。大凶之相已应验在泠苞身上了，主公不必忧心，可急进兵。",1,"","","刘备",202,},
        [5848] = {13801,4,1,"刘备",202,0,"好，既然如此，我们兵分两路去取雒城。","好，既然如此，我们兵分两路去取雒城。",1,"","","庞统",211,},
        [5849] = {13802,1,1,"刘备",202,0,"这劣马，竟将军师掀了下来！军师何故乘此劣马？","这劣马，竟将军师掀了下来！军师何故乘此劣马？",1,"","","庞统",211,},
        [5850] = {13802,2,2,"庞统",211,0,"此马乘久，都不曾如此。","此马乘久，都不曾如此。",1,"","","刘备",202,},
        [5851] = {13802,3,1,"刘备",202,0,"临阵眼生，误人性命，吾所骑的白马，性极驯熟，军师可骑，万无一失。","临阵眼生，误人性命，吾所骑的白马，性极驯熟，军师可骑，万无一失。",1,"","","庞统",211,},
        [5852] = {13802,4,2,"庞统",211,0,"深感主公厚恩，万死不能报也。","深感主公厚恩，万死不能报也。",1,"","","刘备",202,},
        [5853] = {13803,1,1,"庞统",211,0,"此处是何地？","此处是何地？",1,"","","",0,},
        [5854] = {13803,2,1,"庞统",211,0,"此地名为落凤坡，我道号凤雏，这里不利于我。还是赶快撤退。","此地名为落凤坡，我道号凤雏，这里不利于我。还是赶快撤退。",1,"","","",0,},
        [5855] = {13803,3,2,"张任",148,0,"骑白马者必是刘备，他来了，弓箭手！发射！","骑白马者必是刘备，他来了，弓箭手！发射！",1,"","","",0,},
        [5856] = {13803,4,1,"庞统",211,0,"不好！有埋伏！快撤！","不好！有埋伏！快撤！",1,"","","",0,},
        [5857] = {13901,1,1,"诸葛亮",203,0,"哀哉！痛哉！西方星坠，庞士元命必休矣！","哀哉！痛哉！西方星坠，庞士元命必休矣！",1,"","","",0,},
        [5858] = {13901,2,1,"诸葛亮",203,0,"我前者算今年罡星在西方，不利于军师；天狗犯于军师，太白临于雒城，已拜书主公，教谨防之。","我前者算今年罡星在西方，不利于军师；天狗犯于军师，太白临于雒城，已拜书主公，教谨防之。",1,"","","",0,},
        [5859] = {13901,3,1,"诸葛亮",203,0,"今吾主丧一臂矣！马上就会收到消息了。","今吾主丧一臂矣！马上就会收到消息了。",1,"","","",0,},
        [5860] = {13901,4,2,"关平",218,0,"报告诸葛军师，庞军师被张任在落凤坡前箭射身故。","报告诸葛军师，庞军师被张任在落凤坡前箭射身故。",1,"","","诸葛亮",203,},
        [5861] = {13902,1,1,"诸葛亮",203,0,"既然主公现在涪关进退两难，亮不得不去。","既然主公现在涪关进退两难，亮不得不去。",1,"","","",0,},
        [5862] = {13902,2,2,"关羽",205,0,"军师去，谁保荆州？荆州乃重地，干系非轻。","军师去，谁保荆州？荆州乃重地，干系非轻。",1,"","","诸葛亮",203,},
        [5863] = {13902,3,1,"诸葛亮",203,0,"主公的书中虽不名言其人，吾已知用意了。主公叫我自量才委用，云长公方能担此重任。","主公的书中虽不名言其人，吾已知用意了。主公叫我自量才委用，云长公方能担此重任。",1,"","","关羽",205,},
        [5864] = {13902,4,2,"关羽",205,0,"大丈夫既领重任，至死方休。","大丈夫既领重任，至死方休。",1,"","","诸葛亮",203,},
        [5865] = {13903,1,1,"诸葛亮",203,0,"如果曹操带兵来到，当如何应对？","如果曹操带兵来到，当如何应对？",1,"","","关羽",205,},
        [5866] = {13903,2,2,"关羽",205,0,"以力拒之。","以力拒之。",1,"","","诸葛亮",203,},
        [5867] = {13903,3,1,"诸葛亮",203,0,"如果曹操，孙权一齐起兵来，如之奈何？","如果曹操，孙权一齐起兵来，如之奈何？",1,"","","关羽",205,},
        [5868] = {13903,4,2,"关羽",205,0,"分兵拒之。","分兵拒之。",1,"","","诸葛亮",203,},
        [5869] = {13903,5,1,"诸葛亮",203,0,"若如此，荆州危矣。吾有八个字，将军牢记，可保荆州。","若如此，荆州危矣。吾有八个字，将军牢记，可保荆州。",1,"","","关羽",205,},
        [5870] = {13903,6,2,"关羽",205,0,"军师请讲，军师之言，当铭肺腑。","军师请讲，军师之言，当铭肺腑。",1,"","","诸葛亮",203,},
        [5871] = {13903,7,1,"诸葛亮",203,0,"北拒曹操，东和孙权。","北拒曹操，东和孙权。",1,"","","关羽",205,},
        [5872] = {14001,1,1,"诸葛亮",203,0,"西川豪杰甚多，不可轻敌。所到之处，并宜存恤，勿得恣逞鞭挞士卒。望将军早会雒城，不可有误。","西川豪杰甚多，不可轻敌。所到之处，并宜存恤，勿得恣逞鞭挞士卒。望将军早会雒城，不可有误。",1,"","","张飞",206,},
        [5873] = {14001,2,2,"张飞",206,0,"听军师的，但投降者秋毫无犯。","听军师的，但投降者秋毫无犯。",1,"","","诸葛亮",203,},
        [5874] = {14002,1,1,"严颜军士",129,0,"张飞在当阳长坂，一声喝退曹兵百万之众，不可轻敌。如今我们坚守不出，他们无粮，自会退去。","张飞在当阳长坂，一声喝退曹兵百万之众，不可轻敌。如今我们坚守不出，他们无粮，自会退去。",1,"","","",0,},
        [5875] = {14002,2,2,"张飞随从",142,0,"我家将军说了，教你早早来降，饶你满城百姓性命，如果不归顺就踏平了你们这，老幼不留！","我家将军说了，教你早早来降，饶你满城百姓性命，如果不归顺就踏平了你们这，老幼不留！",1,"","","严颜",122,},
        [5876] = {14002,3,1,"严颜",122,0,"岂有此理！匹夫怎敢如此无礼！来人割了这走狗的耳鼻！","岂有此理！匹夫怎敢如此无礼！来人割了这走狗的耳鼻！",1,"","","",0,},
        [5877] = {14003,1,1,"张飞",206,0,"严颜这老匹夫！气死我了！","严颜这老匹夫！气死我了！",1,"","","",0,},
        [5878] = {14003,2,2,"张飞随从",142,0,"将军不须心焦，近几日军士砍柴打探到一条小路，可以偷过巴郡。","将军不须心焦，近几日军士砍柴打探到一条小路，可以偷过巴郡。",1,"","","张飞",206,},
        [5879] = {14003,3,1,"张飞",206,0,"事不宜迟，只今二更造反，趁三更明月，悄悄而行。我在前面开路，你们依次在后面跟着。","事不宜迟，只今二更造反，趁三更明月，悄悄而行。我在前面开路，你们依次在后面跟着。",1,"","","",0,},
        [5880] = {14003,4,2,"严颜",122,0,"果然张飞忍耐不得，中我计也。前面那个就是张飞！我们过去擒了他！","果然张飞忍耐不得，中我计也。前面那个就是张飞！我们过去擒了他！",1,"","","",0,},
        [5881] = {14003,5,1,"张飞",206,0,"老贼休走！我在这呢！前面那是假的！","老贼休走！我在这呢！前面那是假的！",1,"","","",0,},
        [5882] = {14003,6,2,"严颜",122,0,"糟糕！中计了！","糟糕！中计了！",1,"","","",0,},
        [5883] = {14004,1,1,"张飞",206,0,"大将到此，为何不降，而敢拒敌？","大将到此，为何不降，而敢拒敌？",1,"","","严颜",122,},
        [5884] = {14004,2,2,"严颜",122,0,"汝等无义！侵我州郡！只有断头将军，没有投降将军！","汝等无义！侵我州郡！只有断头将军，没有投降将军！",1,"","","张飞",206,},
        [5885] = {14004,3,2,"严颜",122,0,"贼匹夫，要砍头你就砍！","贼匹夫，要砍头你就砍！",1,"","","张飞",206,},
        [5886] = {14004,4,1,"张飞",206,0,"（亲自给严颜松绑，穿好衣服）刚才言语冒犯，请勿责怪。吾素知老将军乃豪杰之士也。","（亲自给严颜松绑，穿好衣服）刚才言语冒犯，请勿责怪。吾素知老将军乃豪杰之士也。",1,"","","严颜",122,},
        [5887] = {14004,5,2,"严颜",122,0,"（被张飞恩义感动，投降）败军之将，荷蒙厚恩，无可以报，愿施犬马之劳。","（被张飞恩义感动，投降）败军之将，荷蒙厚恩，无可以报，愿施犬马之劳。",1,"","","张飞",206,},
    }
}

