-- metatable
local mt = { 
    __index = function(t, k) 
        assert(__key_map[k], "cannot find " .. k .. " in story_chat")
        return t._raw[__key_map[k]]
    end
}

-- 
function story_chat.length()
    return #story_chat._data
end

-- 
function story_chat.hasKey(k)
    if __key_map[k] == nil then
        return false
    else
        return true
    end
end

--
function story_chat.indexOf(index)
    if index == nil or not story_chat._data[index] then
        return nil
    end

    return setmetatable({_raw = story_chat._data[index]}, mt)
end

--
function story_chat.get(story_touch,step)
    
    local k = story_touch .. '_' .. step
    return story_chat.indexOf(__index_story_touch_step[k])
        
end

--
function story_chat.set(story_touch,step, tkey, nvalue)
    local record = story_chat.get(story_touch,step)
    if record then
        local keyIndex = __key_map[tkey]
        if keyIndex then
            record._raw[keyIndex] = nvalue
        end
    end
end

--
function story_chat.index()
    return __index_story_touch_step
end

return story_chat