-- metatable
local mt = { 
    __index = function(t, k) 
        assert(__key_map[k], "cannot find " .. k .. " in story_stage")
        return t._raw[__key_map[k]]
    end
}

-- 
function story_stage.length()
    return #story_stage._data
end

-- 
function story_stage.hasKey(k)
    if __key_map[k] == nil then
        return false
    else
        return true
    end
end

--
function story_stage.indexOf(index)
    if index == nil or not story_stage._data[index] then
        return nil
    end

    return setmetatable({_raw = story_stage._data[index]}, mt)
end

--
function story_stage.get(id)
    
    return story_stage.indexOf(__index_id[id])
        
end

--
function story_stage.set(id, tkey, nvalue)
    local record = story_stage.get(id)
    if record then
        local keyIndex = __key_map[tkey]
        if keyIndex then
            record._raw[keyIndex] = nvalue
        end
    end
end

--
function story_stage.index()
    return __index_id
end

return story_stage